UPDATE `locales_npc_text` SET `Text0_0_loc4`='我们简陋的旅馆馆位於蓝色守望北边;查伦将好好的照顾你。' WHERE `entry`=10074;
UPDATE `locales_npc_text` SET `Text0_0_loc4`='对人和善的朵拉斯是一名高尚的妇女，和一名英勇的圣光防卫者。她在旅馆里提供训练。' WHERE `entry`=10083;
