UPDATE `locales_npc_text` SET `Text0_0_loc4`='蓝色守望这里没有裁缝师。但是，你可以去海滩上的营地找找看 - 他们有许多从海外来经商的训练师。' WHERE `entry`=10099;
UPDATE `locales_npc_text` SET `Text0_0_loc4`='你要什么？！我听说英雄谷里有一个古怪的德莱尼女人。或许她知道。' WHERE `entry`=10101;
