UPDATE `locales_npc_text` SET `Text0_0_loc4`='在不同的时间和地点我可能只是一个没经验的小兵。但现在我是一位经验丰富的战士，为了那鲁的荣誉而活，为了那鲁的荣誉而死。$B$B我们之中的十位进入法力熔炉艾拉。其他人致力於对付燃烧军团。' WHERE `entry`=10209;
UPDATE `locales_npc_text` SET `Text0_0_loc4`='是时候面对索克雷萨了，$N。你准备好了吗？' WHERE `entry`=10210;
