UPDATE `locales_npc_text` SET `Text0_0_loc4`='欢迎来到秘境领地，旅游者。我要警告你谨慎处理那个地精，休利。我相信我之前从没听过任何人使用这个字「哥们」作为标点符号。' WHERE `entry`=10233;
UPDATE `locales_npc_text` SET `Text0_0_loc4`='由於艾兰里堡垒四周都被敌人包围了，是时候让我们去与他们战斗了。' WHERE `entry`=10234;
