UPDATE `locales_npc_text` SET `Text0_0_loc4`='很高兴又见到你了，$C$N。朵娜一直探问你的消息。' WHERE `entry`=10742;
UPDATE `locales_npc_text` SET `Text0_0_loc4`='外域的冲突比我看过的所有都还要致命。而最不幸的牺牲就是那些被遗留下来的孩童。$B$B现在，随着来自司凯堤斯的阿拉卡这股反抗撒塔斯的威胁，将会有更多的孤儿需要照顾。这些孩童通常都活在没有一个光明未来的绝望之中。' WHERE `entry`=10744;
