UPDATE `locales_npc_text` SET `Text0_0_loc4`='阿尔萨斯曾是万民敬仰的人类王子。他所率领的诺森德远征军遭遇了可怕的命运。$B$B我的哥哥萨萨里安就是军中的一员。' WHERE `entry`=12534;
UPDATE `locales_npc_text` SET `Text0_0_loc4`='是，我们不过是一群手无寸铁的矿工、农夫和工匠。可是情况危急时，我们也会赤手空拳地抗争到底，像士兵那样守卫我们的家园。' WHERE `entry`=12538;
