UPDATE `locales_quest` SET `title_loc4`='沉思圣契',`offerrewardtext_loc4`='拿着你的东西，愚蠢的$R。赶快从我面前消失!$B$B' WHERE `entry`=4463;
UPDATE `locales_quest` SET `RequestItemsText_loc4`='拿出原料来，渺小的$R。$B$B另外我还需要三十枚金币，这是我帮你制作东西的报酬。' WHERE `entry`=4463;
UPDATE `locales_quest` SET `title_loc4`='被腐化的轻歌花',`offerrewardtext_loc4`='你将一些塞纳里奥植物药膏涂抹在这株轻歌花上。它立即摆脱了刚才奄奄一息的状态，重新变成了一棵活力十足的健康植物。$B$B' WHERE `entry`=4464;
