UPDATE `locales_quest` SET `title_loc4`='被腐化的轻歌花',`offerrewardtext_loc4`='你将一些塞纳里奥植物药膏涂抹在这株轻歌花上。它立即摆脱了刚才奄奄一息的状态，重新变成了一棵活力十足的健康植物。$B$B' WHERE `entry`=4465;
