UPDATE `locales_quest` SET `title_loc4`='捐献毛料',`offerrewardtext_loc4`='喔，毛料。我会确保所有人都知道你的慷慨捐赠。$B$B' WHERE `entry`=7820;
UPDATE `locales_quest` SET `RequestItemsText_loc4`='我们现在接受毛料的捐赠。只要捐赠60块毛料，暴风城就会对你的慷慨行为表示感谢。我们只需要你捐赠60块毛料就够了，更多的毛料可以从其它志愿者的捐赠获得。$B$B如果你有60块毛料并愿捐献的话，那现在就可以给我了。' WHERE `entry`=7820;
