UPDATE `locales_quest` SET `title_loc4`='战歌先遣骑 <NYI> <TXT>',`details_loc4`='我是科珀斯，您是个可怜的标本匠，是的！您会做得很好，在这个小镇上已经人满为患，找到空墓的难度越来越大。您能帮我摆脱一些投资我坟墓的肮脏僵尸吗，5个足以确保我有一个休息的空间。',`objectives_loc4`='杀死5个可怜的僵尸' WHERE `entry`=8001;
