UPDATE `locales_quest` SET `title_loc4`='碎石长者',`offerrewardtext_loc4`='战争的历史是传奇茁壮的种子。你正处於英雄的时代，年轻人。愿你的英勇传奇深植人心、源远流长。$B$B祝你平安， $N，这是你应有的奖赏……$B$B' WHERE `entry`=8635;
