UPDATE `locales_quest` SET `title_loc4`='暗淡的死灵水晶',`details_loc4`='在死去的活尸身上，你找到了一个散发着死灵能量的水晶。',`objectives_loc4`='将黯淡的亡域水晶带给幽暗城庭院的卢卡中尉。' WHERE `entry`=9310;
