UPDATE `locales_npc_text` SET `Text0_0_loc2`='Bonjour, $C. Je suis un officier du mérite au service d\'Orgrimmar. Ma tâche est d\'aider les aventuriers qui ont reçu des chevalières de mérite de la Horde.$B$BVous pouvez m\'en remettre différentes quantités, mais le plus intéressant pour vous est de me les donner par dix. En échange, je noterai vos exploits dans mes registres, et vos services seront reconnus par Orgrimmar.',`Text0_1_loc2`='Bonjour, $C. Je suis un officier du mérite au service d\'Orgrimmar. Ma tâche est d\'aider les aventuriers qui ont reçu des chevalières de mérite de la Horde.$B$BVous pouvez m\'en remettre différentes quantités, mais le plus intéressant pour vous est de me les donner par dix. En échange, je noterai vos exploits dans mes registres, et vos services seront reconnus par Orgrimmar.' WHERE `entry`=8131;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='Bonjour, $C. Je suis un officier du mérite au service des Pitons-du-Tonnerre. Ma tâche est d\'aider les aventuriers qui ont reçu des chevalières de mérite de la Horde.$B$BVous pouvez m\'en remettre différentes quantités, mais le plus intéressant pour vous est de me les donner par dix. En échange, je noterai vos exploits dans mes registres, et vos services seront reconnus aux Pitons-du-Tonnerre.',`Text0_1_loc2`='Bonjour, $C. Je suis un officier du mérite au service des Pitons-du-Tonnerre. Ma tâche est d\'aider les aventuriers qui ont reçu des chevalières de mérite de la Horde.$B$BVous pouvez m\'en remettre différentes quantités, mais le plus intéressant pour vous est de me les donner par dix. En échange, je noterai vos exploits dans mes registres, et vos services seront reconnus aux Pitons-du-Tonnerre.' WHERE `entry`=8132;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='Bonjour, $C. Je suis un officier du mérite au service de Fossoyeuse. Ma tâche est d\'aider les aventuriers qui ont reçu des chevalières de mérite de la Horde.$B$BVous pouvez m\'en remettre différentes quantités, mais le plus intéressant pour vous est de me les donner par dix. En échange, je noterai vos exploits dans mes registres, et vos services seront reconnus par Fossoyeuse.' WHERE `entry`=8133;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='Le feu d\'artifice commencera à 18 heures, depuis le plus haut piton !',`Text1_0_loc2`='Les barils débordent de bière forte ! Buvez, mon $Gami:amie;, et faites la fête !' WHERE `entry`=8134;
UPDATE `locales_npc_text` SET `Text2_0_loc2`='J\'ai entendu dire qu\'après le feu d\'artifice, des coupes seront apportées sur les tables. Nous pourrons boire à la santé de tous nos amis !',`Text2_1_loc2`='J\'ai entendu dire qu\'après le feu d\'artifice, des coupes seront apportées sur les tables. Nous pourrons boire à la santé de tous nos amis !',`Text3_0_loc2`='Salutations, $C ! Bienvenue à la fête !' WHERE `entry`=8134;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='Ha ! Ce tauren veut une bise !' WHERE `entry`=8138;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='Joyeux Voile d\'hiver, mon $Gami:amie; !',`Text1_0_loc2`='Joyeuses fêtes, et que la paix soit sur vous !' WHERE `entry`=8139;
UPDATE `locales_npc_text` SET `Text2_0_loc2`='Je vous souhaite de bonnes fêtes, mon $Gami:amie; !',`Text2_1_loc2`='L\'endroit grouille de partout… Il va falloir sortir d\'ici avec les reliques le plus vite possible.',`Text3_0_loc2`='Ah, du gui…' WHERE `entry`=8139;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='Quel temps superbe, pas vrai ?',`Text1_0_loc2`='Comment se fait-il qu\'on ne trouve jamais un gyrochromatome quand on en a besoin ?' WHERE `entry`=8143;
UPDATE `locales_npc_text` SET `Text2_0_loc2`='C\'est moi qui rirai la dernière… ils verront ! Oh, salut $C, je ne vous avais pas $Gvu:vue;.',`Text2_1_loc2`='L\'endroit grouille de partout… Il va falloir sortir d\'ici avec les reliques le plus vite possible.',`Text3_0_loc2`='L\'invention, $Gmon cher:ma chère; se compose de deux pour cent d\'inspiration en bouteille, de quatre pour cent d\'exposition au soleil, de six pour cent de chances d\'un gros orage en milieu d\'après-midi, et de quatre-vingt-treize pour cent de Sarcafroisse.$B$BHeu… dites, $C, est-ce que le total fait bien cent pour cent ? J\'ai laissé mes notes quelque part…' WHERE `entry`=8143;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='Il faudra qu\'on trouve ce que les qiraji donnent à manger à ces monstres ! Vous avez vu leur taille ?' WHERE `entry`=8145;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='Étudier et comprendre le Colosse nous donnera un avantage sur les Qiraji.' WHERE `entry`=8146;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='Les secrets de la puissance du Colosse pourraient faire faire de grands progrès à la science !' WHERE `entry`=8147;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='L\'Ancien Né-au-Tombeau passe son temps près de Brill, dans les clairières de Tirisfal.' WHERE `entry`=8148;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='L\'Ancienne Heurtelune se trouve dans la Scholomance des Maleterres de l\'Ouest.' WHERE `entry`=8149;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='L\'Ancien Sombre-Noyau se trouve à Fossoyeuse.' WHERE `entry`=8150;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='L\'Ancien Obsidien erre près du Sépulcre, dans la forêt des Pins-Argentés.',`Text0_1_loc2`='L\'Ancien Obsidien erre près du Sépulcre, dans la forêt des Pins-Argentés.' WHERE `entry`=8152;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='Vous pouvez trouver l\'Ancien Couronne-de-neige dans la chapelle de l\'Espoir de lumière des Maleterres de l\'est.' WHERE `entry`=8153;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='L\'Ancien Haut-pic veille sur les Hinterlands depuis une colline située en leur centre.' WHERE `entry`=8154;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='L\'Ancien Tracevent réside dans la Tour de garde de la couronne des Maleterres de l\'est.' WHERE `entry`=8155;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='L\'Ancien Murmeloin erre dans les rues de Stratholme.' WHERE `entry`=8156;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='L\'Ancien Baguefer vit dans la caverne de Noircharbon de la gorge des Vents brûlants.' WHERE `entry`=8157;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='L\'Ancien Puits-Doré passe son temps au village de Kharanos, en Dun Morogh.',`Text0_1_loc2`='L\'Ancien Puits-Doré passe son temps au village de Kharanos, en Dun Morogh.' WHERE `entry`=8158;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='L\'Ancien Barbe-de-Bronze réside dans le domaine ancestral des Barbe-de-Bronze, Forgefer.' WHERE `entry`=8162;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='L\'Ancien Veine-argent se trouve près de Thelsamar, au Loch Modan.',`Text0_1_loc2`='L\'Ancien Veine-argent se trouve près de Thelsamar, au Loch Modan.' WHERE `entry`=8163;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='Qu\'Élune soit avec vous, $C.',`Text1_0_loc2`='La grande prêtresse ne peut pas quitter Darnassus. Fandral est d\'une humeur épouvantable.' WHERE `entry`=8164;
UPDATE `locales_npc_text` SET `Text2_0_loc2`='Tyrande prie pour vous. Mère Lune nous bénit tous.',`Text2_1_loc2`='L\'endroit grouille de partout… Il va falloir sortir d\'ici avec les reliques le plus vite possible.' WHERE `entry`=8164;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='L\'Ancien Gouffre-du-Matin passe son temps au Cercle de la loi, dans les profondeurs de Rochenoire.',`Text0_1_loc2`='L\'Ancien Gouffre-du-Matin passe son temps au Cercle de la loi, dans les profondeurs de Rochenoire.' WHERE `entry`=8166;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='L\'Ancien Fort-de-pierre s\'est installé dans les oubliettes du pic Rochenoire.',`Text0_1_loc2`='L\'Ancien Fort-de-pierre s\'est installé dans les oubliettes du pic Rochenoire.' WHERE `entry`=8167;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='L\'ancien Pierre-Grondante regarde passer les années dans une caverne au sommet du rocher des Cognepeurs, dans les steppes Ardentes.' WHERE `entry`=8168;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='Aux dernières nouvelles, l\'Ancienne Chantétoile se trouvait au Temple englouti, dans le marais des Chagrins.' WHERE `entry`=8169;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='L\'Ancien Front-de-Tempête se trouve probablement au village de Comté-de-l\'Or, dans la forêt d\'Elwynn.' WHERE `entry`=8170;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='Sabot-d\'Hiver ? Il paraît qu\'il s\'est installé à Baie-du-Butin.' WHERE `entry`=8171;
UPDATE `locales_npc_text` SET `Text0_0_loc2`='L\'ancienne Chasse-le-Ciel observe la marche de l\'Ouest du haut de la colline des Sentinelles.' WHERE `entry`=8172;
