UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> MANUEL DE L\'UTILISATEUR DE CRYSTAL PYLON </H1><BR/><P align = "center"> "Un guide pour la collecte et l\'utilisation des cristaux de puissance du cratère Un\'Goro" </P><BR/><BR/><P align = "left"> Chapitre 1: Le pylône nord </P><BR/><P align = "left"> Chapitre 2: Le pylône oriental </P><BR/><P align = "left"> Chapitre 3: Le pylône ouest </P><BR/></BODY></HTML>' WHERE `entry`=1491;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> MANUEL DE L\'UTILISATEUR DE CRYSTAL PYLON </H1><BR/><P align = "center"> "Un guide pour la collecte et l\'utilisation des cristaux de puissance du cratère Un\'Goro" </P><BR/><BR/><P align = "left"> Chapitre 1: Le pylône nord </P><BR/><P align = "left"> Chapitre 2: Le pylône oriental </P><BR/><P align = "left"> Chapitre 3: Le pylône ouest </P><BR/></BODY></HTML>' WHERE `entry`=1510;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> Chapitre 1: LE PYLON DU NORD </H1><BR/><BR/><IMG src = "Interface  Pictures  11482_crystals_mini_north" align = "left" / <P align = "right"> Le pylône <BR/> Nord accepte les cristaux <BR/> jaunes, rouges, <BR/> et verts <BR/>. </P><BR/><BR/><BR /><BR/><P align = "left"> Le pylône se concentre sur l\'énergie pouvant être créée à l\'aide de cristaux jaunes comme source principale d\'énergie. </P></BODY></HTML>' WHERE `entry`=1511;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> Combinaisons de cristaux du nord </H1><BR/><P align = "left"> Il existe deux effets possibles: un qui causera des dégâts et un qui annulera dommage. </P><BR/><P align = "left"> CHARGE DE CRISTAUX: Requiert des cristaux rouges et jaunes.<BR/> Ceci produira une grande explosion. </P><BR/><P align = "left"> RESTAURATION DE CRISTAUX: Requiert des cristaux jaunes et verts. <BR/> Cela vous donnera une aura de guérison. </P></BODY></HTML>' WHERE `entry`=1512;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> DIAGRAMME DE TABLE DE PYLON DU NORD </H1><BR/><IMG src = "Interface  Pictures  11482_crystals_north" /></BODY></HTML>' WHERE `entry`=1513;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> Chapitre 2: LE PYLON ORIENTAL </H1><BR/><BR/><IMG src = "Interface  Pictures  11482_crystals_mini_east" align = "left" /><P align = "right"> Le pylône <BR/> oriental accepte les cristaux <BR/> bleus, jaunes, <BR/> et verts <BR/>. </P><BR/><BR/><BR /><BR/><P align = "left"> Le pylône se concentre sur l\'énergie pouvant être générée à l\'aide de cristaux bleus comme source principale d\'énergie. </P></BODY></HTML>' WHERE `entry`=1514;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> Combinaisons de cristaux orientaux </H1><BR/><P align = "left"> Il existe deux effets possibles: un effet bonus sur l\'esprit et un effet bouclier dommageable. </P><BR/><P align = "left"> FORCE CRISTAL: Requiert des cristaux bleus et verts. <BR/> Cela vous donnera une augmentation d\'esprit. </P><BR/><P align = "left"> CRYSTAL SPIRE: Requiert des cristaux bleus et jaunes. <BR/> Ceci créera un bouclier causant des dégâts aux attaquants. </P></BODY></HTML>' WHERE `entry`=1515;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> DIAGRAMME DE LA TABLE DE PYLON ORIENTAL </H1><BR/><IMG src = "Interface  Pictures  11482_crystals_east" /></BODY></HTML>' WHERE `entry`=1516;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> DIAGRAMME DE LA TABLE DE PYLON ORIENTAL </H1><BR/><IMG src = "Interface  Pictures  11482_crystals_east" /></BODY></HTML>' WHERE `entry`=1530;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> Chapitre 3: LE PYLON OUEST </H1><BR/><BR/><IMG src = "Interface  Pictures  11482_crystals_mini_west" align = "left" /><P align = "right"> Le pylône <BR/> occidental accepte les cristaux <BR/> rouges, verts, <BR/> et <BR/> bleus. </P><BR/><BR/><BR /><BR/><P align = "left"> Le pylône se concentre sur l\'énergie pouvant être créée à l\'aide de cristaux rouges comme principale source d\'énergie. </P></BODY></HTML>' WHERE `entry`=1531;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> Combinaisons de cristaux occidentaux </H1><BR/><P align = "left"> Il existe deux effets possibles: un qui augmente votre protection contre les dommages et l\'autre affaiblit les défenses de l\'ennemi. </P><BR/><P align = "left"> CRISTAL WARD: Requiert des cristaux rouges et verts. <BR/> Cela vous augmentera l\'armure. </P><BR /><P align = "left"> RENDEMENT EN CRISTAUX: Requiert des cristaux bleus et rouges. <BR/> Ceci peut abaisser l\'armure d\'une cible spécifique. </P></BODY></HTML>' WHERE `entry`=1532;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> DIAGRAMME DE LA TABLE DE PYLON OUEST </H1><BR/><IMG src = "Interface  Pictures  11482_crystals_west" /></BODY></HTML>' WHERE `entry`=1533;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> DIAGRAMME DE LA TABLE DE PYLON OUEST </H1><BR/><IMG src = "Interface  Pictures  11482_crystals_west" /></BODY></HTML>' WHERE `entry`=1550;
UPDATE `locales_page_text` SET `text_loc2`='Salutations et salutations, aventuriers!$B$BI, Krinkle Goodsteel, ont besoin de personnes prêtes à prendre des risques pour mener à bien certaines de mes tâches les plus ardues. En raison de mes nombreuses tâches et de la charge de travail que j\'ai réussi à assumer, je m\'assure que quiconque aura le courage de compléter cette liste d\'objets et de me les retourner intacts sera payé généreusement.$B$BLa liste est contenue dans les pages suivantes ...' WHERE `entry`=1551;
UPDATE `locales_page_text` SET `text_loc2`='Jambes en cristal massif -$B$BLes araignées Glassweb de Searing Gorge sont fabriquées dans un incroyable matériau ressemblant à du cristal. Leurs jambes, si elles ne sont pas endommagées lorsqu\'elles sont tuées, constituent un merveilleux chef d\'orchestre dans les expériences d\'ingénierie de mes compagnons.$B$BI aura besoin d\'un certain nombre de tests. Les araignées ayant huit jambes possibles ne devraient pas poser de problème à un guerrier ou à un mage pratiquant.' WHERE `entry`=1552;
UPDATE `locales_page_text` SET `text_loc2`='Un paquet en retard -$B$BI a embauché quelqu\'un que je pensais être un pilote fiable pour m\'apporter un paquet de Theramore. J\'avais apparemment tort.Et même si le pilote vivait pour raconter l\'histoire, il a laissé tout son zepplin et toute sa cargaison derrière lui pour sauver son propre cou. $B$BEh bien, j\'ai besoin de ce colis. D\'après ce que Beezil m\'a dit, il s\'est écrasé juste à l\'ouest de la baie, dans Dustwallow Marsh. Il est tombé juste après avoir traversé les collines au bord de l\'eau.$B$BHmmm ... ne m\'a jamais dit ce qui l\'avait fait planter ...' WHERE `entry`=1553;
UPDATE `locales_page_text` SET `text_loc2`='Goodsteel\'s Balanced Flameberge -$B$BEnfin, je veux l\'une de mes meilleures œuvres de forgeron à ce jour. Ce flamboyant était une incroyable épée que j\'ai attribuée à ce que je pensais être un très honorable et un grand guerrier. Eh bien, il a été repoussé par un murloc nommé Jarquai? Quelque chose comme ca.$B$BC\'était pendant qu\'il voyageait le long de la côte du Marais des Chagrins lorsqu\'il a pensé attaquer la créature. Il a échoué et a apparemment fui, mais pas avant de laisser l\'arme pour que ce murloc puisse le prendre.' WHERE `entry`=1554;
UPDATE `locales_page_text` SET `text_loc2`='Goodsteel\'s Balanced Flameberge -$B$BEnfin, je veux l\'une de mes meilleures œuvres de forgeron à ce jour. Ce flamboyant était une incroyable épée que j\'ai attribuée à ce que je pensais être un très honorable et un grand guerrier. Eh bien, il a été repoussé par un murloc nommé Jarquai? Quelque chose comme ca.$B$BC\'était pendant qu\'il voyageait le long de la côte du Marais des Chagrins lorsqu\'il a pensé attaquer la créature. Il a échoué et a apparemment fui, mais pas avant de laisser l\'arme pour que ce murloc puisse le prendre.' WHERE `entry`=1555;
UPDATE `locales_page_text` SET `text_loc2`='Trouvez-moi ces objets et revenez à Gadgetzan avec eux, avec ce registre, et je vous récompenserai.' WHERE `entry`=1556;
