UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> GRADES MILITAIRES DE L\'HORDE & amp; ALLIANCE </H1><BR/><BR/><IMG src = "Interface  PvPRankBadges  PvPRankHorde" align = "left" /><BR/><P align = "right"> Throm\'ka! Dans ce livre </P><P align = "right">, vous trouverez </P><P align = "right"> de nombreuses listes de </P><P align = "right"> des rangs militaires </P><P align = "right"> de la Horde et </P><P align = "right"> de la chétive Alliance. </P><P align = "right"> D\'abord viendra le </P><P align = "right"> Horde, puis l\'Alliance, </P><P align = "right"> avec des listes d\'officiers et de grades. Comme il convient,les plus forts sont listés en haut, les plus faibles en dessous. </P></BODY></HTML>' WHERE `entry`=2661;
