UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> GRADES D\'OFFICIERS DE L\'HORDE </H1><BR/><P align = "center"> Partie 2 </P><IMG src = "Interface  PvPRankBadges  PvPRank09 "align =" left "/><BR/><P align =" right "> Centurion </P><BR/><BR/><IMG src =" Interface  PvPRankBadges  PvPRank08 "align =" left " /><BR/><P align = "right"> Légionnaire </P><BR/><BR/><IMG src = "Interface  PvPRankBadges  PvPRank07" align = "left" /><BR/>< P align = "right"> Garde de sang </P><BR/><BR/><IMG src = "Interface  PvPRankBadges  PvPRank06" align = "gauche" /><BR/><P align = "right" > Garde de pierre </P><BR/><BR/></BODY></HTML>' WHERE `entry`=2663;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> RANKS HORDE ENTRÉS </H1><BR/><IMG src = "Interface  PvPRankBadges  PvPRank05" align = "left" /><BR/><P align = "right"> Premier sergent </P><BR/><BR/><IMG src = "Interface  PvPRankBadges  PvPRank04" align = "à gauche" /><BR/><P align = "right"> Senior Sergent </P><BR/><BR/><IMG src = "Interface  PvPRankBadges  PvPRank03" align = "left" /><BR/><P align = "right"> Sergent </P><BR /><BR/><IMG src = "Interface  PvPRankBadges  PvPRank02" align = "left" /><BR/><P align = "right"> Grunt </P><BR/><BR/>< IMG src = "Interface  PvPRankBadges  PvPRank01" align = "left "/><BR/><P align =" right "> Scout </P><BR/><BR/></BODY></HTML>' WHERE `entry`=2664;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> GRADES D\'OFFICIERS DE L\'ALLIANCE </H1><BR/><P align = "center"> Partie 1 </P><IMG src = "Interface  PvPRankBadges  PvPRank14 "align =" left "/><BR/><P align =" right "> Grand maréchal </P><BR/><BR/><IMG src =" Interface  PvPRankBadges  PvPRank13 "align =" left "/><BR/><P align =" right "> Maréchal </P><BR/><BR/><IMG src =" Interface  PvPRankBadges  PvPRank12 "align =" left "/><BR / ><P align = "right"> Marshal </P><BR/><BR/><IMG src = "Interface  PvPRankBadges  PvPRank11" align = "à gauche" /><BR/><P align = "à droite "> Commandant </P><BR/><BR/><IMG src = "Interface  PvPRankBadges  PvPRank10" align = "left" /><BR/><P align = "right"> Lieutenant-commandant </P><BR/><BR/></BODY></HTML >' WHERE `entry`=2665;
UPDATE `locales_page_text` SET `text_loc2`='$B$BQuelques instants plus tard, la prêtresse est envahie par les cadavres des paysans qui l\'entouraient ...$B$BLes morts sont ressuscités.' WHERE `entry`=2666;
UPDATE `locales_page_text` SET `text_loc2`='Simone la séductrice:$B$BVous trouverez Simone en train de souiller le cratère Un\'Goro. Ne vous laissez pas berner par son déguisement. Abordez-la avec prudence et défiez-la au combat.' WHERE `entry`=2671;
UPDATE `locales_page_text` SET `text_loc2`='Klinfran le Fou:$B$BKlinfran erre dans les Steppes Ardentes. Adressez-vous à lui avec précaution et forcez-le à montrer sa vraie forme.' WHERE `entry`=2672;
UPDATE `locales_page_text` SET `text_loc2`='Solenor the Slayer:$B$BDans le paysage aride de Silithus se cache le démon, Solenor the Slayer. Sans doute déguisé, définitivement un danger.Approche-le et force-le à se transformer.' WHERE `entry`=2673;
UPDATE `locales_page_text` SET `text_loc2`='Artorius the Doombringer:$B$BArtorius apporte la corruption et le malheur à tout ce qu\'il touche dans Winterspring. Trouvez-le, tuez-le.' WHERE `entry`=2674;
UPDATE `locales_page_text` SET `text_loc2`='Soyez averti, chasseur, ces démons doivent être combattus selon leurs propres conditions. S\'ils sentent qu\'ils ne vous surpassent pas, ils vont fuir.$B$BALLEZ SEUL.' WHERE `entry`=2675;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> CLASSEMENTS MILITAIRES DE L\'ALLIANCE </H1><BR/><P align = "center"> OFFICIERS </P><BR/><P align = "center"> Grand Maréchal </P><P align = "centre"> Maréchal </P><P align = "centre"> Maréchal </P><P align = "centre"> Commandant </P><P align = "centre"> Lieutenant commandant </P><P align = "centre"> Chevalier-champion </P><P align = "centre"> Capitaine de chevalier </P><P align = "centre"> Chevalier- Lieutenant </P><P align = "centre"> Chevalier </P><BR/><P align = "centre"> INSCRIT </P><BR/><P align = "centre"> Sergent Major < / P><P align = "center">Sergent-chef </P><P align = "centre"> Sergent </P><P align = "centre"> Caporal </P><P align = "centre"> Privé </P></BODY>< / HTML>' WHERE `entry`=2676;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> CLASSES HORDE MILITAIRES </H1><BR/><P align = "center"> OFFICIERS </P><BR/><P align = "center"> Grand seigneur de guerre </P><P align = "center"> Seigneur de guerre </P><P align = "center"> Général </P><P align = "center"> Lieutenant général </P><P align = "centre"> Champion </P><P align = "centre"> Centurion </P><P align = "centre"> Légionnaire </P><P align = "centre"> Garde de sang </P>< P align = "center"> Garde de pierre </P><BR/><P align = "center"> INSCRIT </P><BR/><P align = "center"> Premier sergent </P><P align = "center"> Sergent supérieur </P><P align = "center"> Sergent </P><P align = "center"> Grunt </P><P align = "center"> Eclaireur </P></BODY></HTML>' WHERE `entry`=2677;
UPDATE `locales_page_text` SET `text_loc2`='Pour lier une dreadsteed, vous devez effectuer les trois opérations suivantes:$B$BCréez un cercle d\'invocation supérieur.$B$BDans le cercle, ouvrez un portail vers Xoroth et passez le dreadsteed à travers.$B$BBattez le dreadsteed, puis dominez son esprit.$B$BLes pages suivantes détailleront la manière dont chacune de ces étapes peut être effectuée. Ce ne sera pas facile, mais vous vous en êtes montré très capable. Avec concentration et habileté, je suis convaincu que le dreadsteed sera le vôtre.$B$BLire, $B-Mor\'zul Bloodbringer' WHERE `entry`=2691;
UPDATE `locales_page_text` SET `text_loc2`='Outils du rituel$B$BAvant de commencer votre tâche, vous devez disposer des outils magiques suivants:$B$BLe pot de J\'eevee$B$BA Noir Lodestone$B$BGlyphes Xorothiens$B$BMon serviteur Gorzeeki les avoir pour vous, pour un prix. Ne tentez aucune étape de votre rituel sans tous ces outils. Chacun est essentiel. $B$BDans les pages suivantes, je décrirai comment chaque outil doit être utilisé.' WHERE `entry`=2692;
UPDATE `locales_page_text` SET `text_loc2`='Cercle de reliure supérieure$B$BA Le cercle de reliure supérieure doit être créé sur un site où la magie est forte. Il existe un tel endroit au fond des ruines d\'Eldre\'Thalas, également appelé Dire Maul. Dans Eldre\'Thalas est emprisonné un être de grande puissance, Immol\'thar; c\'est sur le piédestal de sa prison où vous devez effectuer le rituel pour créer le cercle. $B$BFrayez-vous un chemin jusqu\'au piédestal, puis laissez J\'eevee sortir de son pot.' WHERE `entry`=2693;
UPDATE `locales_page_text` SET `text_loc2`='La cloche, la roue et la bougie$B$BAprès avoir relâché J\'eevee, il place ensuite la cloche, la roue et la bougie, et un cercle apparaît. C\'est le début du rituel. Vous devez être vigilant. Les objets susmentionnés conduisent de vastes énergies et sont sujets à l\'échec. Lorsque cela se produit, vous devez rapidement utiliser votre Black Lodestone pour le redémarrer avant que tout votre rituel échoue. $B$BSi les trois objets ont échoué avant de pouvoir les redémarrer, alors votre rituel se termine et vous devez recommencer.' WHERE `entry`=2694;
UPDATE `locales_page_text` SET `text_loc2`='En plus de mener les énergies du rituel, la cloche, la roue et la bougie possèdent des propriétés uniques. $B$BLa cloche de Dethmoora, lorsqu\'on sonne, confère aux sorciers dans le cercle vigueur et énergie. $B$BLors de la rotation, la Roue de la Marche noire protège le cercle des blessures. $B$BLa bougie Doomsday, lorsqu\'elle brûle, envoie de l\'énergie cruelle aux ennemis qui entrent dans le cercle. $B$BÀ cause de ces bénédictions, il est très important de faire en sorte que tous ces objets fonctionnent pendant le rituel.' WHERE `entry`=2695;
UPDATE `locales_page_text` SET `text_loc2`='Il faut également noter que le Black Lodestone, utilisé pour redémarrer la cloche, la roue ou la bougie en cas d\'échec, nécessite des fragments d\'âme. Chaque fois que vous redémarrez un objet rituel avec le Lodestone, un de vos fragments d\'âme sera consommé. Assurez-vous donc de disposer d\'un grand stock d\'entre eux avant le début du rituel.' WHERE `entry`=2696;
UPDATE `locales_page_text` SET `text_loc2`='Terminer le rituel$B$BVous pouvez suivre votre progression grâce aux runes magiques situées le long du cercle. Lorsque neuf runes apparaissent, le rituel est terminé et vous verrez l\'énergie monter du cercle nouvellement investi.$B$BÀ partir de là, vous pouvez invoquer les glyphes xorothiens et ouvrir un portail sur Xoroth et y glisser un fil de peur.$B$BBattez le dreadsteed et libérez son esprit. Confrontez l\'esprit et il sera captivé, et vous serez récompensé par le secret de sa convocation.' WHERE `entry`=2697;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><BR/><BR/><P align = "left"> C\'est là que réside Durotan, premier chef du clan Loup-de-givre et père de notre chef de guerre honoré, Thrall. Il était le plus courageux de notre espèce - trahi par ceux qui verraient notre peuple asservi. Durotan a donné sa vie pour que notre liberté soit gagnée. Nous l\'honorons - et l\'héritage qu\'il nous a légué par son fils. </P><BR/><P align = "left"> Drek\'Thar, l\'observateur lointain des Loups-de-givre </P></BODY>< / HTML>' WHERE `entry`=2731;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> CLASSES HORDE MILITAIRES </H1><BR/><P align = "center"> OFFICIERS </P><BR/><P align = "center"> Grand seigneur de guerre </P><P align = "center"> Seigneur de guerre </P><P align = "center"> Général </P><P align = "center"> Lieutenant général </P><P align = "centre"> Champion </P><P align = "centre"> Centurion </P><P align = "centre"> Légionnaire </P><P align = "centre"> Garde de sang </P>< P align = "center"> Garde de pierre </P><BR/><P align = "center"> INSCRIT </P><BR/><P align = "center"> Premier sergent </P><P align = "center"> Sergent supérieur </P><P align = "center"> Sergent </P><P align = "center"> Grunt </P><P align = "center"> Eclaireur </P></BODY></HTML>' WHERE `entry`=2770;
UPDATE `locales_page_text` SET `text_loc2`='Peeling the Onion Le guide sur le démontage de la foudrepike - Par Drek\'Thar Dans ces pages, vous trouverez une mine de connaissances sur les tactiques de combat et la politique. Le soldat instruit est le soldat qui s\'appuie sur l\'épaule de l\'expérience. Laissez ce livre vous guider dans votre bataille pour la vallée d\'Alterac.' WHERE `entry`=2771;
UPDATE `locales_page_text` SET `text_loc2`='Chapitre 1 - La ligne de front Démanteler l\'armée de Foudrepique revient à éplucher un oignon. Pour atteindre le cœur, vous devez commencer par supprimer les couches les plus externes. Les lignes de front, composées principalement de montagnards Foudrepique et de sentinelles de l\'Alliance, sont liées au bunker du capitaine. C\'est le capitaine Balinda Stonehearth qui habilite ces unités et prévoit des renforts lorsque les lignes sont en état de siège. La grève chez le capitaine et les forces de la ligne de front vont s\'effondrer.' WHERE `entry`=2772;
UPDATE `locales_page_text` SET `text_loc2`='Chapitre 2 - Les tours jumelles Ne vous y trompez pas. Vanndar Stormpike est un ennemi rusé et certainement pas un imbécile. Une fois la ligne de front franchie, les cibles défensives secondaires doivent toujours être détruites. Ce sont les tours jumelles de Stonehearth et Icewing qui contrôlent les gardes de Foudrepiques à l\'extérieur de Dun Baldar et les patrouilles de Foudrepique.' WHERE `entry`=2773;
UPDATE `locales_page_text` SET `text_loc2`='Méfiez-vous, soldat, ces deux tours étant fortement fortifiées et gouvernées par l\'un des commandants d\'élite de Vanndar. Si vos forces violent les fortifications, assurez-vous que le commandant qui se trouve à l\'intérieur a été ... réduit au silence. Cela aussi fera partie du démantèlement de la Foudrepique.' WHERE `entry`=2774;
UPDATE `locales_page_text` SET `text_loc2`='Chapitre 3 - Les quatre commandants La troisième couche de l\'oignon Strompike est composée des quatre commandants. À cet égard, Vanndar a imité nos propres défenses glorieuses Frostwolf. Bien sûr, il vous dira le contraire ... Mais je m\'éloigne du sujet. Les quatre commandants contrôlent le flux et le reflux des Gardiens de Foudrepique qui gardent farouchement Dun Baldar. Tais-les tous et les gardiens de Foudrepique tomberont. Ensuite, il ne reste qu\'une couche à éplucher.' WHERE `entry`=2775;
UPDATE `locales_page_text` SET `text_loc2`='Chapitre 4 - Les bunkers de Dun Baldar Comme vous l\'avez sans doute remarqué, les couches défensives de Foudrepique sont en place pour empêcher un puissant mouvement de puissance de démanteler toute l\'armée. Avant que vous ne posiez la question, oui, c\'est exactement comme cela que nos forces sont constituées et non, je ne changerai pas notre structure défensive.Ils nous ont copié, pourquoi devrions-nous changer?' WHERE `entry`=2776;
UPDATE `locales_page_text` SET `text_loc2`='Où étais-je?Oui, les bunkers de Dun Baldar (situés à Dun Baldar) contrôlent les Stormpike Defenders - les unités de garde d\'élite les plus fiables et les plus fiables de Vanndar. Détruisez ces deux tours et les renforts envoyés pour aider Vanndar cesseront d\'exister.' WHERE `entry`=2777;
UPDATE `locales_page_text` SET `text_loc2`='Épilogue Après avoir fait tout cela, vous vous retrouvez avec le centre de l\'oignon: le noyau sucré. Vanndar Stormpike sera laissé seul et sans défense. Les règles de conduite militaire exigent que nous demandions sa reddition avant toute exécution. Assurez-vous de mentionner la chose de la reddition quand vous le voyez ... puis tuez-le.' WHERE `entry`=2778;
UPDATE `locales_page_text` SET `text_loc2`='Artichaut du Loup-de-givre: Contes de gloire au foudrepique$B$B-Par Vanndar Foudrepique$B$BQu\'est-ce que le Givre-givre? La réponse est simple: les loups de givre sont des sauvages qui tentent de mettre un terme à notre impératif souverain impérialiste.$B$BTout comme un artichaut, le Loup-de-givre possède un ensemble de défenses épineuses et blindées et ressemble beaucoup à un artichaut. Une fois que la couche externe est pelée, le cœur est exposé, prêt à être mangé avec une fine trempette à l\'ail.$B$BDélicieux!$B$B' WHERE `entry`=2779;
UPDATE `locales_page_text` SET `text_loc2`='Chapitre 1 - La ligne de front$B$BLes lignes de front des défenses de Frostwolf sont composées de guerriers de loup-de-givre. Les guerriers sont déployés sur les lignes de front depuis la forteresse du capitaine Galvangar: la garnison Iceblood.La manière la plus efficace de supprimer cette couche de défense consiste à détruire la garnison Iceblood. $B$BFaites le siège de la forteresse et détruisez le capitaine Galvangar. Une fois que cela est fait, les lignes de front vont s\'effondrer.$B$B' WHERE `entry`=2780;
UPDATE `locales_page_text` SET `text_loc2`='Chapitre 2 - Iceblood et Tower Point$B$BUne fois que la première couche est abaissée, la deuxième couche de l\'artichaut est exposée. Détruire les tours de loup-de-givre situées près des lignes de front paralysera le légionnaire et les patrouilles qui envahiront le flanc arrière. $B$BLes tours sont très fortifiées, chacune abritant l\'un des commandants d\'élite de Drek\'Thar.Ils doivent être tués. Cela accélérera le démantèlement des défenses de Frostwolf.' WHERE `entry`=2781;
UPDATE `locales_page_text` SET `text_loc2`='Chapitre 3 - Les quatre commandants$B$BSi vous avez suivi la sagesse de ce guide, deux des commandants de Drek\'Thar sont maintenant morts. Bon travail, soldat! Leur résolution s\'affaiblit déjà. Vous devez maintenant terminer la diffusion de la chaîne de commandement en tuant les deux commandants restants. $B$BAvec les quatre commandants tués, les légionnaires qui gardent le donjon de Givre-loup seront dispersés - laissés sans ordre. Mûr pour la cueillette!$B$B' WHERE `entry`=2782;
UPDATE `locales_page_text` SET `text_loc2`='Chapitre 4 - Les tours est et ouest des loups-de-givre$B$BIl n\'y a plus qu\'une couche entre vous et la victoire; Les unités de garde les plus fiables et les plus puissantes de Drek\'Thar: les Gardiens de Loup-de-Givre. $B$BLes gardiens de Loup-de-givre sont envoyés des pelotons retenus en réserve dans les tours est et ouest de Frostwolf. Détruisez ces tours et regardez les derniers Gardiens de Loup-de-givre s\'enfuir honteux.' WHERE `entry`=2783;
UPDATE `locales_page_text` SET `text_loc2`='Épilogue$B$B"Étouffez le cœur de l\'artichaut avec du beurre à l\'ail et de la mayonnaise. Complétez le repas avec une bière vieille et capiteuse." $B$BDrek\'Thar est maintenant seul, ses deux chiens à ses côtés. Capturez la hutte de secours du loup-de-givre pour vous aider dans votre combat et pour détruire Drek\'Thar. Lorsque le général tombera, cette terre sera finalement cédée à son propriétaire légitime, le Foudrepique.$B$B' WHERE `entry`=2784;
UPDATE `locales_page_text` SET `text_loc2`='Nous vous appelons, Zanza de Zuldazar. $B$BBénissez ceux qui demandent votre aide, Loa Zanza. Bénissez ceux qui s\'allieraient avec les Zandalariens$B$B.$B$BAidez-nous en cette période difficile. Aide-nous Loa. Donnez-nous le pouvoir d\'abattre nos ennemis. Donnez-nous le pouvoir de vaincre encore une fois le dieu du sang.$B$B' WHERE `entry`=2785;
UPDATE `locales_page_text` SET `text_loc2`='Tournoi de pêche de Strangleronce! $B$BDimanche après-midi - 14h à 16h. $B$BPêchez le long de la côte de Stranglethorn (sauf dans la baie Booty) et cherchez les bancs de Tastyfish qui apparaîtront le long de la côte! Assurez-vous que votre canne à pêche atterrit à l\'école et que vous attraperez un poisson spécial au lieu de la variété normale de Strangleronce. La première personne à attraper quarante Tastyfish peut les transformer en Fishmaster Bassbait à Booty Bay pour un prix! $B$BMême si vous n\'êtes pas le premier à attraper quarante poissons savoureux, vous pouvez toujours les rendre contre de l\'argent et il y a des poissons spéciaux à capturer, qui récompenseront des prix spéciaux!$B$B' WHERE `entry`=2786;
UPDATE `locales_page_text` SET `text_loc2`='Son nom a amené la lumière à Azeroth le plus sombre. $B$BSon courage a déchiré les murs de la misère. $B$BLa Main d\'argent ne ferait pas mieux de faire plus en$B$Bque de donner au monde le chevalier qui se donnerait.' WHERE `entry`=2787;
UPDATE `locales_page_text` SET `text_loc2`='Sa renommée n\'était pas celle qu\'il cherchait à développer,$B$B, ni la seigneurie sur son propre genre. $B$BAu lieu de cela, il a choisi de se battre pour Lordaeron$B$Bdans l\'espoir que ses proches n\'auraient plus à se battre.' WHERE `entry`=2788;
UPDATE `locales_page_text` SET `text_loc2`='Le récit tragique de ce très saint chevalier,$B$B, ne se terminera jamais par sa mort impie. $B$BNous lui rendons hommage, et nous le remercierons! $B$BUther doit toujours connaître la gloire et la lumière!' WHERE `entry`=2789;
UPDATE `locales_page_text` SET `text_loc2`='Son nom ne mourra pas. $B Son sacrifice sera toujours $B réservé pour montrer le chemin. $B$BDes chaînes qui étouffaient autrefois. $Ble souffle d\'air de l\'honneur $Bn ne nous lie plus.' WHERE `entry`=2790;
UPDATE `locales_page_text` SET `text_loc2`='Entendez-vous son cri? $B La bataille de BA se fait entendre pour la Horde: $Bictoire ou mort! $B$BNous devons nous souvenir de la force de $Bhis face à la mort. $BDe son rêve devenu réalité.' WHERE `entry`=2791;
UPDATE `locales_page_text` SET `text_loc2`='Des dangers partout! $B Les ennemis cherchent à nous rapporter $Bback aux chaînes. $B$BQuand nous nous battons, pensez à $Bhe qui a fait ce qui doit être fait. $BHellscream, pour toujours!' WHERE `entry`=2792;
UPDATE `locales_page_text` SET `text_loc2`='Près de mille ans se sont écoulés depuis la «guerre des sables mouvants». C\'est au cours de cette guerre, au cœur du désert de Silithus, qu\'une grande tragédie et un fardeau encore plus lourd assaillent Fandral Staghelm. On pense que les événements décrits ici sont ce qui a finalement façonné la disposition peu recommandable du druide arch.' WHERE `entry`=2793;
UPDATE `locales_page_text` SET `text_loc2`='Une ancienne race d\'insectoïdes sensibles connue sous le nom de Qiraji a rivalisé pour la domination d\'une grande partie de Kalimdor à un moment donné. Ce sont Staghelm, pompier et impérialiste, et son armée d\'elfes de la nuit qui ont affronté les Qiraji au combat.' WHERE `entry`=2794;
UPDATE `locales_page_text` SET `text_loc2`='Le père et le fils se sont battus avec ferveur pour contenir la marée montante de l\'essaim d\'insectoïdes dans Silithus. Depuis la forteresse d\'Ahn\'Qiraj, un flot apparemment infini de silithides a continué de renforcer les rangs des forces d\'invasion Qiraji. Tout Silithus était assiégé.' WHERE `entry`=2795;
UPDATE `locales_page_text` SET `text_loc2`='Dans une décision que Staghelm regretterait, le jeune Staghelm, Valstann, a été chargé de défendre l\'avant-poste elfe de la nuit de Southwind Village.Malheureusement, Valstann perdrait la vie ce jour-là, alors que Southwind Village tomberait aux mains des armées d\'Ahn\'Qiraj.' WHERE `entry`=2796;
UPDATE `locales_page_text` SET `text_loc2`='Intrépide et le cœur lourd, un Staghelm fandral vengeur continuerait. La défaite à Silithus entraînerait une grande perte pour la nation des elfes de la nuit et très probablement pour le monde. $B$BLa bataille a duré des mois. Tout le Silithus a été dépassé par le Qiraji. Staghelm et son armée ont été repoussés à Tanaris. Avec une armée décimée et d\'innombrables innocents tués, l\'arrogance qui avait conduit Fandral Staghelm a été complètement étouffée.' WHERE `entry`=2797;
UPDATE `locales_page_text` SET `text_loc2`='Sa fierté complètement écrasée. Staghelm sentirait pour la deuxième fois de son existence le battement de son estomac qui ne pouvait être provoqué que par la peur.C\'est donc à Tanaris que Staghelm s\'approchera d\'Anachronos, descendance de Nozdormu, et plaidera pour l\'aide de la Volée du Bronze.' WHERE `entry`=2798;
UPDATE `locales_page_text` SET `text_loc2`='Au départ, Anachronos avait refusé d\'associer le vol de bronze aux affaires des races mineures. Ce n\'est que lorsque les Qiraji attaquèrent les cavernes du temps avec précipitation que Anachronos acceptera de s\'associer aux elfes de la nuit.' WHERE `entry`=2799;
UPDATE `locales_page_text` SET `text_loc2`='Anachronos se rendrait bientôt compte que même le puissant vol de bronze ne pourrait pas endiguer la marée des forces envahissantes Qiraji. Pour cinquante insectes annihilés par son souffle, cent attendraient leur place. Alors que les lignes des elfes de la nuit faiblissaient et que la menace d\'être poussé plus au nord vers les étendues sauvages de Féralas était imminente, Anachronos décida que les enfants des Aspects devaient en être informés.' WHERE `entry`=2800;
UPDATE `locales_page_text` SET `text_loc2`='Il appellerait d\'abord le Dragonflight d\'Ysera. Merithra du rêve répondrait à son appel et à partir de là, Dragonflight d\'Alexstrasza et Dragonflight de Malygos seraient mis au courant.' WHERE `entry`=2801;
UPDATE `locales_page_text` SET `text_loc2`='Les dragons se rencontreraient dans la stratosphère au-dessus de Silithus: des milliers de pieds au-dessus de la forteresse d\'Ahn\'Qiraj. $B$BIl y avait Anachronos, enfant du Nozdormu, Aspect of Time. $B$BDu rêve émeraude viendrait Merithra, enfant d\'Ysera, Aspect of Nature. $B$BCaelestrasz, enfant d\'Alexstrasza, Aspect of Life, représenterait le vol du dragon rouge.$B$BArygos, enfant de Malygos, Aspect of Magic, représenterait le Vol bleu.' WHERE `entry`=2802;
UPDATE `locales_page_text` SET `text_loc2`='Les dragons observèrent le flot incessant de silithides et Qiraji jaillir d\'Ahn\'Qiraj. Il leur est apparu qu\'aucune force ne pourrait jamais mettre fin à cette guerre. Des dizaines de milliers de membres des forces Qiraji attendaient de l\'intérieur du temple principal. A chaque seconde, de plus en plus étaient éclos et apprêtés; prêt à se déchaîner dans le monde.' WHERE `entry`=2803;
UPDATE `locales_page_text` SET `text_loc2`='Arygos fut le premier à remarquer les étranges émanations. Le temple monolithique situé dans le quadrant sud d\'Ahn\'Qiraj émettait une magie faible à la différence de tout ce qu\'ils avaient senti venir du Qiraji. Malgré leurs efforts, personne n\'a pu pénétrer plus avant dans le temple pour trouver la source de cette énergie. Seule une puissance immense pouvait se protéger aussi complètement.Peut-être ... quelque chose qui contrôle les insectes. Quelque chose contrôlant même le Qiraji.' WHERE `entry`=2804;
UPDATE `locales_page_text` SET `text_loc2`='Les dragons savaient qu\'ils devaient réagir rapidement, de peur que le monde ne subisse une autre catastrophe épique.Après mûre réflexion, il a été décidé que le meilleur plan d\'action serait d\'arrêter les armées hôtes près de leur source.' WHERE `entry`=2805;
UPDATE `locales_page_text` SET `text_loc2`='Une grande barrière serait créée, puisant dans l\'essence des elfes de la nuit et du vol du dragon. Une barrière magique qui pourrait contenir les silithides et leurs seigneurs dans les murs d\'Ahn\'Qiraj et empêcher de futures incursions. Ahn\'Qiraj deviendrait elle-même une prison.' WHERE `entry`=2806;
UPDATE `locales_page_text` SET `text_loc2`='Jour 1 Le pèlerinage $BOur est terminé. Nous avons finalement atteint Silithus, où nous devons établir un contact. Nous sommes venus de nombreux endroits pour collaborer à ce qui sera une entreprise monumentale.$B$BIl y a un formidable sentiment d\'excitation dans l\'air. Demain, nous consacrerons toute notre énergie à construire ce qui permettra la communication avec eux.$B$BDemain, nous construirons une pierre à vent!' WHERE `entry`=2807;
UPDATE `locales_page_text` SET `text_loc2`='Jour 2 $BL\'emplacement de la pierre à vent a fait l\'objet de nombreux débats. Même les plus sages d\'entre nous ne peuvent déterminer avec une grande certitude à quel endroit se réfère réellement le lieu mentionné dans l\'ancienne prophétie.$B$BJe suis resté avec le groupe nordique d\'origine et le temps nous prouvera sans doute raison. L\'argument selon lequel les lignes d\'énergie se coupent à des angles nuisibles à cet endroit est stupide et naïf.$B$BNous rassemblons déjà les matériaux de construction nécessaires à grande vitesse.' WHERE `entry`=2808;
UPDATE `locales_page_text` SET `text_loc2`='Jour 15, la dédicace BOUR a porté ses fruits. Nous sommes le premier camp à terminer la construction d\'une pierre à vent. Si les autres imbéciles étaient restés avec nous, nous aurions terminé la tâche en moins d\'une semaine!$B$BDemain, nous commencerons les rituels nécessaires pour convoquer le baron Kazum lui-même. Il a exigé d\'être le premier à tester le système et nous ne pouvions pas le contredire, car il était prompt à la colère.$B$BI s\'attend à ce qu\'il soit le plus satisfait de nous.' WHERE `entry`=2809;
UPDATE `locales_page_text` SET `text_loc2`='Pour diffusion immédiate à tous les membres de Twilight\'s Hammer. $B$BLa recherche du traître, Ortell, progresse à un rythme très satisfaisant. Nos éclaireurs ont réduit son emplacement à un réseau de cavernes dans la marche de l\'Ouest. $B$BLa trahison d\'Ortell ne doit pas être pardonnée et son évasion représente un grand risque pour la sécurité de notre organisation. $B$BToute personne qui dissimule des informations sur l\'endroit où se trouve Ortell sera dûment punie. $B$B-Twilight Lord Everun' WHERE `entry`=2810;
UPDATE `locales_page_text` SET `text_loc2`='Pour diffusion immédiate à tous les membres de Twilight Hammer. $B$BÉtant donné l\'état actuel des choses à Silithus, toute la distribution des textes de True Believer doit s\'effectuer sous l\'escorte de quatre membres du personnel armés de Twilight Hammer. $B$BTout membre trouvé en violation de cette nouvelle politique sera puni de manière sommaire. $B$B-Twilight Lord D\'Sak' WHERE `entry`=2811;
UPDATE `locales_page_text` SET `text_loc2`='Pour diffusion immédiate à tous les membres de Twilight\'s Hammer. $B$BLes informations suivantes décrivent l\'utilisation de pierres à vent pour la communication avec nos maîtres du conseil abyssal. $B$BToute utilisation frivole de pierres à vent sera punie de la manière la plus douloureuse. $B$B-Twilight Keeper Havunth' WHERE `entry`=2812;
UPDATE `locales_page_text` SET `text_loc2`='Templiers abyssaux$B$BEn tant qu\'initié, vous êtes autorisé à établir un contact avec les Templiers abyssaux pour des rituels de routine. N\'oubliez pas de toujours revêtir le vêtement approprié lorsque vous utilisez des pierres à vent pour éviter d\'activer nos enchantements défensifs. $B$BSi une urgence imprévue survient en l\'absence de surveillants, vous êtes autorisé à convoquer un templier abyssal. $B$BPour plus de détails sur les rituels d\'invocation impliqués, reportez-vous à True Believer du mois dernier.' WHERE `entry`=2813;
UPDATE `locales_page_text` SET `text_loc2`='Ducs abyssaux$B$BSeuls les acolytes possédant un médaillon de station sont autorisés à entrer en contact avec les ducs abyssaux. Les médaillons de poste ne doivent être utilisés que par les individus auxquels ils ont été attribués à l\'origine. $B$BLes membres du Twilight\'s Hammer impliqués dans une communication non approuvée avec des Ducs abyssaux ou dans l\'utilisation non autorisée de médaillons de station seront personnellement disciplinés par moi-même.' WHERE `entry`=2814;
UPDATE `locales_page_text` SET `text_loc2`='Le Conseil supérieur des seigneurs abyssaux$B$BSeuls les seigneurs du Crépuscule sont autorisés à contacter le prince Skaldrenox, le grand maréchal Whirlaxis, le baron Kazum ou le seigneur Skwol. $B$BEn aucun cas, les membres de rang inférieur du Twilight\'s Hammer ne sont autorisés à établir une communication avec le Haut Conseil. Tous les étrangers trouvés en utilisant une pierre à vent de cette manière seront réduits en cendres sur place.' WHERE `entry`=2815;
UPDATE `locales_page_text` SET `text_loc2`='L\'état du culte, volume 92$B$BC\'est avec un cœur joyeux que je vous salue, mes frères, car l\'état de notre culte ne pourrait être plus grand. Nous sommes légion et nos objectifs se rapprochent de plus en plus. $B$BLes nouvelles recrues continuent à se rassembler. Les chants de louange à nos seigneurs à Darkshore, ils s\'incluent dans une rêverie joyeuse dans la gorge Searing, ils embrassent le serviteur de nos maîtres, Aku\'mai, à Blackfathom Deeps. En effet, mes frères et sœurs, bientôt un essaim de fidèles couvrira tout Azeroth!' WHERE `entry`=2816;
UPDATE `locales_page_text` SET `text_loc2`='State of the Cult, Volume 127$B$BC\'est vraiment un jour heureux! Nous avons été testés à Silithus et nous avons réussi! $B$BLes informations sur la destruction de notre camp le plus au nord pourraient défier les cœurs des infidèles, mais ce massacre ne doit pas être perçu comme une tragédie.Non!' WHERE `entry`=2817;
UPDATE `locales_page_text` SET `text_loc2`='Le déversement de notre sang par l\'un des hauts conseils abyssaux est une bénédiction! Nous devons envier nos camarades tombés au combat, car leurs esprits résident maintenant dans le ventre d\'une bête des plus justes! $B$BLouange aux anciens dieux! Louange à leurs serviteurs! Louange au Marteau du Crépuscule!' WHERE `entry`=2818;
UPDATE `locales_page_text` SET `text_loc2`='État de la secte, volume 233$B$BL\'occasion frappe encore une fois, mes frères et soeurs! Le Cercle Cénarien, culte vil des amoureux de la nature, envoie maintenant des agents contre nous. Ils doivent vouloir nous empêcher d\'invoquer des pierres leurres rouges. Nous ne pouvons pas le permettre! $B$BLes pierres rouges sont essentielles pour nos nouveaux alliés. Nous ne devons pas laisser les chiens de garde du Cercle Cénarien réussir. Restez ferme, mes fidèles amis. Rester ferme!' WHERE `entry`=2819;
UPDATE `locales_page_text` SET `text_loc2`='Comment savons-nous qu\'ils nous aiment?$B$BCertains peuvent se demander comment les vieux dieux et leurs sbires peuvent nous aimer. Est-ce qu\'ils ne nous tuent pas? Ne causent-ils pas de la douleur et de la souffrance à tous? Comment des êtres aussi malins sur la malveillance pourraient-ils connaître l\'amour dans leur cœur? $B$BIl existe une réponse simple à cette question. Si simple qu\'il n\'a pas besoin d\'être prononcé?' WHERE `entry`=2820;
UPDATE `locales_page_text` SET `text_loc2`='Foi.La foi est la source d\'où la joie de la servitude nous envahit. La foi nous gardera au chaud les nuits les plus froides de Silithus. La foi nous gardera dans les bonnes grâces de nos seigneurs lorsque les derniers jours arrivent. $B$BFaith nous sauvera.' WHERE `entry`=2821;
UPDATE `locales_page_text` SET `text_loc2`='Cher journal,$B$BAujourd\'hui, mon principal ennemi, Narain Soothfancy, a tenté de me tromper en envoyant des copains pour le remplacer lors de l\'exécution. SON exécution. Peux-tu le croire?$B $BI avait eu la peine de concevoir ce plan diabolique pour le sortir de cette maudite hutte et il tire ça? Peu importe ... Trop en colère pour continuer à écrire. Je serais de retour plus tard.' WHERE `entry`=2829;
UPDATE `locales_page_text` SET `text_loc2`='Cher journal,$B$BI a laissé ma cachette en colère après ma dernière entrée. Mon garçon était en colère. J\'ai décidé de redessiner les uniformes de mes sbires pour mieux refléter mon angoisse. Je me sens un peu mieux mais il manque quelque chose. Je reviens tout de suite...' WHERE `entry`=2830;
UPDATE `locales_page_text` SET `text_loc2`='Cher journal,$B$BOk, je vais bien maintenant. J\'ai battu l\'un de mes compagnons jusqu\'à ce qu\'il pleure comme une petite fille. $B$BHrm ... Je me sens un peu mal maintenant. Attends une minute!Merde, je suis un génie du mal! Mal, tu sais? Je suis tellement déçu de moi-même. Je reviens tout de suite.' WHERE `entry`=2831;
UPDATE `locales_page_text` SET `text_loc2`='Cher journal,$B$BI lui a fait un câlin et lui a dit de continuer son bon travail. Je me sens mieux maintenant.Garde ça entre toi et moi, journal. $B$BOù étais-je? Oh oui, NARAIN! ARGH !!! Je déteste ce gars.C\'est Narain, après tout, qui a anéanti tous les examens lorsque nous étions étudiants à l\'Institut Gnomeregan de bricolage. $B$BNe me quitte pas de l\'école, vont-ils? En ce qui me concerne, Gnomeregan a obtenu ce qu\'il méritait! $B$BC\'est tout pour aujourd\'hui. Dormez bien, agenda!' WHERE `entry`=2832;
UPDATE `locales_page_text` SET `text_loc2`='Cher journal,$B$BI doit vieillir. J\'ai totalement oublié que je vous parlais de Narain et de sa déception. Quoi qu\'il en soit, j\'étais tellement en colère quand j\'ai vu un leurre déguisé en Narain au lieu de Narain que j\'ai commandé au numéro deux de détruire le fou!$B$BI est entré dans ma machine volante et a commencé à déchirer son précieux livre stupide et stupide sur Draconic. Qu\'est-ce qu\'un spectacle? Qui lit Draconic? Je le déteste tellement!' WHERE `entry`=2833;
UPDATE `locales_page_text` SET `text_loc2`='Cher Journal,$B$BDésolé, je me suis endormi. Alors je volais en colère et je jetais des chapitres de ce livre stupide. J\'ai vu un démon au-dessous de moi dans Darkwhisper et j\'ai décidé de transformer le premier chapitre en une tornade de papier magique et de déchiqueter son corps en un million de morceaux! C\'était libérateur.Magie + papier = amusant. $B$BCela m\'a rendu assez heureux alors j\'ai décidé de prendre l\'avion pour les Royaumes de l\'Est. Mon premier arrêt était le noyau fondu. Narain a toujours parlé de cet endroit! "Fondu Core ceci et Ragnaros que ..."' WHERE `entry`=2834;
UPDATE `locales_page_text` SET `text_loc2`='Eh bien tu sais quoi? S\'il l\'aime tant, peut-être voudra-t-il essayer de reconstituer son livre en se rendant au Molten Core et en faisant revivre le chapitre à partir d\'un tas de cendres! Hah! $B$BPendant que j\'étais dans le quartier, j\'ai pensé que certains de ces techniciens en état d\'esclavage du laboratoire de Victor Nefarius pourraient avoir une utilisation pour ce produit draconique. Bombes away!' WHERE `entry`=2835;
UPDATE `locales_page_text` SET `text_loc2`='Cela commençait à devenir un sport en quelque sorte. Je commençais vraiment à diviser ce maudit livre! $B$BLes prochaines gouttes ont été faciles. $B$BUndercity, la maison des morts fétides pourris et probablement le meilleur chili que j\'ai jamais eu, a un chapitre. Hurlevent, capitale du fromage, l\'autre. Journal, as-tu déjà mis du fromage de Hurlevent sur du chili Undercity? BONJOUR? Cela devrait déclencher des alarmes.' WHERE `entry`=2836;
UPDATE `locales_page_text` SET `text_loc2`='Je commençais à être fatigué à ce moment-là, alors j\'ai fait tourner la machine volante et je suis rentré chez moi, mais pas avant de faire deux autres arrêts. J\'ai donné à Lord Kazzak et à ses démons une mouche dans la cicatrice contaminée (il déteste quand je fais ça) et j\'en ai collé une autre dans la cheminée d\'Onyxia. $B$BFatigué. Aller dormir.' WHERE `entry`=2837;
UPDATE `locales_page_text` SET `text_loc2`='À qui de droit,$B$BComme j\'aime ces jeux auxquels nous jouons.$B$BI ne peut que supposer que si vous lisez cette lettre, vous êtes parvenu, par miracle ou par une intervention divine, à me vaincre. Des préposés, je le sais, mais des choses étranges se sont produites dans l\'histoire de ce monde.$B$B' WHERE `entry`=2838;
UPDATE `locales_page_text` SET `text_loc2`='Comme pour toutes les choses de la vie, il y a une glorieuse feuille d\'argent de tragédie, de chaos et de mal dans ce récit douloureux. Pendant que vous m\'avez tué, vous l\'avez fait d\'une manière si lente et désordonnée que j\'ai eu le temps d\'écrire cette lettre APRÈS avoir détruit votre seul espoir de sauver ce monde. La poudre de cristal rouge broyée est tout ce qui reste de votre éclat de sceptre.$B$B' WHERE `entry`=2839;
UPDATE `locales_page_text` SET `text_loc2`='Profitez des armements Nemesis et Stormrage que vous pouvez trouver dans mon trésor. Croyez-moi quand je vous dis ceci: je suis très heureux de savoir que j\'ai fait ce que mon père ne pouvait pas ...$B$BSincèrement,$B$BSeigneur Victor Nefarius$B$B"Nefarian"$B$B' WHERE `entry`=2840;
UPDATE `locales_page_text` SET `text_loc2`='Au nom de Smokywood Pastures, merci encore d\'avoir examiné la question. Nous ne savons pas quel groupe est composé de Metzen, mais une enquête serait un bon point de départ!$B$BLe temps, c\'est de l\'argent, et nous en avons très peu dans ce cas. Soyez rapide dans vos efforts de sauvetage, car ces groupes ne doivent pas être pris à la légère dans ce dont ils sont capables.$B$BEn tout cas, les deux pages suivantes sont les billets de la rançon que nous avons reçus. Bonne chance...' WHERE `entry`=2841;
UPDATE `locales_page_text` SET `text_loc2`='Si vous voulez que le renne revienne en vie, vous serez rapide avec la rançon. Les pirates des mers du sud ne craignent pas les retards de paiement et nous savons à quel point la bête est précieuse pour VOTRE forme de piratage.$B$BApportez la somme de 1000 pièces d\'or à l\'embouchure de Lost Rigger Cove à Tanaris. Toute tentative de sauvetage du renne aura pour conséquence que Metzen entreprendra une très longue marche sur une planche très courte.$B$BYeargh ...$B$BLes Pirates de Southsea' WHERE `entry`=2842;
UPDATE `locales_page_text` SET `text_loc2`='Votre animal de compagnie prisé ne va pas très bien ici, dans la chaleur incessante de Searing Gorge ... vous serez avisé de répondre à nos demandes sans tarder. $B$BVous nous apporterez cinq étoiles rubis et la somme de 700 pièces d\'or - placez-les tous les deux dans un seul paquet, sans aucune marque. Laissez le colis aux portes de Stonewrought Pass dans une semaine.$B$BSi nous ne répondons pas à nos exigences, Metzen sera le plat principal d\'un festin traditionnel du Veil d\'hiver des nains de fer sombre. $B$BMmmm ... du renne ...' WHERE `entry`=2843;
UPDATE `locales_page_text` SET `text_loc2`='Plus vite que ses aigles, ses coursiers sont venus,$B$BEt il siffla, cria et les appela par leur nom; $B$B"Maintenant, Mercer! Maintenant, Chilton! Maintenant, Jordan et Kaplan!$B$BOn, Nagle! Sur Pardo! On, Goodman et Metzen!$B$BAu sommet du porche!$B$BMaintenant élancez-vous! élancez-vous! éloignez-vous tous! "$B$B' WHERE `entry`=2844;
UPDATE `locales_page_text` SET `text_loc2`='J\'espère sincèrement que, grâce à mes recherches, je pourrai trouver une source supplémentaire d\'énergie magique qui sera sans danger pour mon peuple. Avec le Sunwell parti, nous devons trouver un moyen de continuer notre mode de vie sans succomber à l\'attrait de la magie des arcanes.$B$BI pense que l\'avenir du sin\'dorei peut à nouveau servir d\'exemple à tous!' WHERE `entry`=2846;
UPDATE `locales_page_text` SET `text_loc2`='Pas de chance pour le moment.Le peu de magie à laquelle j\'ai accès doit être canalisé à travers le Spire. J\'ai cependant un certain nombre d\'idées intrigantes. Je demanderai à mes plus brillants apprentis de suivre ces pistes d\'enquête et de voir ce que nous allons faire.' WHERE `entry`=2847;
UPDATE `locales_page_text` SET `text_loc2`='Cela fait longtemps que je n\'ai rien écrit dans ces pages. Toujours rien de prometteur. Hier, j\'ai appris qu\'un des élèves de Sunstrider Isle, l\'un des Felendren, n\'avait pas tenu compte des conseils de ses mentors et avait succombé à l\'affliction. $BI redoublera d\'effort.' WHERE `entry`=2848;
UPDATE `locales_page_text` SET `text_loc2`='Rien.Je ne vais pas perdre espoir, cependant. Les Sin\'dorei ne peuvent pas se permettre de se trouver dans une position de dépendance magique en ce moment où nous sommes assiégés au sud. $B$BJ\'ai mis au point une approche entièrement nouvelle et, si elle réussit, elle me permettra de filtrer les "impuretés" de certaines sources d\'énergie magiques corrompues et magiques que j\'ai cachées. Je dois procéder avec prudence.' WHERE `entry`=2849;
UPDATE `locales_page_text` SET `text_loc2`='Incroyable!Nous avons rencontré un certain succès, même si la quantité de magie que nous avons pu extraire était infime. Je vais tirer la plupart des apprentis de leurs études pour me concentrer sur cette nouvelle approche prometteuse. $B$BAvec un peu de chance, nous devrions être en mesure d\'affiner le processus et de faire d\'une pierre deux coups - une source d\'énergie abondante pour nous-mêmes, et un moyen de contrer toute magie de féline que nous pourrions rencontrer à l\'avenir!' WHERE `entry`=2850;
UPDATE `locales_page_text` SET `text_loc2`='Non!Tout en expérimentant le nouveau procédé, mon apprenti principal, Telethayon, a soudainement et sans avertissement se ratatiné devant mes yeux, succombant dans l\'état qui afflige mes frères. J\'ai essayé de l\'arrêter, mais il était trop loin. Je n\'avais pas d\'autre choix que de le sortir de sa misère. $BSuch est le prix de la découverte, mais je sens déjà trop le poids de ce coût.' WHERE `entry`=2851;
UPDATE `locales_page_text` SET `text_loc2`='C\'est trop difficile à supporter ... deux autres apprentis ont succombé. Nous étions si prudents ... Je ne comprends pas ce qui ne va pas.$B$BI devra abandonner ces enquêtes et recommencer à zéro.' WHERE `entry`=2852;
UPDATE `locales_page_text` SET `text_loc2`='J\'étais trop tard.Sans le savoir, un troisième apprenti avait partagé les recherches encrassées avec d\'autres. Je vais essayer de contenir la situation, mais je dois d\'abord faire partir les apprentis non affectés.$B$BI le fera en leur faisant savoir que je pars en congé sabbatique à la retraite Farstrider, dans l\'espoir de trouver une nouvelle approche du problème.$B$BI doit trouver un moyen de racheter cette horrible erreur de jugement.' WHERE `entry`=2853;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> GRADES D\'OFFICIERS DE L\'HORDE </H1><BR/><P align = "center"> Partie 2 </P><IMG src = "Interface  PvPRankBadges  PvPRank09 "align =" left "/><BR/><P align =" right "> Centurion </P><BR/><BR/><IMG src =" Interface  PvPRankBadges  PvPRank08 "align =" left " /><BR/><P align = "right"> Légionnaire </P><BR/><BR/><IMG src = "Interface  PvPRankBadges  PvPRank07" align = "left" /><BR/>< P align = "right"> Garde de sang </P><BR/><BR/><IMG src = "Interface  PvPRankBadges  PvPRank06" align = "gauche" /><BR/><P align = "right" > Garde de pierre </P><BR/><BR/></BODY></HTML>' WHERE `entry`=2854;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> GRADES ENRICHIS DE L\'HORDE </H1><BR/><IMG src = "Interface  PvPRankBadges  PvPRank05" align = "left" /><BR/>< P align = "right"> Premier sergent </P><BR/><BR/><IMG src = "Interface  PvPRankBadges  PvPRank04" align = "gauche" /><BR/><P align = "right" > Sergent supérieur </P><BR/><BR/><IMG src = "Interface  PvPRankBadges  PvPRank03" align = "left" /><BR/><P align = "right"> Sergent </P><BR/><BR/><IMG src = "Interface  PvPRankBadges  PvPRank02" align = "left" /><BR/><P align = "right"> Grunt </P><BR/><BR / ><IMG src = "Interface  PvPRankBadges  PvPRank01"align = "left" /><BR/><P align = "right"> Scout </P><BR/><BR/></BODY></HTML>' WHERE `entry`=2855;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> CLASSEMENT DES OFFICIERS DE L\'ALLIANCE </H1><BR/><P align = "center"> Partie 2 </P><IMG src = "Interface  PvPRankBadges  PvPRank09 "align =" left "/><BR/><P align =" right "> Chevalier-Champion </P><BR/><BR/><IMG src =" Interface  PvPRankBadges  PvPRank08 "align =" left "/><BR/><P align =" right "> Capitaine de chevalier </P><BR/><BR/><IMG src =" Interface  PvPRankBadges  PvPRank07 "align =" left "/>< BR /><P align = "right"> Lieutenant-chevalier </P><BR/><BR/><IMG src = "Interface  PvPRankBadges  PvPRank06" align = "à gauche" /><BR/><P align = "right"> Chevalier </P><BR/><BR/></BODY></HTML>' WHERE `entry`=2856;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> RANKS ALLIANCE INSCRITS </H1><BR/><IMG src = "Interface  PvPRankBadges  PvPRank05" align = "left" /><BR/><P align = "right"> Sergent Major </P><BR/><BR/><IMG src = "Interface  PvPRankBadges  PvPRank04" align = "à gauche" /><BR/><P align = "right"> Maître Sergent </P><BR/><BR/><IMG src = "Interface  PvPRankBadges  PvPRank03" align = "left" /><BR/><P align = "right"> Sergent </P><BR /><BR/><IMG src = "Interface  PvPRankBadges  PvPRank02" align = "left" /><BR/><P align = "right"> Caporal </P><BR/><BR/>< IMG src = "Interface  PvPRankBadges  PvPRank01"align = "left" /><BR/><P align = "right"> Privé </P><BR/><BR/></BODY></HTML>' WHERE `entry`=2857;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><H1 align = "center"> d\'Anthony Ray Stark </H1><H2 align = "center"> 1961 - 2005 </H2></BODY></HTML>' WHERE `entry`=2858;
UPDATE `locales_page_text` SET `text_loc2`='BRAZIER D\'INVOCATION: MANUEL DE L\'UTILISATEUR$B$BOù trouver les locus hantés?$B$BINTRODUCTION$B$BVous trouverez dans ces pages les emplacements des esprits et des âmes déchus et corrompus impliqués dans l\'incident de Lord Valthalak\'s Amulet. Bien que vous ayez déjà sauvé mes anciens compagnons, ce serait ma conjecture que des échos répugnants du mal qui tordaient jadis leur esprit s\'attardent toujours sur ces loci hantés. Tel était le pouvoir malveillant de l\'esprit de Lord Valthalak. $B$BDe plus, le brasero peut être utilisé pour invoquer Lord Valathalak, bien que, comme avant, une extrême prudence soit de mise.' WHERE `entry`=2859;
UPDATE `locales_page_text` SET `text_loc2`='INTRODUCTION, suite ...$B$BLe brasier lui-même fonctionne exactement comme le brasier de Beckoning. Placez-le simplement à portée des cinq loci hantés et invoquerez les esprits des morts.$B$BEn remarque, parce que je sais que vous serez intéressé à savoir, la bannière de provocation peut être utilisée de la même manière sur l\'arène de Blackrock Depths. Assurez-vous de donner à Theldren et à ses sbires une leçon chaque fois que vous en avez l\'occasion.$B$BContinuez votre lecture si vous souhaitez approfondir vos connaissances.' WHERE `entry`=2860;
UPDATE `locales_page_text` SET `text_loc2`='ISALIEN$B$BPrêtresse de la lune elfe de la nuit$B$BQuand Isalien a décidé de prendre congé de la compagnie, elle l\'a fait avec l\'intention de faire un pèlerinage à Dire Maul, la ville déchue et ancestrale d\'un elfe de la nuit Eldre\'Thalas. Il y a encore une bibliothèque de grande connaissance, un sanctuaire qu\'un petit nombre d\'elfes de la nuit ont réussi à retenir. Elle était déterminée à trouver des réponses à la fois à son héritage et éventuellement aux pièces d\'amulettes. Il convient de noter qu\'elle était également une bonne amie de Falrin Treeshaper.' WHERE `entry`=2861;
UPDATE `locales_page_text` SET `text_loc2`='ISALIEN, a poursuivi ...$B$BMalheureusement, alors qu\'elle entrait dans la ville avec son compagnon d\'hippogriffe, Empyrean, ils ont été attaqués par Alzzin le Chasseur Sauvage et ses compagnons. Là, elle tomba pour ne plus jamais se relever. C\'est peut-être pour le mieux qu\'Isalien n\'a jamais révélé d\'autres secrets de la magie maléfique mise par Lord Valthalak pour contenir une partie de son essence dans l\'amulette.$B$BLa chambre à ciel ouvert où réside Alzzin est l\'un de ces loci hantés.' WHERE `entry`=2862;
UPDATE `locales_page_text` SET `text_loc2`='MOR GRAYHOOF$B$BDruide Tauren et premier à tomber$B$BMor Grayhoof a toujours été un compagnon fidèle et de confiance au sein de The Veiled Blade. Il est regrettable qu\'il ait été le premier à tomber sous le pied des malfaiteurs de Lord Valthalak.$B$BLorsque nous avons fui Valthalak, nous étions poursuivis par une véritable armée d\'assassins et de harceleurs spectraux. Le vol de Blackrock Spire était pour le moins chaotique. Malheureusement, en cours de route, Mor a perdu pied, bien que je soupçonne qu\'il a été poussé. En tout cas, il est tombé dans les renfoncements de la partie inférieure de la flèche.' WHERE `entry`=2863;
UPDATE `locales_page_text` SET `text_loc2`='MOR GRAYHOOF, a poursuivi ...$B$BPlus tard, nous avons découvert que Mor avait effectivement survécu à la chute. Mais il n\'y aurait pas de fin heureuse pour mon bon ami; il fut fait prisonnier par les trolls de Tazz\'Alaor. Peu de temps après, alors qu\'il était en train d\'être torturé par War Master Voone, un groupe d\'assassins de Valthalak est intervenu et a terminé le travail.$B$BSi vous cherchez à faire écho à l\'esprit de Mor Grayhoof, la chambre de War Master Voone est un lieu hanté.' WHERE `entry`=2864;
UPDATE `locales_page_text` SET `text_loc2`='JARIEN ET SOTHOS$B$BFrères et soeurs et futurs membres de la Croisade Scarlet$B$BJarien et Sothos étaient deux des personnes les plus intolérantes que je connaisse. Pourtant, leurs compétences en tant que combattants étaient exceptionnelles et provenaient de l\'argent. C\'est pourquoi je suppose qu\'Anthion, le dirigeant de The Veiled Blade, les a conservés. $B$BLes frères et sœurs se sont finalement épris des enseignements et de la philosophie de la Croisade Écarlate. Ils avaient toujours manifesté un zèle certain pour vaincre tous les fléaux que nous avons rencontrés au cours de nos voyages, et il était soupçonné que leur famille avait des liens avec l\'organisation.' WHERE `entry`=2865;
UPDATE `locales_page_text` SET `text_loc2`='JARIEN ET SOTHOS, a poursuivi ...$B$BC\'est donc qu\'ils se sont retrouvés devant les épreuves imposées à ceux qui cherchent à se joindre à la croisade. Un poste de chevalier au sein des rangs les intéressait tout particulièrement. quelque chose qui a apparemment nécessité des tests plus difficiles. Car il a été découvert plus tard que Sothos avait en réalité échoué à son dernier test. Jarien ne l\'aurait pas cependant et, défiant ouvertement, elle insista pour qu\'il puisse la rejoindre parmi les chevaliers. D\'après ce que nous avons recueilli, le grand croisé Dathrohan les a tués tous les deux sur place, au beau milieu de ses appartements.$B$BAinsi, un autre de ces loci hantés est Le Trône Pourpre à l\'intérieur du Bastion Écarlate à Stratholme.' WHERE `entry`=2866;
UPDATE `locales_page_text` SET `text_loc2`='KORMOK$B$BNécromancien à ogres à deux têtes$B$BQue peut-on dire de Kormok? C\'était un ogre à deux têtes du type lanceur qui n\'était pas si mauvais auparavant. Lorsque nous l\'avons amené pour la première fois au bercail, c\'était en tant que prisonnier d\'un travail que nous avions accepté à Burning Steppes. Il s\'est révélé un atout précieux pour nous aider à nous sortir de la situation délicate dans laquelle nous sommes tombés, cependant, et il est donc devenu l\'un des premiers mages de la société.$B$BLa chose étrange à son sujet cependant était la nature complètement différente de ses deux têtes. L\'un était toujours gai, sinon un peu idiot, tandis que l\'autre était sournois, calculateur et avait les yeux presque morts. C\'est vraiment effrayant.' WHERE `entry`=2867;
UPDATE `locales_page_text` SET `text_loc2`='KORMOK, a poursuivi ...$B$BPlus tard, au cours de nos voyages, Kormok a été exposé au côté nécromantique de la magie.Malheureusement, la tête effrayante manifestait une affinité pour élever des esprits morts et y prenait un plaisir pervers, notamment en convoquant les esprits exilés de l\'Ile de la Purgation pour exécuter ses ordres. $B$BAprès la dissolution de la société, Kormok s\'est inscrit à la Scholomance pour poursuivre ses «études». Les assassins spectraux ont trop facilement trouvé l\'entrée à l\'école et l\'ont assommé devant son nouveau maître, Ras Frostwhisper. $B$BCeci fait de la chambre de Ras Frostwhisper l\'un des endroits hantés où le brasero peut être utilisé avec succès.' WHERE `entry`=2868;
UPDATE `locales_page_text` SET `text_loc2`='LORD VALTHALAK$B$BDraconid Noble et ancien général de Blackrock Spire$B$BAvant le général Drakkisath, il y avait Lord Valthalak, un démoniste draconide d\'un pouvoir immense. Il est courant que les dirigeants de Spire changent de mains et malheureusement pour nous, nous allions jouer un rôle dans le dernier changement de pouvoir.$B$BNous avions été commandités par un Grendik Rizzlesprang, un magicien gobelin passeur de Booty Bay, pour voler le livre de sorts de Valthalak. Nous ne savons pas s\'il voulait l\'étudier ou le vendre (ou les deux), et cela n\'a pas d\'importance maintenant. Ce qui compte, c\'est que nous nous sommes infiltrés dans la partie supérieure de Blackrock Spire et avons tué Lord Valthalak dans son repaire. C\'est à ce moment que le plaisir a vraiment commencé.' WHERE `entry`=2869;
UPDATE `locales_page_text` SET `text_loc2`='LORD VALTHALAK, a poursuivi ...$B$BNous ne savions pas à quel point la connaissance magique de Valthalak était profonde en ce qui concerne la manipulation de l\'âme, sinon nous n\'aurions probablement jamais pris le poste. Mais prenons cela comme nous l\'avons fait et c\'est le dernier travail que nous aurions à prendre.$B$BAprès avoir tué Lord Valthalak et pris son livre de sorts, Kormok a dû sentir quelque chose à propos de son amulette. Il a insisté pour que ce soit sa part du butin, qui s\'est rapidement transformée en un débat houleux et presque une bagarre autour de la chose. Nous ne savions pas à l\'époque que l\'amulette abritait une bonne partie de l\'esprit de Valthalak; nous l\'avons découvert plus tard, à notre plus grand déplaisir.' WHERE `entry`=2870;
UPDATE `locales_page_text` SET `text_loc2`='LORD VALTHALAK, continua ...$B$BEn séparant l\'amulette en morceaux, nous fuyâmes le fantôme nouvellement ressuscité de Valthalak, ainsi que son armée d\'assassins spectraux. Et le bruit que nous avons fait à propos de l\'amulette avait réveillé un certain nombre des derniers défenseurs de la Spire!$B$BNous sommes presque tous sortis de Blackrock et nous pensions être en sécurité. Mais, même de l\'au-delà, Valthalak a envoyé ses sbires sur nous, peu importe où nous nous sommes cachés. La lame voilée s\'est brisée peu de temps après cette mission et nous avons ensuite été très faciles à choisir.$B$BComme vous le savez sûrement, cela fait de Lord Valthalak le lieu de résidence actuel de la chambre de The Beast à Blackrock Spire, le dernier des lieux hantés.' WHERE `entry`=2871;
UPDATE `locales_page_text` SET `text_loc2`='A tous les hommes et femmes valides:$B$BLe Fléau est revenu et nous devons mettre de côté nos querelles pour faire face à cette menace. L\'Aube d\'argent ne refusera personne qui souhaite prendre les armes contre la menace du Fléau.$B$B Ceux qui veulent défendre nos terres doivent se rassembler à la chapelle de l\'Espoir de Lumière dans les Maleterres de l\'Est. Ensemble, nous vaincrons.$B$B Gardien des registres$BL\'Aube d\'argent' WHERE `entry`=2872;
UPDATE `locales_page_text` SET `text_loc2`='Mon Emily,$B$BA il y a quelques jours à peine, nous avons fait une pause dans ce lieu abandonné par Light, opérant sous les ordres du roi de rentrer chez eux. Mon cœur était léger malgré mon environnement morne, car je savais qu\'après le glacial trekking jusqu\'au rivage et ce long et pénible voyage, je trouverais un réconfort dans vos bras.$B$BNous avons atteint le rivage aujourd\'hui pour trouver nos navires, notre moyen de retour, rien de plus que des cosses carbonisées; nous ne pouvons pas partir et n\'avons pas d\'autre choix que de nous enfoncer au cœur de cette terre désolée.$B$BI sont allés jusqu\'au bout du monde pour assurer votre sécurité, Emily ... et maintenant ... je souhaite de tout mon cœur d\'être restée à Lordaeron avec vous.$B$BDes pensées de vous remplissent chaque moment de mon réveil. Tu es ma chaleur dans cette terre glacée, bien-aimée, et personne ne peut me la prendre.$B$BMaxwell' WHERE `entry`=2878;
UPDATE `locales_page_text` SET `text_loc2`='Reuben,$B$BI, écris cette lettre en sachant que tu ne la verras peut-être jamais; Je ne peux tout simplement pas rester inactif, en écoutant le martèlement constant contre les murs de Hearthglen. Les morts-vivants se trouvent en dehors de notre village, sans relâche dans leur assaut, et nous avons été accusés de défendre les habitants de la ville jusqu\'à l\'arrivée des renforts.$B$BMa jambe était cassée lors de la dernière charge, je me suis donc assis, inutile, avec mon épée à mes côtés s\'il devait y avoir une brèche dans nos défenses. Il n\'y a pas de plaisanteries inutiles ... seulement des bruits de combat et de mort. L\'air est épais de peur.$B$BLe prince Arthas est ici et se bat sur les lignes de front avec les hommes. S\'il n\'était pas présent, nous serions tombés il y a longtemps. Son amour pour cette terre et ses habitants est contagieux. Je sers volontiers sous lui et le ferai jusqu\'à la fin de mes jours.$B$BLes combats deviennent plus intenses; jambe cassée ou pas, je ne peux pas m\'asseoir ici.Chaque épée est nécessaire. J\'espère que ces mots vous trouveront dans des moments plus heureux.$B$BVotre ami, $BLeagrem' WHERE `entry`=2880;
UPDATE `locales_page_text` SET `text_loc2`='$B$BHeliana,$B$BI, Vargus, mais en vie, malgré vos souhaits. Je méprise les virées à la campagne dans cette armure ridicule.$B$BOur, selon les dernières commandes, nous dirigeons vers Andorhal après un grain ou une telle absurdité. Pourquoi devrais-je me soucier des magasins d\'alimentation à Andorhal, de tous les lieux? Toute cette idée d\'être dans l\'armée est ridicule.$B$BRest a assuré, tante Heliana, que je reviendrai pour votre héritage; même si je suis grièvement blessé et que je reste sans nourriture, la pure méchanceté me poussera toujours plus loin.$B$BVargus$B$B' WHERE `entry`=2881;
UPDATE `locales_page_text` SET `text_loc2`='Très chère Amelia,$B$BCe soir, j\'ai vu des choses qui me hanteront jusqu\'à la fin des temps.$B$BStratholme est en flammes ... et nous en sommes responsables.$B$BNotre prince nous a conduits dans les rues de la ville ce soir; il nous a ordonné d\'entrer par effraction dans les maisons des citadins et ... de les tuer dans leur sommeil. Ils ont été tourmentés, ont réclamé Lord Arthas et ont dû être tués avant de nous tuer.$B$BC\'était un massacre. Des centaines de personnes sont mortes sous le sabre de ceux qui ont juré de les protéger. Je ne pouvais plus le supporter Je me suis enfui.$B$BDesserteur possible, mais je ne pouvais pas commettre de telles atrocités. Dans chaque foyer, je n\'ai pu m\'empêcher de voir votre visage, ou celui de nos enfants, sur les victimes au moment de leur mort. Si se tenir contre cela signifie être un traître, alors qu\'il en soit ainsi.$B$BI espère pouvoir retrouver votre chemin à temps, mais les routes sont dangereuses. Donne à nos enfants mon amour en mon absence.$B$BJames' WHERE `entry`=2882;
UPDATE `locales_page_text` SET `text_loc2`='Samla,$B$BAlors que nous suivons notre Seigneur Arthas au nord, toujours au nord, mon cœur devient lourd. Alors qu\'il brillait jadis avec la Lumière, je ressens maintenant un assombrissement de l\'esprit du jeune paladin. Son zèle est assombri par une couvée de cauchemar dans son âme que je ne peux pas deviner ...$B$BNous allons bientôt atteindre les terres gelées.Bien que beaucoup de ses hommes tombent malades du froid et des combats contre des bêtes putrides, Arthas nous dit que ce qu\'il cherche sur la glace va inverser le cours de la bataille. Mais je ne pris aucune réconfort dans ses paroles, car après qu\'il les ait dites ... le sourire sinistre planté sur ses lèvres bouclées me glaça plus profondément que ne pouvait le blizzard. $B$BPriez pour nous, Samla, et pour notre monde,$B$BTorgal' WHERE `entry`=2883;
UPDATE `locales_page_text` SET `text_loc2`='Chère Pamela,$B$BDemain, nous nous installerons à Andorhal et je crains que ce ne soit ma dernière lettre, mon amour. Les morts-vivants ne sont pas fascinés et nous craignons que nous ne soyons qu\'humains. Nous ne pouvons pas les retenir. Mais ne vous inquiétez pas, car même si nous sommes sûrs de périr, notre espoir demeure fort. La lumière prévaudra!$B$BEt, ma chérie, je suis réconfortée sachant que, lorsque ces terreurs sombres envahissent nos murs et s\'écroulent sur nous, mes dernières pensées ne seront pas la mort. Je penserai à toi dans mes bras et je saurai enfin la paix.$B$BChristoph' WHERE `entry`=2884;
UPDATE `locales_page_text` SET `text_loc2`='Bonjour, $N. En fin de compte, j\'ai trouvé quelques informations dans cette note. $B$BSur James, je n\'ai que peu de choses à trouver. Cependant, j\'ai découvert un peu d\'informations familiales sur Amelia. $B$BAlors que ni elle ni ses enfants n\'ont survécu à la guerre, son frère, un certain Alexandre Calder; vous pouvez le trouver dans la caverne perdue de Forgefer, en train d\'étudier la dangereuse magie de la Légion Ardente. $B$BElle avait également une cousine qui lui était proche, une femme maintenant connue sous le nom de Deze Snowbane, qui promeut la bataille du bassin Arathi au sein d\'Orgrimmar. $B$BVous devriez en visiter un lors de votre prochain passage dans la région. Je suis sûr qu\'ils seront intéressés d\'apprendre leur Amelia perdue, si rien d\'autre.' WHERE `entry`=2885;
UPDATE `locales_page_text` SET `text_loc2`='Salutations, $N. Je pensais que vous pourriez être intéressé par les résultats de mes recherches.$B$BIl s\'avère que Reuben et Leagrem ont survécu - pour ainsi dire - à leur famille. Même s\'ils sont déjà conscients du sort du soldat perdu, visiter l\'un d\'eux peut s\'avérer une entreprise louable.$B$BVous pouvez trouver l\'ex-femme de Reuben, Joanna Whitehall, dans le quartier de la guerre d\'Undercity, où elle passe la plupart de son temps. Je ne sais pas à quel point elle sera réceptive à la nouvelle, l\'esprit.$B$BQuant à Leagrem, sa tante est devenue la matronne de l\'orphelinat à Hurlevent, une position admirable à occuper.$B$BQuel que soit votre parcours dans la vie, je vous souhaite la meilleure des chances dans vos projets.' WHERE `entry`=2886;
UPDATE `locales_page_text` SET `text_loc2`='$N, j\'espère que cette lettre vous trouve bien.Après quelques recherches, j\'ai découvert des informations sur ceux qui ont écrit la note que vous avez retrouvée. $B $BLe père de BSamla dirige les Réprouvés et les Trolls qui font le pèlerinage à Thunder Bluff selon les principes de sa foi. Il s\'appelle Miles Welsh et vous le retrouverez avec son compagnon de morts-vivants dans Pools of Vision.$B$BTorgal, quant à lui, laisse dans le deuil son cousin, Elissa Dumas. C\'est une sorte de solitaire de ce que j\'ai compris; elle passe ses journées dans le Temple de la Lune à Darnassus.$B$BI vous encourage à rendre visite à qui vous voulez, $N. Ils n\'ont peut-être jamais appris le sort de Torgal ou de Samla.' WHERE `entry`=2887;
UPDATE `locales_page_text` SET `text_loc2`='Le gardien a transmis la lettre que vous avez découverte, $N, et j\'ai pensé que vous pourriez apprécier une mise à jour.$B$BLe dernier membre de la famille survivant du "Vargus" mentionné a été conduit dans le désert de Silithus, dans le Fort cénarien. Il s\'appelle Garon Hutchins.$B$BVous devriez parler avec lui lors de votre prochaine excursion dans les déchets. Il peut être intéressé d\'entendre ce que nous avons appris.' WHERE `entry`=2888;
UPDATE `locales_page_text` SET `text_loc2`='SalutationsJ\'ai enfin trouvé le temps d\'examiner cette missive que vous avez trouvée sur le Fléau. Bien que cela ait pris du temps, je pense avoir découvert un parent vivant du défunt.$B$BLa sœur de Maxwell, nommée Zarena Cromwind, exploite un petit magasin d\'armurerie à Booty Bay. Je ne sais pas à quel point elle était proche de son frère, mais si vous vous trouvez de passage, vous devriez organiser une réunion. La fermeture sur ces questions est précieuse et rare de nos jours.' WHERE `entry`=2889;
UPDATE `locales_page_text` SET `text_loc2`='J\'ai enfin mis au jour des informations sur l\'ancien soldat que vous avez combattu. La piste était difficile à suivre car cet arbre généalogique particulier n\'était pas correctement documenté, mais j\'ai enfin trouvé un parent par le sang. $B$BCroiriez-vous que le gardien Alen de la chapelle de Light\'s Hope est en fait le fils de Christoph? Cela pourrait apaiser son esprit d\'entendre parler de son père. Si jamais vous vous retrouvez de nouveau au service de l\'Aube d\'argent dans les Plaguelands, parlez-lui s\'il vous plaît. Ce serait une gentillesse.' WHERE `entry`=2890;
UPDATE `locales_page_text` SET `text_loc2`='En souvenir du commandant suprême des forces alliées, Anduin Lothar: un homme qui sacrifierait tout pour la défense de son roi, de son peuple, de sa maison ...$B$BQue nos ennemis connaissent nos noms.Laissons nos alliés honorer notre décès.Nous sommes les fils de Lothar. $B$B- Général Turalyon' WHERE `entry`=2891;
UPDATE `locales_page_text` SET `text_loc2`='Ici se trouve les restes d\'un gros Peon de cœur. Il a essayé de se battre, mais seulement pété.' WHERE `entry`=2892;
UPDATE `locales_page_text` SET `text_loc2`='Voici un pot de cendres. Ce sont les cendres de ma santé mentale, de ma passion et de mon dynamisme. Tous, complètement détruits par eux-mêmes. Que tous ceux qui contemplent ces terres désolées de Hellfire se souviennent de ce péon déchu. Il a versé du sang pour l\'Alliance et s\'est sacrifié pour la Horde uniquement pour être rendu complètement fou de ceux qui étaient méchants et sans âme et qui dévoraient ce qu\'il tenait le plus à cœur. Comme ils se régalent de ses labeurs, qu\'ils subissent sa colère. Peut-être pas dans ce monde, mais dans chaque monde ci-après. C\'est ma déclaration, mon serment solem et ma promesse éternelle.Je vengerai ma souffrance.' WHERE `entry`=2905;
UPDATE `locales_page_text` SET `text_loc2`='Enquêter sur l\'étendue des îles et faire rapport. $B$BN\'oubliez pas, ne vous faites pas prendre! $B$BNe me manque pas, Candress! $B$BS.' WHERE `entry`=2906;
UPDATE `locales_page_text` SET `text_loc2`='Le croquis montre Shadra\'Alor et ses trois temples, avec un certain nombre de marques «x», correspondant aux emplacements des œufs volés. Les marques apparaissent à l\'extrémité sud du temple oriental, près d\'une hutte au nord-est du temple oriental, près d\'un autel à l\'est du lac central et sur la pente du temple sud près du mot "exil". $B$BLes notes de Featherbeard indiquent qu\'il avait l\'intention de transmettre cette information à Falstad Wildhammer, qui, espérait-il, enverrait une bande de guerriers pour sauver les œufs.' WHERE `entry`=2907;
UPDATE `locales_page_text` SET `text_loc2`='Diathorus,$B$BI, espère que cette lettre finira par vous parvenir. Ces démons sont stupides.Tout ce à quoi ils pensent, ce sont leurs ongles, leurs fouets ou quelque chose d\'ennuyeux avec leurs pointes sur la tête. Et je n\'ose pas utiliser l\'un des infernaux de peur qu\'il ne soit réduit en cendre! $B$BMalheureusement, je souhaite ardemment le temps où nous pourrions utiliser les orcs.Au moins, ils avaient un demi-cerveau et pouvaient suivre les ordres. Peut-être y a-t-il une opportunité de prendre de nouveaux thralls des environs?' WHERE `entry`=2909;
UPDATE `locales_page_text` SET `text_loc2`='Les récits des elfes de la nuit assistant les draeneï sont vrais. Ils tentent d\'installer une nouvelle base d\'opérations à Forest Song, au nord, près de Satyrnaar. Nous devrions nous concerter pour combiner nos forces en vue d\'une attaque avant la prochaine pleine lune de ce monde pathétique. Si nous les écrasons maintenant, ils ne gagneront jamais un pied à terre à notre porte! $BRegards, $BGorgannon $Bp.s. - Arrêtez d\'utiliser toute l\'encre de sang sur les lettres d\'amour adressées à mes guignols! Les vierges elfes de la nuit se font rares ces jours-ci.' WHERE `entry`=2910;
UPDATE `locales_page_text` SET `text_loc2`='$B$BDiathorus,$B$BI espérons que cette lettre finira par vous parvenir.Ces démons sont stupides. Tout ce à quoi ils pensent, ce sont leurs ongles, leurs fouets ou quelque chose d\'ennuyeux avec leurs piques. Et je n\'ose pas utiliser l\'un des infernaux de peur qu\'il ne soit réduit en cendres!$B$BSadly, je souhaite ardemment le temps où nous pourrions utiliser les orcs. Au moins, ils avaient un demi-cerveau et pouvaient suivre les ordres. Peut-être y a-t-il une chance de prendre de nouveaux thralls des environs?' WHERE `entry`=2911;
UPDATE `locales_page_text` SET `text_loc2`='$B$BLe moment est venu de reprendre quelques courses mineures, je pense.Splintertree Post au nord-ouest est une cible tentante. Nous devrions nous concerter pour combiner nos forces en vue d\'une attaque avant la prochaine pleine lune de ce monde pathétique. Si nous les soumettons maintenant, ils se souviendront de ce que le mot "Horde" voulait vraiment dire!$B$BRegards,$B$BGorgannon$B$Bp.s. - Arrêtez d\'utiliser toute l\'encre de sang sur les lettres d\'amour adressées à mes guignols! Les vierges elfes de la nuit se font rares ces jours-ci.' WHERE `entry`=2912;
UPDATE `locales_page_text` SET `text_loc2`='Bien que presque défait $B, son courage est resté fort et a dégainé son épée $BTook.$B$BDepuis ce jour violent, $BNe\'r ne fléchirait pas, pas plus que $BFind, en bataille ou en bataille, $BIts menant au seigneur nain' WHERE `entry`=2913;
UPDATE `locales_page_text` SET `text_loc2`='... mais elle le savait déjà.Elle s\'était résignée à vivre en paria après avoir troqué son âme en morceaux. La vengeance en valait la peine, du moins le pensait-il.$B$Billet était une chance de retrouver son humanité et tout l\'étranger demandé en échange était sa confiance.$B$B "Je vais le faire", a déclaré Katreyn. "Je n\'ai rien à perdre."' WHERE `entry`=2914;
UPDATE `locales_page_text` SET `text_loc2`='Vazruden,$B$BDepuis la réouverture de la Porte des ténèbres, Honor Hold et Thrallmar ont reçu des renforts importants d\'Azeroth. J\'ai vu des rapports suggérant qu\'ils envisagent une attaque sur la citadelle, et nous devons également nous battre avec la Légion Ardente. C\'est inacceptable!$B$BNe mettez pas en péril l\'opération dans la fournaise à sang! Les félins qui y sont produits sont la clé de notre succès. Renforcez les défenses sur les remparts et postez immédiatement plus de gardes du périmètre! $B$BFaites-moi confiance, vous ne voulez pas que je vous rende visite. $B$B- Illidan' WHERE `entry`=2916;
UPDATE `locales_page_text` SET `text_loc2`='Vazruden,$B$BDepuis la réouverture de la Porte des ténèbres, Honor Hold et Thrallmar ont reçu des renforts importants d\'Azeroth. J\'ai vu des rapports suggérant qu\'ils envisagent une attaque sur la citadelle, et nous devons également nous battre avec la Légion Ardente. C\'est inacceptable!$B$BNe mettez pas en péril l\'opération dans la fournaise à sang! Les félins qui y sont produits sont la clé de notre succès. Renforcez les défenses sur les remparts et postez immédiatement plus de gardes du périmètre! $B$BFaites-moi confiance, vous ne voulez pas que je vous rende visite. $B$B- Illidan' WHERE `entry`=2917;
UPDATE `locales_page_text` SET `text_loc2`='J\'ai fait ça.Je ne me souviens pas de l\'avoir fait, mais il ne peut en être autrement.$B$BI ont commis des péchés dans ma vie auparavant. J\'ai même tué un homme une fois qui ne le méritait pas. Mais c\'est différent.$B $BI suis changé. $B$BCes meurtres n\'étaient pas mon choix. Une bête habite en moi.$B$BI sera chassé et évité.J\'ai besoin de me cacher. Courir.Enterrer les corps.$B$BI commence à marcher dehors quand je réalise ce qui me dérange vraiment.$B$BI ne ressentons aucune culpabilité.' WHERE `entry`=2918;
UPDATE `locales_page_text` SET `text_loc2`='Servant,$B$BPlusieurs années de planification ont été perdues. Les draeneï et leur pitoyable chef ont encore échappé à la main du Maître! De plus, ils ont volé mon navire et l\'ont écrasé sur Azeroth! $B$BS\'il est vrai que l\'agent eredar de Master a échoué, elle a pu nous transmettre l\'emplacement de l\'Exodar. Heureusement, son identité reste un mystère pour les draeneï. Nous pourrons peut-être encore l\'utiliser pour nos besoins.' WHERE `entry`=2919;
UPDATE `locales_page_text` SET `text_loc2`='Contactez notre espion et préparez-vous à un assaut à grande échelle contre les draeneï. Tuez-les tous et récupérez mon vaisseau. Vous êtes le seul lien que j\'ai avec Azeroth capable de réparer les dégâts infligés par les brutes à l\'Exodar. $B$BNe me manque pas, gnome. Si votre désir de richesse interfère avec mes ordres, vous passerez l\'éternité dans la prière. Vous prierez pour que la mort vienne vous libérer des tortures que je déchaînerais si vous échouiez. $B$B-King Sunstrider' WHERE `entry`=2920;
UPDATE `locales_page_text` SET `text_loc2`='Chers leaders de la Horde,$B$BC\'est avec un grand plaisir que moi - Lor\'themar Theron, régent seigneur de Quel\'Thalas - j\'annonce la chute du commandant du Fléau connu sous le nom de Dar\'Khan Drathir. $B$BLe pied de Dar\'Khan sur nos terres a été évoqué par les détracteurs de la race des sin\'dorei lors de nombreuses négociations précédentes. Nous espérons que cet événement répond à toutes les objections précédentes que vos grâces auraient pu susciter. $B$B<Signé> Lor\'themar Theron' WHERE `entry`=2923;
UPDATE `locales_page_text` SET `text_loc2`='Chers leaders de la Horde,$B$BC\'est avec un grand plaisir que moi - Lor\'themar Theron, régent seigneur de Quel\'Thalas - j\'annonce la chute du commandant du Fléau connu sous le nom de Dar\'Khan Drathir. $B$BLe pied de Dar\'Khan sur nos terres a été évoqué par les détracteurs de la race des sin\'dorei lors de nombreuses négociations précédentes. Nous espérons que cet événement répond à toutes les objections précédentes que vos grâces auraient pu susciter. $B$B<Signé> Lor\'themar Theron' WHERE `entry`=2924;
UPDATE `locales_page_text` SET `text_loc2`='$B$BPS - Notre grand magister a reçu une réponse de Outland concernant la question de Thrall. La réponse est oui, avec une grande certitude.$B$B <Cette lettre porte le sceau supplémentaire de Lady Sylvanas Windrunner>' WHERE `entry`=2925;
UPDATE `locales_page_text` SET `text_loc2`='$B$BPS - Notre grand magister a reçu une réponse de Outland concernant la question de Thrall. La réponse est oui, avec une grande certitude.$B$B <Cette lettre porte le sceau supplémentaire de Lady Sylvanas Windrunner>' WHERE `entry`=2926;
UPDATE `locales_page_text` SET `text_loc2`='$N,$B$BLa création du ranseur à la trempe de sang nécessite une variété de matériaux rares et volatiles, ce qui en fait une arme redoutable d\'artisanat de qualité supérieure. Je listerai les matériaux du plus facile au plus difficile à obtenir.$B$BN\'oubliez pas que vous devez également sécuriser votre insigne Blood Knight avant que les matériaux ne soient acceptés et que le processus de fabrication ne puisse commencer.' WHERE `entry`=2930;
UPDATE `locales_page_text` SET `text_loc2`='Sang du courroucé - Afin de bien tempérer le métal de votre arme, il faut la tremper dans le sang du courroucé. Bien que le sang de la plupart des démons détienne une quantité substantielle de pouvoir, le sang ordinaire ne suffit pas pour forger votre ranseur.$B$BLes enquêtes discrètes avec les démonistes d\'Orgrimmar se sont révélées utiles pour localiser une réserve de sang. Les sectateurs Searing Blade de Ragefire Chasm possèdent un orbe rempli du genre de sang dont nous avons besoin pour votre arme. On me dit que l\'orbe est gardé par un orc qui s\'appelle lui-même Jergosh l\'invocateur.$B$BFaites preuve de prudence, comme dans toutes vos relations avec des pouvoirs démoniaques. Vous ne pouvez jamais dire ce qui pourrait arriver lors de la manipulation d\'une substance aussi puissante.' WHERE `entry`=2931;
UPDATE `locales_page_text` SET `text_loc2`='Caisse de lingots forgés dans le sang - L\'acier est un métal précieux, mais une arme forgée à partir de celui-ci n\'a rien de remarquable. Les lingots forgés par le sang représentent un matériau de qualité supérieure créé selon un processus qui est un secret bien gardé. La rumeur veut que tout forgeron assez audacieux pour manipuler et travailler le métal devient maudit, mais nos artisans l\'ont prouvé faux.$B$BLa réputation du métal augmente sa rareté, mais les Réprouvés ont annoncé qu\'une réserve pourrait exister dans le donjon de Shadowfang. Un forgeron qualifié du nom de Landen Stilwell devint assez curieux pour introduire une caisse de lingots dans le donjon. On ne sait pas où Stilwell garde sa réserve, mais notre informateur nous dit que le forgeron est retenu prisonnier dans le cachot du donjon. Utilisez tous les moyens à votre disposition pour obtenir les lingots.' WHERE `entry`=2932;
UPDATE `locales_page_text` SET `text_loc2`='Une pierre corrompue - Les tomes des magistres indiquent que ces pierres précieuses sont utilisées par certains lanceurs de sorts pour contenir de l\'énergie dans leurs magies, les rendant encore plus meurtrières. Bien que des versions purifiées de la pierre précieuse puissent être utilisées en toute sécurité par les lanceurs de sorts compagnons, elles sont trop faibles pour nos besoins.$B$BDans les grottes de Blackfathom Deeps, les lanceurs de sorts naga ont imprégné leurs gorges de kor de leurs propres pouvoirs obscurs, en les augmentant de manière à ce qu\'ils soient parfaits pour une utilisation dans votre ranseur. Tuez-les sans pitié jusqu\'à ce que vous ayez trouvé la gemme dont vous aurez besoin.' WHERE `entry`=2933;
UPDATE `locales_page_text` SET `text_loc2`='Les insignes que vous allez porter en tant qu\'adepte ont été portés pour la dernière fois par un chevalier de sang courageux et intrépide de la famille Dawnstar. Son service exemplaire est de bon augure pour votre propre vie dans l\'ordre. $B$BLe chevalier sanglant Dawnstar a mené une attaque audacieuse mais infructueuse contre le bastion du Fléau de Deatholme.Son corps repose là où il est mort, au combat contre nos plus grands ennemis.Trouvez-le, $N, et rendez-moi ses insignes.Votre succès lui fera honneur, à vous et à tous les vrais chevaliers de sang.' WHERE `entry`=2934;
UPDATE `locales_page_text` SET `text_loc2`='Vous avez découvert l\'emplacement du sanctuaire! Après un examen plus approfondi, vous ressentez une impulsion plus forte du pouvoir étrange qui s\'est emparé de l\'île. Vous vous sentez un peu mal à l\'aise à côté du sanctuaire ... et peut-être un peu dérangé. $B$BSur la plaque de bronze placée à côté du sanctuaire, on peut lire:$B$B"Le sanctuaire de Dath\'Remar, hommage à un elfe noble, est ici. Laissez tous ceux qui contemplent ce monument se souvenir de son sacrifice contre le Fléau et son dévouement à la cause de notre survie continue. Tous ceux qui prospèrent à Quel\'Thalas le font grâce à lui. "' WHERE `entry`=2936;
UPDATE `locales_page_text` SET `text_loc2`='J\'ai regardé comme ils l\'ont battu sans pitié.Leur lieutenant impitoyable a agi comme si une telle cruauté était un acte de normalité.À travers les passages à tabac sauvages, je me sentais casser. $B$BAlors que je regardais Vindicator Saruan prendre leurs coups, je me suis mis à pleurer. Pas par peur ... J\'ai pleuré de tristesse. Voir un draeneï du Triumvirat traité de cette manière. Si seulement j\'avais pu me libérer de mes liens. Si seulement ...$B$B' WHERE `entry`=2937;
UPDATE `locales_page_text` SET `text_loc2`='Après ce qui semblait être une éternité, le vindicateur a perdu conscience. La torture était trop, même pour lui. Le cruel - Matis, comme j\'ai appris qu\'il s\'appelait - a tenté de réveiller Saruan en éclaboussant de l\'eau de contagion sur son corps ravagé. Mais Saruan ne s\'est pas réveillé.$B$B' WHERE `entry`=2938;
UPDATE `locales_page_text` SET `text_loc2`='Je voulais hurler mais seul un faible hoquet est sorti de ma bouche. J\'ai aussitôt reçu une volée de coups pour cet acte de rébellion. Je me suis retrouvé étendu au sol, et j\'ai vu que Matis faisait les cent pas devant le corps de Saruan. Il avait l\'air visiblement inquiet. Il a dit que Sironas, ou Sirona, ou une entité de ce type lui arracherait la chair des os si Saruan était mort à cause d\'eux.' WHERE `entry`=2939;
UPDATE `locales_page_text` SET `text_loc2`='Que l\'entité Sironas avait des projets pour Saruan ...$B$B<L\'écriture s\'arrête brusquement et une longue ligne défile sur la page. Ce draeneï est mort en faisant l\'entrée.>$B$B' WHERE `entry`=2940;
UPDATE `locales_page_text` SET `text_loc2`='Lady Liadrin,$B$BLe problème récent avec la créature captive a endommagé certains de nos équipements les plus vitaux. Le rétablissement de son contrôle a mis à rude épreuve mes magistères et mes ressources. J\'ai soumis des rapports sur l\'incident au grand magistère Rommath, mais il a tardé à réagir. Je crains que sans les documents suivants, nous puissions être forcés de réduire nos opérations et vous savez ce que cela signifierait pour vos chevaliers.$B$BS\'il y a quelque chose que vous pouvez faire pour aider, j\'ai inclus une liste de documents à la page suivante. Toute aide que votre commande pourrait fournir serait grandement appréciée.$B$BMagister Astalor Bas-sang' WHERE `entry`=2941;
UPDATE `locales_page_text` SET `text_loc2`='* 40 Tissu runique$B$B * 6 Barres d\'arcanite$B$B * 10 Sungrass$B$B * 5 Runes Noires$B$B * 150 Or' WHERE `entry`=2942;
UPDATE `locales_page_text` SET `text_loc2`='Déclaration du roi soleil$B$BSunhawks, mes précieux et loyaux enfants, votre roi vous félicite. Votre présence sur Azeroth est de la plus haute importance pour moi et pour le Maître.$B$BBientôt, le portail solaire sera prêt à transporter des renforts de Tempest Keep directement à Bloodmyst Isle.$B$BNous allons récupérer l\'Exodar. Nous allons tuer les bâtards à la peau bleue là où ils se trouvent. Velen va payer pour ses crimes.$B$B-King Kael\'thas Sunstrider' WHERE `entry`=2943;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><BR/><BR/><H1 align = "center"> LES HAUTES COLLINES VERTES DE L\'ÉTRANGLE </H1><BR/><H3 align = "center"> de Hemet Nesingwary </H3></BODY></HTML>' WHERE `entry`=2945;
UPDATE `locales_page_text` SET `text_loc2`='Notre première journée s\'est déroulée aussi bien que l\'on peut s\'y attendre. La plupart de notre temps a été occupé à prendre les dispositions nécessaires pour établir un camp de base. J\'ai localisé un cadre idéal près d\'une crique d\'eau douce. À en juger par les vieux quais abandonnés à proximité, ce site a déjà été habité. En ce qui concerne les habitants d\'origine, seul le temps peut raconter cette histoire.' WHERE `entry`=2946;
UPDATE `locales_page_text` SET `text_loc2`='Pour cette expédition, j\'ai réuni Ajeck Rouack et Sir S.J. Erlgadin, ainsi que mon fidèle serviteur, Barnil Stonepot. J\'ai combattu aux côtés du père d\'Ajeck dans de nombreuses batailles pour la défense de l\'Alliance. La voir grandir est assez spéciale.Son père l\'a bien instruite dans le maniement des armes. Ses talents d\'archet me font me demander s\'il y a du sang elfique qui coule dans ses veines.' WHERE `entry`=2947;
UPDATE `locales_page_text` SET `text_loc2`='Sir SJ Erlgadin vient de l\'aristocratie humaine. Son père, le comte Erlgadin, était réputé pour sa générosité. Ce fut le comte qui fit pression pour que les conditions de travail de la guilde des tailleurs de pierre soient améliorées lors de la restauration de Hurlevent après la seconde guerre mondiale.' WHERE `entry`=2948;
UPDATE `locales_page_text` SET `text_loc2`='Dans les années qui suivirent, après que Stormwind eut trahi la guilde des tailleurs de pierre, Sir Erlgadin devint très amer face au rôle des nobles dans le royaume. Il ne souhaitait plus défendre la position que la lignée de son père lui avait acquise à la Maison des Nobles. $B$BMais je m\'éloigne du sujet. Le but de cette histoire n\'est pas d\'agir comme un traité politique ou une biographie. Ceci est le récit de mes expériences de chasse au gros gibier dans les collines verdoyantes de Stranglethorn.' WHERE `entry`=2949;
UPDATE `locales_page_text` SET `text_loc2`='Nous nous sommes levés avec le soleil.Barnil commença à préparer le repas du matin. J\'ai remarqué que l\'attention d\'Ajeck était quelque peu distraite. La journée de trek serait longue et notre chasse nous rapprocherait du danger. Un manque de concentration pourrait facilement conduire à un accident errant. Pourtant, Ajeck semblait incapable de détourner son regard de Barnil qui se tenait au bord de la rivière en train de rincer son sac à dos.' WHERE `entry`=2950;
UPDATE `locales_page_text` SET `text_loc2`='Au moment où je m\'interrogeais sur le manque d\'intérêt d\'Ajeck pour la stratégie de chasse du jour, elle prit son carquois, tira une flèche et décocha un coup de feu en direction du pauvre Barnil. Mais Ajeck ne tirait pas sur Barnil. Car quand Barnil s\'éloigna, bouche bée, un grand crocilisk de rivière flotta à la surface avec la flèche d\'Ajeck parfaitement placée entre ses deux grands yeux.$B$B' WHERE `entry`=2951;
UPDATE `locales_page_text` SET `text_loc2`='Nous nous sommes dirigés vers l\'ouest, à travers la végétation épaisse de la jungle enchevêtrée. En marchant à pas lents et délibérés, nous arpentions un feuillage épais à la recherche d\'une proie. La matinée s\'est passée dans un silence frustré. Rien ne bougeait dans la vallée, pas même une brise. Dans l\'après-midi, l\'expédition était devenue agitée. Barnil ne marchait plus avec les pas prudents d\'une proie traquante. Au lieu de cela, il glissait maladroitement le long du sentier en marchant souvent bruyamment sur des feuilles sèches ou des branches tombées.' WHERE `entry`=2952;
UPDATE `locales_page_text` SET `text_loc2`='Au cours d\'une de ces erreurs, Erlgadin posa une main lourde sur l\'épaule de Barnil.Ajeck et moi avons jeté un coup d\'œil désinvolte, en supposant que l\'homme donnait simplement à Barnil un reproche bien nécessaire pour son insouciance.Erlgadin, cependant, fit lentement un geste de la tête en direction d\'un arbre abattu à proximité. Deux yeux noirs perçants nous regardaient, juste au-dessus d\'une gueule de crocs acérés.' WHERE `entry`=2953;
UPDATE `locales_page_text` SET `text_loc2`='La bête était un tigre Stranglethorn mâle.Avant que je puisse armer mon fusil, Erlgadin a levé son arbalète et a tiré sur la bête. Le verrou manqua sa marque et attrapa la bête lourdement dans le flanc gauche. Le tigre fit une tentative futile de s\'enfuir mais sa blessure était trop grave. La bête trébucha pendant quelques secondes tragiques jusqu\'à ce que Barnil termine le massacre avec une hache jetée.' WHERE `entry`=2954;
UPDATE `locales_page_text` SET `text_loc2`='La mise à mort a créé une ambiance de fête parmi les membres de l\'expédition. Barnil a versé l\'hydromel pour le plaisir de tous.Mais nos festivités ont été de courte durée.Alors que nous préparions le cadavre pour le transport vers le camp de base, nous avons tous été pris au dépourvu par un grognement effroyable. Au cours de toutes mes années, je n\'ai jamais rien entendu d\'aussi pressé.' WHERE `entry`=2955;
UPDATE `locales_page_text` SET `text_loc2`='Sur un précipice rocheux au-dessus, se découpant au soleil couchant, je distinguais le plus gros chat de proie que j\'ai jamais vu.J\'ai pu perdre une volée maladroite avec mon fusil, mais le chat a tenu son terrain. Il grogna encore une fois, cette fois plus fort que le premier, et disparut. Nous avons rassemblé nos affaires et sommes retournés solennellement au camp.' WHERE `entry`=2956;
UPDATE `locales_page_text` SET `text_loc2`='J\'avais promis à l\'expédition que nous passerions le lendemain à la recherche de panthères, car leurs fourrures sont très recherchées dans tout Azeroth. Il est logique que cette demande existe et que tous les chasseurs, les trappeurs et les marchands de fourrures valides puissent donner leur vie si vaillamment au nom de l\'Alliance.' WHERE `entry`=2957;
UPDATE `locales_page_text` SET `text_loc2`='Ajeck et Sir Erlgadin avaient hâte d\'apprendre à chasser efficacement avec un fusil nain. Les deux humains ont laissé leurs armes primitives au camp de base.Barnil et moi les avons équipés de certaines des meilleures armes à feu d\'Ironforge.' WHERE `entry`=2958;
UPDATE `locales_page_text` SET `text_loc2`='Ce jour-là, nous nous sommes aventurés vers le sud en suivant de nouvelles traces de panthères. Bientôt, nous atteignons un ravin traversé par un formidable pont de corde. Je n\'ai pas pu m\'empêcher de penser aux écrits descriptifs de Brann sur cette région lorsque j\'ai vu la merveille de l\'ingénierie. Si souvent, on a supposé que les Trolls autochtones étaient une race primitive et peu instruite, alors que je contemplais le savoir-faire artisanal du pont, j\'étais capable de reconnaître le talent avec lequel les constructeurs de Troll avaient surmonté cet exploit apparemment impossible.$B$B' WHERE `entry`=2959;
UPDATE `locales_page_text` SET `text_loc2`='Peu de temps après, Ajeck suivit la panthère au sud-ouest. Nous avons marché tranquillement, pistolets prêts, en prévision de notre proie. Un claquement de brindilles d\'un bosquet d\'arbres à proximité a immédiatement attiré notre attention.Quelque chose était dedans. Un seul regard sévère sur Barnil fut suffisant pour transmettre mes pensées. Barnil baissa lentement son fusil. Ce meurtre n\'était pas pour nous; c\'était pour nos compagnons humains. D\'innombrables panthères ont perdu la vie devant nos barils fumants. Ce tuer serait pour les humains.' WHERE `entry`=2960;
UPDATE `locales_page_text` SET `text_loc2`='Ajeck et Sir Erlgadin se tenaient debout, leurs armes alignées sur la végétation touffue au pied des arbres qui se balançaient. Le soleil de midi frappait lourdement sur nous. Un léger filet de transpiration descendit de la tempe d\'Elrgadin alors qu\'il retirait la goupille. Au son du déclic, l\'épaisse flore se sépara et une grande panthère noire - un beau spécimen - s\'élança dans la plaine.$B$B' WHERE `entry`=2961;
UPDATE `locales_page_text` SET `text_loc2`='Les humains se dirigèrent vers la panthère qui longeait la limite des arbres. Les canons de leurs armes se sont déplacés dans un tandem parfaitement parallèle. Barnil me lança un regard pressant mais je secouai la tête. Cette chasse était pour les humains, pas Barnil ou moi. Erlgadin a tiré un coup puissant, manquant complètement la panthère. Apparemment, il n\'était pas préparé au violent contrecoup de l\'explosion de la carabine.' WHERE `entry`=2962;
UPDATE `locales_page_text` SET `text_loc2`='Son arme se retourna violemment dans ses bras. Le canon a plongé sur le côté et est passé sous le fusil d\'Ajeck. Ajeck avait choisi ce moment précis pour appuyer sur sa gâchette. Le fusil, dont l\'objectif était maintenant assommé maladroitement vers la limite des arbres, a explosé. Une volée d\'oiseaux a crié hors de la canopée, se dispersant dans toutes les directions. Un panache de fumée s\'éleva de l\'arbre. Nous avons regardé avec émerveillement une formidable branche s\'effondrer sur la panthère en fuite, lui brisant le dos.' WHERE `entry`=2963;
UPDATE `locales_page_text` SET `text_loc2`='Au fil des semaines, notre stock de peaux de panthères et de tigres est devenu immense. J\'ai décidé qu\'il était temps que l\'expédition se concentre sur un nouveau défi: Raptors. $B$BLes Humains, bien que reconnaissants de la formation offerte par Barnil et moi-même, ont décidé de s\'abstenir de chasser avec des armes à feu.Ajeck était beaucoup plus à l\'aise avec un arc finement tendu et Sir Erlgadin ne quittait jamais le camp sans sa solide arbalète.' WHERE `entry`=2964;
UPDATE `locales_page_text` SET `text_loc2`='Nous partons au premier feu, en direction du sud, après les ruines de Tkashi. Barnil a déclaré craindre que nous rencontrions des membres de la tribu Bloodscalp. J\'ai rappelé à Barnil que les Bloodscalps étaient plus concernées par la destruction de leur ennemi tribal, les Skullsplitters. Inutile de dire que Barnil n\'était pas du tout réconforté. Cependant, j\'avais avec moi un fusil chargé, une sacoche remplie de poudre à canon et trois chasseurs meurtriers pour atténuer les craintes d\'une embuscade hostile.' WHERE `entry`=2965;
UPDATE `locales_page_text` SET `text_loc2`='Je me suis tenu devant un infernal dominant sur le champ de bataille, l\'armée de la Légion ardente avançant de toutes les directions. Un groupe indiscipliné de Trolls semble aussi inoffensif qu\'un jackrabbit dans les collines de Dun Morogh. $B$BNous avons traversé les ruines de Tkashi sans événement, au grand soulagement de Barnil. Le groupe s\'est ensuite dirigé vers l\'ouest, en direction de la Grande Mer, en longeant les ruines de Zul\'Kunda, juste au sud. En montant les hautes falaises, nous avons repéré notre premier raptor.$B$B' WHERE `entry`=2966;
UPDATE `locales_page_text` SET `text_loc2`='La bête n\'a jamais autant détecté notre présence. En fait, le seul message de bienvenue qu\'il ait reçu de l\'expédition était une balle dans les yeux. $B$BSir Erlgadin laissa échapper un chaleureux * hourra * tandis qu\'Ajeck hocha la tête en direction de moi avec une vive approbation.Je parcourus mon sac à la recherche de ma pipe, espérant profiter d\'une fumée de fête.Barnil commença à gravir la colline pour récupérer le cadavre du Raptor. Je fixai la bête déchue avec la satisfaction qui accompagne chaque grande tuerie.' WHERE `entry`=2967;
UPDATE `locales_page_text` SET `text_loc2`='Mais je ne pouvais pas me reposer longtemps dans la gloire de la tuerie. Car lorsque je tournai les yeux vers l\'horizon, plusieurs silhouettes apparurent au sommet de la colline, juste au-dessus du pauvre Barnil. $B$B* Fuyez, Barnil! * Ai-je crié.Ajeck, Sir Erlgadin et moi-même avons échappé une poignée de balles, de flèches et de boulons au-dessus de Barnil et des poursuivants. L\'un de nous a tué un soldat au milieu de la confusion.' WHERE `entry`=2968;
UPDATE `locales_page_text` SET `text_loc2`='Nos tirs dirigés à la hâte ont suffi à acheter l\'évasion de Barnil. Barnil redescendit la colline et rejoignit la fête. Nous nous sommes enfuis dans la jungle; un groupe de féroces rapaces à queue de lièvre qui suivent chacun de nos gestes. $B$BLes chasseurs étaient maintenant les chassés.' WHERE `entry`=2969;
UPDATE `locales_page_text` SET `text_loc2`='J\'ai conduit le groupe vers la mer, espérant que le rivage fournirait un refuge aux Raptors. Dans notre hâte, nous avions dérivé trop au nord, à une altitude extrêmement élevée. L\'erreur a été faite. La faute était à moi. Nous nous sommes arrêtés juste à côté d\'une falaise escarpée, les Raptors à quelques pas en arrière.' WHERE `entry`=2970;
UPDATE `locales_page_text` SET `text_loc2`='Je m\'avançai lentement, le pistolet levé.J\'avais conduit ces braves chasseurs à leur mort. Je mourrais en les défendant. Les Raptors à Queue sont particulièrement féroces, connus pour leur soif de sang implacable. Ils nous ont surpassé de loin en nombre. Mais je serais damné si je les laissais me tuer, moi et mes camarades, sans verser de leur propre sang au préalable.' WHERE `entry`=2971;
UPDATE `locales_page_text` SET `text_loc2`='Ajeck et Sir Erlgadin ont préparé leurs armes, me plaçant de chaque côté, le dos à la mer. Barnil laissa échapper un soupir vaincu et sortit sa hache. Les Lashtails étaient presque sur nous. Leur pas régulier avait ralenti. Ils traquaient leur proie maintenant car ils savaient qu\'ils nous avaient piégés.' WHERE `entry`=2972;
UPDATE `locales_page_text` SET `text_loc2`='Et puis quelque chose de miraculeux s\'est passé. De notre côté, nous avons entendu le rugissement distinct et terrifiant du grand tigre blanc. Malgré leur nombre, les Raptors se retournèrent et se dispersèrent dans toutes les directions. Nous n\'avons vu qu\'un bref éclair blanc lorsque le tigre a passé devant nous et s\'est jeté sur l\'un des Raptors. Aucune commande nécessaire à donner. Les quatre membres de notre parti savaient qu\'il était temps de courir.' WHERE `entry`=2973;
UPDATE `locales_page_text` SET `text_loc2`='Nous avons sprinté jusqu\'au camp de base sans jamais ralentir. Plus tard dans la nuit, nous nous sommes assis tranquillement autour du feu de camp, sachant que nos vies avaient été sauvées par un sort étrange. Tels sont les risques du chasseur de gros gibier. Nous jouons avec le destin en le livrant. Pourtant, chacun de nous, à un moment donné, sera confronté aux dents acérées du destin. Ce nain est juste heureux que ce moment ne soit pas venu sur les collines verdoyantes de Stranglethorn. $B$B- La fin--' WHERE `entry`=2974;
UPDATE `locales_page_text` SET `text_loc2`='Stupide,$B$BTrouver de la nourriture. Le goût bleu est bon. Lantresor dit que ça va.Apportez de la nourriture à Lump. Un morceau mange d\'abord, puis un repas stupide. D\'accord?Va vite ou Lump te mange. $B$B-Lump' WHERE `entry`=2975;
UPDATE `locales_page_text` SET `text_loc2`='(1) Raliq the Drunk: Ogre. Il ne quitte jamais la cantina. Signifier. Laid. Graisse. Signifier. Aussi très gros.$B$B[Page suivante]' WHERE `entry`=2976;
UPDATE `locales_page_text` SET `text_loc2`='(2) Coosh\'coosh: Ce petit homme de spores agaçant de Zangarmarsh possède un champignon aux champignons à la frontière de Zangarmarsh et de Terokkar.Sal\'salabim perdit beaucoup d\'or au jeu "Qu\'est-ce que ça sent?"J\'ai essayé de payer Sal\'salabim avec un stupide champignon appelé Glowcap. Dis-le comme l\'or! Hah! $B [Page suivante]' WHERE `entry`=2977;
UPDATE `locales_page_text` SET `text_loc2`='(3) Floon: Arakkoa. Perdu beaucoup d\'or à Sal\'salabim. Sal\'salabim a tué un homme-oiseau pour ne pas avoir payé.Maintenant, l\'homme-oiseau fantôme se promène dans la partie nord-ouest de Bone Wastes.' WHERE `entry`=2978;
UPDATE `locales_page_text` SET `text_loc2`='La racine Olemba est une racine assez commune dans la forêt de Terokkar. Recherchez les racines renversées près des arbres Olemba, qui constituent l\'essentiel des forêts de Terokkar.$B$BLe marshberry ne pousse que dans Zangarmarsh. On ne peut le trouver que dans la forteresse Ango\'rosh, située dans la région nord-ouest du marais. Méfiez-vous des ogres qui gardent avec ferveur les précieuses baies.$B$BLa fronde de Telaari est peut-être la plus facilement accessible des herbes de cette liste. Au sud de Garadar, dans le bassin de Talaari, vous trouverez les frondes. Ils poussent près de l\'eau.$B$BLa dernière herbe de la liste est unique aux montagnes de Blade\'s Edge. Vous trouverez Blade\'s Edge au nord de Zangarmarsh. Dragonspine se développe sur les extrémités des formations rocheuses déchiquetées qui gardent les canyons de Blade\'s Edge. Vous saurez de quoi je parle lorsque vous verrez pour la première fois Blade\'s Edge.Les formations rocheuses ressemblent à des rangées de dents acérées et pointues.' WHERE `entry`=2979;
UPDATE `locales_page_text` SET `text_loc2`='Serviteur. $B$BCes orcs deviennent plus qu\'un ennui mineur. Je veux que vous veilliez personnellement à ce que nos forces attaquant les défenseurs sur le Portail des Ténèbres consacrent toute leur énergie à dévaster Thrallmar. $B$BConsidérez ceci comme une commande,$B$BArazzius' WHERE `entry`=2981;
UPDATE `locales_page_text` SET `text_loc2`='Serviteur. $B$BCes orcs deviennent plus qu\'un ennui mineur. Je veux que vous veilliez personnellement à ce que nos forces attaquant les défenseurs sur le Portail des Ténèbres consacrent toute leur énergie à dévaster Thrallmar. $B$BConsidérez ceci comme une commande,$B$BArazzius' WHERE `entry`=2982;
UPDATE `locales_page_text` SET `text_loc2`='Pour permettre à la vision de la teinture d\'imbiber de voir les morts, certains composants matériels doivent être rassemblés. $B$BEn premier lieu, un globe oculaire de la montagne de l\'ouest de Nagrand, près des camps de forge maudits de la Légion Ardente, au sud de Warmaul Hill. $B$BOn trouve également dans l\'ouest de Nagrand les plus grandes vrilles.Seul un spécimen sans faille fera l\'affaire! $B$BEnfin, à la fois dans l\'ouest et le sud-ouest de Nagrand, on retrouve la graisse du vieux sabot. $B$BRassemblez ces objets et remettez-les-moi! Faites vite! $B$B-Nitrin' WHERE `entry`=2983;
UPDATE `locales_page_text` SET `text_loc2`='<Après un certain nombre de pages de «science» abrutissante qui semblent n\'avoir aucun sens, il y a une note gribouillée au bas de la dernière page ....>$B$BDésolé, patron. On dirait que la Raz-de-Néant va bientôt exploser. Il emportera probablement la majeure partie du reste de l\'Outland. $B$BJe ne suis pas certain à 100%, mais je pense que le problème est lié à ce que ces elfes de sang font dans les manges. $B$BC\'est une bonne chose que vous construisiez cette fusée! $B$B- Coppernickels$B$Bps - pensez-vous que je pourrais avoir une place dans la fusée? Vous aurez sûrement besoin d\'un nethéologiste pendant vos recherches dans le Néant tordu!' WHERE `entry`=2984;
UPDATE `locales_page_text` SET `text_loc2`='Général,$B$BNous tenons la ligne à l\'escalier du destin, mais la Légion envoie vague après vague attaquer nos rangs. Ils doivent avoir à proximité une aire de rassemblement que nos éclaireurs n\'ont pas encore localisée. Bien que les forces de l\'Alliance ici se tiennent à nos côtés, je crains que les nombres supérieurs de la Légion envahissent nos défenses. Envoyez ce que vous pouvez, de peur que le portail ne tombe - et nous serons piégés dans ce monde de cauchemar pour toujours.$B$BLieutenant General Orion' WHERE `entry`=2985;
UPDATE `locales_page_text` SET `text_loc2`='Alors que Nahni se relevait, le guerrier grisonnant lui lança un regard dur. "Je suppose que vous êtes ici pour collecter la récompense pour avoir tué ces murlocs?" $B$BSes yeux se posèrent sur l\'épais sabre brillant à ses côtés. "Cela dépend de la récompense, Marcus." Elle fit tournoyer ses cheveux de façon amusante, faisant semblant de ne pas remarquer à quel point il se déplaçait mal à l\'aise en sa présence."Je peux ne pas en vouloir."' WHERE `entry`=2986;
UPDATE `locales_page_text` SET `text_loc2`='Marcus s\'avança vers elle, hérissé d\'un mélange de peur et de colère. "La récompense n\'est pas négociable!" Il s\'arrêta un moment tout en rassemblant ses nerfs et se pressa contre sa forme diminutive. Leurs lèvres se rencontrèrent, fondant son sort d\'armure de givre dans un torrent de vapeur étouffante. $B$B"Alors, y a-t-il une autre étape dans cette quête?"elle taquina, ses yeux brillants d\'excitation.$B$B<Ceci continue pendant plusieurs centaines de pages sans faire avancer l\'intrigue.>' WHERE `entry`=2987;
UPDATE `locales_page_text` SET `text_loc2`='Cet objet étrange semble être une sorte de dispositif technique, bien que rien ne ressemble à ce que vous avez vu auparavant. $B$BÀ en juger par son apparence, vous diriez que quoi que ce soit, il a déjà été utilisé. $B$BEt à en juger par la disposition des cadavres d\'ici là, il vous faudrait deviner qu\'elle était utilisée pour des effets mortels, quelle qu\'elle soit.' WHERE `entry`=2988;
UPDATE `locales_page_text` SET `text_loc2`='Commandant,$B$BLe chiffre doit être livré à Zuluhed. Il restera sous sa garde jusqu\'à nouvel ordre. $B$B-Lord Illidan Hurlorage' WHERE `entry`=2989;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><BR/><H1 align = "center"> LAMENT DE L\'ENFANT </H1><BR/><IMG src = "Interface  FlavorImages  BloodElfLogo-small" /></BODY>< / HTML>' WHERE `entry`=2990;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><BR/><P> Version lâchement traduite ... </P><BR/><P> À la lumière, à la lumière du soleil, </P><P> Enfants de le sang, </P><P> Nos ennemis se frayent un chemin. </P><P> Enfants du sang, </P><P> À la lumière, </P><P> Enfants défaillants du sang, </P><P> Ils traversent. </P><P> O enfants du sang, </P><P> À la lumière du soleil. </P><P> Enfants défaillants du sang, </P><P> Ils traversent. </P><P> O enfants du sang, </P><P> À la lumière du soleil, </P><P> Le soleil. </P></BODY></HTML>' WHERE `entry`=2991;
UPDATE `locales_page_text` SET `text_loc2`='<HTML><BODY><BR/><P> Version thalassienne ... </P><BR/><P> Anar\'alah, anar\'alah belore, </P><P> Sin\'dorei, < / P><P> Shindu fallah na. </P><P> Sin\'dorei, </P><P> Anar\'ala, </P><P> Shindu sin\'dorei, </P><P > Shindu fallah na. </P><P> Sin\'dorei, </P><P> Anar\'alah belore. </P><P> Shindu sin\'dorei, </P><P> Shindu fallah na . </P><P> Sin\'dorei, </P><P> Anar\'alah belore, </P><P> Bélore. </P></BODY></HTML>' WHERE `entry`=2992;
UPDATE `locales_page_text` SET `text_loc2`='Mon cher chancelier,$B$BLa tâche d\'amener les géants d\'Ombrelune dans le giron est maintenant entre vos mains capables. Utilisez votre charme elfe de sang pour leur faire voir les choses à ma façon. Si cela ne fonctionne pas, exterminez chacun d\'entre eux ...$B$B- Seigneur Illidan Hurlorage' WHERE `entry`=2993;
UPDATE `locales_page_text` SET `text_loc2`='En toutes choses, un paladin doit réfléchir la Lumière qui nourrit notre force. Tendre vers la pureté, se battre pour l\'un des nôtres, ne veut pas dire que nous nous battons pour une déité. Nous nous battons pour être bons dans toutes nos actions.$B$BBien que nous soyons appelés pour frapper le Mal en ces temps difficiles, vous ne devez jamais oublier que c\'est en aidant les autres que vous vous démarquerez véritablement des citoyens d\'Azeroth. La compassion, la patience, la bravoure, ces valeurs sont aussi importantes pour un paladin que la force dans la bataille.$B$BPrenez-en conscience, et ne l\'oubliez pas.' WHERE `entry`=2994;
UPDATE `locales_page_text` SET `text_loc2`='Bien que appelé à vaincre le mal en ces temps difficiles, vous devez toujours vous rappeler que c\'est aider les autres qui vous distinguera vraiment des autres citoyens. La compassion, la patience, le courage - ces choses comptent autant pour un paladin que sa force au combat. $B$BSachez-le bien et ne l\'oubliez jamais.' WHERE `entry`=2995;
UPDATE `locales_page_text` SET `text_loc2`='DÉGAGEMENT DE DÉCHARGE DE RESPONSABILITÉ.$B$BLE PILOTE DE TEST EST TOTALEMENT CONSCIÉ ET RECONNAÎT UN RISQUE DE BLESSURE, DE DÉMEMBREMENT OU DE MORT, SOUS FORME: PULVERISATION, ÉLECTROCUTION, TRAUMA FORCE FORCE EN ORDRE VITAL, CONSOMMATION PAR UN INDÉPENDANT FAUNA, UTILISATION DU CAPACITORIUM DE ZEPHYRIUM (LE "DISPOSITIF"). TEST PILOTE, CELUI-CI, CHOISIT D\'ENTRER VOLONTAIREMENT DANS L\'APPAREIL ET ASSUMER TOUS LES RISQUES CI-DESSUS.$B$BPILOTE DE TEST ACCEPTE DE RETIRER, DE LIBÉRER ET D\'ÉCHANGER TOUTE RÉCLAMATION DE BLESSURE OU DE DOMMAGE CONTRE TALLY ZAPNABBER ET SA FAMILLE, ET EN CAS DE $GHIS: ELLE; Décès, accepte de donner $GHIS: HER; RESTE AU DOMAINE ZAPNABBER POUR L\'ETUDE.$B$Bsigné,$B$BX: ___ $N' WHERE `entry`=2996;
UPDATE `locales_page_text` SET `text_loc2`='Salutations, Monseigneur Antonivich: J\'espère que cette lettre te trouvera à Thrallmar, productive dans tes études. Mes attributs ici dans Spinebreaker conviennent à mes propres expériences, même si parfois je souhaite être de retour dans The Undercity avec une gamme complète d\'outils et de serviteurs pour m\'aider ... $BAh, les lamentations d\'un cadavre. , J\'ai des nouvelles intéressantes - le sang que $N a recueilli auprès des feld orcs montre une concentration de souillures démoniaques. Il est presque identique à la souillure des feld orques d\'Azeroth… à la souillure de Mannoroth.' WHERE `entry`=2997;
UPDATE `locales_page_text` SET `text_loc2`='$N,$B$BLe chiffre de Damnation est en effet une incantation puissante.Ancien et chaotique, le chiffre lui-même a été responsable de nombreuses tragédies dans l\'histoire de nos mondes. C\'est-à-dire qu\'il n\'est pas propre à Draenor.$B$BLe symbole en flammes que vous avez vu était la marque de Kael\'Thas. Sa connaissance des mots anciens en explique beaucoup.$B$BOURDE notre découverte a réveillé le naaru. A\'dal a demandé votre présence à Shattrath City. Si vous choisissez de venir, cherchez-moi à la terrasse de la lumière.$B$BHumbly Vôtre,$B$BKhadgar$B$B' WHERE `entry`=2998;
UPDATE `locales_page_text` SET `text_loc2`='Antonivich,$B$BTout d\'abord, laissez-moi vous dire que votre dernier agent, $N, a perfromed $Ghis: her; devoirs bien à ma satisfaction. J\'ai pris possession de $Ghis: elle; body et écrivez maintenant ceci avec $Ghis: her; propre main. Un tel amusement me donne - J\'espère qu\'un jour j\'aurai la chance de vous posséder, oh apothicaire!$B$BDonc, le rapport que vous m\'avez commandé: la citadelle des Flammes infernales est hantée par un démon plus puissant que tous ceux que j\'ai rencontrés. J\'imagine que Mannoroth - le seigneur des mines de la troisième guerre mondiale - possédait la même force, mais très peu. Je sens cependant qu\'il y a un air de confinement autour de ce démon. Cela ne m\'étonnerait pas qu\'il agisse contre son gré, soit qu\'il soit emprisonné ou sous l\'influence d\'un pouvoir encore plus puissant.$B$BLe temps nous dira ...' WHERE `entry`=2999;
UPDATE `locales_page_text` SET `text_loc2`='Le temps nous dira, comme on dit, car j\'ai aussi senti un changement de destin dans les murs de la citadelle des Flammes infernales.$B$BVous, les immigrés d\'Azeroth - cet endroit vert, luxuriant et horrible où des êtres tels que moi trouvent un enfer de lumière et de vie - verrez le visage de ce démon. Et vous vous rendrez compte que les maux à l\'intérieur de la citadelle des Flammes infernales ne sont que la pointe de - si vous permettez la métaphore - un vaste iceberg.$B$BOh, quelles batailles glorieuses nous attendent! Si seulement je pouvais être là pour goûter à la douleur et à la peur qui vont s\'opérer!$B$BAh bon, je vais mettre fin à mon épilation prosaïque et vous permettre de mâcher ces mots, chère apothicaire: votre corps est peut-être mort, Antonivich, mais votre âme est encore intacte, et j\'aurai cette âme et ce festin sur elle avec un plaisir sublime le jour où tes défenses glissent ... et je me fraye un chemin en toi!$B $BI devra attendre le quatrième jour du jugement si je le dois, mais ... vous serez à moi!$B$BBonsoir,$B$B-J' WHERE `entry`=3000;
UPDATE `locales_page_text` SET `text_loc2`='<Il ne reste plus de mots reconnaissables sur cette page gravement brûlée.>' WHERE `entry`=3001;
UPDATE `locales_page_text` SET `text_loc2`='Depuis votre départ de l\'Evergrove, j\'ai poursuivi mes études sur le Livre du Corbeau et j\'ai découvert des informations qui vous aideront à vaincre le dieu corbeau.$B$BLibérer les esprits les a liés à vous. J\'espère donc qu\'ils offriront gratuitement leur aide dans la lutte contre le dieu corbeau. $B$BLorsque vous libérerez les esprits pour la première fois, ils resteront en sommeil, comme ils le sont depuis des siècles. En utilisant vos sorts de guérison, vous pouvez les insuffler de l\'énergie, les ramener à la vie et les aider au combat.Quand la magie s\'estompe, ils retourneront à leur sommeil. $B$BCe qui suit est une collection de mes notes sur chaque esprit d\'oiseau:' WHERE `entry`=3002;
UPDATE `locales_page_text` SET `text_loc2`='L\'esprit du faucon$B$BL\'esprit du faucon est imprégné d\'une rapidité et d\'une agilité incroyables, très apprécié des arakkoa pour ses talents de chasseur. Il vous donnera une mesure de sa vitesse en matière de lancer de sorts et de combat au corps à corps si vous dynamisez l\'esprit avec un sortilège de guérison.$B$B' WHERE `entry`=3003;
UPDATE `locales_page_text` SET `text_loc2`='Esprit de l\'aigle$B$BLa tradition d\'Arakkoa veut que l\'esprit de l\'aigle ne soit pas aussi rapide que son frère faucon, mais qu\'il possède le plus grand talent dans la chasse. L\'esprit aigle utilisera ses compétences contre vos ennemis si vous utilisez un sortilège de guérison pour le réveiller de son sommeil.$B$B.' WHERE `entry`=3004;
UPDATE `locales_page_text` SET `text_loc2`='L\'esprit de faucon$B$BLa tablette parle hautement du faucon, comme s\'il avait déjà été en ligue avec le corbeau. Ancien symbole arakkoa de vengeance, l\'esprit du faucon était souvent invoqué par les victimes d\'actes répréhensibles. Utiliser un sortilège de guérison pour réveiller l\'esprit vous permettra de partager son pouvoir et d\'endommager ceux qui vous font du mal.' WHERE `entry`=3005;
UPDATE `locales_page_text` SET `text_loc2`='Au nom de notre grand maître ... Pour la gloire d\'Illidan! $B$BMor\'ghor,$B$BI vous envoient ce misérable maintenant en tant que serviteur d\'Illidan. Bien qu\'il n\'ait aucun avenir en tant que chevalier de la mort, vous découvrirez peut-être qu\'il possède les compétences adaptées à vos goûts. Battez-le dans la soumission.Transformez-le en guerrier des Illidari. $B$BEt s\'il échouait ... S\'il devait montrer de la faiblesse ... Le jeter. Aux fosses! $B$BSeigneur Teron Gorefiend$B$B' WHERE `entry`=3006;
UPDATE `locales_page_text` SET `text_loc2`='Ne désespérez pas. Votre ogre spécial est quelque part.' WHERE `entry`=3007;
UPDATE `locales_page_text` SET `text_loc2`='Lève ton épée, ton maul et ta lance. Les cristaux nous ont dit de donner une chance à la paix.' WHERE `entry`=3008;
UPDATE `locales_page_text` SET `text_loc2`='Ne craignez rien avec une tête pleine de bière!' WHERE `entry`=3009;
UPDATE `locales_page_text` SET `text_loc2`='Deux têtes valent mieux qu\'une! Ensuite, vous aurez toujours un ami.' WHERE `entry`=3010;
UPDATE `locales_page_text` SET `text_loc2`='La sagesse se présente sous de nombreuses formes, mais découle toujours des cristaux.' WHERE `entry`=3011;
UPDATE `locales_page_text` SET `text_loc2`='Penser positivement! Un gros intestin pour l\'un est une paire de poignées d\'amour pour un autre.' WHERE `entry`=3012;
UPDATE `locales_page_text` SET `text_loc2`='Vous rencontrerez demain quelqu\'un de spécial, alors souvenez-vous: vous attirez plus d\'amis avec gentillesse que de les matraquer à mort.' WHERE `entry`=3013;
UPDATE `locales_page_text` SET `text_loc2`='Numéros chanceux: 13 9 17 4 n' WHERE `entry`=3014;
UPDATE `locales_page_text` SET `text_loc2`='Votre inclination pour la danse fait sourire tout le monde.' WHERE `entry`=3015;
UPDATE `locales_page_text` SET `text_loc2`='Vous êtes spécial, peu importe ce que vos amis, votre famille, vos associés, des inconnus ou votre conscience peuvent vous dire.' WHERE `entry`=3016;
UPDATE `locales_page_text` SET `text_loc2`='Quand tout le reste échoue, il est temps de renflouer.' WHERE `entry`=3017;
UPDATE `locales_page_text` SET `text_loc2`='L\'ivresse est à côté de la piété.' WHERE `entry`=3018;
UPDATE `locales_page_text` SET `text_loc2`='Avertissement: le giblet n\'était pas sans danger pour la consommation humaine.' WHERE `entry`=3019;
UPDATE `locales_page_text` SET `text_loc2`='Vous avez été choisi par la grande main de métal dans le ciel! Maintenant au travail.' WHERE `entry`=3020;
UPDATE `locales_page_text` SET `text_loc2`='Laisse dormir Gronn mentir.' WHERE `entry`=3021;
UPDATE `locales_page_text` SET `text_loc2`='Ce message vous a été présenté par le Comité d\'exploitation d\'Ogri\'la. Fabriqué à Gadgetzan.' WHERE `entry`=3022;
UPDATE `locales_page_text` SET `text_loc2`='Le sens de la vie est ... (Le reste de ce message a été mangé accidentellement.)' WHERE `entry`=3023;
UPDATE `locales_page_text` SET `text_loc2`='Désolé, vous n\'êtes pas un gagnant cette fois. S\'il vous plaît jouer à nouveau.' WHERE `entry`=3024;
UPDATE `locales_page_text` SET `text_loc2`='Vous allez créer un ennemi aujourd\'hui et le tuer rapidement.' WHERE `entry`=3025;
UPDATE `locales_page_text` SET `text_loc2`='Cherchez un chemin plus élevé dans la vie.De cette façon, vous pouvez éviter les vies courtes.' WHERE `entry`=3027;
UPDATE `locales_page_text` SET `text_loc2`='Au nom de notre grand maître ... Pour la gloire d\'Illidan! $B$BLes composants communs du canon à âme sont obtenus par le biais de divers métiers. Les ingénieurs et les mineurs devraient être en mesure de vous fournir les barres en feutre, le noyau énergétique en khorium et le cadre en adamantite. $B$BPour une essence arcanique sans faille, vous devez vous rendre dans la forêt de Terokkar. Au centre de la forêt, au-dessus de l\'avant-poste de la Horde de Stonebreaker Hold, vous trouverez la maison de Sar\'this, un hérétique arakkoa. La région est connue sous le nom de lac Jorune. $B$BParlez à Sar\'this et dites-lui ce que vous recherchez.Assurez-vous de mentionner que je vous ai envoyé car il me doit une faveur ou deux. $B$BFaites ce que Sar\'this demande et vous devriez avoir l\'essence. $B$B-Balthas' WHERE `entry`=3028;
UPDATE `locales_page_text` SET `text_loc2`='Lors de ma dernière visite dans les couloirs, le roi Talon Ikiss a apporté la plume de Terokk avec lui, en signe de son autorité. Il le porte probablement toujours pour convaincre ses partisans qu\'il est un Terokk rené. $B$BMon frère, Darkweaver Syth, portait le masque de Terokk tout en menant les rituels Sethekk. $B$BQuant à la saga de Terokk, elle est conservée dans un cercle de runes dans la grande salle voûtée située juste à l\'extérieur de celle du Talon King Ikiss.' WHERE `entry`=3029;
UPDATE `locales_page_text` SET `text_loc2`='Il est impossible que l\'influence de Mannoroth s\'étende jusqu\'en Outland - la bête est morte depuis des années - mais je crains qu\'un autre Seigneur des Pitons ne soit en jeu ici. Le pouvoir d\'un seigneur des mines de Mannoroth ... capable de corrompre les orcs indigènes.$B$BI poursuivra mes études sur Bleeding Hollow Blood, mais je vous exhorte à poursuivre sur cette lancée. Et je crains que ce sentier ne mène profondément à la citadelle des Flammes infernales. $B$BScholarly Yours, $BApothecary Albreck' WHERE `entry`=3030;
UPDATE `locales_page_text` SET `text_loc2`='Écoute, Budd! $B$BCe sera le dernier imbécile que je vous envoie jusqu\'à ce que vous me payiez tout ce que vous voulez. $B$BPas plus de stallin \'! $B$BGriftah' WHERE `entry`=3031;
UPDATE `locales_page_text` SET `text_loc2`='Garn,$B$BI n\'a pas besoin de vous rappeler l\'importance de votre mission - protégez votre précieux chargement à tout prix. Comme je l\'ai promis, le succès et la discrétion seront récompensés. $B$BLimitez au minimum la frénésie linguistique, écoutez l\'équipage et tout ira bien.J\'attendrai le mot de votre succès.' WHERE `entry`=3032;
UPDATE `locales_page_text` SET `text_loc2`='Je vous ai envoyé une force de taille considérable, mais vous devez faire attention à vos activités. Votre première priorité sera de stopper l\'avancée de l\'Alliance sur nos terres. Certains des humains les plus aventureux ont déjà commencé la construction d\'un grand bâtiment à la frontière avec les Tarides. $B$BVeillez à ce qu\'ils sachent que leurs semblables ne sont pas les bienvenus au-delà des frontières de leur misérable Theramore. Enfin, un mot de prudence pour vous, ancien: ne faites pas connaître vos plans aux ogres de Brackenwall. Ces brutes sont incapables du moindre soupçon de discrétion. $B$B- Agashem' WHERE `entry`=3033;
UPDATE `locales_page_text` SET `text_loc2`='Une des créatures les plus effrayantes que j\'ai jamais vues de mes propres yeux est Téthyr. Je n\'ai vu la bête qu\'une seule fois et je me considère chanceux d\'avoir survécu. $B$BC\'était une nuit sombre et orageuse. À travers les mers tourbillonnantes et la pluie battante, le phare du phare de Theramore était à peine visible. Nous avons fait de notre mieux pour y parvenir. Soudain, la bête a éclaté entre les vagues devant notre navire!' WHERE `entry`=3034;
UPDATE `locales_page_text` SET `text_loc2`='La gueule de la puissante créature s\'est largement ouverte, jetant des éclairs d\'eau glacée vers notre vaisseau. Ces terribles mâchoires se sont écrasées sur la proue de notre navire, le déchirant de la coque. Les marins et la cargaison ont été violemment jetés à la mer lorsque les débris du navire ont disparu sous les vagues. $B$BLa dernière chose dont je me souvienne, c\'était voler de la coque brisée, frapper les eaux noires et plonger sous les vagues. Le monde devint sombre et je pensai avec certitude que j\'avais pris mon dernier souffle. Quand je me suis réveillé à l\'infirmerie du Theramore, j\'ai su que quelqu\'un devait raconter notre histoire.$B$B' WHERE `entry`=3035;
UPDATE `locales_page_text` SET `text_loc2`='Enfants: Joël et Gina. Femme: Suzannah' WHERE `entry`=3037;
UPDATE `locales_page_text` SET `text_loc2`='Ne reste pas devant ma tombe et pleure, je ne suis pas là, je ne dors pas.$B$BI souffre des mille vents qui traversent la neige brillante et brillante de Northrend.$B$BI suis sous la pluie légère, sur les champs de grain doré de Westfall.$B$BI je suis dans le silence matinal de la jungle de Stranglethorn, verte et luxuriante.$B$BI suis dans les tambours puissants et puissants, les sabots tonitruants à travers Nagrand.$B$BI, les étoiles brillent chaleureusement sur Darnassus en train de rêver doucement.$B$BI Je suis dans les oiseaux qui chantent, je suis dans chaque belle chose.$B$BNe restez pas debout devant ma tombe et pleurez, je ne suis pas là. Je ne meurs pas' WHERE `entry`=3039;
UPDATE `locales_page_text` SET `text_loc2`='Première montre. Sept cloches$B$BCedric retrouvé dans le placard à rhum. $B$BDiscipliné. $B$BPremière montre.Huit cloches$B$BLes montres ont changé. Tout est bien.$B$Bmontre du milieu. Une cloche$B$BCedric découvrit qu\'il tentait de pénétrer dans l\'armoire à vin de$B$BCaptain. Discipliné.$B$Bmontre du milieu. Deux cloches$B$BCedric a grimpé dans le gréement. Est devenu enchevêtré$B$Bet est tombé.Assisté par le chirurgien du navire. Envoyé$B$Bci-dessous pour dégriser. $B$Bmontre du milieu. Quatre cloches$B$BUne bouteille de rhum trouvée dans le pantalon de Cédric. $B$BDiscipliné. $B$Bmontre du milieu. Cinq cloches$B$BLes vêtements de Cédric sont retrouvés.Cédric trouvé séparément. $B$BCedric discipliné. $B$Bmontre du milieu. Six cloches$B$BCedric chantant fort.Réveillé capitaine. $B$BCedric discipliné par le capitaine. $B$Bmontre du milieu.Huit cloches$B$BLes montres ont changé. Tout le monde se tait.Deux hommes$B$Benvoyés pour localiser Cédric. $B$Bmontre du matin. Une cloche$B$BIncendie dans les quartiers du capitaine. Toutes les mains$B$Bréveillé. Jeter de la poudre à la mer.' WHERE `entry`=3044;
UPDATE `locales_page_text` SET `text_loc2`='Fidèles serviteurs des seigneurs élémentaires, NOTRE TEMPS, C\'EST MAINTENANT. $B$BNous traînons trop longtemps dans l\'ombre, attendons notre heure, servons nos maîtres, cherchons la fin que nous savons devoir venir.Maintenant, alors que les flammes de l\'apaisement de Ragnaros brûlent toute la nuit, nous avons entre les mains les outils nécessaires pour inciter à la guerre et au chaos à l\'échelle cataclysmique! $B$BLe Firelord est emprisonné dans notre monde.Il n\'est pas à sa pleine puissance, mais sa puissance est redoutable. Face à un adversaire tout aussi formidable dans ce domaine, le choc qui en résulterait commencerait la grande guerre des éléments qui mènera à la fin que nous avons recherchée.' WHERE `entry`=3065;
UPDATE `locales_page_text` SET `text_loc2`='Dans le service de Neptulon, il existe un grand seigneur du froid nommé Ahune.Même maintenant, il est en train de mobiliser son pouvoir pour faire la guerre à Ragnaros. Tout ce dont il a besoin est une porte d\'entrée dans notre monde; une passerelle nous allons fournir. Nous avons les alliés. Nous avons l\'emplacement. Nous avons la force et la volonté. $B$BLes négociations finales avec nos nouveaux alliés fidèles et nos invités se dérouleront dans un lieu sûr, à l\'écart du nord-ouest de notre site principal, à Ashenvale. $B$BAvant la fin de cette "fête" des masses ignorantes, Ahune doit faire face à Ragnaros à l\'ombre de Rochenoire. Le monde tremblera avec les forces déchaînées.' WHERE `entry`=3066;
UPDATE `locales_page_text` SET `text_loc2`='Tous nos travaux ont travaillé vers ce moment. Nos maîtres se lèveront les uns contre les autres dans une bataille glorieuse qui déchirera Azeroth. $B$BNous vivons à la fin des temps, mes frères. Tenez-vous à vos tâches. S\'efforcer. Nous allons bientôt triompher!$B$B' WHERE `entry`=3072;
UPDATE `locales_page_text` SET `text_loc2`='<Il ne reste plus de mots reconnaissables sur cette page gravement brûlée.>' WHERE `entry`=3076;
UPDATE `locales_page_text` SET `text_loc2`='Salut! Vous avez trouvé un bogue de butin dans MaNGOS. Nous en sommes terriblement désolés et nous allons essayer de régler le problème aussi vite que possible.Cependant, vous devez nous aider un peu.Si vous allez sur http://www.udbforums.org et remplissez un rapport de bogue de butin, avec l\'emplacement où vous l\'avez trouvé, nous essaierons de le réparer dès que possible. Merci à l\'équipe MaNGOS!' WHERE `entry`=9999;

SET NAMES 'latin1';
