UPDATE `locales_quest` SET `title_loc2`='Parchemin simple',`details_loc2`='Ah, pendant que vous vous occupiez du Fléau, ce parchemin est arrivé pour vous. Je suppose qu\'il est de quelque importance, car il porte le sceau du Maître des guerriers, Dannal. À votre place, je prendrais le temps de le lire avant de repartir.',`objectives_loc2`='Lire le Parchemin simple, et parler à Dannal Stern, au Glas.' WHERE `entry`=3095;
