UPDATE `locales_quest` SET `title_loc2`='Rouleau glyphé',`details_loc2`='Ah, pendant que vous vous occupiez du Fléau, ce parchemin est arrivé pour vous. Je suppose qu\'il est de quelque importance, car il porte le sceau du Maître des mages, Isabella. À votre place, je prendrais le temps de le lire avant de repartir.',`objectives_loc2`='Lire le Rouleau glyphé, et parler à Isabella, dans l\'église du Glas.' WHERE `entry`=3098;
