UPDATE `locales_quest` SET `title_loc2`='Lettre simple',`details_loc2`='$N, vous vous êtes $Gchargé:chargée; des espions rochenoires ? On m\'a demandé de vous apporter ceci dès votre retour. Il semble que ce soit une lettre scellée aux armes de Llane, notre maître des guerriers. À votre place, je la lirais avant d\'entreprendre quoi que ce soit d\'autre, ici, à l\'abbaye.',`objectives_loc2`='Lire la Lettre simple et parler à Llane Beshere, à l\'Abbaye de Comté-du-Nord.' WHERE `entry`=3100;
UPDATE `locales_quest` SET `title_loc2`='Lettre consacrée',`details_loc2`='$N, vous vous êtes $Gchargé:chargée; des espions rochenoires ? On m\'a demandé de vous apporter ceci dès votre retour. Il semble que ce soit une lettre scellée aux armes de frère Sammuel, notre maître des paladins. À votre place, je la lirais avant d\'entreprendre quoi que ce soit d\'autre, ici, à l\'abbaye.',`objectives_loc2`='Lire la Lettre consacrée, et parler à Frère Sammuel, à l\'Abbaye de Comté-du-Nord.' WHERE `entry`=3101;
UPDATE `locales_quest` SET `title_loc2`='Lettre codée',`details_loc2`='$N, vous vous êtes $Gchargé:chargée; des espions rochenoires ? On m\'a demandé de vous apporter ceci dès votre retour. Il semble que ce soit une lettre scellée aux armes de Jorik, l\'un de nos maîtres. À votre place, je la lirais avant d\'entreprendre quoi que ce soit d\'autre, ici, à l\'abbaye.',`objectives_loc2`='Lire la Lettre codée, et parler à Jorik Kerridan, dans l\'écurie, derrière l\'Abbaye de Comté-du-Nord.' WHERE `entry`=3102;
UPDATE `locales_quest` SET `title_loc2`='Lettre bénie',`details_loc2`='$N, vous vous êtes $Gchargé:chargée; des espions rochenoires ? On m\'a demandé de vous apporter ceci dès votre retour. Il semble que ce soit une lettre scellée aux armes de la prêtresse Anetta, notre maître des prêtres. À votre place, je la lirais avant d\'entreprendre quoi que ce soit d\'autre, ici, à l\'abbaye.',`objectives_loc2`='Lire la Lettre bénie, et parler à la prêtresse Anetta, à l\'abbaye de Comté-du-Nord.' WHERE `entry`=3103;
UPDATE `locales_quest` SET `title_loc2`='Lettre glyphée',`details_loc2`='$N, vous vous êtes $Gchargé:chargée; des espions rochenoires ? On m\'a demandé de vous apporter ceci dès votre retour. Il semble que ce soit une lettre scellée aux armes de Khelden, notre maître des mages. À votre place, je la lirais avant d\'entreprendre quoi que ce soit d\'autre, ici, à l\'abbaye.',`objectives_loc2`='Lire la Lettre glyphée, et parler à Kheldon Bremen, à l\'intérieur de l\'Abbaye de Comté-du-Nord.' WHERE `entry`=3104;
