UPDATE `locales_quest` SET `title_loc2`='Rune simple',`details_loc2`='Pendant que vous m\'aidiez, on m\'a donné cette rune pour vous, $N. Prenez le temps de la lire. Je pense qu\'elle provient du maître des guerriers, Thran. Jetez-y un coup d\'œil et allez le retrouver à l\'intérieur de Courbenclume.',`objectives_loc2`='Lisez la Rune simple, et parlez à Thran Khorman, dans la Vallée des Frigères.' WHERE `entry`=3106;
