UPDATE `locales_quest` SET `title_loc2`='Note de service glyphée',`details_loc2`='Pendant que vous m\'aidiez, on m\'a donné cette note de service pour vous. Prenez le temps de la lire. Je pense qu\'elle provient du Maître des mages, Marryk. Jetez-y un coup d\'œil et allez le retrouver, à l\'intérieur de Courbenclume, quand vous en aurez le temps.',`objectives_loc2`='Lire la Note de service glyphée, et parler à Marryk Nurribit, dans Courbenclume, au-dessus de la Vallée des Frigères.' WHERE `entry`=3114;
UPDATE `locales_quest` SET `title_loc2`='Rune contaminée',`details_loc2`='Pendant que vous m\'aidiez, on m\'a donné ce mémorandum pour vous. Prenez le temps de le lire. Je pense qu\'il vient de Saripal, le maître des démonistes. Allez la retrouver dans Courbenclume quand vous en aurez le temps.$B$BEt faites attention à vous, $N, on n\'apprécie pas trop les oiseaux dans votre genre, sur ces terres.',`objectives_loc2`='Lisez la Rune contaminée et parlez à Saripal Couvebrassin, à Courbenclume, au-dessus de la Vallée des Frigères.' WHERE `entry`=3115;
UPDATE `locales_quest` SET `title_loc2`='Cachet simple',`details_loc2`='Ce cachet m\'a été donné par un messager de notre maître des guerriers, Alyissia. Elle souhaiterait vous parler, dès que vous aurez un moment à lui accorder. Lisez ce message et rapportez-le-lui.',`objectives_loc2`='Allez voir Alyissia, à l\'intérieur d\'Aldrassil, au Sombrevallon.' WHERE `entry`=3116;
