UPDATE `locales_quest` SET `title_loc2`='La voie du chasseur',`details_loc2`='Salut, jeune adepte de la chasse. Vous me semblez assez habile pour apprivoiser une bête et en faire votre compagnon.$B$BVous devez aller voir Dazalar, à Dolanaar. Il vous mettra sur la voie, et vous montrera comment avoir votre propre familier.',`objectives_loc2`='Parler à Dazalar, à Dolanaar, en Teldrassil.' WHERE `entry`=6071;
UPDATE `locales_quest` SET `title_loc2`='La voie du chasseur',`details_loc2`='Salut, jeune adepte de la chasse. Vous me semblez assez habile pour apprivoiser une bête et en faire votre compagnon.$B$BVous devez aller voir Dazalar, à Dolanaar. Il vous mettra sur la voie, et vous montrera comment avoir votre propre familier.',`objectives_loc2`='Parler à Dazalar, à Dolanaar, en Teldrassil.' WHERE `entry`=6072;
