UPDATE `locales_quest` SET `title_loc2`='Voile de sagesse infinie',`details_loc2`='Je suis content de voir que vous allez nous aider pour notre offensive en Ahn\'Qiraj, $N. J\'aimerais vous offrir un objet de protection, qui pourra vous servir contre notre ennemi, $C.$B$BJ\'ai besoin pour cela d\'un drapé qiraji, que je retaillerai à vos mesures, bien sûr. J\'ai aussi besoin d\'idoles et de scarabées dont je me servirai pour fabriquer le fermoir.$B$BJe vous promets que vous ne serez pas $Gdéçu:déçue;.',`objectives_loc2`='Apporter 1 Drapé martial qiraji, 2 Idoles de jaspe, 5 Scarabées d\'or et 5 Scarabées d\'argile à Keyl Vivegriffe, en Silithus. Vous devrez aussi être $GRévéré:Révérée; par le Cercle cénarien pour terminer cette quête.' WHERE `entry`=8689;
UPDATE `locales_quest` SET `title_loc2`='Cape de la tempête imminente',`details_loc2`='Je suis content de voir que vous allez nous aider pour notre offensive en Ahn\'Qiraj, $N. J\'aimerais vous offrir un objet de protection, qui pourra vous servir contre notre ennemi, $C.$B$BJ\'ai besoin pour cela d\'un drapé qiraji, que je retaillerai à vos mesures, bien sûr. J\'ai aussi besoin d\'idoles et de scarabées dont je me servirai pour fabriquer le fermoir.$B$BJe vous promets que vous ne serez pas $Gdéçu:déçue;.',`objectives_loc2`='Apportez 1 Drapé royal qiraji, 2 Idoles d\'obsidienne, 5 Scarabées d\'or et 5 Scarabées d\'argile à Keyl Vivegriffe, en Silithus. Vous devrez aussi être $GRévéré:Révérée; par le Cercle cénarien pour terminer cette quête.' WHERE `entry`=8690;
