UPDATE `locales_quest` SET `title_loc2`='Tunique polaire',`details_loc2`='Grâce à vous et Omarion (qu\'il repose en paix), je suis maintenant capable de confectionner une tunique polaire. Vous n\'avez plus qu\'à m\'apporter les matériaux... et à me verser une modeste rétribution.',`objectives_loc2`='L\'artisan Wilhelm de la chapelle de l\'Espoir de Lumière, dans les Maleterres de l\'Est, veut 8 Runes de givre, 16 Cuirs enchantés, 5 Essences d\'eau, 5 Peaux robustes traitées et 300 pièces d\'or. Vous devez aussi être $GExalté:Exaltée; par l\'Aube d\'argent.',`offerrewardtext_loc2`='Voici votre commande, $N. Livré comme promis!' WHERE `entry`=9243;
UPDATE `locales_quest` SET `title_loc2`='Brassards glacécaille',`details_loc2`='Grâce à vous et Omarion (qu\'il repose en paix), je suis maintenant capable de confectionner des brassards glacécaille. Vous n\'avez plus qu\'à m\'apporter les matériaux... et à me verser une modeste rétribution.',`objectives_loc2`='L\'artisan Wilhelm de la chapelle de l\'Espoir de Lumière, dans les Maleterres de l\'Est, veut 4 Runes de givre, 16 Ecailles de scorpide épaisses, 5 Essences d\'eau, 3 Peaux robustes traitées et 200 pièces d\'or.',`offerrewardtext_loc2`='Voici votre commande, $N. Livré comme promis!' WHERE `entry`=9244;
