UPDATE `locales_quest` SET `title_loc2`='Cristal nécrotique fêlé',`details_loc2`='Sur le cadavre de l\'abomination, vous découvrez un cristal lézardé, animé d\'une lente pulsation d\'énergie nécrotique.',`objectives_loc2`='Apportez le Cristal nécrotique fêlé au lieutenant Orrin à l\'extérieur des portes de Hurlevent.' WHERE `entry`=9292;
