UPDATE `locales_quest` SET `title_loc2`='Lettre du front',`details_loc2`='Vous avez trouvé cette lettre sur l\'un des nombreux serviteurs du Fléau que vous avez tués. À en juger par son apparence et son odeur, elle a longtemps été en la possession de morts-vivants. Peut-être que quelqu\'un à la chapelle de l\'Espoir de Lumière souhaiterait l\'examiner plus attentivement...',`objectives_loc2`='Apportez la Lettre déchirée au gardien des registres à la chapelle de l\'Espoir de Lumière dans les Maleterres de l\'Est.' WHERE `entry`=9295;
UPDATE `locales_quest` SET `title_loc2`='Une note du front',`details_loc2`='Vous avez trouvé cette lettre sur l\'un des nombreux serviteurs du Fléau que vous avez tués. À en juger par son apparence et son odeur, elle a longtemps été en la possession de morts-vivants. Peut-être que quelqu\'un à la chapelle de l\'Espoir de Lumière souhaiterait l\'examiner plus attentivement...',`objectives_loc2`='Apportez la Note détériorée au gardien des registres à la chapelle de l\'Espoir de Lumière dans les Maleterres de l\'Est.' WHERE `entry`=9299;
