UPDATE `locales_command` SET `help_text_loc3`='Syntax: .explorecheat #flag\r\n\r\nAlle Karten für den ausgewählten Spieler ein- oder ausblenden. Wenn kein Spieler ausgewählt ist, verstecke oder enthülle deine Karten.\r\n\r\nVerwende ein #flag mit dem Wert 1, um alle Karten zu enthüllen, verwende einen #flag-Wert von 0, um alle Karten auszublenden.' WHERE `id`=175;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .gm [on/off]\r\n\r\nAktiviert oder deaktiviert im Spiel den GM-MODUS oder zeigt den aktuellen Status an, wenn on/off nicht angegeben wird.' WHERE `id`=190;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .gm chat [on/off]\r\n\r\nAktiviert oder deaktiviert den Chat-GM-MODUS (zeigt das GM-Abzeichen in Nachrichten) oder zeigt den aktuellen Status an, wenn on/off nicht angegeben wird.' WHERE `id`=191;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .gm fly [on/off]\r\n\r\nGM-Flugmodus aktivieren/deaktivieren.' WHERE `id`=192;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .gm ingame\r\n\r\nZeigt eine Liste der im Spiel verfügbaren Game Master an.' WHERE `id`=193;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .gm list\r\n\r\nZeigt eine Liste aller Game Master-Konten und Sicherheitsstufen an.' WHERE `id`=194;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .gm setview\r\n\r\nStellt die Fernsicht auf die ausgewählte Einheit ein. Wähle dich selbst aus, um die Ansicht zurückzusetzen.' WHERE `id`=195;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .gm visible on/off\r\n\r\nAktuellen Sichtbarkeitsstatus ausgeben oder GM für andere Spieler sichtbar(on) und unsichtbar(off) machen.' WHERE `id`=196;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .go  [$playername|pointlink|#x #y #z [#mapid]]\r\n\r\nTeleportiert deinen Charakter zum Punkt mit Koordinaten von Spieler $playername oder Koordinaten von einem der Shift-Link-Typen: Spieler, Tele, Taxinode, Kreatur/Kreatureneintrag, Spielobjekt/Spielobjekt_Eintrag oder explizite #x #y #z #mapid-Koordinaten.' WHERE `id`=200;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .go creature (#creature_guid|$creature_name|id #creature_id)\r\n\r\nTeleportiert deinen Charakter zu einer Kreatur mit guid #creature_guid oder teleportiert deinen Charakter zu einer Kreatur mit einem Namen, der als Teil des Namens $creature_name enthalten ist, oder teleportiert deinen Charakter zu einer Kreatur, die aus der Vorlage mit diesem Eintrag #creature_id gespawnt wurde.' WHERE `id`=201;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .go graveyard #graveyardId\r\n\r\nZum angegebenen Friedhof GraveyardId teleportieren.' WHERE `id`=202;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .go grid #gridX #gridY [#mapId]\r\n\r\nTeleportiere den GM in die Mitte des Rasters mit den bereitgestellten Indizes auf der Karte #mapId (oder der aktuellen Karte, falls sie nicht eingegeben wird).' WHERE `id`=203;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .go object (#gameobject_guid|$gameobject_name|id #gameobject_id)\r\n\r\nTeleportiert deinen Charakter zu einem Spielobjekt mit der Guid #gameobject_guid, oder teleportiert deinen Charakter zu einem Spielobjekt mit einem Namen, der als Teil des Namens $gameobject_name enthalten ist, oder teleportiert deinen Charakter zu einem Spielobjekt, das aus der Vorlage mit diesem Eintrag #gameobject_id gespawnt wurde.' WHERE `id`=204;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .go taxinode #taxinode\r\n\r\nSpieler zu den Taxinode-Koordinaten teleportieren. Die Zone kann .lookup taxinode $namepart nachgeschlagen werden.' WHERE `id`=205;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .go trigger (#trigger_id|$trigger_shift-link|$trigger_target_shift-link) [target]\r\n\r\nTeleportiert deinen Charakter mit der ID #trigger_id oder der Trigger-ID, die mit der Umschalttaste verknüpft ist, zu einem Bereichstrigger. Wenn zusätzliches Argument "Ziel" angegeben wird, wird der Charakter zum Zielpunkt des Triggers teleportiert.' WHERE `id`=206;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .go xy #x #y [#mapid]\r\n\r\nSpieler auf Karte #mapid oder dieselbe Karte, wenn #mapid nicht angegeben ist, mit (#x,#y) Koordinaten auf Bodenhöhe (Wasser) teleportieren.' WHERE `id`=207;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .go xyz #x #y #z [#mapid]\r\n\r\nSpieler zu (#x,#y,#z) Koordinaten auf Bodenhöhe (Wasser) auf der Karte #mapid oder derselben Karte, wenn #mapid nicht angegeben ist, teleportieren.' WHERE `id`=208;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .go zonexy #x #y [#zone]\r\n\r\nSpieler zu (#x,#y) Client-Koordinaten auf Bodenhöhe (Wasser) in der Zone #zoneid oder der aktuellen Zone teleportieren, wenn #zoneid nicht angegeben ist. Die Zone kann mithilfe von .lookup area $namepart nachgeschlagen werden.' WHERE `id`=209;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .gobject add #id <spawntimeSecs>\r\n\r\nFüge ein Spielobjekt aus den Spielobjektvorlagen zur Welt an deinem aktuellen Standort hinzu, indem du die #id verwendest.\r\nspawntimesecs legt die Spawnzeit fest, sie ist optional.\r\nHinweis: Dies ist eine Kopie von .gameobject.' WHERE `id`=210;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .gobject delete #go_guid\r\n\r\nSpielobjekt mit Guid #go_guid löschen.' WHERE `id`=212;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .gobject move #goguid [#x #y #z]\r\n\r\nVerschiebt das Spielobjekt #goguid zu den Koordinaten des Characters (oder zu den (#x,#y,#z)-Koordinaten, falls vorhanden).' WHERE `id`=214;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .gobject near  [#distance]\r\n\r\nGibt Spielobjekte in Entfernung #Distanz vom Spieler aus. Gibt Spielobjekt-Guides und Koordinaten sortiert nach Entfernung vom Charakter aus. Wenn #distance nicht angegeben ist, wird 10 als Standardwert verwendet.' WHERE `id`=215;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .gobject target [#go_id|#go_name_part]\r\n\r\nLokalisiert und zeigt die Position zum nächsten Spielobjekt an. Wenn #go_id oder #go_name_part angegeben werden, dann wird die Position des nächsten Spielobjekts mit der Spielobjekt-Vorlagen-ID #go_id oder dem Namen gesucht und angezeigt, dessen #go_name_part als Teil enthält.' WHERE `id`=218;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .gobject turn #goguid\r\n\r\nLegt für gameobject #goguid die gleiche Ausrichtung wie die aktuelle Charakterausrichtung fest.' WHERE `id`=219;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .gps [$name|$shift-link]\r\n\r\nZeigt die Positionsinformationen für einen ausgewählten Charakter oder eine ausgewählte Kreatur an (auch wenn der Spielername $name angegeben ist, dann für den benannten Spieler, oder wenn ein Shift-Link für Kreatur/Spielobjekt angegeben ist, dann Kreatur/Spielobjekt, wenn es geladen wurde). Positionsinformationen umfassen X, Y, Z und Ausrichtung, Karten-ID und Zonen-ID.' WHERE `id`=220;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .quest complete #questid\r\n\r\nMarkiert alle Questziele für die aktive Quest des Zielcharakters als abgeschlossen. Danach kann der Zielcharakter die Questbelohnung einlösen.' WHERE `id`=221;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .groupgo [$charactername]\r\n\r\nTeleportiert den angegebenen Charakter und seine Gruppe zu dir. Teleportiert nur Online-Charaktere, aber ursprünglich ausgewähltes Gruppenmitglied kann offline sein.' WHERE `id`=225;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .guid\r\n\r\nZeigt die GUID für das ausgewählte Zeichen an.' WHERE `id`=230;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .guild create [$GuildLeaderName] "$GuildName"\r\n\r\nErstelle eine Gilde namens $GuildName mit dem Spieler $GuildLeaderName (oder ausgewähltem Spieler) als Anführer. Gildenname muss in Anführungszeichen gesetzt werden.' WHERE `id`=235;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .guild delete "$GuildName"\r\n\r\nGilde $GuildName löschen. Gildenname muss in Anführungszeichen gesetzt werden.' WHERE `id`=236;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .guild invite [$CharacterName] "$GuildName"\r\n\r\nSpieler $CharacterName (oder ausgewählten Spieler) zu einer Gilde $GuildName hinzufügen. Gildenname muss in Anführungszeichen gesetzt werden.' WHERE `id`=237;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .guild rank [$CharacterName] #Rank\r\n\r\nLegt für Spieler $CharacterName (oder einen ausgewählten Spieler) Rang #Rank in einer Gilde fest.' WHERE `id`=238;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .guild uninvite [$CharacterName]\r\n\r\nSpieler $CharacterName (oder ausgewählter Spieler) aus einer Gilde entfernen.' WHERE `id`=239;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .help [$command]\r\n\rBedienungsanleitung für den gegebenen $Befehl anzeigen. Wenn kein $-Befehl angegeben wird, wird die Liste der verfügbaren Befehle gezeigt.' WHERE `id`=240;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .hidearea #areaid\r\n\r\nBlendet den Bereich von #areaid für den ausgewählten Charakter aus. Wenn kein Charakter ausgewählt ist, wird für dich dieser Bereich ausgeblendet.' WHERE `id`=245;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .honor add $amount\r\n\r\nFügt dem ausgewählten Spieler eine bestimmte Menge an Ehre hinzu (die Sie zu Lebzeiten erhalten haben).' WHERE `id`=250;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .honor addkill\r\n\r\nFügt die anvisierte Einheit heute als einen deiner PvP-Kills hinzu (du erhältst nur Ehre, wenn es sich um einen Rassenführer oder einen Spieler handelt).' WHERE `id`=251;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .honor show\r\n\r\nZeigt die Ehrenstatistik des ausgewählten Spielers an.' WHERE `id`=252;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .honor update\r\n\r\nErzwingt eine Aktualisierung der Ehre.' WHERE `id`=253;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .instance listbinds\r\n\r\nListet die Dungeon-Bindungen des ausgewählten Spielers auf.' WHERE `id`=260;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .instance savedata\r\n\r\nSpeichert die Instanzdaten für die Karte des aktuellen Spielers in der DB.' WHERE `id`=261;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .instance stats\r\n\r\nZeigt Statistiken zu Instanzen an.' WHERE `id`=262;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .instance unbind all\r\n\r\nAlle Dungeon-Bindungen des ausgewählten Spielers werden gelöscht.\r\n.instance unbind #mapid\r\nNur die angegebene #mapid-Instanz wird gelöscht.' WHERE `id`=263;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .itemmove #sourceslotid #destinationslotid\r\n\r\nVerschiebt einen Gegenstand von Slot #sourceslotid nach #destinationslotid in Ihrem Inventar\r\n\r\nNoch nicht implementiert.' WHERE `id`=265;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .kick [$charactername]\r\n\r\nWirft den angegebenen Charakternamen aus der Welt. Wenn kein Charaktername angegeben wird, wird der ausgewählte Spieler (außer dir selbst) gekickt.' WHERE `id`=270;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .learn #spell [all]\r\n\r\nDer ausgewählte Charakter lernt einen Zauber der ID #spell. Wenn \'all\' angegeben ist, dann werden alle Ränge gelernt.' WHERE `id`=275;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .learn all\r\n\r\nLerne alle großen unterschiedlichen Zaubersprüche, vielleicht nützlich für Administratoren.' WHERE `id`=276;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .learn crafts\r\n\r\nLerne alle Berufe und Rezepte.' WHERE `id`=277;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .learn all_default [$playername]\r\n\r\nLerne für den ausgewählten/$playername-Spieler alle Standardzauber für seine Rasse/Klasse und Zauber, die durch abgeschlossene Quests erlernt werden können.' WHERE `id`=278;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .learn all_gm\r\n\r\nLernt alle Standardzauber für Game Master.' WHERE `id`=279;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .learn all_lang\r\n\r\nLerne alle Sprachen.' WHERE `id`=280;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .learn all_myclass\r\n\r\nLerne alle Zaubersprüche und Talente, die für deine Klasse verfügbar sind.' WHERE `id`=281;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .learn all_myspells\r\n\r\nErlerne alle Zauber (außer Talente und Zauber mit erstem Rang, die als Talent erlernt werden), die für deine Klasse verfügbar sind.' WHERE `id`=283;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .learn all_mytalents\r\n\r\nErlerne alle Talente (und Zauber mit erstem Rang, die als Talent erlernt werden), die für deine Klasse verfügbar sind.' WHERE `id`=284;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .learn all_recipes [$profession]\r\n\r\nLerne alle Rezepte des angegebenen Berufes und setze die Fertigkeitsstufe auf das Maximum.\r\nBeispiel: .learn all_recipes enchanting' WHERE `id`=285;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .levelup [$playername] [#numberoflevels]\r\n\r\nErhöht/verringt das Level des Charakters mit $playername (oder des ausgewählten Spielers, wenn kein Name angegeben ist) um #numberoflevels oder +1, wenn kein #numberoflevels angegeben ist). Wenn #numberoflevels weggelassen wird, wird das Level um 1 erhöht. Wenn #numberoflevels 0 ist, wird das gleiche Level neu gestartet. Wenn kein Charakter ausgewählt und kein Name angegeben ist, erhöht sich dein Level. Befehl kann für Offline-Charakter verwendet werden. Alle Statistiken und abhängigen Werte werden neu berechnet. Bei Levelabstieg können Talente bei Bedarf zurückgesetzt werden. Auch bei Levelabstieg können ausgerüstete Items mit höheren Levelanforderungen verloren gehen.' WHERE `id`=290;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .linkgrave #graveyard_id [alliance|horde]\r\n\r\nVerbindet die aktuelle Zone mit dem Friedhof für beliebige Geister (oder Allianz-/Horde-Fraktionsgeister). Dies lässt Charaktergeister aus der Zone nach dem Tod zum Friedhof teleportieren, wenn der Friedhof am nächsten an der Zone liegt und Geister dieser Fraktion akzeptiert. Füge nur einen einzelnen Friedhof auf einer anderen Karte hinzu und nur, wenn keine Friedhöfe verknüpft sind (oder auf derselben Karte verknüpft sind).' WHERE `id`=295;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .list creature #creature_id [#max_count]\r\n\r\nGibt Kreaturen mit der Kreaturen-ID #creature_id aus, die in der Welt gefunden wurden. Ausgabe von Kreaturenführern und -koordinaten, sortiert nach Entfernung vom Charakter. Es werden maximal #max_count Kreaturen ausgegeben. Wenn #max_count nicht angegeben ist, wird 10 als Standardwert verwendet.' WHERE `id`=300;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .list item #item_id [#max_count]\r\n\r\nAusgabegegenstände mit der Gegenstands-ID #item_id, die in allen Charakterinventaren, Mails, Auktionen und Gildenbanken zu finden sind. Ausgabe von Item-Guids, Item-Besitzer-Guid, Besitzerkonto und Besitzername (Gildenname und Guid, falls Gildenbank). Es werden maximal #max_count Elemente ausgegeben. Wenn #max_count nicht angegeben ist, wird 10 als Standardwert verwendet.' WHERE `id`=301;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .list object #gameobject_id [#max_count]\r\n\r\nSpielobjekte mit der Spielobjekt-ID #gameobject_id ausgeben, die in der Welt gefunden wurden. Gibt Spielobjekt-Guides und Koordinaten sortiert nach Entfernung vom Charakter aus. Es wird maximal #max_count gameobject ausgegeben. Wenn #max_count nicht angegeben ist, wird 10 als Standardwert verwendet.' WHERE `id`=302;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .list talents\r\n\r\nZeigt alle wirklich bekannten (als erlernte Zauber) Talentrang-Zauber für den ausgewählten Spieler oder sich selbst an.' WHERE `id`=303;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .loadscripts $scriptlibraryname\r\n\r\nEntfernt die aktuelle und lädt die Skriptbibliothek $scriptlibraryname oder lädt die aktuelle neu, wenn $scriptlibraryname ausgelassen wurde, falls sie geändert wurde, während der Server ausgeführt wurde.' WHERE `id`=305;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup account email $emailpart [#limit] \r\n\r\nSucht Konten, die E-Mail enthalten, einschließlich $emailpart mit optionalem Parameter #limit of results. Wenn #limit nicht angegeben wird, wird 100 angenommen.' WHERE `id`=310;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup account ip $ippart [#limit] \r\n\r\nSucht Konten, die zuletzt IP verwendet haben, einschließlich $ippart (textuell) mit optionalem Parameter #$limit der Ergebnisse. Wenn #limit nicht angegeben wird, wird 100 angenommen.' WHERE `id`=311;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup account name $accountpart [#limit] \r\n\r\nSucht Konten, deren Benutzername $accountpart mit optionalem Parameter #limit der Ergebnisse enthält. Wenn #limit nicht angegeben wird, wird 100 angenommen.' WHERE `id`=312;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup area $namepart\r\n\r\nSucht ein Gebiet mit $namepart und gibt alle Übereinstimmungen mit ihren Gebiets-IDs zurück.' WHERE `id`=314;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup creature $namepart\r\n\r\nSucht eine Kreatur nach $namepart und gibt alle Übereinstimmungen mit ihren Kreaturen-IDs zurück.' WHERE `id`=315;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup event $name\r\n\r\nVersucht, die ID des Ereignisses mit dem angegebenen $name zu finden.' WHERE `id`=317;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup faction $name\r\n\r\nVersucht, die ID der Fraktion mit dem angegebenen $name zu finden.' WHERE `id`=318;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup item $itemname\r\n\r\nSucht einen Gegenstand mit $itemname und gibt alle Übereinstimmungen mit ihren Gegenstand-IDs zurück.' WHERE `id`=319;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup itemset $itemname\r\n\r\nSucht einen Item-Set mit $itemname und gibt alle Übereinstimmungen mit ihren Gegenstand-IDs zurück.' WHERE `id`=320;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup object $objname\r\n\r\nSucht ein Spielobjekt mit $objname und gibt alle Übereinstimmungen mit ihren Spielobjekt-IDs zurück.' WHERE `id`=321;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup player account $accountpart [#limit] \r\n\r\nSucht Spieler, deren Konto-Benutzername den $Kontoteil enthalten mit optionalem Parameter #limit der Ergebnisse. Wenn #limit nicht angegeben wird, wird 100 angenommen.' WHERE `id`=322;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup player email $emailpart [#limit] \r\n\r\nSucht nach Spielern, deren Konto-E-Mail $emailpart enthalt mit optionalem Parameter #limit der Ergebnisse. Wenn #limit nicht angegeben wird, wird 100 angenommen.' WHERE `id`=323;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup player ip $ippart [#limit] \r\n\r\nSucht nach Spielern, deren Konto zuletzt IP verwendet hat, einschließlich $ippart (Text) mit optionalem Parameter #limit der Ergebnisse. Wenn #limit nicht angegeben wird, wird 100 angenommen.' WHERE `id`=324;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup pool $pooldescpart\r\n\r\nListet die Pools (irgendwo) mit Teilstring in der Beschreibung auf.' WHERE `id`=325;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup quest $namepart\r\n\r\nSucht eine Quest mit $namepart und gibt alle Übereinstimmungen mit ihren Quest-IDs zurück.' WHERE `id`=326;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup skill $$namepart\r\n\r\nSucht einen Skill mit $namepart und gibt alle Übereinstimmungen mit ihren Skill-IDs zurück.' WHERE `id`=327;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup spell $namepart\r\n\r\nSucht einen Zauber mit $namepart und gibt alle Übereinstimmungen mit ihren Zauber-IDs zurück.' WHERE `id`=328;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup taxinode $substring\r\n\r\nSucht und gibt alle Taxinodes mit $substring im Namen aus.' WHERE `id`=329;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .lookup tele $substring\r\n\r\nSucht und gibt alle .tele-Befehlsorte mit $substring im Namen aus.' WHERE `id`=330;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .maxskill\r\n\r\nSetzt alle Fertigkeiten des anvisierten Spielers auf ihre Maximalwerte für sein aktuelles Level.' WHERE `id`=340;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify aspeed #rate\r\n\r\nÄndert alle Geschwindigkeiten – Laufen, Schwimmen, Zurücklaufen, Zurückschwimmen – des ausgewählten Spielers auf „normale Grundgeschwindigkeit für diese Bewegungsart“*Rate. Wenn kein Spieler ausgewählt ist, wird deine Geschwindigkeit geändert.\r\n\r\n#rate kann von 0,1 bis GM.MaxSpeedFactor reichen, welcher in mangosd.conf definiert ist."' WHERE `id`=346;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify bwalk #rate\r\n\r\nÄndert die Geschwindigkeit des ausgewählten Spielers, während er rückwärts läuft, auf „normale Rücklaufgeschwindigkeit“*Rate. Wenn kein Spieler ausgewählt ist, wird deine Geschwindigkeit geändert.\r\n\r\n#rate kann von 0,1 bis GM reichen.MaxSpeedFactor definiert in mangosd.conf."' WHERE `id`=347;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify drunk #value\r\n\r\nBetrunkenheitsgrad auf #value (0..100) setzen. Der Wert 0 entfernt den Betrunkenheitszustand, 100 ist der maximale Betrunkenheitszustand.' WHERE `id`=349;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify energy #energy\r\n\r\nÄndert die Energie des ausgewählten Spielers. Wenn kein Spieler ausgewählt ist, wird deine Energie geändert.' WHERE `id`=350;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify faction #factionid #flagid #npcflagid #dynamicflagid\r\n\r\nÄndert die Fraktion und die Flaggen der ausgewählten Kreatur. Zeigt ohne Argumente die Fraktion und die Flaggen der ausgewählten Kreatur an.' WHERE `id`=351;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify gender male/female\r\n\r\nGeschlecht des ausgewählten Spielers ändern.' WHERE `id`=353;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify honor $field $value\r\n\r\n$value zu einem Ehren $field hinzufügen (es dient nur zur Fehlersuche, Werte werden beim Abmelden des Spielers nicht gespeichert) Feldliste: points / rank / todaykills / yesterdaykills / yesterdayhonor /thisweekkills / thisweekhonor / lastweekkills / lastweekhonor /lastweekstanding / lifetimedishonorablekills / lifetimehonorablekills ' WHERE `id`=354;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify hp #newhp\r\n\r\nÄndert die HP des ausgewählten Spielers. Wenn kein Spieler ausgewählt ist, werden deine HP geändert.' WHERE `id`=355;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify mana #newmana\r\n\r\nÄndert das Mana des ausgewählten Spielers. Wenn kein Spieler ausgewählt ist, wird dein Mana geändert.' WHERE `id`=356;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify money #money\r\n\r\nFügt dem ausgewählten Spieler Geld hinzu oder entfernen es. Wenn kein Spieler ausgewählt ist, wird dein Geld geändert.\r\n\r\n #gold kann negativ sein, um Geld zu entfernen.' WHERE `id`=357;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify morph #displayid\r\n\r\nÄndert deine aktuelle Modell-ID in #displayid.' WHERE `id`=358;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify mount #id #speed\r\n\r\nZeigt den ausgewählten Spieler auf #id Kreatur reitend an und setzt die Geschwindigkeit auf den Wert #speed.' WHERE `id`=359;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify rage #newrage\r\n\r\nÄndert die Wut des ausgewählten Spielers. Wenn kein Spieler ausgewählt ist, wird deine Wut geändert.' WHERE `id`=361;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify rep #repId (#repvalue|$rankname [#delta])\r\n\r\nSetzt die Reputation des ausgewählten Spielers mit Fraktion #repId auf #repvalue oder auf $reprank.\r\nWenn der Reputationsrangname angegeben wird, ist die resultierende Reputation die niedrigste Reputation für diesen Rang plus den Delta-Betrag, falls angegeben.\r nEs kann \'.pinfo rep\' verwenden, um alle bekannten Reputations-IDs aufzulisten, oder \'.lookup faction $name\', um eine bestimmte Fraktions-ID zu finden.' WHERE `id`=362;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify scale #scale\r\n\r\nÄndert den Modellmaßstab für den anvisierten Spieler (bis zum nächsten Login) oder Kreatur (bis respawn).' WHERE `id`=364;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify speed #rate\r\n\r\nÄndert die Laufgeschwindigkeit des ausgewählten Spielers auf „normale Grundlaufgeschwindigkeit“*rate. Wenn kein Spieler ausgewählt ist, wird deine Geschwindigkeit geändert.\r\n\r\n#rate kann von 0,1 bis GM.MaxSpeedFactor, definiert in mangosd.conf, reichen."' WHERE `id`=365;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify standstate #emoteid\r\n\r\nÄndert das Emote deines Charakters im Stehen zu #emoteid.' WHERE `id`=366;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .modify swim #rate\r\n\r\nÄndert die Schwimmgeschwindigkeit des ausgewählten Spielers auf „normale Schwimmgeschwindigkeit“*rate. Wenn kein Spieler ausgewählt ist, wird deine Geschwindigkeit geändert.\r\n\r\n#rate kann von 0,1 bis GM.MaxSpeedFactor, definiert in mangosd.conf, reichen."' WHERE `id`=367;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .movegens\r\n\r\nBewegungsgenerator-Stack für ausgewählte Kreatur oder Spieler anzeigen.' WHERE `id`=370;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .mute [$playerName] $timeInMinutes\r\n\r\nDeaktiviert Chatnachrichten für jeden Charakter vom Konto des Charakters $playerName (oder aktuell ausgewählten Spieler) für $timeInMinutes Minuten. Der Spieler kann offline sein.' WHERE `id`=375;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .neargrave [alliance|horde]\r\n\r\nFindt den nächstgelegenen Friedhof, der mit der Zone verbunden ist (oder nur den Nächstgelegenen der Geistern der Allianz- oder Hordenfraktion akzeptiert).' WHERE `id`=380;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .notify $MessageToBroadcast\r\n\r\nSendt eine globale Nachricht an den Bildschirm alle Spieler, die online sind.' WHERE `id`=385;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc add #creatureid\r\n\r\nSpawne eine Kreatur mit der angegebenen Template-ID von #creatureid.' WHERE `id`=390;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc additem #itemId <#maxcount><#incrtime>\r\n\r\nGegenstand #itemid zur Gegenstandsliste des ausgewählten Verkäufers hinzufügen. Legen Sie optional auch die maximale Anzahl von Gegenständen in der Verkäufergegenstandsliste und die Zeit bis zur Wiederherstellung der Gegenstandsanzahl fest.' WHERE `id`=392;
UPDATE `locales_command` SET `help_text_loc3`='Noch nicht hinzugefügt.' WHERE `id`=394;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc npc aiinfo\r\n\r\nNPC-KI und Skriptinformationen anzeigen.' WHERE `id`=395;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc allowmove\r\n\r\nAktiviert oder deaktiviert Bewegungskreaturen in der Welt. Nicht implementiert.' WHERE `id`=396;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc changelevel #level\r\n\r\nÄndert die Stufe der ausgewählten Kreatur auf #level.\r\n\r\n#level kann zwischen 1 und 63 liegen.' WHERE `id`=397;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc delete [#guid]\r\n\r\nKreatur mit Guid #guid löschen (oder die ausgewählte Kreatur, wenn keine Guid angegeben ist)' WHERE `id`=399;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc delitem #itemId\r\n\r\nGegenstand #itemid aus der Gegenstandsliste des ausgewählten Verkäufers entfernen.' WHERE `id`=400;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc factionid #factionid\r\n\r\nSetzt die Fraktion der ausgewählten Kreatur auf #factionid.' WHERE `id`=401;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc flag #npcflag\r\n\r\nSetzt die NPC-Flags der Kreaturenvorlage der ausgewählten Kreatur und der ausgewählten Kreatur auf #npcflag. NPC-Flags werden nach dem Neustart des Servers oder dem Entladen/Laden des Grids auf alle Kreaturen der ausgewählten Kreaturenvorlage angewendet.' WHERE `id`=402;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc follow\r\n\r\nAusgewählte Kreaturen folgen dir bis zum Tod/Kampf/etc.' WHERE `id`=403;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc info\r\n\r\nZeigt eine Liste mit Details für die ausgewählte Kreatur an.\r\n\r\nDie Liste enthält:-\r\nGUID, Fraktion, NPC-Flags, Eintrags-ID, Modell-ID,\r\n- Level,\r\n- Gesundheit (aktuell/maximal),\r\n- Feldflags, dynamische Flags, Fraktionsvorlage,\r\n- Positionsinformationen,\r\n- und der Kreaturentyp, z.B. ob die Kreatur ein Verkäufer ist.' WHERE `id`=404;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc move [#creature_guid]\r\n\r\nBewegt den anvisierten Kreaturen-Spawnpunkt zu deinen Koordinaten.' WHERE `id`=405;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc name $name\r\n\r\nÄndert den Namen der ausgewählten Kreatur oder des ausgewählten Charakters in $name.\r\n\r\nBefehl deaktiviert.' WHERE `id`=406;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc playemote #emoteid\r\n\r\nLässt die ausgewählte Kreaturen den Emote mit der ID #emoteid ausführen.' WHERE `id`=407;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc say #text\r\n\r\nLässt den ausgewählten NPC #text sagen.' WHERE `id`=408;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc setdeathstate on/off\r\n\r\nLegt den Standard-Todesstatus (tot/lebendig) für den NPC beim Spawn fest.' WHERE `id`=409;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc setmodel #displayid\r\n\r\nÄndert die Modell-ID der ausgewählten Kreatur in #displayid.' WHERE `id`=410;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc setmovetype [#creature_guid] stay/random/way [NODEL]\r\n\r\nStellt für die Kreatur ein, auf die der Bewegungstyp #creature_guid zeigt (oder die ausgewählt Kreatur, wenn #creature_guid nicht angegeben ist), und bewegt sie in die Respawn-Position (wenn die Kreatur lebt). Alle vorhandenen Wegpunkte für Kreaturen werden aus der Datenbank entfernt, wenn Sie NODEL nicht verwenden. Wenn die Kreatur tot ist, wird der Bewegungstyp beim Respawn der Kreatur angewendet.\r\nVergewissere dich, dass du NODEL verwendest, wenn du die Wegpunkte behalten möchtest.' WHERE `id`=411;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc spawndist #dist\r\n\r\nSpawndistanz der ausgewählten Kreatur auf #dist anpassen.' WHERE `id`=413;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc spawntime #time\r\n\r\nPasst die Spawnzeit der ausgewählten Kreatur an die Zeit #time an.' WHERE `id`=414;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc subname $Name\r\n\r\nÄndert den Unternamen der ausgewählten Kreatur oder des ausgewählten Spielers zu $Name.\r\n\r\nBefehl deaktiviert.' WHERE `id`=415;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc tame\r\n\r\nAusgewählte Kreatur zähmen (zähmbare Nicht-Haustier-Kreatur). Sie müssen kein Haustier haben.' WHERE `id`=416;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc textemote #emoteid\r\n\r\nBringe die ausgewählte Kreatur dazu, Textemote mit einem Emote der ID #emoteid auszuführen.' WHERE `id`=417;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc unfollow\r\n\r\nAusgewählte Kreatur (kein Haustier) hört auf, dir zu folgen.' WHERE `id`=418;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc whisper #playerguid #text\r\n\r\nLässt den ausgewählten NPC #text an #playerguid flüstern.' WHERE `id`=419;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .npc yell #text\r\n\r\nLässt den ausgewählten NPC #text schreien.' WHERE `id`=420;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .pdump load $filename $account [$newname] [$newguid]\r\n\r\nLaden Sie den Charakter-Dump aus der Dump-Datei in die Charakterliste von $account mit gespeichertem oder neuem Namen $newname, mit gespeicherter (oder erster freier) oder $newguid GUID.' WHERE `id`=425;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .pdump write $filename $playerNameOrGUID\r\n\r\nSchreibt einen Charakter-Dump mit dem Namen/der GUID $playerNameOrGUID in die Datei $filename.' WHERE `id`=426;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .pinfo [$player_name]\r\n\r\nKontoinformationen für ausgewählten Spieler oder Spielersuche nach $player_name ausgeben.' WHERE `id`=440;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .pool #pool_id\r\n\r\nPool-Informationen und vollständige Liste der Kreaturen/Spielobjekte, die im Pool enthalten sind ausgeben.' WHERE `id`=450;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .pool list\r\n\r\nListet die Pools mit Spawn in der aktuellen Karte auf (funktioniert nur in Instanzen. Nicht instanziierbare Karten teilen sich den Pool-Systemstatus oder nutzlosen Versuch werden alle Pools auf allen Kontinenten ausgegeben.' WHERE `id`=451;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .pool spawns #pool_id\r\n\r\nListet aktuelle Kreaturen/Objekte auf, die in Pools (oder in einer bestimmten #pool_id) aufgelistet und gespawnt sind (zu Rasterdaten hinzugefügt, was nicht bedeutet, dass sie in der Welt angezeigt werden).' WHERE `id`=452;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .quest add #quest_id\r\n\r\nQuest #quest_id zum Charakter-Questlog hinzufügen. Von Gegenständen gestartete Quests können mit diesem Befehl nicht hinzugefügt werden, aber der korrekte .additem-Aufruf wird in der Befehlsausgabe bereitgestellt.' WHERE `id`=460;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .quest remove #quest_id\r\n\r\nSetzt den Status der Quest #quest_id für den ausgewählten Spieler auf nicht abgeschlossen und nicht aktiv (und entfernt ihn von der aktiven Questliste).' WHERE `id`=462;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: quit\r\n\r\nSchließt die RA-Verbindung. Der Befehl muss vollständig eingegeben werden (quit).' WHERE `id`=470;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .recall [$playername]\r\n\r\nTeleportiert $playername oder einen ausgewählten Spieler an den Ort, an dem er sich vor der letzten Verwendung eines Teleportationsbefehls befunden hat. Wenn kein $playername eingegeben und kein Spieler ausgewählt ist, wirst du teleportiert.' WHERE `id`=480;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reload all\r\n\r\nAlle Tabellen mit Reload-Unterstützung neu laden, die _sicher_ neu geladen werden können.' WHERE `id`=490;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reload all_area\r\n\r\nAlle `areatrigger_*`-Tabellen neu laden, wenn Reload-Unterstützung für diese Tabelle hinzugefügt wurde und diese Tabelle _sicher_ neu geladen werden kann.' WHERE `id`=492;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reload all_eventai\r\n\r\n`creature_ai_*`-Tabellen neu laden, wenn Reload-Unterstützung für diese Tabellen hinzugefügt wurde und diese Tabellen _sicher_ neu geladen werden können.' WHERE `id`=493;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reload all_item\r\n\r\nLädt die Tabellen „item_required_target“, „page_texts“ und „item_enchantment_template“ neu.' WHERE `id`=494;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reload all_locales\r\n\r\nLädt alle `locales_*`-Tabellen neu, bei denen die Neuladeunterstützung hinzugefügt wurde und die _sicher_ neu geladen werden können.' WHERE `id`=495;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reload all_loot\r\n\r\nLädt alle `*_loot_template`-Tabellen neu. Dies kann den Betrieb verlangsamen und zu Verzögerungen bei der Serverausführung führen.' WHERE `id`=496;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reload all_npc\r\n\r\n`Points_of_Interest`- und `npc_*`-Tabellen neu laden, wenn Reload-Unterstützung für diese Tabellen hinzugefügt wurde und diese Tabellen _sicher_ neu geladen werden können.' WHERE `id`=497;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reload all_quest\r\n\r\nAlle questbezogenen Tabellen neu laden, wenn Reload-Unterstützung für diese Tabelle hinzugefügt wurde und diese Tabelle _sicher_ neu geladen werden kann.' WHERE `id`=498;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reload all_scripts\r\n\r\nLädt die `dbscripts_on_*`-Tabellen neu.' WHERE `id`=499;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reload all_spell\r\n\r\nAlle `spell_*`-Tabellen mit Reload-Unterstützung neu laden, die _sicher_ neu geladen werden können.' WHERE `id`=500;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reload config\r\n\r\nKonfigurationseinstellungen neu laden (die standardmäßig in mangosd.conf gespeichert sind). Nicht alle Einstellungen können beim Neuladen geändert werden: Einige neue Einstellungswerte werden bis zum Neustart ignoriert, einige Werte werden mit Verzögerung oder nur auf neue Objekte/Karten angewendet, einige Werte werden explizit abgelehnt, um beim Neuladen geändert zu werden.' WHERE `id`=501;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .repairitems\r\n\r\nRepariert alle Gegenstände des ausgewählten Spielers.' WHERE `id`=510;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reset all\r\n\r\nFordert beim nächsten Login für jeden vorhandenen Charakter das Zurücksetzen von Zaubersprüchen, Talenten usw. an.' WHERE `id`=521;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reset honor [Playername]\r\n\r\nAlle Ehrendaten für den anvisierten Charakter zurücksetzen.' WHERE `id`=522;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reset items equiped|bags|bank|keyring|all|allbags.\r\n\r\nLöscht alle Gegenstände im Inventar des ausgewählten Spielers (Ausrüstung, Bank, Taschen usw.), je nach gewählter Option.' WHERE `id`=523;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reset level [Playername]\r\n\r\nZurücksetzen des Levels auf 1, einschließlich Zurücksetzen von Statistiken und Talenten. Ausgerüstete Gegenstände mit höheren Stufenanforderungen können verloren gehen.' WHERE `id`=524;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reset spells [Playername]\r\n\r\nEntfernt alle nicht originalen Zauber aus dem Zauberbuch.\r\nSpielername kann der Name eines Offline-Charakters sein.' WHERE `id`=526;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reset stats [Playername]\r\n\r\nSetzt (neu berechnen) alle Statistiken des Zielspielers auf ihre ursprünglichen Werte auf dem aktuellen Level zurück.' WHERE `id`=527;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .reset talents [Playername]\r\n\r\nEntfernt alle Talente des Zielspielers. Der Spielername kann der Name eines Offline-Charakters sein.' WHERE `id`=528;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .respawn\r\n\r\nAusgewählte Kreatur respawnen oder alle Kreaturen in der Nähe respawnen (wenn keine ausgewählt ist) und GEHEN, ohne auf den Ablauf der Respawn-Zeit zu warten.' WHERE `id`=530;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .revive\r\n\r\nBelebe den ausgewählten Spieler wieder. Wenn kein Spieler ausgewählt ist, wirst du wiederbelebt.' WHERE `id`=531;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .save\r\n\r\nSpeichert deinen Charakter.' WHERE `id`=540;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .saveall\r\n\r\nSpeichert alle Charaktere im Spiel.' WHERE `id`=541;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .send items #playername "#subject" "#text" itemid1[:count1] itemid2[:count2] ... itemidN[:countN]\r\n\r\nSendet eine E-Mail an einen Spieler. Betreff und Mailtext müssen in "" sein. Wenn für die Gegenstands-ID keine zugehörige Anzahl angegeben wird, wird 1 angenommen. Wenn die Anzahl > max. Gegenstände im Stapel ist, werden die Gegenstände in der erforderlichen Anzahl von Stapeln gesendet. Alle Stapel sind in der Post auf 12 begrenzt.' WHERE `id`=550;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .send mail #playername "#subject" "#text"\r\n\r\nSendet eine E-Mail an einen Spieler. Betreff und Mailtext müssen in "" sein.' WHERE `id`=551;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .send mass items #racemask|$racename|alliance|horde|all "#subject" "#text" itemid1[:count1] itemid2[:count2] ... itemidN[:countN]\r\n\r\nSendet eine E-Mail an die Spieler. Betreff und Mailtext müssen in "" sein. Wenn für die Gegenstands-ID keine zugehörige Anzahl angegeben wird, wird 1 angenommen. Wenn die Anzahl > max. Gegenstände im Stapel ist, werden die Gegenstände in der erforderlichen Anzahl von Stapeln gesendet. Alle Stapel sind in der Post auf 12 begrenzt.' WHERE `id`=552;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .send mass mail #racemask|$racename|alliance|horde|all "#subject" "#text"\r\n\r\nSendet eine E-Mail an die Spieler. Betreff und Mailtext müssen in "" sein.' WHERE `id`=553;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .send mass money #racemask|$racename|alliance|horde|all "#subject" "#text" #money\r\n\r\nSendet Post mit Geld an Spieler. Betreff und Mailtext müssen in "" sein.' WHERE `id`=554;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .send message $playername $message\r\n\r\nBildschirmnachricht vom ADMINISTRATOR an den Spieler senden.' WHERE `id`=555;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .send money #playername "#subject" "#text" #money\r\n\r\nSchickt Post mit Geld an einen Spieler. Betreff und Mailtext müssen in "" sein.' WHERE `id`=556;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .server corpses\r\n\r\nEntfernt alle Leichen in der Welt.' WHERE `id`=560;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .server exit\r\n\r\nMangosd JETZT beenden. Exit-Code 0.' WHERE `id`=561;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .server idlerestart #delay\r\n\r\nStartet den Server nach #delay Sekunden neu, wenn keine aktiven Verbindungen vorhanden sind (keine Spieler). Verwendet #exist_code oder 2 als Exist-Code des Programms.' WHERE `id`=562;
UPDATE `locales_command` SET `help_text_loc3`='Syntax: .server idlerestart cancel\r\n\r\nBricht den Timer für Neustart/Herunterfahren ab, falls vorhanden.' WHERE `id`=563;
