UPDATE `locales_quest` SET `RequestItemsText_loc3`='Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, das wie eine Peitscherwurzelplanze aussieht. Die sichtbaren Wurzeln der Pflanze sehen zäh und ledrig aus und ein schleimiger Film tropft von der Pflanze.. Es muss sich dringend jemand um die Planzen kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll.' WHERE `entry`=4117;
UPDATE `locales_quest` SET `title_loc3`='Verderbte Liedblume' WHERE `entry`=4118;
