UPDATE `locales_quest` SET `title_loc3`='Der Weg des Jägers',`details_loc3`='Hallo, so jung und schon auf der Jagd. Ihr seht mir talentiert genug aus, um ein Tier zu führen und es zu Eurem Gefährten abzurichten.$B$BIhr müsst Dazalar in Dolanaar aufsuchen. Er kann Euch auf den Weg zu einem eigenen Tier führen.',`objectives_loc3`='Sprecht mit Dazalar in Dolanaar in Teldrassil.',`offerrewardtext_loc3`='Ja, es ist Zeit, $C.' WHERE `entry`=6071;
