UPDATE `locales_quest` SET `title_loc3`='Meister der Arena',`details_loc3`='Um der Meister der Arena zu werden, bedarf es bestimmt noch weiterer Taten! Vielleicht weiß der Pirat, der das Ereignis in Gurubashi veranstaltet, mehr.',`objectives_loc3`='Begebt Euch in die Arena des Schlingendorntals und sprecht mit Störtebrecher.',`offerrewardtext_loc3`='Aye, mein\' Kind. Ein son\' Schmuckstück is nich genuch, um Euch zum Meister der Arena zu machen! Jetz\' is es an der Zeit Euch richtig zu beweisen.' WHERE `entry`=7810;
