UPDATE `locales_quest` SET `title_loc3`='Ein aufrichtiges Angebot',`details_loc3`='Sammelt 15 Blutproben aus Winterquell und bringt sie zusammen mit 20 Gold und einem Paar Armschienen der Ehre zu Deliana in Eisenschmiede.',`objectives_loc3`='Sammelt 15 Blutproben aus Winterquell und bringt sie zusammen mit 20 Gold und einem Paar Armschienen der Schattenkunst zu Deliana in Eisenschmiede.' WHERE `entry`=8910;
