UPDATE `locales_quest` SET `title_loc3`='Gesprungener nekrotischer Kristall',`details_loc3`='Ihr entdeckt beim Körper der toten Monstrosität einen gesprungenen Kristall, der langsam mit nekrotischer Energie pulsiert.',`objectives_loc3`='Bringt den gesprungenen nekrotischen Kristall zu Leutnant Orrin außerhalb der Tore von Sturmwind.' WHERE `entry`=9292;
