UPDATE `locales_quest` SET `title_loc3`='Dokument von der Front',`details_loc3`='Ihr habt dieses Schreiben bei einem der zahlreichen Diener der Geißel gefunden, die Ihr getötet habt. Sein Äußeres und der Geruch lassen darauf schließen, dass es sich schon seit längerem im Besitz des Untoten befunden haben muss. Vielleicht wäre ja jemand bei der Kapelle des Hoffnungsvollen Lichts an einer weiteren Untersuchung interessiert...',`objectives_loc3`='Bringt das verschmierte Dokument zum Bewahrer der Schriften bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.' WHERE `entry`=9304;
UPDATE `locales_quest` SET `title_loc3`='Schwacher nekrotischer Kristall',`details_loc3`='Bringt den schwachen nekrotischen Kristall zu Leutnant Rukag im Hof von Unterstadt.',`objectives_loc3`='Bringt den schwachen nekrotischen Kristall zu Leutnant Rukag im Hof von Unterstadt.' WHERE `entry`=9310;
