UPDATE `locales_npc_text` SET `Text1_0_loc9`='Jocaste è stata la più grande cacciatrice del suo tempo. In questi giorni puoi trovarla nell \'Enclave Cenarion dove insegna a coloro che stanno appena iniziando il percorso del cacciatore.' WHERE `entry`=3023;
