UPDATE `locales_page_text` SET `text_loc9`='Maestro Barbanera,$B$BCome sapete, noi Stormpikes abbiamo un occhio per l\'eccellenza, e quindi la qualità dei vostri scudi non è un mistero per noi.$B$BI, quindi, sono ansioso di commissionarti un tale scudo. Ho incluso le specifiche nella pagina seguente:$B$' WHERE `entry`=607;
