UPDATE `locales_page_text` SET `text_loc9`='Simone la seduttrice:$B$BTroverai Simone travolgere il cratere di Un\'Goro.Non farti ingannare dal suo travestimento.Avvicinala con cautela e sfidala a combattere.' WHERE `entry`=2671;
UPDATE `locales_page_text` SET `text_loc9`='Klinfran the Crazed:$B$BKlinfran vaga per le Burning Steppes. Avvicinati a lui con cautela e costringilo a mostrare la sua vera forma.' WHERE `entry`=2672;
UPDATE `locales_page_text` SET `text_loc9`='Solenor the Slayer:$B$BNel paesaggio arido di Silithus si nasconde il diavolo, Solenor the Slayer. Indubbiamente mascherato, sicuramente un pericolo.Avvicinati a lui e costringilo a trasformare.' WHERE `entry`=2673;
UPDATE `locales_page_text` SET `text_loc9`='Artorius the Doombringer:$B$BArtorius porta corruzione e rovina a tutto ciò che tocca in Winterspring. Trovalo, uccidilo.' WHERE `entry`=2674;
UPDATE `locales_page_text` SET `text_loc9`='Stai attento, cacciatore, questi demoni devono essere combattuti alle loro condizioni. Se sentono che non riescono a sconfiggerti, fuggiranno.$B$BVAI SOLO.' WHERE `entry`=2675;
