UPDATE `locales_npc_text` SET `Text2_0_loc1`='플러거 그 친구가 맥주통 하나를 너무 채운 모양이야. 곧 터질 것 같아!',`Text2_1_loc1`='플러거 그 친구가 맥주통 하나를 너무 채운 모양이야. 곧 터질 것 같아!',`Text3_0_loc1`='한참 즐기는 중인데 귀찮게 하지 마시오...' WHERE `entry`=2758;
