UPDATE `locales_npc_text` SET `Text0_0_loc8`='Не обращай на меня внимания. Я всего лишь старик, ждущий своего старого… друга.' WHERE `entry`=13079;
UPDATE `locales_npc_text` SET `Text0_0_loc8`='Ооо, эта боль… непереносима…' WHERE `entry`=13080;
UPDATE `locales_npc_text` SET `Text0_0_loc8`='Быстрее, |3-6($R).$B$BМое время истекает.' WHERE `entry`=13081;
