UPDATE `locales_page_text` SET `text_loc8`='<HTML><BODY><H1 align="center">Глава 1: СЕВЕРНЫЙ ПИЛОН</H1><BR/><BR/><IMG src="Interface\Pictures\11482_crystals_mini_north" align="left"/><P align="right">Северный<BR/>пилон принимает<BR/>желтые, красные<BR/>и зеленые<BR/>кристаллы.</P><BR/><BR/><BR/><BR/><P align="left">Пилон фокусирует энергию, которая возникает при использовании желтых кристаллов в качестве основного источника энергии.</P></BODY></HTML>' WHERE `entry`=1511;
UPDATE `locales_page_text` SET `text_loc8`='<HTML><BODY><H1 align="center">Северная комбинация кристаллов</H1><BR/><P align="left">Есть два возможных эффекта: наносящий урон и нейтрализующий его. </P><BR/><P align="left">КРИСТАЛЬНЫЙ ЗАРЯД: требует желтых и красных кристаллов.<BR/>Провоцирует сильный взрыв.</P><BR/><P align="left"> КРИСТАЛЬНОЕ ВОССТАНОВЛЕНИЕ: требует желтых и зеленых кристаллов. <BR/>Наделяет вас целительной аурой.</P></BODY></HTML>' WHERE `entry`=1512;
