UPDATE `locales_page_text` SET `text_loc8`='<HTML><BODY><H1 align="center">Глава 2: ВОСТОЧНЫЙ ПИЛОН</H1><BR/><BR/><IMG src="Interface\Pictures\11482_crystals_mini_east" align="left"/><P align="right">Восточный<BR/>пилон принимает<BR/>синие, желтые<BR/>и зеленые<BR/>кристаллы.</P><BR/><BR/><BR/><BR/><P align="left">Пилон фокусирует энергию, которая может быть создана при использовании синих кристаллов в качестве основного источника энергии.</P></BODY></HTML>' WHERE `entry`=1514;
UPDATE `locales_page_text` SET `text_loc8`='<HTML><BODY><H1 align="center">Восточная комбинация кристаллов</H1><BR/><P align="left">Существует два возможных эффекта: один оказывает положительный эффект на дух, второй создает щит, наносящий урон.</P><BR/><P align="left">КРИСТАЛЬНАЯ СИЛА: требуются синие и зеленые кристаллы.<BR/>Повышает вашу характеристику "дух".</P><BR/><P align="left">КРИСТАЛЬНЫЙ ШИП: требует синих и желтых кристаллов.<BR/>Создает щит, который будет наносить урон атакующим.</P></BODY></HTML>' WHERE `entry`=1515;
