UPDATE `locales_page_text` SET `text_loc8`='<HTML><BODY><H1 align="center">Глава 3: ЗАПАДНЫЙ ПИЛОН</H1><BR/><BR/><IMG src="Interface\Pictures\11482_crystals_mini_east" align="left"/><P align="right">Западный<BR/>пилон принимает<BR/>красные, зеленые<BR/>и синие<BR/>кристаллы.</P><BR/><BR/><BR/><BR/><P align="left">Пилон фокусирует энергию, которая создается при использовании красных кристаллов в качестве основного источника энергии.</P></BODY></HTML>' WHERE `entry`=1531;
UPDATE `locales_page_text` SET `text_loc8`='<HTML><BODY><H1 align="center">Западные комбинации кристаллов</H1><BR/><P align="left">Существует два возможных эффекта: один повышает вашу защиту от урона, второй ослабляет защиту противника.</P><BR/><P align="left">КРИСТАЛЛ-ХРАНИТЕЛЬ: Требуются красные и зеленые кристаллы.<BR/>Повышает вашу характеристику "броня".</P><BR/><P align="left">КРИСТАЛЛ-ГУБИТЕЛЬ: Требуются синие и красные кристаллы.<BR/>Может снизить характеристику "броня" выбранной цели.</P></BODY></HTML>' WHERE `entry`=1532;
