UPDATE `locales_npc_text` SET `Text0_0_loc6`='Como mago, perteneces a un grupo selecto que puede invocar un portal para transportarte de nuevo a este lugar. Puedo enseñarte si eres capaz de aprender.' WHERE `entry`=5884;
