UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C. Soy una oficial encomiada en misión en nombre de Entrañas. Mi deber es ayudar a los aventureros que hayan recibido un sello de honor de la Horda.$B$BYo acepto sellos en diferentes cantidades, pero lo mejor para ti es que me entregues un lote de diez de una sola vez. Inscribiré tus hazañas en nuestros registros cuando entregues tus sellos. De esta forma, te ganarás el reconocimiento de Entrañas por tu cumplimiento del deber y buen servicio.' WHERE `entry`=8133;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡A las 18.00 echarán fuegos artificiales desde el risco más alto!',`Text1_0_loc6`='¡Los barriles están llenos de un brebaje muy fuerte! ¡Bebe, $Gamigo:amiga; y diviértete!' WHERE `entry`=8134;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='He oído que tras el espectáculo de fuegos artificiales, pondrán copas en las mesas. ¡Y podremos hacer unos buenos brindis!',`Text2_1_loc6`='He oído que tras el espectáculo de fuegos artificiales, pondrán copas en las mesas. ¡Y podremos hacer unos buenos brindis!',`Text3_0_loc6`='¡Ave, $C! ¡$GBienvenido:Bienvenida; a la fiesta!' WHERE `entry`=8134;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ja! ¡$GEste:Esta; tauren quiere un beso!' WHERE `entry`=8138;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Feliz Festival de Invierno, $Gamigo:amiga;!',`Text1_0_loc6`='¡Felices Fiestas y paz!' WHERE `entry`=8139;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¡Te deseo felices fiestas, $Gamigo:amiga;!',`Text2_1_loc6`='Este lugar está infestado... Bueno, tendremos que salir de aquí con las reliquias tan rápido como nos sea posible.',`Text3_0_loc6`='Ah, muérdago...' WHERE `entry`=8139;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Qué buen tiempo estamos teniendo aquí, ¿no crees?',`Text1_0_loc6`='¿Por qué nunca hay ningún girocronátomo cerca cuando lo necesitas?' WHERE `entry`=8143;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Yo me reiré la última... ¡ya verán! Ah, hola $C. No te había visto.',`Text2_1_loc6`='Este lugar está infestado... Bueno, tendremos que salir de aquí con las reliquias tan rápido como nos sea posible.',`Text3_0_loc6`='La inventiva, mi $Gquerido:querida; $C, se compone de un dos por ciento de inspiración embotellada, cuatro por ciento de exposición al sol, seis por ciento de probabilidad de que la tormenta pase antes del mediodía y noventa y tres por ciento de Risitas.$B$BEspera, ¿eso hace el cien por cien? Tengo mis notas por aquí, en alguna parte...' WHERE `entry`=8143;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Tenemos que averiguar qué les dan de comer los qiraji a esos monstruos! ¡Mira qué tamaño tienen!' WHERE `entry`=8145;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estudiar y entender a los Colosos nos dará una valiosa ventaja sobre los qiraji.' WHERE `entry`=8146;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡El secreto que se esconde tras el poder de los Colosos podría significar una revolución para la ciencia!' WHERE `entry`=8147;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Sepulnato se encuentra cerca de Rémol en los Claros de Tirisfal.' WHERE `entry`=8148;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Encontrarás al ancestro Lunaplena en Scholomance en las Tierras de la Peste del Oeste.' WHERE `entry`=8149;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Cuoreoscuro se encuentra en Entrañas.' WHERE `entry`=8150;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Obsidiano vaga cerca de El Sepulcro en el Bosque de Argénteos.',`Text0_1_loc6`='El ancestro Obsidiano vaga cerca de El Sepulcro en el Bosque de Argénteos.' WHERE `entry`=8152;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Encontrarás al ancestro Corona Nevada en la Capilla de la Esperanza de la Luz en las Tierras de la Peste del Este.' WHERE `entry`=8153;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Altacumbre vela por las Tierras del Interior desde una montaña cerca del centro.' WHERE `entry`=8154;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Fuerteviento reside en la Torre de la Corona en las Tierras de la Peste del Este.' WHERE `entry`=8155;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedes encontrar al ancestro Levesusurro vagando por las calles de Stratholme.' WHERE `entry`=8156;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Vetaferro vive en la Cueva Tizonegro en La Garganta de Fuego.' WHERE `entry`=8157;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Pozo de Oro se encuentra en la aldea de Kharanos en Dun Morogh.',`Text0_1_loc6`='El ancestro Pozo de Oro se encuentra en la aldea de Kharanos en Dun Morogh.' WHERE `entry`=8158;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedes encontrar al ancestro Barbabronce en Forjaz, hogar ancestral de los Barbabronce.' WHERE `entry`=8162;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Venaplata se encuentra cerca de Thelsamar en Loch Modan.',`Text0_1_loc6`='El ancestro Venaplata se encuentra cerca de Thelsamar en Loch Modan.' WHERE `entry`=8163;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Que Elune esté contigo, $C.',`Text1_0_loc6`='La suma sacerdotisa no podría abandonar Darnassus. Fandral está de muy mal humor.' WHERE `entry`=8164;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Tyrande envía sus plegarias. La Madre Luna nos bendice a todos.',`Text2_1_loc6`='Este lugar está infestado... Bueno, tendremos que salir de aquí con las reliquias tan rápido como nos sea posible.' WHERE `entry`=8164;
