UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Filoveloz vaga por los caminos de Darnassus en lo alto de Teldrassil.' WHERE `entry`=8176;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Relucielo se encuentra cerca del Monumento Cresta Cuervo en Azshara.',`Text0_1_loc6`='El ancestro Relucielo se encuentra cerca del Monumento Cresta Cuervo en Azshara.' WHERE `entry`=8177;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La aldea de Dolanaar en lo alto de Teldrassil es el hogar del ancestro Filohoja.',`Text0_1_loc6`='La aldea de Dolanaar en lo alto de Teldrassil es el hogar del ancestro Filohoja.' WHERE `entry`=8178;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedes encontrar al ancestro Tejestrellas en la villa costera de Auberdine en Costa Oscura.',`Text0_1_loc6`='Puedes encontrar al ancestro Tejestrellas en la villa costera de Auberdine en Costa Oscura.' WHERE `entry`=8179;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Nochebrisa pasa los días en el santuario de Jaedenar en Frondavil.' WHERE `entry`=8180;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La aldea de Astranaar en Vallefresno es el actual hogar del ancestro Riosona.' WHERE `entry`=8181;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo último que supe es que el ancestro Picopiedra estaba en Vista Eterna en Cuna del Invierno.',`Text0_1_loc6`='Lo último que supe es que el ancestro Picopiedra estaba en Vista Eterna en Cuna del Invierno.' WHERE `entry`=8182;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedes encontrar al ancestro Lanza Reluciente vagando entre las ruinas de Kel\'Theril en Cuna del Invierno.' WHERE `entry`=8183;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Pezuña de Sangre se encuentra en el Poblado Pezuña de Sangre en Mulgore.' WHERE `entry`=8184;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Tótem de Viento está en compañía de los goblins de Trinquete en la costa de Los Baldíos.' WHERE `entry`=8185;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Altomonte está en el Campamento Taurajo en Los Baldíos.' WHERE `entry`=8186;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Guardaluna está vigilando El Cruce en Los Baldíos.',`Text0_1_loc6`='El ancestro Guardaluna está vigilando El Cruce en Los Baldíos.' WHERE `entry`=8187;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Astaorgullo está disfrutando de las vistas de Mulgore desde Cima del Trueno.' WHERE `entry`=8188;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Astaoscura se encuentra entre las rocas rojas de Orgrimmar.' WHERE `entry`=8189;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Albarrocío le ha tomado gusto a observar las carreras en el Circuito del Espejismo en Las Mil Agujas.' WHERE `entry`=8190;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo último que he sabido es que el ancestro Tótem de Runa estaba de camino a Cerrotajo en Durotar.' WHERE `entry`=8191;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Encontrarás al ancestro Tótem Siniestro en el Pabellón Lariss en Feralas.',`Text0_1_loc6`='Encontrarás al ancestro Tótem Siniestro en el Pabellón Lariss en Feralas.' WHERE `entry`=8192;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Caminaniebla disfruta observando las peleas en La Marra en Feralas.',`Text0_1_loc6`='El ancestro Caminaniebla disfruta observando las peleas en La Marra en Feralas.' WHERE `entry`=8193;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ve al Poblado Viento Libre en Las Mil Agujas y allí encontrarás al ancestro Vidente del Cielo.',`Text0_1_loc6`='Ve al Poblado Viento Libre en Las Mil Agujas y allí encontrarás al ancestro Vidente del Cielo.' WHERE `entry`=8194;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Creo que el ancestro Parterroca se ha marchado a Maraudon en Desolace.',`Text0_1_loc6`='Creo que el ancestro Parterroca se ha marchado a Maraudon en Desolace.' WHERE `entry`=8195;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Visionírico reside en Gadgetzan en el Desierto de Tanaris.',`Text0_1_loc6`='El ancestro Visionírico reside en Gadgetzan en el Desierto de Tanaris.' WHERE `entry`=8196;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Tótem de Ira pasa sus días en el Valle de los Vigías en el Desierto de Tanaris.',`Text0_1_loc6`='El ancestro Tótem de Ira pasa sus días en el Valle de los Vigías en el Desierto de Tanaris.' WHERE `entry`=8197;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Zul\'Farrak en el Desierto de Tanaris es el hogar del ancestro Ferocrín.',`Text0_1_loc6`='Zul\'Farrak en el Desierto de Tanaris es el hogar del ancestro Ferocrín.' WHERE `entry`=8198;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Por motivos que no logro entender, el ancestro Tronacuerno se ha marchado a la Cicatriz Serpenteante en el Cráter de Un\'Goro.' WHERE `entry`=8199;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si viajas hasta el Fuerte Cenarion en Silithus, encontrarás al ancestro Cantofilo.',`Text0_1_loc6`='Si viajas hasta el Fuerte Cenarion en Silithus, encontrarás al ancestro Cantofilo.' WHERE `entry`=8200;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Primapiedra lleva muchos años en La Vega de Cristal en Silithus.' WHERE `entry`=8201;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Honrar a tus ancestros es honrar a tu cultura y a tu pueblo, $C. Por ello, las monedas de linaje son un medio tangible de reconocer a aquellos que eligen seguir el venerable camino del respeto.$B$BDurante la celebración del Festival Lunar, tendrás la oportunidad de honrar a muchos Ancestros. Cuando lo hagas, obtendrás las monedas de linaje. Tráeme las monedas y recompensaré tu diligencia con una selección de objetos únicos.' WHERE `entry`=8202;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $N. Venimos desde Claro de la Luna para propagar el espíritu del Festival Lunar por todas partes.$B$B¿No te vas a unir a nuestra celebración?' WHERE `entry`=8205;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Seguimos al lado de la Alianza, $R. Un mal, tan antiguo que el mundo ya no es capaz de recordar, intenta destruirnos a todos; primero a Kalimdor y después al resto de Azeroth. Se mueve por el odio por todas las cosas que emana de los grandes Creadores: los titanes eternos.$B$BNosotros somos los únicos que se interponen en el camino de los ejércitos de C\'Thun. Atacaremos en primer lugar y acabaremos con las fuerzas de Ahn\'Qiraj. Que todo Azeroth tiemble antes las fuerzas combinadas de la Horda y la Alianza.$B$B¡La campaña para la guerra te necesita!' WHERE `entry`=8209;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La última vez que vi al ancestro Correpradera fue en La Cueva del Llanto en las Tierras de la Peste del Oeste.' WHERE `entry`=8210;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Encontrarás al ancestro Claroestelar fuera de las puertas de Zul\'Gurub en la Vega de Tuercespina.' WHERE `entry`=8211;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='"Ha hecho un buen trabajo reuniendo material para la guerra", dijeron, "se merece un ascenso". Lo que realmente querían decir es "¡Está emplazado al lado de una colmena de bichos en Silithus con un montón de enanos sudorosos, Carnes!"$B$B¡Gracias por el ascenso, Capitán!' WHERE `entry`=8212;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo, al igual que mi hermano, honro a aquellos que honran a sus antepasados. ¡Qué gran dicha compartir los secretos de mi oficio con los discípulos del Festival Lunar!' WHERE `entry`=8213;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo, al igual que muchos otros, vine en ayuda de mis compañeros druidas al comenzar la guerra.$B$BAhora que las puertas de Ahn\'Qiraj se han abierto, me he dado cuenta de que aún queda mucho trabajo por hacer. He decidido quedarme en Silithus y hacer todo lo posible por contribuir a la causa, $N.' WHERE `entry`=8214;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aunque los silítidos semejen muy peligrosos, no son más que dóciles criaturas al lado de los señores supremos qiraji.' WHERE `entry`=8215;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Esta guerra no ha acabado sino que no ha hecho más que comenzar! Contamos con valientes hombres y mujeres que luchan día y noche contra el enemigo en el interior de Ahn\'Qiraj, $N. La ciudad qiraji no tardará mucho en caer.' WHERE `entry`=8216;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestros progresos en la lucha contra los qiraji se deben a la ayuda que estamos recibiendo de casi todos los rincones de Kalimdor.$B$BNecesitas algo más que Soldados para luchar en una guerra, $N. Los héroes olvidados de la guerra son los comerciantes y artesanos que suministran a nuestras tropas todo lo que necesitan.' WHERE `entry`=8217;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En Claro de la Luna sí que saben hacer fiestas. ¿Quién lo hubiera imaginado?',`Text1_0_loc6`='Prueba estos lanzacohetes. ¡Son geniales!' WHERE `entry`=8218;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Este destello de luna conduce hasta Ventormenta. Utiliza tu invitación cuando estés $Glisto:lista;.',`Text2_1_loc6`='Este destello de luna conduce hasta Ventormenta. Utiliza tu invitación cuando estés $Glisto:lista;.',`Text3_0_loc6`='No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero en casa las viejas rencillas no se olvidan...' WHERE `entry`=8218;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Los de Amparo de la Noche sí que saben lo que es una buena fiesta!',`Text1_0_loc6`='Estos fuegos artificiales seguro que provocan un buen estruendo, ¿no crees?' WHERE `entry`=8219;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Esta luz lunar te llevará hasta Forjaz. ¿Pero quién querría perderse esta fiesta?',`Text2_1_loc6`='Esta luz lunar te llevará hasta Forjaz. ¿Pero quién querría perderse esta fiesta?',`Text3_0_loc6`='No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero en casa las viejas rencillas no se olvidan...' WHERE `entry`=8219;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡La celebración lunar este año es magnífica!',`Text1_0_loc6`='¡Se dice que estos fuegos artificiales utilizan el poder de la luna!' WHERE `entry`=8220;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Este rayo de luna más intenso te llevará hasta Darnassus.',`Text2_1_loc6`='Este rayo de luna más intenso te llevará hasta Darnassus.',`Text3_0_loc6`='No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero en casa las viejas rencillas no se olvidan...' WHERE `entry`=8220;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si no lo supiera, ¡diría que esta fiesta la organizaron los orcos!',`Text1_0_loc6`='¿Has visto? ¡El último cohete casi le quema la camisa a ese enano! ¡Ja, ja, ja!' WHERE `entry`=8221;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Este rayo de luna te llevará hasta Orgrimmar. Utiliza tu invitación cuando desees ir.',`Text2_1_loc6`='Este rayo de luna te llevará hasta Orgrimmar. Utiliza tu invitación cuando desees ir.',`Text3_0_loc6`='No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero en casa las viejas rencillas no se olvidan...' WHERE `entry`=8221;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Me encanta el Festival Lunar! ¡Es la ocasión ideal para mover las patas!',`Text1_0_loc6`='No sé exactamente cómo funcionan estos fuegos artificiales, ¡pero seguro que son geniales!' WHERE `entry`=8222;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Esta luz lunar te llevará hasta Cima del Trueno. Utilízala cuando desees abandonar el festival.',`Text2_1_loc6`='Esta luz lunar te llevará hasta Cima del Trueno. Utilízala cuando desees abandonar el festival.',`Text3_0_loc6`='No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero en casa las viejas rencillas no se olvidan...' WHERE `entry`=8222;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Para animarse no hay nada como una pequeña celebración lunar!',`Text1_0_loc6`='Si no sientes tus propios dedos ten mucho cuidado al prender los fuegos artificiales...' WHERE `entry`=8223;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Este destello de luna conduce hasta Entrañas. Ve cuando estés $Glisto:lista;.',`Text2_1_loc6`='Este destello de luna conduce hasta Entrañas. Ve cuando estés $Glisto:lista;.',`Text3_0_loc6`='No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero en casa las viejas rencillas no se olvidan...' WHERE `entry`=8223;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Augurio descansa en las profundidades del Lago Elune\'ara pero puede ser invocado desde la orilla sudeste, cerca del Túmulo de Tempestira. Allí encontrarás unos lanzacohetes muy especiales...$B$BLanza varios de tus cohetes y aparecerán los esbirros de Augurio. Si continúas lanzando cohetes, unas tres docenas aproximadamente, o si enciendes velas de Elune ante sus esbirros, pronto se despertará.' WHERE `entry`=8225;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos trabajado muy duro para ver cómo nuestras tierras volvían a su antigua gloria, $C. Debemos estar siempre alerta contra aquellos que han invadido y profanado Quel\'Thalas. No descansaremos hasta que estén fuera de nuestra vista.$B$B¡Que nadie ose interponerse en el camino de los sin\'dorei!' WHERE `entry`=8226;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es un caos, $C, y es todo por mi culpa. Me dijeron que saliera del muelle mientras tenía tiempo, pero no hice caso. No.$B$B¿Qué voy a hacer ahora? Esos malditos elfos de sangre, los desdichados, se han largado con todo mi material mágico y se lo han comido, creo. Estoy segura de que no lo recuperaré nunca. ¡Y los múrlocs Malaescama me robaron lo poco que quedaba en medio de la noche! Para rematar la faena, mi tripulación ha abandonado el barco.$B$BCreo que voy a llorar.' WHERE `entry`=8227;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, $C. Estamos en tiempos verdaderamente preocupantes si esto es en lo que se va a convertir nuestra amada Quel\'Thalas.' WHERE `entry`=8228;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los desdichados son una espina que tengo clavada, $N. Cuando no son un peligro para ellos mismos porque explotan fuentes Arcanas inestables para satisfacer sus antojos, traman ardides alocados contra nuestra respetable sociedad.' WHERE `entry`=8229;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Espero que Loralthalis esté bien, $C. Quizás debería subir a Aguja Ocaso Marchito yo mismo. ¿Quizás es lo que debería haber hecho en primer lugar?' WHERE `entry`=8232;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un hombre solo puede vivir consigo y su vergüenza durante tanto tiempo, $C, antes de que tenga que actuar para deshacer los errores que ha cometido.' WHERE `entry`=8233;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Un desastre, eso es lo que es! ¡Todas esas criaturas han aparecido de la nada y corretean por todas partes, fuera de control! Y, ¿dónde se han metido el magister y sus aprendices? Me dejan aquí solo sin siquiera decir "Hola, Wyllithen, ¿te quieres venir?"$B$B¡Menuda cara!' WHERE `entry`=8234;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos estado tan concentrados en la guerra que muchos de nosotros olvidamos dedicarle tiempo al amor.' WHERE `entry`=8235;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Menuda pelea... Necesitamos un momento para recuperar el aliento.' WHERE `entry`=8237;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Eh! Tienes pinta de ser $Gun:una:c; $C con un paladar muy exigente para los vinos. Tengo que pedirte un favor y creo que no podrás rechazar la oferta que te voy a hacer... ¡Olvídalo!' WHERE `entry`=8238;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué estás mirando, $Gmuchacho:muchacha;? ¿Es que nunca has visto un enano?$B$BSerá mejor que te pongas en camino si no quieres tener problemas.' WHERE `entry`=8239;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ah, debes de ser $Gel muchacho:la muchacha; $Gdesignado:designada; para mostrarme los alrededores del bosque Canción Eterna. Te llevó tu tiempo llegar hasta aquí, ¿no es así?$B$BMuy bien, vámonos. No perdamos ni un minuto más.' WHERE `entry`=8240;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Parece que ya has encontrado el amor. Puedes entregarme una prueba de amor pero yo no te daré nada a cambio. Estoy esperando a alguien especial.' WHERE `entry`=8241;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Parece que ya has encontrado el amor. Puedes entregarme una prueba de amor pero yo no te daré nada a cambio. Estoy esperando a alguien especial.' WHERE `entry`=8242;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Parece que ya has encontrado el amor. Puedes entregarme una prueba de amor pero yo no te daré nada a cambio. Estoy esperando a alguien especial.' WHERE `entry`=8243;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos estado tan concentrados en la guerra que muchos de nosotros olvidamos dedicarle tiempo al amor.' WHERE `entry`=8244;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos estado tan concentrados en la guerra que muchos de nosotros olvidamos dedicarle tiempo al amor.' WHERE `entry`=8245;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No sé lo que me ha pasado pero mi corazón está ardiendo como una pieza de acero recién forjada.' WHERE `entry`=8251;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Llevas tanto tiempo trabajando entre máquinas que en ocasiones te olvidas de la gente de verdad.' WHERE `entry`=8254;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cuando has vivido tanto como yo, no es extraño olvidarse del amor durante años. Pero siempre es bueno recordarlo.' WHERE `entry`=8255;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Por lo general, los orcos somos muy reservados pero últimamente nadie oculta ya sus sentimientos.' WHERE `entry`=8263;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Por lo general, los orcos somos muy reservados pero últimamente nadie oculta ya sus sentimientos.' WHERE `entry`=8265;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Por lo general, los orcos somos muy reservados pero últimamente nadie oculta ya sus sentimientos.' WHERE `entry`=8266;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ave, $C. ¿No te he visto hace poco en una reunión privada? Me suena tu cara pero esta ciudad está tan concurrida...$B$BSi has estado en esa reunión, te habrán dado un código secreto. Dime tu código y recibirás como recompensa un premio especial.' WHERE `entry`=8269;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Que no sea mi corazón no significa que no pueda estar roto.' WHERE `entry`=8270;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La mansión Ravenholdt te da la bienvenida, $Gaventurero:aventurera;.' WHERE `entry`=8280;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Parece que te hayan roto el corazón. Vuelve cuando ya no estés tan triste.' WHERE `entry`=8282;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Parece que te hayan roto el corazón. Vuelve cuando ya no estés tan triste.' WHERE `entry`=8283;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Parece que te hayan roto el corazón. Vuelve cuando ya no estés tan triste.' WHERE `entry`=8284;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hablaría contigo si olieras mejor. Un poco de colonia no estaría de más.' WHERE `entry`=8285;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Cómo? ¿No tienes ninguna prueba de amor que darme? ¿Es que no me quieres?' WHERE `entry`=8286;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hablaría contigo si olieras mejor. Un poco de colonia no estaría de más.' WHERE `entry`=8287;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Cómo? ¿No tienes ninguna prueba de amor que darme? ¿Es que no me quieres?' WHERE `entry`=8288;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me gustarías más si te echaras un poco de perfume.' WHERE `entry`=8289;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me gustarías más si te echaras un poco de perfume.' WHERE `entry`=8290;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Cómo? ¿No tienes ninguna prueba de amor que darme? ¿Es que no me quieres?' WHERE `entry`=8291;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡¿Qué?! ¿Cómo has logrado entrar sin que se diera cuenta mi... eh... mi esbirro que está ahí fuera? Bueno, supongo que es lo mejor. Últimamente, las ventas no van nada bien.$B$B¿Cómo puedo complacer tus infernales necesidades, $C?',`Text1_0_loc6`='Has demostrado gran habilidad en tu modo de entrar aquí, $N.$B$B¿Qué? ¿Te sorprende que sepa tu nombre? Soy un demonio, es nuestro trabajo. También conozco tu verdadero nombre así que es mejor que me compres algo caro.' WHERE `entry`=8292;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¿Vienes a por la vara? ¿O simplemente quieres comprar runas?$B$B¡Espera un momento! ¿Intentas cargarme con un montón de porquerías? ¡Maldita sea mi eterna obsesión por comprar, comprar y comprar!',`Text3_0_loc6`='¿Qué? No, no reparo armaduras. Vista Eterna está a tan solo una milla en dirección norte, $Ghermano: hermana;.$B$B¡Y ahora compra algo o desaparece antes de que llame a mi brujo para que se ocupe de ti!' WHERE `entry`=8292;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Parece que ya has encontrado el amor. Puedes entregarme una prueba de amor pero yo no te daré nada a cambio. Estoy esperando a alguien especial.' WHERE `entry`=8296;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me gustarías más si te echaras un poco de perfume.' WHERE `entry`=8298;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me gustarías más si te echaras un poco de perfume.' WHERE `entry`=8301;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tenemos órdenes de arriba de acabar con Rajaxx. Causad daño extremo si queréis mejorar vuestra técnica.' WHERE `entry`=8304;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='He cumplido mi misión. Creo que tengo suministros de sobra por si necesitas algo.' WHERE `entry`=8305;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo no, pero muchos por ahí sí buscan amor. ¡Deberías comprarme pruebas de amor, echarte un poco de colonia o perfume y pasar a su lado!' WHERE `entry`=8306;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las pruebas de amor son mensajes de cariño que puedes entregar a la gente de la ciudad que esté buscando amor. Esas personas te recompensarán con un regalo de adoración.$B$BSi ya tienes a alguien que te adore, entonces te entregarán un regalo de amistad.' WHERE `entry`=8307;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si te echas colonia o perfume, podrás ver quién está buscando amor. Esos aceptarán pruebas de amor... y te entregarán un regalo a cambio.$B$BPero recuerda: para darle a alguien una prueba de amor, ¡deberás oler bien! A los hombres les gusta el perfume mientras que las mujeres prefieren el olor de la colonia.' WHERE `entry`=8308;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola! ¡Soy Kwee Q. Pies Rápidos! ¡He venido para averiguar quién es el héroe o la heroína más popular de Azeroth!$B$BMe gusta ver que los líderes tienen tantos seguidores adorándoles, pero... ¿sabes lo que me gustaría todavía más?$B$B¡Que me dieras un beso!' WHERE `entry`=8313;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Esta es la clasificación final!$B$BThrall: $2200w$BCairne: $2201w$BSylvanas: $2202w$BTotal de la Horda: $2207w$B$BBolvar: $2203w$BMagni: $2204w$BTyrande: $2205w$BTotal de la Alianza: $2206w$B$B¡La Alianza cuenta con $Gel héroe:la heroína; más popular!' WHERE `entry`=8315;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cuando entregues pruebas de amor a los habitantes de la ciudad, recibirás diferentes regalos a cambio. Algunos podrás guardarlos y utilizarlos, y otros ¡podrás juntarlos y dárselos a tu $Ghéroe favorito:heroína favorita;! Visita a Kwee Q. Pies Rápidos y entrégaselos.$B$BEncontrarás a Kwee cerca de Bolvar, Magni o Tyrande.' WHERE `entry`=8317;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cuando entregues pruebas de amor a los habitantes de la ciudad, recibirás diferentes regalos a cambio. Algunos podrás guardarlos y utilizarlos, ¡mientras que otros podrás juntarlos y dárselos a tu $Ghéroe favorito:heroína favorita;! Visita a Kwee Q. Pies Rápidos y entrégaselos.$B$BEncontrarás a Kwee cerca de Cairne, Sylvanas o Thrall.' WHERE `entry`=8319;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El contenido de las páginas de este tomo parece sufrir continuos cambios. Sientes como si el libro absorbiera los pensamientos de tu mente.' WHERE `entry`=8321;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La gama de imágenes y formas aparecen ante tus ojos. El libro parece conocer tu habilidad para la peletería y te deja elegir una. Pero ten cuidado pues una vez que hagas tu elección ¡no podrás cambiar de parecer!:' WHERE `entry`=8326;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No puedo enseñarte peletería dragontina, $N. Has de ir a Tanaris y encontrar la casa de Narain Sabelotodo. Una vez allí, consulta el libro "Adivinar el futuro para torpes".' WHERE `entry`=8327;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Perder la vida era un riesgo que había aceptado. Sin embargo, perder a Ysida es algo que no puedo soportar, incluso tras mi propia muerte.' WHERE `entry`=8334;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Saludos, $Gamigo:amiga;! ¿Puedo ofrecerte un condensador ectoplásmico? ¿O quizás un fabuloso asimilador de espectros omnidireccional?' WHERE `entry`=8335;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Y ahora qué? ¿No ves que estoy ocupado?' WHERE `entry`=8336;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estaré bien, no te preocupes por mí. Saldré de aquí cuando haya realizado algunos rituales de purificación en esta habitación. Este lugar está contaminado con magia negativa, $N.' WHERE `entry`=8344;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estos tanques de miniatura o tonques, que es el ingenioso nombre que les hemos puesto, son nuestra nueva atracción, totalmente gratuita, por cierto, y se controlan mediante cualquiera de las consolas de control que se encuentran por toda la feria.$B$BSolo tienes que activar cualquier consola de control que esté disponible. Cada tonque estará equipado con una serie de artefactos y un poder especial aleatorio que podrás desatar contra tus enemigos.$B$BVive batallas épicas con tus amigos, pasea con tu tonque o provoca el caos. ¡Tú eliges!' WHERE `entry`=8347;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Ultracañón Pimpampum 5000 es un artefacto único para el lanzamiento de personas que proyecta su carga a una velocidad cercana a la del vuelo de un dragón. Este cañón es el orgullo de la familia Pimpampum, conocida en todo el mundo por sus derroches en el campo de la aeronáutica.$B$BSi quieres probar el cañón y volar como un pájaro, busca a Maxima Pimpampum en la parte este del Alto de los Cazadores en Cima del Trueno. ¡Se reunirá contigo en tu punto de destino al sur del Lago Toro de Piedra!' WHERE `entry`=8349;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Se comenta que Kel\'Thuzad ansía el legendario Atiesh, el gran báculo del guardián.$B$BSí, el mismísimo báculo que Medivh portó en su día.' WHERE `entry`=8350;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los de El Alba Argenta serán unos idealistas impotentes pero su ilusorio líder sabe que no son capaces de aguantar. Estoy aquí en calidad de emisaria de la Cruzada Escarlata. Una embajadora, si preferís... No hay duda de que no podríamos infligir ni el menor de los daños a los ejércitos de Kel\'Thuzad, unidos o no, pero debemos intentar contenerlos el mayor tiempo posible a la espera de que se produzca un milagro.$B$B¿En cuanto a mí? Moriré luchando...$B$B<Marjhan se encoge de hombros.>' WHERE `entry`=8354;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mi título es engañoso. Cuando me conozcas bien te darás cuenta de que soy muy simpático...' WHERE `entry`=8355;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Casi envían a mi hermano, Loksey, a esta reunión. No es capaz ni de cuidar a sus canes y mucho menos de representar de manera competente a la Cruzada Escarlata.' WHERE `entry`=8356;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estaba espe...$B$B<Rimblat tose.>$B$B¡Oh, perdona! Soy Rimblat del Anillo de la Tierra.' WHERE `entry`=8357;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, $C. Por favor, discúlpame. Estoy manteniendo una conversación con Rimblat. Él cree que la tierra todavía tiene salvación.' WHERE `entry`=8358;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué es lo que quieres, roedor? Desaparece inmediatamente de mi vista si no quieres descubrir por qué me llaman el Colérico.' WHERE `entry`=8359;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La primera armadura acorator fue creada exclusivamente para el Alto Señor Escarlata Mograine. Cuando se hubo puesto la armadura al completo, el Alto Señor semejaba un demonio proveniente del mismísimo infierno.$B$BSí, su aspecto debía ser impactante pues Mograine se sumergió en un mar de guerreros de la Plaga, con la espada Crematoria erguida, y salió indemne envuelto en una nube de polvo y llamas dejando a sus enemigos hechos cenizas.' WHERE `entry`=8363;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tras su polémica muerte, los caballeros y guerreros de Lordaeron empezaron a portar armaduras similares a la del héroe caído. Mientras que la muerte de Mograine significó una gran victoria para Kel\'Thuzad, el sufrimiento derivado de ella perduraría hasta el fin de la guerra.$B$BAunque uno cayó, muchos otros se erigieron...' WHERE `entry`=8364;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Soy lo que vosotros denomináis un capitalista, $R.' WHERE `entry`=8366;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Oh, buenas... mmmm ¿cómo te llamas?$B$BNo importa, ¿qué te trae a mi hogar alejado del hogar, aquí en la bella campiña?' WHERE `entry`=8374;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, $C. Confío en que te lo estés pasando bien. ¿Qué opinas de la fiesta? Realmente espero que no se nos esté acabando nada. Nada mata una fiesta más rápido que quedarse sin bebidas y entremeses, ¿no crees?$B$BSí, bueno, tengo que ir a atender a mis otros invitados. Disfruta de tu estancia.' WHERE `entry`=8375;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, mi $Gseñor:señora;. Confío en que se lo esté pasando bien.' WHERE `entry`=8378;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿No es divertidísimo? Es magnífico que Lord Saltheril organice estas fiestas todas las noches, especialmente con todo lo que está ocurriendo en el sur. La verdad es que una fiesta te permite olvidarlo todo, ¿verdad?' WHERE `entry`=8379;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Al igual que Kel\'Thuzad, yo fui una maga del Kirin Tor. Y al igual que Kel\'Thuzad, busco el legendario Atiesh, el gran báculo del guardián. Por este motivo, nuestros destinos están unidos, $C.' WHERE `entry`=8388;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La progenie de Aegwynn, el que sería el último guardián de Tirisfal... Medivh.' WHERE `entry`=8389;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es una historia de leyenda que no te voy a contar por falta de tiempo y apetencia. Tan solo has de saber que Medivh fue el último de los guardianes. El gran báculo de Atiesh fue la piedra angular tanto de su poder como de su corrupción.' WHERE `entry`=8390;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La corrupción de un titán caído. La corrupción del Destructor. Después de todo, el báculo fue forjado a partir de la semilla del odio. Eso es, por supuesto, lo que me ha traído hasta aquí...' WHERE `entry`=8391;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tras la caída de Karazhan, el Kirin Tor envió a un grupo de magos a buscar la torre y a hacerse con todos los objetos que tuvieran relevancia con la excusa de "investigar." Fue entonces cuando se encontró el legendario báculo y se trajo de nuevo a Dalaran.' WHERE `entry`=8392;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo que vino después fue una serie de trágicos acontecimientos que acabaron con la muerte de todos los magos del Kirin Tor que habían estado en contacto con Atiesh.$B$BSi te das cuenta, se trataba de un objeto que tenía sentimientos, cualidad que Sargeras le otorgó...' WHERE `entry`=8393;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Angela asiente con la cabeza.>$B$BTras esta sucesión de muertes, los Seis decidieron encerrar el báculo tras unas barreras de magia impenetrables que alguien debía custodiar: el vigía del báculo. Yo ocuparía ese puesto.' WHERE `entry`=8394;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No queda mucho que contar. Archimonde destruyó Dalaran casi por completo y el báculo se perdió. Se cuenta que podría haber sido hecho añicos por los propios mecanismos de seguridad de la barrera.$B$BLos hay que creen que el báculo fue la causa de la represalia de Archimonde.' WHERE `entry`=8395;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tras la caída de Dalaran, abandoné el Kirin Tor. Durante años busqué el báculo para poder liberarme de la labor que el Kirin Tor me había encomendado. Encontraría el báculo aunque me llevara toda una vida. Así es como entré a formar parte de El Alba Argenta y el motivo por el que estoy aquí.' WHERE `entry`=8396;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Le he seguido la pista a los diversos trozos del báculo por todo este mundo y así descubrí que Kel\'Thuzad ansía reconstruirlo. Varios fragmentos obran ya en su poder, custodiados por sus tenientes más fieles.$B$BNadie puede imaginar lo importante que es que el gran báculo permanezca fuera del alcance de Kel\'Thuzad. Francamente, desconozco cuáles serían las consecuencias aparte, claro está, de una catástrofe de enormes proporciones.' WHERE `entry`=8397;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es poder, imbécil.$B$B<Los ojos de Mataus se ensombrecen.>$B$BEstos insensatos envían a Naxxramas a idiotas como tú para recuperar la armadura profanada y así purificarla y devolverle su antigua gloria.$B$B<Mataus escupe.>$B$BTan solo pensar en tal estupidez me hace subir la bilis a la boca.$B$BYo también busco esa armadura pero en su estado actual.' WHERE `entry`=8398;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No engaño, gusano. La oscuridad que tienen tales objetos podría destruirnos a los dos. Por eso el precio es tan elevado. Si voy a morir, que sea como un hombre rico.$B$BNo me importa ni una pizca de saliva de gnoll cómo mueras, solo que hagas lo que te pido. Tráeme lo que necesito y los dos nos beneficiaremos – o moriremos, pero eso ya te lo he explicado...' WHERE `entry`=8399;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Somos demasiado compasivos con los desdichados. Deberíamos clavarle una espada a cada uno de ellos.$B$BSu osadía en el fondeadero Vela del Sol es imperdonable.' WHERE `entry`=8402;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Llevo todo el día matando descerebrados seres de la Plaga en La Cicatriz Muerta. Espero que vengas a echar una mano y no solo a entretenerme con tu charla.' WHERE `entry`=8403;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No puedo enseñarle a $Gun:una; tauren a montar un raptor.' WHERE `entry`=8407;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Esas horribles bestias están por todas partes.$B$BSi prefieres cazarlos al aire libre, los encontrarás en gran número en el Bosque de la Peste y el Valle del Terror, sobre todo en el Túnel Terroarácnido. También se pueden encontrar casi en cualquier campamento de la Plaga de las Tierras de la Peste del Este.$B$BSi te gustan las mazmorras, encontrarás muchos mefistos en Stratholme y algunos necromalignos en Scholomance.' WHERE `entry`=8408;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Eres $Gtonto:tonta;? ¿Te has olvidado de las CRIATURAS ESQUELÉTICAS que ensombrecían el paisaje en tu camino hasta aquí?$B$B<Rohan suspira.>$B$BSi prefieres el aire libre, encontrarás criaturas esqueléticas por todas las Tierras de la Peste tanto del Este como del Oeste. Créeme, no puedes dejarlos escapar. Ellos no lo harán contigo si tienen la oportunidad.$B$BSi por el contrario prefieres las mazmorras, has de ir sin duda a Stratholme y Scholomance en busca de trozos de hueso.' WHERE `entry`=8409;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Otro día maravilloso y lóbrego aquí en Tranquillien, ¿no te parece, $C?$B$B¿Qué puedo hacer por ti?' WHERE `entry`=8410;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La mayor libertad es volar por los cielos, $C. ¿Qué puedo hacer hoy por ti?' WHERE `entry`=8411;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, amigo. ¿En qué puedo ayudarte hoy?' WHERE `entry`=8412;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ah! Buena pregunta.$B$BSi prefieres ir de caza al aire libre, encontrarás elementales en las Tierras de la Peste, Azshara, Las Estepas Ardientes, Frondavil, Silithus, Cuna del Invierno e incluso en el Cráter de Un\'Goro.$B$BSi te gusta correr riesgos quizás prefieras ir a La Masacre. He oído que sus muros encierran muchos elementos. También puedes ir en busca de los elementos que trabajan para los enanos Hierro Negro de las Profundidades de Roca Negra.' WHERE `entry`=8414;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Estás de broma? ¿De verdad no lo sabes?$B$BSi eres más bien cobarde y no te gusta mancharte mucho las manos, encontrarás fragmentos de hierro negro en las criaturas que habitan Las Estepas Ardientes y La Garganta de Fuego.$B$BSi tienes espíritu aventurero, en las mazmorras de Cumbre de Roca Negra y las Profundidades de Roca Negra hay gran número de fragmentos.' WHERE `entry`=8415;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si eres de los que prefieren estar al aire libre, los antárboles de Frondavil y los azotadores de Un\'Goro son una excelente fuente de hojas.$B$BSi te gusta correr riesgos, La Masacre Este y Oeste albergan una cantidad casi ilimitada de hojas salvajes.' WHERE `entry`=8416;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$C, mira lo que han hecho con nuestras queridas tierras. ¡La Plaga debe ser destruida!' WHERE `entry`=8417;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Va a costar un poco, pero creo que podemos salvar la vida de este elfo de sangre.' WHERE `entry`=8418;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Quién eres? Siento... tanto... frío.' WHERE `entry`=8419;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es vergonzoso que nosotros, los sin\'dorei, hayamos perdido tanto de lo que solíamos llamar hogar. No pasará mucho tiempo antes de que recobremos nuestra fuerza y recuperemos nuestras tierras del sur del control de la Plaga.' WHERE `entry`=8421;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estoy demasiado ocupado como para andarme con juegos, $R. ¡Dime qué quieres o desaparece de mi vista!' WHERE `entry`=8422;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡El Sumo Ejecutor y la Dama Aurífera me están haciendo esperar, pero no tengo tiempo para esto! ¡Mis amigos están ahí fuera muriendo, $C!' WHERE `entry`=8423;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Envié a la forestal Salissa al sur al enclave y al forestal Lethvalin al oeste, a Tranquillien, hace más de un día! ¿Has visto a alguno de los dos ¿Eres la ayuda que debían enviar?' WHERE `entry`=8424;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='A veces siento que nunca volveré a ser un simple funcionario al servicio de la Dama Aurífera. No estoy hecho para la guerra.$B$BAfortunadamente están aquellos, como tú, que arden en deseos de ver la antigua belleza de Quel\'Thalas devuelta a ella. ¡Aplasta a la Plaga, $C! ¡Aplástalos dondequiera que puedas!' WHERE `entry`=8425;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ah, aquí está $Gel héroe:la heroína; del momento! ¡Me alegro de volver a verte, $N! Confío en que sigues destruyendo a los miembros de la Plaga cuando los encuentras, ¿no es cierto?' WHERE `entry`=8426;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me han informado de que pudiste ayudar a la forestal Valanna y al teniente en Zeb\'Sora.$B$BGracias, $C. Supongo que ahora debería encaminarme de vuelta al Enclave del Errante.' WHERE `entry`=8428;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué te trae por aquí, $C?' WHERE `entry`=8429;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ah, hola, $R. Como puedes ver, todavía no tengo abierto para los clientes. Si me disculpas, tengo mucho que hacer.' WHERE `entry`=8431;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es un gran placer volver a verte, $C. No, en serio. Eres la razón por la que estoy haciendo dinero aquí rápidamente.$B$BBueno, ¿qué te puedo vender hoy?' WHERE `entry`=8432;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida a Tranquillien, $C. Debo disculparme por el estado en que la encuentras. Como ves, hemos tenido algunos problemas, pero los Renegados y su Lady Sylvanas Brisaveloz han sido lo suficientemente corteses como para prestarnos mucha ayuda en nuestro tiempo de necesidad.$B$BSi puedes echar una mano, no dudes en hacerlo. Todos podemos hacer uso de la ayuda que podamos en nuestra lucha contra la miríada de fuerzas formadas contra nosotros.' WHERE `entry`=8433;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No tengas miedo de la espada. ¡Es el cordero!' WHERE `entry`=8435;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es terrible... lo que le ocurrió a las mulas... Metz pedirá mi pellejo por este fracaso.' WHERE `entry`=8438;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Es muy importante que averigüemos qué traman los elfos de la noche, $C! Centra todos tus esfuerzos en ese objetivo.' WHERE `entry`=8439;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nos complacen mucho tus actividades contra los elfos de la noche. Cuando descubriste su plan de emplear los nexos de líneas Ley y sus cristales lunares para espiarnos, reforzaste en gran medida nuestra posición actual.$B$BTe lo agradecemos, $N.' WHERE `entry`=8440;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Sois vosotros los refuerzos? ¡Los elfos de la noche han sido vencidos!' WHERE `entry`=8441;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Menos mal que desactivaste el cristal lunar en An\'owyn cuando lo hiciste, $C! Ven cuando te apetezca ayudarme con mi investigación.' WHERE `entry`=8442;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Cómo he de trabajar en estas condiciones? ¡Y con todos estos Renegados por aquí! ¡Cuánto me aflige ver a Tranquillien y Quel\'Thalas así!' WHERE `entry`=8445;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Informa, $R! ¿Qué noticias importantes tienes? No te entretengas... La Dama Aurífera y yo estamos en plena discusión estratégica.' WHERE `entry`=8447;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; a la Capilla de la Esperanza de la Luz, $Gforastero:forastera;.' WHERE `entry`=8448;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestras caravanas parten dos veces al día pero también es cierto que la Plaga les ha salido al encuentro para detener a Didier y sus mulas y así evitar que el envío llegue a las primeras líneas. Es lo más arriesgado que he visto en mi vida. Si contáramos con valientes que custodiaran los envíos...' WHERE `entry`=8452;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Tengo mucho que hacer y poco tiempo para hacerlo, $R! Por favor, apártate de mi camino.' WHERE `entry`=8454;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Tengo mucho que hacer y poco tiempo para hacerlo, $R! Por favor, apártate de mi camino.$B$BSi te has contagiado con baba de gusano o la putrefacción de los necrófagos, quizás pueda ayudarte.' WHERE `entry`=8455;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El elfo de sangre recupera rápidamente la conciencia cuando le viertes la pócima en la boca.>$B$BTe debo la vida, $C. Sin tu ayuda habría sucumbido a la dolorosa tortura de la Plaga.' WHERE `entry`=8456;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El elfo de sangre yace en el suelo, sumido en un letargo artificial.>' WHERE `entry`=8457;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<La Renegada está retorcida en el suelo haciendo una mueca. No parece que pueda ponerse de pie por sí sola en este momento.>' WHERE `entry`=8459;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ahhhhggg... este dolor de cabeza me está matando.$B$BTe deben de haber enviado a rescatarme. ¡Ya podrían haberse dado más prisa! ¿Tienes idea de lo que les hacen a los prisioneros aquí?' WHERE `entry`=8460;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El elfo de sangre yace desfallecido en la mesa. Aunque aún respira, parece haber sido víctima de potentes toxinas sedativas.>' WHERE `entry`=8461;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me alegro de conocerte, $R. Estoy aquí para actuar como consejero a las órdenes del Sumo Ejecutor. ¡Sin duda juntos podremos convertir la Horda en algo aún más fuerte que antes y obligar a la amenaza de la Plaga a retroceder de una vez por todas!' WHERE `entry`=8462;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Varnis tose con violencia mientras el jarabe vigorizante hace efecto.>$B$BGracias por rescatarme. ¡Debo volver a Tranquillien enseguida!' WHERE `entry`=8463;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las corrientes de aire fresco que soplan desde Canción Eterna de vez en cuando realmente me afectan al estómago, ¿sabes lo que quiero decir?' WHERE `entry`=8464;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si lo que quieres explicar es un asunto de importancia, $C, tal vez sería más apropiado que pidieras una audiencia con la Dama Aurífera. Sin embargo, debo advertirte que está muy ocupada y tal vez tengas que esperar mucho.' WHERE `entry`=8466;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Normalmente yo no debería hablar contigo directamente; lo normal sería que pidieras una audiencia a la señora. Sin embargo, te encargué personalmente una tarea, así que ¿qué querías comentar?' WHERE `entry`=8467;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$C, la Dama Aurífera me ha encargado que te exprese su gran satisfacción con el servicio que nos has prestado. Desea que te comunique que espera que sigas siendo un defensor de los intereses de los sin\'dorei aquí en las Tierras Fantasma.' WHERE `entry`=8468;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este panel contiene varios anuncios y noticias sobre recompensas.' WHERE `entry`=8469;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tenemos trabajo importante que hacer, $N. Ataquemos a nuestros enemigos mientras tengan la guardia baja. Enseñémosles que la venganza de los sin\'dorei no olvida ni perdona.' WHERE `entry`=8470;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Vaya, eso es un problema! ¿Cómo se supone que debo obtener las muestras de metal para los magistri si las minas están inundadas con esa chusma gnoll?' WHERE `entry`=8472;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Dónde podría estar? Envié a mi aprendiz, Shatharia, a las Minas Sondaluz, al oeste, hace algún tiempo. ¡Necesitamos recuperar esas muestras de mena, y rápido, o nuestro experimento fracasará!' WHERE `entry`=8474;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sin la entrega de la mena de Sondaluz de una forma tan oportuna, nuestra investigación habría fracasado.$B$BEspera a que Shatharia vuelva a asomar la cabeza por aquí. ¡Tengo unas cuantas tareas interesantes para ella, puedes estar $Gseguro:segura;!' WHERE `entry`=8475;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los sin\'dorei hemos perdido demasiado a lo largo de los últimos años. Nuestras tierras, nuestras casas, hasta nuestras identidades... $B$BYa va siendo hora de que empecemos a recuperarlo.' WHERE `entry`=8476;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Antes de que podamos superar obstáculos externos, como la Plaga y los trols Amani, debemos superar obstáculos internos. Los desdichados han sido una molestia durante el tiempo suficiente; es hora de derrotarlos.' WHERE `entry`=8477;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Crees que podrías ayudarme a salir de aquí?' WHERE `entry`=8478;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Entre los diversos fragmentos de pergamino que contienen listas de verificación y otra información de los Errantes, destaca uno. ¡Es el dibujo de un trol horripilante, quizás la criatura más espantosa que hayas visto!' WHERE `entry`=8479;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En efecto, las colinas de Cuna del Invierno están intentando resistir un nuevo ataque de la Plaga. Tu ayuda resultará crucial.$B$BNúmero de necrópolis restantes: $2284W' WHERE `entry`=8480;
UPDATE `locales_npc_text` SET `Text7_0_loc6`='La hoguera' WHERE `entry`=8480;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hasta el momento, esta área está libre de la influencia de la Plaga. Me temo que será cuestión de tiempo que regresen.' WHERE `entry`=8481;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si no se envía pronto más apoyo a las Estepas Ardientes, me temo que la Plaga establecerá una base de operaciones aquí. Si puedes, deberías ir allí para ayudar a los defensores.$B$BNúmero de necrópolis restantes: $2281W' WHERE `entry`=8484;
UPDATE `locales_npc_text` SET `Text7_0_loc6`='La hoguera' WHERE `entry`=8484;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Se pueden obtener muchas ventajas conociendo al enemigo. Apréndete esa lección, $N.$B$BCuanto más sepamos sobre la Plaga, más bien preparados estaremos para derrotarlos.' WHERE `entry`=8485;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Plaga está estableciendo pequeños puntos de escala en los lugares que ataca, en los que reciben informaciones y otro tipo de ayudas por parte de las necrópolis que los sobrevuelan. Por lo que sabemos, el único modo de erradicarlos es acabando con los ejércitos de tierra que rodean esos puntos.' WHERE `entry`=8486;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; al Enclave del Errante, $C. Quédate todo el tiempo que quieras. Son bienvenidos todos los que nos apoyan en las Tierras Fantasma.' WHERE `entry`=8487;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las fuerzas Arcanas perdidas pueden hacer cosas raras a la tierra, a los elementos e incluso a los espíritus de los muertos.' WHERE `entry`=8488;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Sumo Ejecutor es un hombre muy ocupado, $C. Tal vez yo pueda ayudarte con lo que necesites.' WHERE `entry`=8489;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nos has prestado un magnífico servicio, $N. Entre ocuparte de los gnolls de las Minas Sondaluz y los trols momificados de las Catacumbas Amani, ¡has asestado un gran golpe a nuestros enemigos!$B$B$G ¡Buen trabajo! : Deberíamos quedar para tomar algo algún día, ¿qué me dices?;' WHERE `entry`=8490;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Me alegro de volver a verte, $Gquerido:querida;! Formamos un buen equipo. Ya nos veremos.' WHERE `entry`=8491;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='He sabido de la reacción de Lady Sylvanas acerca de que recuperaras su collar. Sigo pensando que hiciste lo correcto; no te preocupes demasiado sobre su reacción.$B$BY ahora dime a qué has venido.' WHERE `entry`=8492;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué puedo decir, $C? ¡Eres $Gun auténtico héroe:una auténtica heroína:c;! No solo has salvado personalmente a muchos de mis forestales, sino que has asestado un golpe decisivo contra los trols Sombrapino. ¿Seguro que no quieres quedarte y ser uno de mis Errantes?$B$BAdiós, $N.' WHERE `entry`=8493;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tu ayuda con los trols Sombrapino está siendo indispensable, $C. Sigue así y tal vez podamos volver a ocuparnos de la Plaga.' WHERE `entry`=8495;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Has asestado el golpe decisivo contra los trols Sombrapino, $N. Solo espero que podamos reunir nuestras fuerzas lo suficientemente rápido como para rematarlos antes de que reciban refuerzos de Zul\'Aman.$B$BEstaría bien que Tranquillien nos proporcionara refuerzos.' WHERE `entry`=8496;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La situación es más desalentadora de lo que se ha hecho creer a muchos. Los Renegados están aquí como aliados en la lucha contra nuestro enemigo común, la Plaga.$B$B¡Confía en mí cuando digo que sin ellos, las Tierras Fantasma estarían perdidas y la Plaga ya habría invadido todo el Bosque Canción Eterna!$B$B¡Pero, más que nada en el mundo, necesitamos tu ayuda para recobrar nuestras antaño gloriosas tierras!' WHERE `entry`=8501;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estas piedras rúnicas protegen las tierras de Canción Eterna de ser contaminadas por la Plaga. La piedra rúnica del oeste fracasó hace mucho, forzándonos a quemar la tierra que había a su alrededor.$B$BMientras viva, no permitiré que las piedras rúnicas restantes nos fallen. Tal es mi juramento.' WHERE `entry`=8504;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Bueno, no has venido hasta aquí para parlotear con una taza de té en la mano. Veamos cómo podrías ayudarnos a salir de esta situación.' WHERE `entry`=8505;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay quien dice que la Cruzada Argenta es una reliquia del pasado. Yo soy uno de ellos, pero no porque la Plaga haya caído.$B$BNo... Es por su tozudez. Se han convertido en algo demasiado parecido a la iglesia de la que se escindieron: demasiado rígidos, demasiado sometidos al honor y al orden. Es lo que intento decirle a Barthalomew, en cualquier caso.$B$BSé que suena a demagogia, pero queda bien en los debates.' WHERE `entry`=8506;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Eso es todo lo que saben hacer?$B$B<Omarion reúne fuerzas para reírse.>$B$BNo tenéis lo que hay que tener para enfrentaros a un ser tan monstruoso como Kel\'Thuzad. Caeréis antes sus esbirros, al igual que los que vinieron antes que vosotros...$B$BY ahora déjame morir. Me niego a regresar a un mundo que defiende a los de tu calaña.' WHERE `entry`=8507;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El maestro artesano Omarion simplemente desapareció una noche, como un susurro al viento... ¡Paf! ¡Y desapareció! Ahora soy el único que puede fabricar los objetos para los chicos que están en primera línea de batalla.' WHERE `entry`=8513;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Omarion dice algo por lo bajo.>$B$BPor supuesto que puedo ayudarte, $Gchico:chica;. Es lo que quiero... mi último deseo antes de morir. Por favor, coge este manual. Sus páginas contienen todo mi saber y todo lo que he aprendido en los últimos 30 años de mi miserable vida. Entrégale el manual a mi inepto aprendiz que sin duda estará repanchingado en la Capilla de la Esperanza de la Luz.' WHERE `entry`=8516;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Tarsis refunfuña.>$B$BPuede que todavía quede tiempo... Escucha con atención, mortal.$B$BA través del Vacío y del tiempo he buscado. Con grandes males e infamias me he encontrado...$B$B<Tarsis tose sangre sobre sus manos.>$B$B¿Puedes... puedes creer que incluso Elune me ha dado la espalda, $R? Morir aquí, de este modo...' WHERE `entry`=8517;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las cosas que he hecho... cosas horribles...$B$B<Tarsis comienza a sollozar.>' WHERE `entry`=8518;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Soy un Altonato, $R. Eso ya no importa. Sin embargo, hubo un tiempo en el que este mundo estaba gobernado por Ilustres. De eso hace miles de años...' WHERE `entry`=8519;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo recuperaré, mortal. Recuperaré a Atiesh... y con él traeré de nuevo a los Altonato al mundo. Haré que todo sea como antes. Traeré de vuelta incluso a la Reina...' WHERE `entry`=8520;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Tarsis te agarra débilmente por el cuello. Hasta en la muerte ves un poder inimaginable en sus ojos.>$B$B¿Lo soy? Entonces escucha atentamente, pues hay más tonterías a punto de escapar de mis labios. En esta ciudadela encontrarás el trabajo de mi vida: la ubicación exacta de cada fragmento del bastón astillado.' WHERE `entry`=8521;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Atiesh, el gran báculo del guardián. El báculo heredado a través de los siglos por cada uno de los guardianes de Tirisfal. El báculo corrompido por Sargeras. El báculo del último guardián. El báculo de Medivh.' WHERE `entry`=8522;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Los ojos de Tarsis se iluminan.>$B$BLo tuve una vez en mis manos... a pesar de las advertencias del Kirin Tor. Lo tuve, mortal... Ese infinito poder. El poder para hacer todo lo que anhelaba, a mi antojo. Y tan pronto llegó a mis manos, me fue arrebatado. Pero ya lo había saboreado y nunca más podré olvidar esa sensación.' WHERE `entry`=8523;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Tarsis está gravemente herido.>' WHERE `entry`=8524;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La batalla marcha bien. Las tropas de la Plaga parecen debilitadas. Creo que es cuestión de tiempo que consigamos expulsarlas de nuestras costas. Con mi magia os ayudaré en vuestra lucha.' WHERE `entry`=8526;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los primeros pasos de nuestra lucha contra la Plaga marchan bien. Estamos haciendo bien las cosas y espero que mi ayuda pueda servir para continuar con éxito la batalla.' WHERE `entry`=8527;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Se perdió con la caída de la Ciudadela Violeta... dividido en cuarenta y dos fragmentos.' WHERE `entry`=8529;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Kel\'Thuzad ha intentado por todos los medios reconstruir el báculo. Los distintos fragmentos fueron esparcidos por todo este mundo, desde Kalimdor hasta los Mares del Sur. Yo conseguí reunir veinte fragmentos del báculo hasta que los esbirros de Kel\'Thuzad me los arrebataron. Por eso estoy aquí.' WHERE `entry`=8530;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Acaso no has estado escuchando? Claro que no. Dudo mucho que estuviéramos teniendo esta conversación si Kel\'Thuzad hubiera reconstruido el báculo. Kel\'Thuzad posee la parte superior y sus subordinados guardan cuarenta fragmentos de Atiesh. La última pieza, la base, fue descubierta por Brann Barbabronce. Fue visto por última vez en el desierto de Silithus, sin duda intentando permanecer lo más lejos posible de los implacables subordinados de Kel\'Thuzad desperdigados por todo el mundo.' WHERE `entry`=8531;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestros sagrarios obtienen el poder de las corrientes de líneas-Ley que cruzan nuestras tierras, invisibles a simple vista.$B$BAhora que ha desaparecido La Fuente del Sol, nos proporcionan una fuente de energía continua y fiable.' WHERE `entry`=8534;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El hecho de que la Aldea Brisa Pura siga en pie es una prueba irrefutable de la tenacidad de nuestra raza. Nunca más concederemos la derrota a la Plaga.' WHERE `entry`=8535;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Abrid paso, escoria! Revilgaz me ha librado de la horca pero el castigo no será mucho mejor.' WHERE `entry`=8536;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Será mejor que no causes problemas, $N!' WHERE `entry`=8537;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿En qué puedo ayudarte, $N? ¡Rápido, no tengo todo el día!' WHERE `entry`=8538;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿En qué puedo ayudarte? ¡No, espera! ¿En qué puedes ayudarme tú a mí?' WHERE `entry`=8539;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Has servido a nuestra causa con gran lealtad, $N. Permíteme que te haga entrega de una fuente inagotable de nuestra quintaesencia elemental.' WHERE `entry`=8541;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$GBienvenido:Bienvenida; de nuevo, $N! Has prestado tu valiosa ayuda en la lucha contra Ragnaros y siempre serás bien $Grecibido:recibida; entre nosotros.' WHERE `entry`=8542;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Dar\'Khan cree que nos rendiremos ante la Plaga sin oponer resistencia. Le demostraremos que se equivoca, $N.' WHERE `entry`=8548;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿No ves que estoy intentando mantener una conversación con la dama? Oh, muy bien, de todos modos parece preocupada.$B$BSolo ofrezco las mejores bebidas, tanto de cosecha local como de procedencia exótica. ¿Quieres echar un vistazo?' WHERE `entry`=8549;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Es eso una lista del Refugio de Saltheril? Sin duda, su señoría necesitará una botella de mi mejor calidad. Y, por supuesto, cualquier amigo de Lord Saltheril merece un precio especial por un añejo tan excelente.' WHERE `entry`=8550;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos ganado $2219W batallas contra la Plaga. Prepárate, $N, pues esta guerra no ha hecho más que comenzar.' WHERE `entry`=8551;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si tienes dinero, tengo comida y bebida para satisfacer tu apetito. Todo el dinero se destina a mantener el Retiro en plena forma.' WHERE `entry`=8552;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Eso que llevas ahí es una lista de Lord Saltheril? Sí, te puedo vender un lote de aperitivos, recién sacados del fuego.' WHERE `entry`=8553;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tengo todo tipo de artículos a la venta, $C. Echa un vistazo.' WHERE `entry`=8556;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Una lista... qué curioso. Ah, sí, el envío de fuegos artificiales para Lord Saltheril. No los ha pagado; ¿has venido a comprarlos en su nombre, $C?' WHERE `entry`=8557;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Es que esos no-muertos no tienen fin? Parece como si por cada necrófago o esqueleto que matáramos, dos ocuparan su lugar.' WHERE `entry`=8558;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$C, ¿no habrás visto un mensajero cuando venías hacia aquí? ¡Estamos esperando información de Ciudad de Lunargenta y su mensajero llega tarde!' WHERE `entry`=8561;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='A veces me quedo aquí, observando el bosque más allá del lago, y veo la tierra como una vez fuera... verde y llena de vida.' WHERE `entry`=8564;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Tenemos que encontrar la manera perfecta de ocuparnos de esos apestosos trols Sombrapino!' WHERE `entry`=8565;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Debo decir que administraste mi veneno y te ocupaste de esos trols muy despiadadamente, $C. ¡Tienes mucho talento!' WHERE `entry`=8566;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Debemos mantener un estado de vigilia, $C! Entre Dar\'Khan el Traidor y su Plaga, y los trols Sombrapino respaldados por la fuerza de Zul\'Aman, apenas mantenemos la cabeza sobre el agua. ¡No deberíamos estar dividiendo nuestra atención!$B$BEspero que estés aquí para ayudar.' WHERE `entry`=8569;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me alegro de que hayas venido a ayudarnos, $C. Cuanto antes nos ocupemos de estos trols, antes podremos dedicarnos a devolver a la Plaga al lugar de donde vino.' WHERE `entry`=8570;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ya nos hemos ocupado de un grupo de trols; falta otro. ¡No tienes idea de cuánto aprecio lo que estás haciendo, $N! Antes de que aparecieras no pensaba que fuéramos a tener nunca bajo control el problema de los Sombrapino.' WHERE `entry`=8571;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nunca te imaginarías la multitud que ha pasado por aquí últimamente. La mayoría causan más problemas de los que solucionan. Lo contrario que tú.$B$B¿Seguro que no quieres unirte a los Errantes?' WHERE `entry`=8572;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Plaga ha enviado pocas tropas a las capitales de Azeroth pues el grueso de sus filas ataca periódicamente las siguientes zonas:$B$BAzshara, Las Tierras Devastadas, Las Estepas Ardientes, el Desierto de Tanaris, las Tierras de la Peste del Este y Cuna del Invierno.' WHERE `entry`=8573;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué te trae por aquí, $N? No vemos mucha gente por este lugar. Con razón, supongo.' WHERE `entry`=8575;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Deseas aprender la profesión de herrero o hay algo más que quieras decirme?' WHERE `entry`=8576;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Tenemos mucha suerte de estar vivos!' WHERE `entry`=8579;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No se ven muchos aprendices por aquí, ¿verdad? Estoy empezando a pensar que es porque los magistri nos dan todo el trabajo peligroso que prefieren no hacer ellos mismos.' WHERE `entry`=8580;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La nuestra es la senda de la pureza y la rectitud en la Luz.' WHERE `entry`=8582;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Somos uno en la Luz, $Ghermano:hermana;, pero no puedo instruirte.' WHERE `entry`=8584;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Vienes en busca de instrucción, $Gcazador:cazadora;?' WHERE `entry`=8585;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo soy instructora de cazadores. A ti te interesa el instructor de $C.' WHERE `entry`=8586;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Buscas más adiestramiento en las artes del guerrero?' WHERE `entry`=8587;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te entrenaría, pero no eres un guerrero.' WHERE `entry`=8588;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nosotros, los profesionales de las artes Arcanas y Elementales seguimos un camino interminable de descubrimiento. ¿Por eso buscas mi consejo? ¿Deseas más instrucción?' WHERE `entry`=8589;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En la Luz todos proyectamos sombras, $Ghermano:hermana;. ¿Buscas más orientación hoy?' WHERE `entry`=8591;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aunque no puedo entrenarte, joven $C, puedo ofrecerte mi bendición. ¡Camina con valentía bajo la Luz!' WHERE `entry`=8592;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Muchos son los caminos de la Luz, $C. Debes decidir cuál seguir en este mundo o en cualquier otro.' WHERE `entry`=8616;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='A medida que te vayas ganando un nombre en nuestra ciudad podré ofrecerte más artículos, $N. Disponemos de pocas reservas de equipo y solo podemos permitirnos ofrecérselas a gente en quien confiemos.' WHERE `entry`=8617;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ofrezco el refugio que puedo en nuestras tierras destruidas.' WHERE `entry`=8618;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Me alegro de verte, $G hermano : hermana;! Este lugar me aterroriza. Mientras estás aquí, asegúrate de aprovechar las oportunidades que estos Renegados ofrecen.' WHERE `entry`=8619;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No es culpa mía... ¡¡¡NO ES CULPA MÍA!!!$B$BSi hubieran hecho lo que les dije, todo habría ido bien. ¡Pero no! No me escucharon.$B$BAhora están todos muertos.$B$BSolo quiero irme a casa. A casa, a Lunargenta. Estoy tan cansado...' WHERE `entry`=8620;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Comercio con la comodidad... Calma tu salvaje impulso, $R. Mi posada es tu hogar...' WHERE `entry`=8634;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, $C. Si aún no lo has hecho, deberías hablar con el Errante Solanna.' WHERE `entry`=8638;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si has perdido una pieza de tu armadura Zandalar, quizás pueda ayudarte.' WHERE `entry`=8645;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Te agradezco mucho tu ayuda, $C!' WHERE `entry`=8650;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ah! Tú eres quien ha encontrado la carta. Gracias por... las noticias, supongo, de mi familia. Puedes comer y beber de todo lo que hay.' WHERE `entry`=8653;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me llamo Sid. Me alegro de ver alguna cara nueva por aquí. ¿Qué puedo hacer por ti?' WHERE `entry`=8658;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aunque hemos llegado a la tierra prometida, $N, aún queda mucho por hacer. Nuestra gente no llegará a Kael\'thas si nos lanzamos a la desesperada a través de Terrallende.$B$BDebemos encontrar rutas seguras, asegurarnos los recursos y esquivar o destruir a nuestros enemigos.' WHERE `entry`=8659;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Lo que daría por un cristal de sanación justo ahora!' WHERE `entry`=8662;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Gracias por entregar esa lista! Tengo la pierna casi recuperada y pronto podré volver al lugar del accidente.' WHERE `entry`=8663;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Cómo vamos a lograr salir de aquí?' WHERE `entry`=8664;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gracias por cuidar del lago, los azotadores y el buhíco, $C.' WHERE `entry`=8665;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nadie cuestionará tus acciones, $C. Hiciste lo que había que hacer.' WHERE `entry`=8666;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Me alegro de verte con vida, $N!' WHERE `entry`=8667;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gracias de nuevo por reponer nuestros cristales de sanación. He oído que estás ayudando por todo el lugar. ¡Sigue con el buen trabajo!' WHERE `entry`=8669;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Espero que no busques cerveza. ¡No vamos a compartirla y basta!$B$BNo es culpa nuestra que a nadie se le ocurriera traer cerveza a Terrallende excepto a nosotros.' WHERE `entry`=8673;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Por la esperanza de que no toda Terrallende sea tan inhóspita como esta región. Este es un cambio bastante grande de las verdes praderas de Canción Eterna.' WHERE `entry`=8674;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay en Terrallende presencias demoníacas mucho más poderosas que nada que hayamos visto en el viejo mundo.$B$BHabrá un gran poder para aquellos que no las rehúyan, sino que aprendan a aprovechar y llegar a dominar su fuerza.' WHERE `entry`=8675;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué puedo hacer por ti?' WHERE `entry`=8678;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Terrallende rezuma energía mágica, está en el mismo aire. ¿La sientes, $N? No se me ocurre un lugar más apropiado para nuestra raza. ¡Pronto los sin\'dorei tendrán una nueva tierra de la que dirán que es su hogar!' WHERE `entry`=8680;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mi marido lleva días desaparecido. Nos fuimos juntos de peregrinación y se suponía que deberíamos estar de vuelta juntos también.$B$BEsta angustia me ha hecho abusar un poco de los cristales de maná.' WHERE `entry`=8681;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En realidad, los dracohalcones, si se crían adecuadamente, son bastante mansos. ¿No te parece fascinante? Ser tan agradables y sociables si se crían bien y tan fieros y letales en si hábitat natural... Los adoro con locura.' WHERE `entry`=8682;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No... te preocupes... por... mí' WHERE `entry`=8683;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La verdad es que no creo que nuestra situación sea tan mala. Estoy segura de que todo se rectificará pronto y el poder de La Fuente del Sol se restablecerá. No es posible que sea tan malo como todo el mundo pretende que es.' WHERE `entry`=8684;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ha valido la pena hacerse amigo de los Errantes; yo puedo llevar a cabo investigaciones con sujetos que solo puedo encontrar en Terrallende y ellos... disfrutan de mi encantadora personalidad y de mi atractiva y dura apariencia.' WHERE `entry`=8685;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Era solo un bebé cuando mi padre fue destinado a este páramo. Todo lo que siempre he sabido de él es lo que otros me han contado.$B$B¿Cambian tus sueños, $N? Los míos, no. Tengo un sueño: un horizonte carmesí me envuelve mientras la Legión, en cantidad innumerable, batalla en la distancia. Me arrodillo ante el cadáver de un hombre, es de suponer que mi padre, y lloro. Mientras lucha por conseguir aire, con todo el cuerpo aplastado, susurra algo. A pesar de todos mis esfuerzos, soy incapaz de oír lo que intenta decirme.' WHERE `entry`=8688;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Crees que estoy siendo cruel con este gusano?$B$B¡Han matado a dos de mis hombres este mes! Somos demasiado amables con esta escoria de los desdichados.' WHERE `entry`=8698;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; a la aldea Brisa Pura. Lunargenta prácticamente nos ha abandonado, pero nos negamos a marcharnos. Este es nuestro hogar.' WHERE `entry`=8699;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mi trabajo consiste en una única tarea: asegurar que el Sagrario del Oeste no tenga problemas.$B$BTendrás que disculpar mi temperamento, pero, como puedes ver, la situación se nos ha ido un poco de las manos.',`Text1_0_loc6`='¿Podría haber elfos de la noche saboteadores tras esto? Lo dudo. Con la carga que hemos estado poniendo en el Sagrario del Oeste solo era cuestión de tiempo que algo fuera mal.' WHERE `entry`=8700;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Con la caída de los Emperadores, la protección mágica que impedía teletransportarse en el interior del templo se ha visto gravemente debilitada. Si lo deseas, puedo teletransportarte a la guarida de los Emperadores.' WHERE `entry`=8701;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Con la caída de Ouro, el camino hacia la última cámara está despejado. Puedo enviarte allí, si lo deseas.' WHERE `entry`=8702;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Estás disfrutando del festival, $C?$B$BMientras los celadores de las llamas se ocupan de las hogueras del presente, yo soy más bien un historiador y me interesan mucho los festivales anteriores. Por supuesto, estoy documentando las celebraciones de este año. De momento, todo está saliendo bien, ¿no crees?$B$B¿Sabes, $N? Hay un poder inherente a todas las hogueras del festival que se prenderán durante estas fiestas. Nosotros cuidamos las nuestras pero estoy seguro de que en las ciudades enemigas arden hogueras sagradas...' WHERE `entry`=8703;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si buscas trabajo, $C, agarra un pico y ponte a cavar. ¡Hay ruinas hasta donde alcanza la vista!' WHERE `entry`=8712;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La corrupción de Terrallende llega a extremos increíbles, $C. El Círculo Cenarion intenta comprender cómo han podido sobrevivir esas criaturas. El estudio de esta tierra desolada y maldita puede ayudarnos a encontrar la forma de curar algunas partes de nuestro propio mundo.' WHERE `entry`=8715;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es triste ver lo que le ha pasado a esta tierra. La expedición de Cenarion hará lo que pueda para comprender el daño que se ha causado y repararlo.' WHERE `entry`=8716;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Parece que no tienes muy claro dónde estás, $C. No me hagas ponerte a trabajar en los muros con los otros peones.' WHERE `entry`=8717;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, $C. Tu llegada a Thrallmar no podría haber ocurrido en mejor momento. El Jefe de Guerra cuenta con nosotros, ya que no puede estar aquí él mismo.' WHERE `entry`=8718;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Thrallmar te necesita, $C. El Jefe de Guerra te necesita. Debes estar a punto para cumplir con tu deber.' WHERE `entry`=8719;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Pocos ataques se ganan por pura fuerza. Al final, lo que lleva a la victoria es la astucia, los reflejos y el conocimiento de tu arma.' WHERE `entry`=8724;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Otro héroe? Las parcas nos sonríen en este día, $N.' WHERE `entry`=8733;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='He oído hablar de tus hazañas, $N. Sé que, mientras estés en tierras de los Renegados te comportarás con dignidad y como un auténtico héroe de los sin\'dorei.' WHERE `entry`=8740;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida, $C.' WHERE `entry`=8750;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No tengo tiempo para cháchara, $C. Debo ocuparme de los peregrinos y asegurarme de que están preparados para el largo viaje que hay por delante.' WHERE `entry`=8751;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; a la Avanzada del Halcón, $C. Yo me hago cargo de las necesidades de los peregrinos y los ayudo a continuar su largo viaje. Si todavía no has emprendido la peregrinación, puedo proporcionarte la información que necesites.' WHERE `entry`=8752;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Camina siempre en la Luz, $N.' WHERE `entry`=8754;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; al Retiro del Errante. Somos forestales que hemos jurado defender a nuestra gente de los enemigos cercanos y lejanos.' WHERE `entry`=8755;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Creo que empiezo a arrepentirme de emprender esta peregrinación. Esperaba que el camino sería duro y estoy acostumbrada a viajar, pero no estaba preparada para los rigores de Terrallende.$B$BAdemás de todo lo demás, ese Taleris parece querer alejarme de aquí antes de que haya recobrado mis fuerzas por completo.' WHERE `entry`=8756;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Oh... ¿nos conocemos?',`Text1_0_loc6`='Lo siento, no hablo con el servicio.' WHERE `entry`=8759;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¿Y tú eres...?',`Text2_1_loc6`='¿Y tú eres...?',`Text3_0_loc6`='Creo que me has confundido con alguien que quiere hablar contigo.' WHERE `entry`=8759;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola, $N! ¿Disfrutas de la fiesta?',`Text1_0_loc6`='¡Eh, mirad todos, es $N!' WHERE `entry`=8760;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Las palabras viajan rápido en este círculo. ¡Gracias por reponer los suministros!',`Text2_1_loc6`='Las palabras viajan rápido en este círculo. ¡Gracias por reponer los suministros!',`Text3_0_loc6`='¡Esta fiesta es genial! ¿No te parece?' WHERE `entry`=8760;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='Deberías probar los aperitivos de garragil, ¡están para morirse!',`Text5_0_loc6`='Mmm... Creo que el Toquesol especial de este año no es tan especial, después de todo. ¿No estás de acuerdo, $N?' WHERE `entry`=8760;
UPDATE `locales_npc_text` SET `Text6_0_loc6`='He oído el rumor de que, esta noche, todos vamos a observar la batalla con los miembros de la Plaga en la frontera. ¡Qué deliciosamente malvado!',`Text7_0_loc6`='¿Te puedes creer el descaro de esa Elisara Golpesol? ¡Se cree que puede atraer la atención de Lord Saltheril! Y, ¿has visto lo que lleva puesto? Es de la temporada pasada. Es tan triste...' WHERE `entry`=8760;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me he metido en una buena. La clarividente Regulkult intentó avisarme, pero mi descuido ha terminado por pasarme factura.$B$BBajé aquí para analizar las ruinas de Zeth\'Gor, pero me impliqué con la Legión en mi camino. Ahora todo lo que he logrado es una herida profunda en la pierna.' WHERE `entry`=8767;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aunque no puedo ofrecerte instrucción, joven $C, puedo darte mi bendición. ¡Camina con valentía hacia la Luz!$B$B¿Hay algo más?' WHERE `entry`=8768;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Es muy importante que averigüemos qué traman los elfos de la noche, $C! Centra todos tus esfuerzos en ese objetivo.' WHERE `entry`=8769;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No hace mucho, en este sitio tuvo lugar una terrible batalla. Nosotros, Nethergarde, y El Alba Argenta mantuvimos a raya una oleada de demonios que surgieron del Portal Oscuro.$B$BAquellos eran días oscuros llenos de sangre y desesperación...$B$BPero los reinos de Azeroth se unieron a la batalla y conseguimos expulsar a los demonios. La lucha continúa del otro lado del Portal...$B$BEste campamento ahora es un lugar tranquilo; por desgracia, esa tranquilidad contrasta con los horrores que hay del otro lado.' WHERE `entry`=8770;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las heroicidades de la estirpe de los Aterratrols son legendarias. Idolatro a ese hombre.$B$BY, encontrarlo vivo, en Terrallende...$B$B<Romus mira fijamente más allá de ti.>$B$BCuando lo vi por primera vez, casi me desmayé. La conmoción fue insoportable. Mientras me arrodillaba ante él, tuve que reprimir las lágrimas.$B$BPuedes apostar tu dulce trasero a que voy a hacer todo lo que esté en mi poder para asegurarme de que los Hijos de Lothar se mantienen a salvo.' WHERE `entry`=8771;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Romus me ha informado de la tragedia que sufrió mi hermosa ciudad. No hay palabras que describan la pena que sentí cuando supe lo que le había ocurrido a Lordaeron y a mi amada Stromgarde.$B$BHace veinte años, nadie había oído hablar de la Legión Ardiente... ¡Malditos demonios!$BTe lo aseguro, $N; si algún día vuelvo a Azeroth, haré que Stromgarde recupere toda su antigua gloria. Lo juro...' WHERE `entry`=8772;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Este emisor me está volviendo loca! Me falta todo tipo de piezas que necesito para repararlo.' WHERE `entry`=8775;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$N! ¡Gracias de nuevo por toda tu ayuda.' WHERE `entry`=8776;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No, todavía no he terminado con esas reparaciones. Pero no debería faltar mucho.' WHERE `entry`=8777;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Sollozando> Odio los cementerios. Hay muchos muertos. Y mi dulce Luhanaa...' WHERE `entry`=8780;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Recuerda que siempre debes mostrar respeto por los elementos del mundo.' WHERE `entry`=8785;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Debemos esforzarnos por comprender el equilibrio que mantienen los espíritus elementales de este mundo.' WHERE `entry`=8786;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola! ¡Me alegro de conocerte!' WHERE `entry`=8787;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos. ¿Has venido a ayudar?' WHERE `entry`=8788;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Recuerda, $N, que lo que más nos beneficia es que hagas todo lo posible por esta... esta gente.' WHERE `entry`=8789;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $R. ¿Qué tal te va?' WHERE `entry`=8790;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Tienes noticias de tu casa, $C?' WHERE `entry`=8791;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Acércate, $Gamigo:amiga;. No dejes que mis ojos te asusten.$B$BPuede que perdiera la vista hace muchos años, pero nunca la necesité para ver las cosas que importan de verdad.' WHERE `entry`=8792;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Coge una red, joven $C. ¡Nuestro pueblo necesita sustento!' WHERE `entry`=8793;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola. Confío en que el día te encuentre bien, ¿es así?' WHERE `entry`=8794;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Qué alegría ver a otro sin\'dorei $Ghermano:hermana;! Como puedes imaginar, no hay mucha conversación por aquí.' WHERE `entry`=8795;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me honra conocerte.' WHERE `entry`=8796;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me alegro de que hables conmigo. Por favor, ayuda a la Alianza en todo lo que puedas.' WHERE `entry`=8797;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aunque tenemos mucho que enseñar a las otras razas de la Alianza, también tenemos mucho que aprender de ellas. Mantén los ojos, las orejas y la mente abiertos, $C.' WHERE `entry`=8799;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C.' WHERE `entry`=8800;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿No es preciosa esta ciudad, $C? Me reconforta que hayamos encontrado tan buenos amigos en estos elfos de la noche.' WHERE `entry`=8801;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos. Soy la vindicadora Palanaar, paladín de la Luz y viajera. Estoy aquí para ayudar a los elfos de la noche.' WHERE `entry`=8802;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Espero con fervor que podamos llevar la Luz a aquellos lugares de Vallefresno donde se ha abandonado.' WHERE `entry`=8803;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estoy demasiado ocupado como para perder el tiempo en cháchara. Si buscas trabajo como cazador, habla con Diktynna, que está en el margen del río, al este. Sin duda irá atrasada en sus tareas...' WHERE `entry`=8804;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Esto va a ser muy divertido! ¿No crees, $Gquerido:querida;?' WHERE `entry`=8805;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Te envío Balandar Estrellaclara a rescatarme?' WHERE `entry`=8808;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ah, excelente! Fue divertido, ¿no? Poco a poco voy maldiciendo a más de esos repugnantes Perdidos. Pronto podré probar con los draenei.' WHERE `entry`=8809;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Que los espíritus te guíen, $R.' WHERE `entry`=8810;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Fuerza a los clanes, $C! ¿Qué te trae por aquí?' WHERE `entry`=8811;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te damos la bienvenida entre nosotros, $C. Al menos por el momento.' WHERE `entry`=8813;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿No es adorable? ¿Cómo te las arreglaste para salir del Valle? Quizás deberías volver antes de que alguien envíe un pelotón de búsqueda a por ti.' WHERE `entry`=8821;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No tengo más trabajo para ti, $N. Quiero que sepas que el pargo rojo que compartimos fue algo especial que nunca olvidaré...' WHERE `entry`=8822;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vuelve con tu gente y diles que me envíen uno de vuestros chamanes.' WHERE `entry`=8824;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vuelve con los draenei y diles que me envíen a uno de sus chamanes.' WHERE `entry`=8825;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Tienes los conocimientos para continuar, chamán? ¿Sabes por qué estás aquí?' WHERE `entry`=8827;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es un buen día para estar vivo y fusionarse con los elementos.' WHERE `entry`=8829;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Y por qué iba a querer hablar contigo, $R?' WHERE `entry`=8830;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mira a tu alrededor, chamán. ¿Ves el equilibrio de los elementos en la tierra, el aire, el agua y el fuego que los ha tocado a todos?' WHERE `entry`=8832;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No te asustes... mis grifos puede que no estén muy vivos que digamos, pero aun así te llevarán sin problemas a tu destino. Dime... ¿qué torre buscas?' WHERE `entry`=8834;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si aún no has aprendido a usar las vendas, me encantará enseñarte los fundamentos de su creación y aplicación. La diferencia entre la vida y la muerte puede reducirse a menudo a vendar heridas adecuadamente.' WHERE `entry`=8835;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡TENDRÉ mi venganza!' WHERE `entry`=8836;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ahora estoy muy ocupada, joven $C. Discúlpame, por favor.' WHERE `entry`=8838;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C. Me han enviado de El Exodar para supervisar la construcción de la Avanzada Azur.' WHERE `entry`=8840;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un alquimista puede hacer milagros con hierbas ordinarias, convirtiéndolas en pociones estables cuyos efectos van desde curas a respirar bajo el agua, a todo lo que hay en medio.' WHERE `entry`=8844;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola. ¿Qué te trae por el Campamento del Orvallo hoy?' WHERE `entry`=8846;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Buen día tengas, $C.$B$BEstoy esperando oír de uno de los consejeros de los Altos Señores feudales acerca de una audiencia. Fue una auténtica lucha solo el poder tener una oportunidad de presentar mis credenciales.$B$BHe oído describir al Martillo Salvaje como "cauteloso con los forasteros", pero se ha demostrado que eso es un eufemismo. Con algo de suerte las cosas mejorarán cuando tenga la oportunidad de reunirme con Barbapluma, quien, según me han dicho, aprueba un vínculo más estrecho con la Alianza.' WHERE `entry`=8847;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué quieres?$B$BNo es muy cortés dirigirse a un desconocido sin ninguna razón, ¿no crees?' WHERE `entry`=8848;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GQuieto:Quieta;, $Gcompañero:compañera;. El Cocinitas soy yo, y lo que me va es cocinar. Los MacSalsafloja somos una familia con una larga tradición cocinera y mercader. Sabemos como satisfacer los más delicados paladares.' WHERE `entry`=8849;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Necesitas algo?' WHERE `entry`=8850;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Que la Luz te acompañe, $C.' WHERE `entry`=8851;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Parece que por fin ha llegado la ayuda! Los omenai te damos la bienvenida a los restos de nuestro campamento, $N.' WHERE `entry`=8852;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ikan lo intenta una y otra vez. Creo que esos Zarrapuchas son muy parecidos a Los Perdidos, ya sabes... perdidos.' WHERE `entry`=8853;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Llevará su tiempo, pero estos Tábidos aprenderán los caminos de la Luz!' WHERE `entry`=8854;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida, $N. Soy Landro Tirolargo, patrón de la Llama Negra. Puede que sea la primera vez que oyes hablar de mi local. No me sorprende; hacemos todo lo posible para que no se hable demasiado de nosotros. Hay que evitar meterse en líos entre las facciones.$B$BEn la Llama Negra nos especializamos en ofrecer los mejores servicios y el mejor entretenimiento que el dinero puede comprar; desde objetos difíciles de encontrar hasta peleas de gladiadores, pasando por lucrativos juegos de azar.$B$B¿Buscas algo en particular?' WHERE `entry`=8855;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ah, rarezas y artículos poco comunes. Estamos especializados en los objetos totalmente únicos de Azeroth para los aventureros más exigentes.$B$BSupongo que si estás hablando conmigo es porque tienes una idea de lo que estás buscando. Dime, $N, ¿qué es lo que te interesa?' WHERE `entry`=8856;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Necesitas algo, $C? Quizás pueda ayudarte.' WHERE `entry`=8858;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<La sacerdotisa Il\'dinare te mira con recelo.>' WHERE `entry`=8861;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Soy una elfa de la noche, $Gextranjero:extranjera;. Aunque puede que no sepas nada de mi especie, nosotros sabemos mucho de ti y de tus progenitores. Puede que no te llames eredar, pero, en apariencia, compartís muchas similitudes.$B$BHas ayudado a mis aliados, y te estoy agradecida por ello; pero, por el momento, andas por un camino incierto. Quizás con el tiempo puedas disipar las dudas y temores que albergo en el corazón.' WHERE `entry`=8862;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ándate con cuidado, forastero.' WHERE `entry`=8864;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Has sido de gran ayuda hasta ahora, $N. Harías bien en ayudar a los demás en el campamento.$B$BY no te preocupes por esos goblins, me estoy haciendo cargo de todo.' WHERE `entry`=8865;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me alegro de verte, $N. Puede que cuando se calmen las cosas te hable del preciado tesoro que hemos perdido...' WHERE `entry`=8866;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué miras?' WHERE `entry`=8867;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Soy Adamant Coracero, de la gran ciudad de Forjaz. Como probablemente puedas suponer viendo mi uniforme, una vez formé parte de la guardia de élite de Forjaz. ¡Sí, fui la mano derecha de Magni! Al menos, hasta que me retiré. Ahora me intereso por la arqueología. En realidad es una afición; además, ¡pensé que sería una gran forma de ver mundo!' WHERE `entry`=8868;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Cowlen solloza.>' WHERE `entry`=8869;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si no los dioses, ¿quién? ¿Cómo? ¿Qué podría causar tal disputa?' WHERE `entry`=8871;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Legión? ¿Demonios? ¿Te dan caza? Mi pueblo también ha sido perseguido por la Legión: blanco de sus maestros oscuros para la condenación.$B$B<Cowlen se seca las lágrimas.>$B$BEsa es la razón por la que trasladé a mi familia aquí hace tantos años.' WHERE `entry`=8872;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C. Antes de que los elfos de la noche invitaran a los draenei a unirse a ellos aquí, este claro estaba casi olvidado.$B$BLos kaldorei tienen un profundo vínculo con estas tierras, y nosotros, los draenei, nos sentimos honrados de compartir la tarea de revitalizar Canción del Bosque.' WHERE `entry`=8873;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me alegra volver a oír las voces de otros aquí, en Canción del Bosque. Me había empezado a sentir como si fuera el último de los kaldorei que fuera a poner la vista en estas tierras antes de su descenso a la corrupción.' WHERE `entry`=8875;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Eres $Gun:una; $R, ¿verdad? ¡Me anima verte aquí! Nos irá bien toda la ayuda que podamos recibir.' WHERE `entry`=8881;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay demonios que matar, $C. ¿Tienes agallas para hacer lo que hay que hacer?' WHERE `entry`=8883;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Con una buena parte de sus demonios asesinados, la Legión se lo pensará dos veces antes de volver a atacar nuestro aserradero. Sin embargo, aún siento cómo despierta un mal demoníaco.' WHERE `entry`=8887;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Saludad todos al héroe exaltado!',`Text1_0_loc6`='¡La historia de nuestro viaje será narrada en años venideros!' WHERE `entry`=8893;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Por fin tengo una historia que contarle a mis nietos... ¡Un traidor del reino vivió entre nosotros!',`Text3_0_loc6`='A decir verdad, nunca me gustó ese gnomo...' WHERE `entry`=8893;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='Tenemos suerte de haber sobrevivido a ese calvario. ¡De no ser por ti, todas las personas de esta isla podrían haber muerto!',`Text5_0_loc6`='Me pregunto cuántos quedarán entre nosotros. Conocía a Chispik desde hacía muchos años. Nunca habría imaginado que era un traidor.' WHERE `entry`=8893;
UPDATE `locales_npc_text` SET `Text7_0_loc6`='La hoguera' WHERE `entry`=8934;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; a Canción del Bosque, $C. ¿Podemos ayudarte?' WHERE `entry`=8939;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El lacayo está de pie con aire decidido y músculos tensos.>' WHERE `entry`=8952;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mientras buscamos la ayuda de los pueblos de este mundo contra la Legión Ardiente, debemos aprender a vivir en paz con ellos.$B$BNo olvides esto mientras viajes por Azeroth. Que la Luz te guíe.' WHERE `entry`=8953;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué te trae hasta aquí, $Gpequeño:pequeña; $R?' WHERE `entry`=8954;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C. Recibo tan pocos visitantes aquí. ¿A qué debo el placer?' WHERE `entry`=8956;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te estaba esperando.' WHERE `entry`=8959;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vete. No tengo nada para ti.' WHERE `entry`=8960;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué es ese $Tchiquillo:chiquilla;? ¿Quieres a tu mami? Bueno, una lástima. ¿Quizás deberías hacer una visita al club de oficiales de Thrallmar para recuperarla?' WHERE `entry`=8961;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡No alces la voz, $C!',`Text1_0_loc6`='¡Si tienes algo que decir, dilo!' WHERE `entry`=8962;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¿Eres $Gun:una; $Tcivil:civil;?',`Text3_0_loc6`='¡Estoy ocupado, hazlo rápido!' WHERE `entry`=8962;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Cómo va tu búsqueda de la Marca?' WHERE `entry`=8964;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hoy es un gran día, $C!' WHERE `entry`=8965;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Nunca podré agradecerte lo suficiente que hicieras posible mi peregrinación!' WHERE `entry`=8966;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Por fin todo está en su lugar, ¿Estás $Glisto:lista; para tu gran debut en el escenario?' WHERE `entry`=8970;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No te preocupes, estarás bien. ¡Pareces natural!' WHERE `entry`=8971;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los cachorros de acechadores nocturnos infectados parecen venir del norte. Si yo fuera tú, ahí es donde empezaría mi caza.' WHERE `entry`=8974;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vaya, has fracasado estrepitosamente. Pensemos que ha sido un ensayo, ¿de acuerdo? ¿Preparado para salir de nuevo?' WHERE `entry`=8975;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿No ves que estoy ocupado?' WHERE `entry`=8978;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un público duro, pero te los ganarás. ¿$GPreparado:Preparada; para otro intento?' WHERE `entry`=8981;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='[Fúrbolg] ¿Yar vartslaf?' WHERE `entry`=8983;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿En qué puedo ayudarte, $Gextranjero:extranjera;?' WHERE `entry`=8990;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mi nombre es Gurf, $Gextranjero:extranjera;. ¡Gurf el Valiente!' WHERE `entry`=8991;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Soy Moordo, cazador anciano de Semprepino.' WHERE `entry`=8992;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$GBienvenido:Bienvenida; a mi tienda!' WHERE `entry`=8993;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ah, $Gel Prometido:la Prometida; ha llegado. $GBienvenido:Bienvenida; al Bastión Semprepino.' WHERE `entry`=8994;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Buenas nuevas, $C. Soy Semprepino el Menor, heredero al trono de Semprepino.' WHERE `entry`=8996;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Eres tú quien defenderá a mi gente y anunciará una nueva era de prosperidad para Semprepino?' WHERE `entry`=8997;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me temo que en este momento estoy bastante ocupado clavando herraduras. ¿Te importaría volver en otro momento?' WHERE `entry`=9002;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Una vez serví a Medivh... o fingí hacerlo. Ayudé a El Ojo Violeta a vigilar muy de cerca al maestro.$B$BPero ahora, todo en lo que pienso es en... hacer herraduras. Una y otra vez.' WHERE `entry`=9003;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Entonces, ¿has oído hablar de El Ojo Violeta? A lo mejor luego tendré tiempo para ti.$B$BTengo que volver al trabajo.' WHERE `entry`=9004;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Eralan parece estar mascando muerteflor.>$B$BSi quieres hacer una poción de amor, estás en el sitio equivocado. Cegar, lisiar y matar es mi negocio, y espero que también el tuyo.' WHERE `entry`=9005;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedo sentir tu deseo de poder, $C.' WHERE `entry`=9006;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Luz nos da la fuerza y la magia para triunfar.' WHERE `entry`=9007;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Bienvenido a la Registradora de Hermandades. ¿En qué puedo ayudarte?' WHERE `entry`=9008;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, mi $Gniño:niña;.' WHERE `entry`=9009;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Son para oírte mejor, cariño.' WHERE `entry`=9010;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo mejor para verte, mi $Gniño:niña;.' WHERE `entry`=9011;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, joven chamán. Mi arrecife es tu casa.' WHERE `entry`=9014;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Juntos abordaremos esta corrupción del agua.' WHERE `entry`=9015;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Has hecho algo estupendo, $N. Eres $Gbienvenido:bienvenida; aquí en cualquier momento.' WHERE `entry`=9016;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Has venido a por suministros de herrería, verdad?' WHERE `entry`=9019;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tengo carne fresca, pescado y setas. ¡Y mucho para beber!' WHERE `entry`=9020;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $N. A medida que asciendas de categoría entre las fuerzas del Bastión del Honor, te otorgaré acceso a algunos de los objetos que tenemos en reserva.' WHERE `entry`=9021;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Me necesitas para cuidar de tus compañeros animales?' WHERE `entry`=9024;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En Argus, Mac\'Aree era nuestra ciudad más sagrada.$B$B¿Me creerías si te dijera que las calles estaban empedradas con joyas? ¿Que los ríos brillaban incluso en las noches más oscuras?$B$BEcho de menos aquellos días... ¿Cuánto tiempo ha pasado? ¿Mil años? ¿Diez mil?' WHERE `entry`=9025;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si aprendes una cosa durante el tiempo que pases en Bruma de Sangre, $C, espero que sea la vigilancia. Esta isla se lleva primero a los imprudentes y, después, empieza a desgastar incluso al más precavido de los guerreros.$B$BMires donde mires, se erige un enemigo dispuesto a arrancarte la vida.' WHERE `entry`=9033;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Todo lo que ha pasado desde el accidente es poco más que una imagen borrosa. Parece que haya pasado toda una vida desde que estaba a bordo de El Exodar.$B$BAhora, mi realidad se ve restringida a esta isla, con sus criaturas mutantes y su interminable mar de agentes elfos. Pronto será como si nunca hubiera conocido nada diferente...' WHERE `entry`=9034;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Algunos ven en este paisaje solo muerte, corrupción y adversidad. Es cierto que no falta nada de eso, pero esa visión no tiene en cuenta las oportunidades que ofrece nuestro entorno.$B$BYo no voy a quedarme de brazos cruzados viendo cómo no se aprovechan esas oportunidades, $C. Con tu ayuda, podremos enseñar a los demás en Avanzada de Sangre a aprovechar el entorno y sus criaturas.' WHERE `entry`=9036;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, $C.$B$BAhí está el viejo Kessel. Es muy listo. Yo tendría cuidado si hiciera tratos con él.$B$BMe ofreció trabajo "cuidando elekks" y yo, tonto de mí, pensé que se trataba de darles de comer y cosas así. Las cosas me quedaron más claras cuando me dejó esta pala...' WHERE `entry`=9037;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Gracias por la advertencia, $N! ¡Nos prepararemos!' WHERE `entry`=9038;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GPrometido:Prometida;, te agradecemos la advertencia. La tribu Semprepino estará preparada.' WHERE `entry`=9039;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gracias por el aviso, $N. Prepararemos nuestras defensas.' WHERE `entry`=9040;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Soy Kessel, señor de los elekk, discípulo de la Mano de Argus. ¿Eres el nuevo recluta?' WHERE `entry`=9041;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo que significa ser de la Mano de Argus... Somos agentes de la Luz. Servimos sin preguntas. Morimos sin preguntas.' WHERE `entry`=9044;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Te han enviado a buscarme? Yo no vuelvo sin mis hombres.' WHERE `entry`=9045;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sí, soy de los Tábidos. Espero que no te moleste. Tábido o draenei, siempre hay quien te margina desde dentro de la sociedad. Lo único que importa es conocerse mejor y comunicarse con los que forman este mundo.' WHERE `entry`=9050;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Siéntate y ponte $Gcómodo:cómoda;.' WHERE `entry`=9051;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Buen día, $C. ¿Necesitas un hipogrifo fiable?' WHERE `entry`=9052;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Shh! Silencio absoluto; estoy observando a los múrlocs.$B$BVivir en un caparazón de tortuga gigante es un sacrificio que estoy dispuesto a hacer por la ciencia. Después de todo, si los múrlocs me ven aquí, ¡reaccionarán ante mi presencia y contaminarán mis datos!$B$B¡Como el primer gnómico de la Liga de Expedicionarios, voy con valentía a lugares a los que ningún enano ha ido antes!' WHERE `entry`=9054;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Has perdido el juicio? Mira a tu alrededor, mortal. ¿No ves que pisas tierras divididas? Abandona este lugar, para no sufrir como mi pueblo y yo lo hemos hecho...' WHERE `entry`=9056;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Estás $Gciego:ciega; además de $Gloco:loca;?$B$BSoy un fantasma - un cruel recuerdo de una civilización que hace mucho que dejó de existir – anclado a una tierra que no supe proteger.$B$BAntaño – hace diez mil años – era de carne y hueso, como tú. Era Príncipe de esta tierra y jinete de dragones, bendecido por Ysera del Sueño.' WHERE `entry`=9057;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿No has oído hablar de Ysera? ¿La Guardiana de la Naturaleza? ¿El Aspecto de los sueños? ¡Es la matrona de los dragones Verdes! Sí, fue Ysera misma la que bendijo a mi pueblo con su camada. Tratábamos a esas nobles criaturas como hermanos y nos permitían montar a sus lomos para combatir al enemigo. Todo fue bien durante muchos siglos, hasta...' WHERE `entry`=9058;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La camada de Alamuerte. La bondad de Ysera despertó la ira de Alamuerte, patrón de los Vuelos Negros. ¡Nos atacaron mientras dormíamos! Murieron muchos en el primer ataque, pero los verdes nos defendieron.$B$B<El príncipe Toreth señala alrededor de la isla.>$B$BLos huesos es lo único que queda de aquellos grandes dragones. No sobrevivió ninguno.' WHERE `entry`=9059;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Fui el último en morir. Mientras siento como mi espíritu deja mi receptáculo mortal, hice un juramento de sangre.$B$B<El príncipe Toreth niega con la cabeza.>$B$BSe llevó a cabo un pacto entre esta tierra y yo: mi sangre por este mundo. Me convertí en el único guardián de la historia de mi gente.$B$BNo puedo descansar hasta que sepa con seguridad que la historia de los jinetes de dragones de Loreth\'Aran no se ha perdido en el laberinto del tiempo.' WHERE `entry`=9060;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los alumnos son un poco como ovejas. Son útiles y hasta agradables cuando los orientas y les prestas mucha atención.$B$BLos dejas solos un minuto y acaban cayéndose por un precipicio o ahogándose en un río poco profundo.' WHERE `entry`=9061;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este letrero contiene varios anuncios y avisos de recompensas por llevar a cabo tareas peligrosas de sondeo. Nada te llama la atención especialmente; quizás deberías volver a mirarlo más adelante.' WHERE `entry`=9062;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $Gciudadano:ciudadana;. Te ruego que sigas tu camino si es que no tienes nada que preguntarme.' WHERE `entry`=9063;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El mariscal Windsor te ha estado esperando, $N. Desafortunadamente, ahora mismo está indispuesto. Seguro que pronto podrá atenderte.' WHERE `entry`=9064;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Lo has conseguido! ¡Excelente! El mariscal Windsor te ha estado esperando. ¿Deseas que lo llame?' WHERE `entry`=9065;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, Campeón. Los relatos de tus hazañas se harán eternos. Quizás algún día se erija una estatua en tu honor entre los héroes de Ventormenta.' WHERE `entry`=9066;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Aléjate de mí, $R! No se sabe cuándo me volverá a tomar la enfermedad.$B$BDéjame solo antes de que vuelva a suceder.' WHERE `entry`=9067;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Examinando el cadáver, concluyes que este tiene que ser el Caballero de sangre Estrella del Alba, el líder del asalto frustrado a la Ciudad de la Muerte.' WHERE `entry`=9070;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te puedo ayudar a fundar una hermandad.' WHERE `entry`=9071;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un registro en la ropa y el equipamiento del cadáver muestra la insignia que necesitas, en perfecto estado tras la batalla y el entorno infecto.' WHERE `entry`=9072;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Que Elune te bendiga. ¿En qué puedo servirte?' WHERE `entry`=9073;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C. Me alegro de que te hayas decidido a visitarnos.' WHERE `entry`=9074;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Has servido a uno de los tres con honor, $N. Nuestro tiempo se acaba. Kuros te llamará cuando esté preparado.' WHERE `entry`=9076;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C. Soy Kuros, segundo del Triunvirato de la Mano.' WHERE `entry`=9077;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vi como lo golpeaban sin piedad. Su despiadado teniente actuaba como si tal crueldad fuera un acto de normalidad. A través de las salvajes palizas, podía sentirme rompiendo.$B$BMientras observaba al vindicador Saruan recibir sus golpes, comencé a llorar. No por miedo ... lloré de tristeza. Para ver un draenei del Triumvirate tratado de tal manera. Si tan solo hubiera podido liberarme de mis ataduras. Si solo ...$B$B' WHERE `entry`=9079;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Quería gritar, pero solo escapó de mis labios un grito ahogado. Me golpearon de inmediatamente por tal acto de rebeldía. Mientras yacía en el suelo, pude ver a Matis paseándose delante del cuerpo de Saruan. Parecía visiblemente preocupado. Afirmó que a Sironas, Sirona o alguna entidad tal haría que se le arrancara la carne de los huesos si Saruan había perecido debido a la paliza.' WHERE `entry`=9081;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Que la entidad Sironas tenía planes para Saruan ...$B$B<La escritura se detiene abruptamente y una larga línea se desplaza de la página. Este draenei murió al hacer la entrada.>$B$B' WHERE `entry`=9082;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Buenos días, $C. Si deseas aprender el arte del herrero, has venido al lugar idóneo.' WHERE `entry`=9084;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Oh, no hagas caso de Buddy. No te hará daño, solo le gusta arañar de vez en cuando.$B$BEs solo un gatito grande y adorable.$B$B¿No es así, Buddy? Sí, bonito, cosita. ¡El pequeñín de tu mami, eso es lo que eres!' WHERE `entry`=9085;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nosotros, los druidas de Cenarius, protegemos el equilibrio de la naturaleza. Lo más importante es el equilibrio. En momentos de necesidad, no deberíamos tardar mucho en tomar acciones decisivas. Que nadie te haga creer lo contrario, ¡sobre todo aquellos de entre nosotros que solo quieren observar!' WHERE `entry`=9086;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Quién eres? Déjame en paz.' WHERE `entry`=9090;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Comienza tu búsqueda al norte, $N. Nuestros exploradores han visto el pistón de distorsión cerca de la línea de costa.' WHERE `entry`=9097;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Mi historia? Me halaga que me preguntes. Bien.$B$BHace mucho tiempo, en el planeta Draenor, cuyos restos son ahora conocidos como Terrallende, todos los draenei vivían en paz. Entonces, llegó la Legión Ardiente y corrompieron a los orcos con sus magias viles.$B$BSin otra escapatoria, luchamos para sobrevivir, pero las energías viles nos afectaron a muchos; con el tiempo, algunos nos transformamos en Tábidos, y otros en algo peor: unos seres sin consciencia aparente, Los Perdidos.$B$B¿Sigo?' WHERE `entry`=9098;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El cambio de nuestros cuerpos tuvo en los Tábidos y los Perdidos el efecto secundario de apartarnos de la Luz. En aquel momento no sabía por qué, pero como sacerdote me empeñé en descubrirlo. Decidí hacer un peregrinaje para ello.$B$BY ocurrió que la guerra cuando llegó a su fatídico fin, viajé al desierto del planeta fragmentado suplicando a la Luz que regresara a nosotros. Pero han transcurrido décadas y la Luz jamás respondió a mis plegarias.$B$B¿Deseas oír algo más?' WHERE `entry`=9099;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tuve la fortuna de encontrar un oasis y tomé la decisión de quedarme allí hasta recibir respuesta. Así que, estaba allí sentado, moviéndome solo cuando era absolutamente necesario, alimentándome de criaturas que se acercaban al estanque. Tal era mi orgullo.$B$BComo he dicho, pasaron décadas sin respuesta. Pero recuerdo una puesta de sol especialmente hermosa. Un céfiro cálido soplaba por toda la tierra y oí una voz, con toda seguridad, pero no había nadie cerca.$B$BComo puedes imaginarte, pensé que por fin perdía el juicio.' WHERE `entry`=9100;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Había algo extraño en ese viento; nunca paraba. Durante semanas, sopló sobre la tierra, gimiendo y aullando. No cesaba nunca. Y durante todo ese tiempo, la voz continuó, aunque yo no la entendía ni sabía de dónde venía.$B$BMi tiempo de soledad me enseñó a tranquilizarme y a mirar en mi interior. Eso fue lo que hice para no perder la cordura. Y así fue cómo empecé a entender a la voz. ¡Venía del propio viento y tenía mucho que contar!' WHERE `entry`=9101;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me habló de secretos y poder, y también de verdades. ¡La Luz no había abandonado a los Tábidos! Las energías viles que los orcos habían usado antaño contra los draenei penetraron en nosotros, provocando una escisión en nuestra capacidad de ser oídos, o quizás de oír, la Luz. Lo mismo que había sido responsable de la degradación de nuestro cuerpo.$B$BPero nuestros espíritus no estaban rotos, y el aire me ofreció otro camino, a través del cual tal vez pueda liberar a mi pueblo.' WHERE `entry`=9102;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me enseñó el chamanismo, que en cierto sentido es solo otra faceta de la Luz. El agua del oasis se reunía con el viento, así como el fuego de mi campamento y la tierra sobre la que me sentaba. Todos tenían algo que decir.$B$BA lo largo de los años siguientes me enseñaron que era invocar y formar parte de los elementos del mundo. Y me dijeron que los mundos de todas las estrellas del cielo, incluso este, están hechos de esos elementos.$B$BPero llegó el día en que sentí que había aprendido suficiente para irme.' WHERE `entry`=9103;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Volví a mi pueblo y me recibieron con abrazos y sonrisas, sobre todo los Tábidos. Los prejuicios contra nosotros, principalmente por parte de los draenei, continuaron durante mi ausencia. Pero fue como si esperaran mi llegada. Pronto descubrí por qué.$B$BVelen, el profeta y líder de todos los draenei, había visto mi regreso en una de sus visiones y había venido a darme la bienvenida también. Quiso entrevistarse conmigo en privado, y, mientras le contaba mi historia, asentía y sonreía, pues también lo había visto en sus sueños.' WHERE `entry`=9104;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Velen me pidió que guiase a los Tábidos, pero sobre todo, me pidió que enseñara el chamanismo a todo el mundo; Tábido y draenei por igual. Yo acepté.$B$BNos dimos cuenta de que, por culpa de los prejuicios, sería una tarea complicada. Algunos desprecian a los Tábidos por que les traemos malos recuerdos del pasado. Otros piensan que el chamanismo es un insulto a la Luz.$B$BSea como sea, esa es mi historia.' WHERE `entry`=9105;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si nuestros enemigos se diesen cuenta de que lo único que queremos es vivir en armonía con ellos, puede que no necesitáramos atacarlos para sobrevivir.' WHERE `entry`=9106;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Llegará su día, $N.$B$BY, cuando llegue, estaré allí.' WHERE `entry`=9107;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Con nuevos enemigos a la vuelta de la esquina, ¡no debemos permitir a nuestros antiguos enemigos que se aprovechen de nuestra situación!' WHERE `entry`=9108;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿No es increíble este lugar? Por supuesto, falta un poco de aire fresco aquí arriba, en mi opinión.' WHERE `entry`=9109;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Di, ¿por casualidad ves aquel arco en el lago, al oeste? Me pregunto adónde va. Debería ir buceando hasta allá.' WHERE `entry`=9110;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si no me hubiera torcido el tobillo viniendo a este lugar, estaría ahí dentro con mis amigos. Mmmm, ahora que lo pienso, quizás sea mejor que esté aquí fuera, ¿no?' WHERE `entry`=9112;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Fhwoor gustar pequeños esporinos. Ellos salvar Fhwoor. No comerlos.$B$BFhwoor odiar nagas. ¡Fhwoor destrozar!' WHERE `entry`=9113;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Soy Aesom, tercero del Triunvirato.' WHERE `entry`=9117;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aquí yacen los restos del desherbador Pulgaverde, uno de los compañeros druidas del vigía Jhang. Todo indica que fue asesinado rápida y limpiamente mientras intentaba escapar del Recinto de los Esclavos.' WHERE `entry`=9118;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Casi lo consigo!$B$BVine aquí para demostrar la existencia de Quagmirran, el legendario gigante fúngico. Y lo conseguí; más o menos.$B$B¡Libérame! A cambio, te proporcionaré un beneficio para cuando tengas que tratar con Quagmirran.' WHERE `entry`=9119;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Como única superviviente de los Tábidos, es un honor y una obligación continuar con las enseñanzas chamanísticas del clarividente Nobundo.' WHERE `entry`=9122;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Malditos sean esos elfos de sangre y su magia imprudente!' WHERE `entry`=9123;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No me hagas perder el tiempo. ¿Qué es lo que quieres?',`Text1_0_loc6`='¿No ves que estoy ocupado?' WHERE `entry`=9124;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¿Sí? ¿Qué quieres?',`Text2_1_loc6`='¿Sí? ¿Qué quieres?',`Text3_0_loc6`='¿Y tú eres...?' WHERE `entry`=9124;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Que la Luz ceda a tu voluntad en todos tus esfuerzos.' WHERE `entry`=9127;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En la naturaleza, todas las cosas están interconectadas en una red; una ecología. Si procuramos no alterarla, sino solo observarla, podremos tener la suerte de comprender las complejas relaciones que hay entre los organismos que nos rodean.$B$BUna vez que hayamos comprendido esto, podremos tomar las medidas necesarias para el bienestar de todas las criaturas. Intervenir sin ese conocimiento, provocaría su destrucción.$B$BYo estudio las relaciones entre los esporinos y los señores de la marisma.' WHERE `entry`=9128;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El vindicador Aesom te saluda.>$B$B¿Has hablado con Admetius? Creo que ha estado preguntando por tu paradero.' WHERE `entry`=9130;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedes dar las gracias de que esté encerrada en esta jaula, $R.' WHERE `entry`=9134;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El agente capturado de los Halcones del Sol te mira recelosamente.>$B$BNo te he visto nunca antes. ¿Quién eres?' WHERE `entry`=9136;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<La agente del Halcón del Sol capturado muestra una sonrisa satisfecha.>$B$BNo te preocupes, sangre joven, todavía quedan muchos controladores de portales y muchos buenos emplazamientos para crearlos. Estoy segura de que Sironas abrirá otro portal muy pronto. Los perros de piel azul no durarán mucho en este mundo. Sironas y la abominación acabarán con todo lo que las fuerzas del Halcón del Sol dejen tras de sí.' WHERE `entry`=9137;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Obviamente. ¿Cómo podría, de no ser así, infiltrarse entre sus filas y pasar por draenei? Había estado viviendo entre ellos durante mucho tiempo.' WHERE `entry`=9140;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Numerosos? Tiene dos puntos débiles: la base de la estructura del lado oeste y la base de la estructura del lado este. Eh, espera un momento. ¿Cómo ibas a saber nada de la Espiral Vectorial? ¿No has dicho que te capturaron antes de materializarte siquiera?' WHERE `entry`=9141;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ha llegado el momento de destruir la Espiral Vectorial!' WHERE `entry`=9142;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aguardo órdenes pacientemente, $C.' WHERE `entry`=9143;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ahora que la grosera interrupción ha terminado, como iba diciendo, ¡Quagmirran está cerca!$B$BSegún lo prometido, puedo fortalecerte con un favor mágico que debería ayudarte en tu lucha contra él.' WHERE `entry`=9144;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='[En lengua común con dificultad] Es hora de celebrar que lo hacemos.' WHERE `entry`=9145;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Has recorrido un largo camino, $N. Muy largo, sí... Pero sabía que tu destino te reservaba grandes cosas. Todos lo sabíamos. ¡Saludad todos a $N!$B$BY gracias por ocuparte de nuestro problema gnomo...' WHERE `entry`=9149;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Quería agradecerte lo que hiciste por mí. Me diste fuerzas para recuperar mi hogar de las bestias del bosque. ¡Y al hacerlo, encontré a Magwin!' WHERE `entry`=9160;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El campeón draenei camina entre nosotros. ¡Es una celebración para el salvador de Bruma Azur y Bruma de Sangre!' WHERE `entry`=9162;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Todo esto es por ti, $N. Tú lo lograste. Salvaste a toda esta gente y a miles más.$B$BHas hecho mucho; más de lo que muchos no habrían hecho en diez vidas. Pero acabas de empezar. Si hubiera mil héroes como tú...$B$BPero escucha bien, $N. Tu destino está en Terrallende. Lo he visto en mis visiones; una profecía por desvelar.$B$BEncuentra a nuestra gente, $N. Encuéntralos y tráelos a casa...' WHERE `entry`=9163;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ysiel esperaba algo de resistencia enemiga a su incursión en Terrallende. Por eso se nos trajo a los celadores.$B$BNunca esperamos que nuestra resistencia viniera con la fuerza o los números que los nagas han mostrado.' WHERE `entry`=9167;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No tengo ni idea de quién eres, pero tu presencia aquí es de lo más bienvenida. Archimonde y su ejército de la Plaga podría atacar en cualquier momento, $C. ¿Estarás con nosotros para luchar contra este mal?' WHERE `entry`=9168;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gracias, $R. Ahora, preparaos. Archimonde está en marcha y debemos retrasar lo inevitable todo lo que podamos.' WHERE `entry`=9169;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Debemos seguir resistiendo todo lo que podamos! ¡Seguid luchando!' WHERE `entry`=9170;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Soy la líder de la defensa de la Avanzada de Sangre. ¿En qué puedo ayudarte?',`Text1_0_loc6`='Hola, ¿estás aquí para aprender acerca del conflicto en Bruma de Sangre?' WHERE `entry`=9172;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¿Puedo ayudarte?',`Text2_1_loc6`='Este lugar está infestado... Bueno, tendremos que salir de aquí con las reliquias tan rápido como nos sea posible.',`Text3_0_loc6`='Servimos a la luz incondicionalmente. ¿Quizás tengas algo más de curiosidad sobre nuestro fin?' WHERE `entry`=9172;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='[En el Ágora] ¡Semprepino, prepárate! ¡Muchas gracias!' WHERE `entry`=9173;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La naturaleza favorece a aquellos que están dispuestos a conocer sus secretos, $N.' WHERE `entry`=9176;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La naturaleza es una fuerza poderosa, $R. Aquellos que intentan dominarla nunca lo consiguen de modo permanente.' WHERE `entry`=9177;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $N.' WHERE `entry`=9179;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los espíritus están agitados, $R. Debes estar vigilante de tus alrededores mientras estés en Nagrand, no sea que su furia te consuma.' WHERE `entry`=9180;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Anillo de la Tierra intenta conservar la fuerza y majestuosidad de los elementos.' WHERE `entry`=9181;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Cómo puede mi pueblo luchar cuando los elementos están en una confusión tal? No ven, que, sin el apoyo de los elementos, el mundo simplemente no puede ser.' WHERE `entry`=9182;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Soy Untrag del Anillo de la Tierra. Intenta no ofender a los elementos mientras permanezcas en el Trono de los Elementos.' WHERE `entry`=9183;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedo sentir tu deseo de poder, $C.' WHERE `entry`=9184;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No tengo tiempo para sermones ahora, $C. Busca tus conocimientos en otra parte.' WHERE `entry`=9186;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Parece que te equivocaste, $C.' WHERE `entry`=9187;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $N. Confío en que no te hayan seguido hasta aquí.' WHERE `entry`=9188;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No tenemos mucho de qué hablar, $C. Quizás deberías buscar a otros individuos de ideas más similares.' WHERE `entry`=9189;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es un placer ver a alguien cuerdo por aquí. ¿En qué puedo ayudarte?',`Text1_0_loc6`='Ah, compañero forestal. ¿Qué necesitas?' WHERE `entry`=9190;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Evidentemente careces de la capacidad para comprender las complejidades de lo Arcano, $C. Largo.' WHERE `entry`=9191;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué ayuda te puedo brindar en tus estudios?' WHERE `entry`=9192;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El nuestro es un camino para aquellos con voluntad férrea y determinación firme. No pareces poseer ninguno de estos rasgos, $C.' WHERE `entry`=9193;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Esta tierra ha sido arrasada por el accidente. Aunque no compartas nuestra filosofía, $C, te ruego que hagas todo lo que puedas para curarla.' WHERE `entry`=9194;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Que los vientos te guíen, $N.' WHERE `entry`=9196;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este libro contiene información sobre las especies de plantas de Terrallende. La mayoría de las plantas descritas son originarias de la marisma de Zangar.$B$BLas tablas y los gráficos son algo complejos para que los comprendas. Quizás el autor pueda darte algunos consejos.' WHERE `entry`=9197;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Siempre me ha gustado ordenar y organizar cosas. Supongo que he salido a mi madre.',`Text1_0_loc6`='Aprender todo lo que se pueda sobre nuevas especies de plantas es muy importante para nuestra expedición. No solo de las comestibles o las medicinales.$B$BLas plantas son la base de otras muchas formas de vida. Comprenderlas mejor significa comprender mejor todo el sistema natural.' WHERE `entry`=9199;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Todavía no sé qué pensar de tu calaña, $R. Algunos de vosotros pueden ser violentos y matar esporinos en cuanto los ven.$B$BPareces agradable... aunque quizás con un aspecto algo extraño, supongo.' WHERE `entry`=9201;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida a Esporaggar, $R. No solemos recibir la visita de gente como tú por aquí, pero tú nos has ayudado. Esta es tu casa.' WHERE `entry`=9202;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gracias a ti, nuestros jóvenes estarán a salvo, $N. Ve a Esporaggar y visita a mi gente. Todos han oído hablar muy bien de ti.' WHERE `entry`=9204;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los defensores de Avanzada de Sangre protegerán este campamento de cualquier peligro.',`Text1_0_loc6`='Los defensores están aquí para proteger la Avanzada de Sangre de la amenaza de Halcón del Sol.' WHERE `entry`=9206;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Hace poco, hemos oído que están invocando una criatura de inmenso poder cerca de la Espiral Vectorial. No temas, si la ciudad corriese peligro, entregaríamos nuestras vidas para salvarla.',`Text2_1_loc6`='Este lugar está infestado... Bueno, tendremos que salir de aquí con las reliquias tan rápido como nos sea posible.' WHERE `entry`=9206;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mantén los ojos bien abiertos, $C. Nunca se sabe cuándo hay alguna bestia acechándote, y no al revés.' WHERE `entry`=9218;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mantén los ojos bien abiertos, $C. Nunca se sabe cuándo hay alguna bestia acechándote, y no al revés.' WHERE `entry`=9219;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Escucha, $Gamigo:amiga;. He dejado la expedición en las competentes manos de mi hijo Júnior. Cuando ya has cazado las mejores presas de Azeroth, empiezas a pensar en algo más grande.$B$B¿Y has visto las bestias que hay aquí?$B$B¡He venido a abatir la presa más peligrosa que hayas imaginado jamás! ¡Quiero cazar a Eburnia y llevarme su corazón como trofeo!' WHERE `entry`=9220;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$N! Recuerdo tus proezas de caza. Puede que no empezaras demasiado bien en Tuercespina, pero has seguido a tu ritmo y ahora eres uno de los mejores tras abatir al viejo Bangalash.$B$BPero escucha, $Gamigo:amiga;, las presas de Terrallende ahora son lo máximo. ¡Mira mis cicatrices!' WHERE `entry`=9221;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida a nuestro templo, espíritu.' WHERE `entry`=9226;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Así que debemos confiar en quienes nos insultan de esta manera. ¿Qué imagen tienen los draenei de nosotros como para usar trucos que llamen nuestra atención?$B$BSabemos que están deseando "redimirnos", pero mi pueblo no necesita redención alguna. Somos capaces de arreglárnoslas solos y perseguiremos nuestro destino como un pueblo independiente y orgulloso. Transmite este mensaje a tus aliados.' WHERE `entry`=9229;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Legión Ardiente nos presiona, pero nuestra defensa resiste. Cada momento que mantenemos a Archimonde lejos del Árbol del Mundo nos lleva más cerca de la victoria.' WHERE `entry`=9230;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Bien. Asegúrate de que lo comprenden.$B$BAhora te agradecería que dejaras mi templo en paz.' WHERE `entry`=9231;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Maktu no te reconoce, $R, pero está encantado de verte. Si necesitas comprar o reparar armadura, ven a ver a Maktu.' WHERE `entry`=9236;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los recuerdos de este templo me envuelven la mente como una bruma espesa, pero no consigo formar una historia coherente con ellos. Este lugar significó algo para mí antaño. Hasta que pueda recuperar ese recuerdo, me sentiré como si faltara parte de mí.$B$BQuizás los draenei puedan ayudarme a recuperar lo que he perdido.' WHERE `entry`=9237;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Algunos me llaman "Tim", pero para mis amigos soy "el especialista".' WHERE `entry`=9238;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si lo preguntas, es que no hay confianza suficiente como para que uses ese apelativo.' WHERE `entry`=9239;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C. ¿Quieres cazar al límite? No busques más.$B$BHemet Nesingwary ha reunido a la mejor expedición jamás vista sobre los restos de este mundo. Ven con nosotros de safari si te atreves.' WHERE `entry`=9240;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No dejes que las ruinas que nos rodean te engañen, $C. Dalaran es tan poderosa como siempre.' WHERE `entry`=9241;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Karazhan es más que un simple edificio, $N. Si trazaras las líneas-ley que atraviesan Azeroth, verías que todas convergen justo ahí.$B$B<Alturus señala un punto invisible bajo la torre.>$B$BFascinante, ¿verdad?',`Text1_0_loc6`='El archimago Alvareux escribió hace tiempo un tratado en el que desarrollaba una teoría fascinante. Proponía la existencia de una copia exacta de Karazhan en un mundo diferente al nuestro, pero que, de algún modo, compartía nuestro propio espacio.$B$BSe volvió loco poco después. Una lástima.' WHERE `entry`=9242;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aquí se publican las recompensas ofrecidas por animales malignos y criminales peligrosos. Para ti, la recompensa publicada aquí es demasiado peligrosa, pero vuelve a comprobar el tablón cuando seas más fuerte.' WHERE `entry`=9244;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Una gruesa línea roja ha sido trazada atravesando las palabras SE BUSCA: Garraletal. "Esta recompensa ha sido exigida por $N, de cualidades heroicas, que ha derrotado a la temible bestia," proclama más escritura en la misma tinta roja.' WHERE `entry`=9245;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este panel contiene varios anuncios y noticias sobre recompensas.' WHERE `entry`=9246;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; a Zabra\'jin, $C. Soy el cazador de las Sombras Denjai, supervisor de esta Avanzada y comandante de las fuerzas de Lanza Negra destacadas en la Marisma de Zangar.' WHERE `entry`=9248;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ni se os ocurra pensar en intentar huir de mí, bebés múrloc! ¡Tengo ojos de halcón y orejas de... orejas de... de algo con orejas muy poderosas!' WHERE `entry`=9249;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tor\'gash cree que puede ver el futuro en las entrañas de los pájaros o lanzando los huesos de caña de sus ancestros. En realidad todo lo que hace falta es preguntar a los espíritus. Pero no intentes contarle esto, $N. Es muy terco.' WHERE `entry`=9250;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Has demostrado ser $Gun cazador:una cazadora; muy capaz, $N. Pero todavía hay una bestia que se aparece en tus sueños... ¡Eburnia!' WHERE `entry`=9256;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los has metido a todos en el saco: Bach\'lor, Banthar, Arrancatripas, y ¡hasta la poderosa Eburnia! ¡Realmente eres $Gel mejor cazador:la mejor cazadora; que conozco!' WHERE `entry`=9257;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Y ahí estaba, cara a cara con un venado talbuk y armado solo con mi cuchillo... <tose>... y mi inteligencia de cazador curtido que me salvaría el pellejo.$B$BDe repente, ¡veo que un par de vientorrocs cazadores se lanzan sobre mí desde el cielo! Su ataque a traición... <tose> no me ayudó demasiado tampoco.$B$BMe quedé con sus picos de trofeo, pero también tengo una cicatriz infectada de recuerdo. Es muy fea y me duele mucho, pero estoy seguro de que curará en un día o dos.' WHERE `entry`=9258;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La primera vez que vi setas más altas que árboles, no tenía tanta seguridad en que pudiera llamar a este lugar hogar. Después de un tiempo, sencillamente dejas de darte cuenta.' WHERE `entry`=9259;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No olvides tomarte tu tiempo para pescar algo mientras estés en la Marisma de Zangar. Nunca sabes cuándo volverás a estar cerca del agua.' WHERE `entry`=9260;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Telredor era un refugio para sacerdotes draenei y peregrinos. Los sacerdotes residentes casi no reciben visitas.$B$BCuando los orcos lanzaron sus primeros ataques a los asentamientos draenei, los refugiados empezaron a llegar en masa. El anacoreta Ahuurn y los demás transformaron Telredor en un refugio seguro. Muchos draenei, yo incluido, debemos nuestras vidas a sus esfuerzos.' WHERE `entry`=9261;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las vistas desde la cima de Telredor son magníficas, ¿verdad?' WHERE `entry`=9262;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; a Telredor, $C. Si necesitas algo, no dudes en preguntar.' WHERE `entry`=9263;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nunca había visto tantos tipos diferentes de setas en un mismo sitio. Si me dieran el tiempo suficiente, estoy seguro de que podría encontrar un uso para cada una.' WHERE `entry`=9264;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Telredor ha cambiado más en los últimos veinte años que en los siglos pasados. Yo no me quejo, $N; no es la primera vez que cambia el mundo.$B$BLa Luz me ha traído amigos y compañeros que no habría conocido como monje de clausura. Aprende lo que puedas del pasado mientras estás aquí.' WHERE `entry`=9265;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El cazador de las Sombras Denjai me ha enviado con un pequeño contingente de albañiles y guerreros para establecerse en esta Avanzada. ¡Apenas hemos terminado de construirla y ya nos pide informes!$B$BSi necesitas material, puedo ofrecerte algunas cosas, pero aún esperamos nuestro primer envío.' WHERE `entry`=9267;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Si te encuentras con el médico brujo Tor\'gash dile que sus brebajes y sus talismanes no funcionan! Hice exactamente lo que me dijo y Magasha sigue coladita por Zurai.$B$B¡No te creas nada de lo que dice ese falso médico brujo!' WHERE `entry`=9268;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Reavij es tan sutil como un kodo! Pero lo peor es que no se da cuenta de que se nota. Veo la forma en que me mira cuando cree que nadie lo ve.$B$BMe asusta. Desearía que el cazador de las Sombras Denjai no lo hubiera mandado con nosotros. Nos podríamos haber deshecho de él cuando montamos la Avanzada Rata del Pantano.' WHERE `entry`=9269;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Garadar es un caos, $Gextranjero:extranjera;. Desde que la Abuela enfermó, Garrosh ha caído en una profunda depresión. Desde luego no es el niño de su padre.$B$B<Kurkush suspira.>' WHERE `entry`=9271;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los anuncios de recompensas por los líderes nagas de las tribus de Cresta Oscura y Escamas de Sangre están enumerados aquí.' WHERE `entry`=9274;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Han muerto. Todos...$B$BLos Sangreoscura pasaron a través de nosotros y algo que no puedo comprender me poseyó. Dime, ¿alguna vez has experimentado una rabia tan grande que sientes que arde todo tu ser? ¿Un ardor que marchita a los vivos? ¿Un fuego que quema la sangre de tus venas? Eso es lo que yo sentí.$B$B<Los ojos de Colmillosauro brillan con una luz roja durante un segundo.>$B$BYo fui el único que sobrevivió. Me rodearon treinta... Sangreoscura... No recuerdo nada más. Perdí el conocimiento y me desperté al poco tiempo.' WHERE `entry`=9275;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Se avecinan tiempos oscuros, $Gextranjero:extranjera;...' WHERE `entry`=9276;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los espíritus están muy agitados. Debemos encontrar la causa de tal alteración.' WHERE `entry`=9277;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemet es el mejor cazador que conozco. Nos topamos en las colinas de Cuna del Invierno, en Azeroth, hace muchas lunas. Él y yo acechábamos a la misma presa: el gran felino conocido como Rak\'shiri.$B$BEn lugar de pelearnos por nuestra presa, hicimos una apuesta; quien consiguiera la piel del sable de hielo, podría exigirle algo al otro. Para mi vergüenza eterna, fui el que perdió.$B$B¿Tengo cara de haber ganado?' WHERE `entry`=9283;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El poderoso cazador, $N, vuelve. Cuéntame la historia de cómo acechaste a tu última presa.' WHERE `entry`=9284;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sí, sí, me siento mejor, pero la verdad es que me gusta estar aquí abajo.' WHERE `entry`=9286;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Consorcio lleva meses planeando esta operación. Queríamos encontrar el Oshu\'gun, el diamante más grande del universo.$B$BUnos minutos antes de que empezáramos, nos llegó un mensaje del príncipe-nexo Haramad pidiéndonos que esperemos hasta que se resolviera una cuestión política en Tormenta Abisal. He transformado la operación en un mercado negro de gemas mientras esperamos, pero nuestros beneficios se reducen cada minuto.' WHERE `entry`=9287;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gezhe está aquí para hacer un trabajo. Yo estoy aquí para asegurarme de que nadie lo moleste mientras trabaja.' WHERE `entry`=9288;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Sabes quiénes somos, $Gextranjero:extranjera;? Somos Mag\'har – orcos marrones, según he oído que tu especie nos llama. Los Mag\'har son supervivientes: supervivientes de una dinastía caída; supervivientes de la enfermedad roja; supervivientes de un mundo destrozado...$B$BSoy Jorin, hijo de Kilrogg; el último del linaje de Mortojo: Jefe de Guerra del clan de Foso Sangrante.' WHERE `entry`=9289;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡No nos habríamos estrellado si Hemet aprendiera a no dispararle a los alerones cuando intenta darle a las presas del suelo!$B$BPero no pasa nada, los arreglaré bastante rápido.' WHERE `entry`=9290;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Me alegro mucho de que Abusón Puñoinfecto recibiera su merecido! Tengo muchas pieles por si quieres darle alguna a Harold.' WHERE `entry`=9292;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $N.' WHERE `entry`=9296;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Abuela nos crió a todos. Todos los Mag\'hari que ves han sentido la influencia y el cariño de la Abuela de una forma u otra. La echaremos mucho de menos cuando ya no esté.',`Text1_0_loc6`='La Abuela dice que me llamo como un gran héroe orco.' WHERE `entry`=9297;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Pareces $Gun:una; $R razonable. Quizás podamos ayudarnos mutuamente.' WHERE `entry`=9312;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿A qué lugar de nuestra bella ciudad deseas ir?' WHERE `entry`=9316;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La casa de subastas del oeste está en el medio del Bazar, al oeste del Camino de los Ancestros.' WHERE `entry`=9318;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué camino quieres seguir?' WHERE `entry`=9320;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si buscas un banco en la parte este de la ciudad, no busques más lejos, ve al Intercambio Real. Está en el muro norte.' WHERE `entry`=9322;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tandrine es la Maestra de la hermandad de Lunargenta. Se encuentra en el Camino de los Ancestros, cerca de la Puerta Magister.' WHERE `entry`=9324;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tenemos una gran abundancia de buzones de correo en nuestra hermosa ciudad. El que se encuentra junto al Banco Real de Cambio debería ajustarse a tus necesidades.' WHERE `entry`=9326;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Shalenn se ocupa de los animales en la ciudad y evita que se pongan nerviosos cuando sus dueños están de paseo. Está en la Plaza del Errante, cerca de la sala de los forestales.' WHERE `entry`=9327;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los Caballeros de sangre de la Plaza del Errante ceden generosamente espacio en su sala para Ileda, la maestra de armas de Lunargenta. Su entrenamiento no es barato, pero apreciarás su experiencia.' WHERE `entry`=9328;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los maestros de batalla descansan detrás de los muros del enclave de los Caballeros de sangre, en la Plaza del Errante. Aunque suelen ser... un poco bruscos, también tienen sus bondades.' WHERE `entry`=9329;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Un druida? Nuestra gente no sigue ese camino..., pero estás de suerte. Una tauren, creo que se trata de una hembra, se ha instalado en una modesta casa al norte del Camino de los Ancestros. Se llama Harene Correllanos.',`Text0_1_loc6`='¿Un druida? Nuestra gente no sigue ese camino..., pero estás de suerte. Una tauren, creo que se trata de una hembra, se ha instalado en una modesta casa al norte del Camino de los Ancestros. Se llama Harene Correllanos.' WHERE `entry`=9330;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué camino quieres seguir?' WHERE `entry`=9331;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ah, buscas a uno... de esos. Bueno, si de veras quieres perder tu tiempo hablando con un raro de esos, encontrarás a los forestales en la Plaza del Errante. Zandine podrá atenderte.' WHERE `entry`=9332;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ah, ¿buscas a los maestros de lo Arcano? Puedes encontrar a Quithas y a los otros magistri en la Aguja Furia del Sol.' WHERE `entry`=9333;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los sacerdotes de nuestra bella ciudad se pueden encontrar en la Aguja Furia del Sol. Busca a la dama Belestra.' WHERE `entry`=9335;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los más ágiles de entre nosotros pueden ser difíciles de ver a veces, pero, no obstante, probablemente te encontrarás con el pícaro Zelanis y con su cohorte en las profundidades del Frontal de la Muerte.' WHERE `entry`=9336;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La invocación de demonios es una práctica peligrosa, pero puede proporcionar un poder enorme. Nuestro experto particular es Alamma; se ocupa de los brujos desde el Frontal de la Muerte.' WHERE `entry`=9337;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué profesión quieres aprender?' WHERE `entry`=9338;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los alquimistas están situados en la Corte del Sol; encontrarás a Camberon ansioso por enseñarte cualquier cosa que quieras saber sobre el arte de elaborar pociones.' WHERE `entry`=9339;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Bemarrin instruye en el oficio de la herrería en la Plaza del Errante. Aunque es un oficio algo... sucio, supongo que tiene sus ventajas.' WHERE `entry`=9340;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Creo que Sedana la encantadora está reclutando aprendices en estos momentos. La encontrarás en las habitaciones de la Corte del Sol.' WHERE `entry`=9341;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Alestus es maestro en... vendar heridas de cualquier manera. Si lo que quieres es aprender primeros auxilios, búscalo en el Camino de los Ancestros, cerca de la entrada al Intercambio Real.' WHERE `entry`=9343;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La botánica Nathera tiene una gran habilidad para encontrar hierbas y me imagino que puede transmitir sus conocimientos a alguien como tú. La encontrarás en una de las habitaciones de la Corte del Sol.' WHERE `entry`=9345;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En el extremo suroriental del Camino de los Ancestros, Lynalis tiene un buen puesto de peletería. Si pagas, te adiestrará.' WHERE `entry`=9347;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si cavar en la suciedad es la clase de pasatiempo que encuentras entretenido, ve a compartir tu locura con Belil en la Plaza del Errante. Seguro que estaría contento al saber que no es la única persona que ha perdido el juicio.' WHERE `entry`=9348;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tyn es la desolladora de talento; fabrica el mejor cuero de lince que haya visto. Si deseas aprender de ella, la encontrarás en el extremo sureste del Camino de los Ancestros.' WHERE `entry`=9349;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El sastre Keelen siempre está dispuesto a recibir nuevos estudiantes para transmitir su delicado saber. Su tienda es un poco difícil de encontrar al principio: se encuentra justo al norte de la casa de subastas del Bazar. Él está en el piso más bajo del edificio.' WHERE `entry`=9350;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Por qué ser cruel con Zoquete? Zoquete hambriento, eso es todo.' WHERE `entry`=9352;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡De acuerdo! Zoquete decirte todo lo que quieras!' WHERE `entry`=9353;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Puño de Roca hambriento! ¡Necesitar comida! Tribu de ogros Mazo de Guerra echar Puño de Roca del montículo al norte. Muchos más Puños de Roca que alimentar ahora.' WHERE `entry`=9354;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No cierto. Mí solo querer mordisquear. Yo no comer todo. Bueno, quizás comer los pequeños.$B$B¡Son deliciosos!' WHERE `entry`=9355;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Sácame de aquí, $R!' WHERE `entry`=9357;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Quién es tan valiente como para presentarse ante Lantresor del Filo? ¿Acaso no reconoces la muerte cuando la tienes delante?' WHERE `entry`=9361;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Entonces eres $Gun insensato:una insensata;. ¿Sabes lo que soy?' WHERE `entry`=9362;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un orco...$B$B<Lantresor se burla.>$B$BSoy un maestro del acero de origen medio orco.' WHERE `entry`=9363;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Draenei... Por esa razón, no soy ninguno de los dos. Ni orco, ni draenei. Formo parte de una raza poco común. La mayoría hemos sido asesinados hace décadas... Sobrevivo gracias a mi fuerza de voluntad.' WHERE `entry`=9364;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Y, ¿qué quiere tu pueblo de Lantresor, $R?' WHERE `entry`=9366;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Lantresor ríe ruidosamente.>$B$B¿Sabes dónde te has metido, $Gestúpido:estúpida;? Este es territorio de los ogros. Antes de los orcos y antes de los draenei estaban los ogros. Y sabiendo eso, quieres empezar una guerra que perderás con toda seguridad... Puño de Roca te aplastará. Y si Puño de Roca no te aplasta, entonces será Mazo de Guerra.$B$BPero creo que tus líderes ya lo saben...' WHERE `entry`=9367;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Lantresor te mira como si viese a través de ti.>$B$BPuede que haya otra solución... Aceptaré lo que pide tu líder, pero solo si haces exactamente lo que diga.$B$BEste trato podría beneficiarnos mucho a los dos.' WHERE `entry`=9368;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Participarás en una serie de acontecimientos que harán que los orcos Mata\'penas de la Fortaleza Mata\'penas piensen que los ogros de Mazo de Guerra han atacado su base. Después, tendrá lugar otra serie de acontecimientos que harán que los ogros de Mazo de Guerra piensen que los orcos de Mata\'penas están atacando su base.$B$BSi lo haces, resolverás nuestros problemas y los tuyos.$B$BPuño de Roca recuperará su territorio y Mazo de Guerra, el Consejo de la Sombra y Puño de Roca dejarán de atacar a tu gente.' WHERE `entry`=9369;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los inscritos tendrán que hablar con el celador Tororroc antes de publicar cualquier tarea.$B$BLos que publiquen anuncios no aprobados serán perseguidos.$B$BNo publiquéis facturas.' WHERE `entry`=9373;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si buscas trabajo, mira el tablón de anuncios, $R.' WHERE `entry`=9375;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No tienen respeto por los muertos, $R. Hay que dar una lección a los ogros.' WHERE `entry`=9376;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Debemos buscar supervivientes. Si hay alguien que todavía viva, al menos debemos intentar traerlo de vuelta...' WHERE `entry`=9377;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Quién eres? ¿Qué quieres?',`Text1_0_loc6`='¿Por qué nos has hecho eso?' WHERE `entry`=9383;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¡Hay CUATRO luces!',`Text3_0_loc6`='¡Mis teropolillas sañosas me vengarán!' WHERE `entry`=9383;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='¿Dónde están todos?',`Text5_0_loc6`='¡Los invitados a la cena deberían ser más respetuosos!' WHERE `entry`=9383;
UPDATE `locales_npc_text` SET `Text6_0_loc6`='¡Oh, qué profanación de este lugar sagrado!',`Text7_0_loc6`='Unce mi frente con un bálsamo de sanación. ¿Te gusta mi corona de espinas?' WHERE `entry`=9383;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedo adiestrarte en minería. ¿Te interesa?' WHERE `entry`=9384;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te puedo instruir en la herboristería. ¿Te interesa?' WHERE `entry`=9385;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Es asombroso! La derrota de Anatheron ha retrasado a Archimonde en sus planes y nos ha acercado a la victoria. Pero mis fuerzas no pueden permanecer aquí. Mis soldados y yo estamos muy cansados y debemos retirarnos. Si todavía te quedan fuerzas, ve al campamento de Thrall, al este. Allí opondremos resistencia de nuevo. Suerte.' WHERE `entry`=9387;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hay que recuperar esas cajas de suministros!' WHERE `entry`=9389;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ten cuidado al ir al matorral, $C. Hay un aura perjudicial en ese sitio. Debe de ser el residuo de lo que fuera que destruyera a mis compañeros druidas.' WHERE `entry`=9391;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este bosque ser un lugar espeluznante. Está lleno de más mal Juju que la Alianza, así que ten cuidado.' WHERE `entry`=9392;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Horda no es el único peligro de este bosque. No te dejes engañar por su aparente tranquilidad.' WHERE `entry`=9393;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Eres muy fuerte, $R, pero ¿crees que tienes lo que hay que tener para ser un campeón de Mazo de Guerra?' WHERE `entry`=9394;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aparta de mi camino, $R. ¡No ves que estamos en medio de una batalla!' WHERE `entry`=9395;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Excelente! Matar a un Señor del foso como Azgalor es una hazaña digna de mención. Si tuviera más tiempo, me gustaría que me contarais cómo lo hicisteis. Pero Archimonde no nos concederá ni un minuto. Mis exploradores me han informado de que la Legión está formando un ejército enorme a las afueras de nuestro campamento y de que Archimonde está muy cerca del Árbol del Mundo. Nosotros estamos evacuando con la ayuda de Lady Valiente. Debéis dirigiros hacia el Árbol del Mundo ahora mismo. El destino de nuestro mundo aún es incierto.' WHERE `entry`=9398;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aquí yace el cadáver de un Tábido. Curiosamente, la causa de muerte no parece ser una polilla mutante.$B$BQuizás tenga algo que ver con el extraño objeto que se encuentra al lado de su mano extendida.' WHERE `entry`=9401;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Saludos, $Gcampeón:campeona;! ¿Nunca te has planteado trabajar de gladiador a tiempo completo?' WHERE `entry`=9403;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Muy ocupado... ¡Muy ocupado! ¿Qué?' WHERE `entry`=9405;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Ahora que me han pegado en todas partes, qué quieres?' WHERE `entry`=9406;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Averiguaremos lo que ha causado esta masacre aunque sea lo último que hagamos!' WHERE `entry`=9407;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tu ayuda en la lucha contra la Legión Ardiente sería de lo más apreciada. Lady Jaina Valiente podría utilizar tu ayuda en las primeras líneas.' WHERE `entry`=9408;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Se acerca el momento que tanto temíamos, $C. Archimonde ha conseguido llegar hasta Nordrassil y está empezando a devorar las energías del Árbol del Mundo. Malfurion necesita más tiempo para que su plan funcione, así que tendremos que hacer lo impensable: hay que atacar a Archimonde directamente, mientras mis tropas se encargan del grueso de la Legión Ardiente que se acerca a la cima.' WHERE `entry`=9410;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$GBienvenido:Bienvenida; a las Tabernas del Tiempo!',`Text1_0_loc6`='Deja que reposen tus pies cansados. Tenemos todo el tiempo del mundo...' WHERE `entry`=9414;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¿Puedo ofrecerte algo de beber? ¡Te prometo que será de un muy buen año!' WHERE `entry`=9414;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué ves cuando miras al oeste, mortal?' WHERE `entry`=9419;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Sabes lo que veo yo? Un preludio de la invasión.$B$BSí, el maestro hizo bien en sellar los portales, pero luego se le fue un poco... ¿Cómo podría decirlo...? Digamos que perdió la concentración. Eso. La concentración.$B$BLa Legión supo bien aprovechar esa ventaja. Ahora han conseguido infiltrarse, y cada día que pasa hay más.' WHERE `entry`=9420;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Legión Ardiente. Demonios. Los veo tan claramente como tú te verías la mano delante de la cara. Bueno, los veía claramente.$B$BLa vuelta de la Legión, ha traído nuevos estratagemas. En circunstancias normales, puedo sentir la presencia de un demonio a miles de leguas.$B$B<Altruis tuerce el gesto.>' WHERE `entry`=9421;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me evitan. No puedo ver a través de sus defensas. Han colocado guardas para dificultar la detección.$B$B<Altruis señala al oeste.>$B$BNos temen porque saben que no tememos nada. El coste de un poder así es grande; los sacrificios, muchos.' WHERE `entry`=9422;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Altruis se vuelve hacia ti y sonríe.>$B$BYo no.$B$B<Altruis señala a su corcel, Nethrandamus.>$B$BDebemos detenerlos antes de que esos campamentos Forja terminen lo que quiera que hayan empezado a construir.' WHERE `entry`=9423;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Así es como se llaman esas estructuras que ensucian el paisaje. La Legión ha empleado a los Mo\'arg y Gan\'arg para construir armas de destrucción masiva en esos campamentos Forja móviles. ¡Que no descubramos lo que intentan construir aquí en Nagrand!$B$BTú serás mis ojos. Tus armas serán mi furia.' WHERE `entry`=9424;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Oh, no, tú otra vez no! ¡Vete!' WHERE `entry`=9425;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Esto es lo que he sacado de la información que conseguiste con la inspección.$B$BHay dos campamentos Forja principales: Campamento Forja: Odio, al oeste de aquí y Campamento Forja: Miedo, al suroeste. Cada campamento tiene un teletransportador de la Legión, una forja y varias torres de amparo de hechizos. Además los campamentos están custodiados por guardias de cólera que manejan cañones viles. Para terminar, cada campamento tiene un señor del foso como sobrestante.$B$BEsto no debería suponer ningún problema...$B$B< Altruis ríe.>' WHERE `entry`=9427;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Creo que sacaré algún dinerillo extra mientras los demás deciden si es buena idea ir a Oshu\'gun.$B$B¡El tiempo es oro!' WHERE `entry`=9429;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las balizas están encendidas, ¡es el momento de atacar! ¡Toma uno de esos estandartes y plántalo en el cementerio por la Alianza!' WHERE `entry`=9432;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Las Ruinas de las Agujas Gemelas no son seguras todavía! Las órdenes son entregar confalones solo cuando las dos balizas estén encendidas y la zona sea segura. ¡Toma el control de las balizas, vamos!' WHERE `entry`=9433;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Sal\'salabim te gruñe y se aleja para ingerir su brebaje espumoso.>' WHERE `entry`=9435;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿¡QUÉ QUERER VOSOTROS!?' WHERE `entry`=9440;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Fshhhhhhsssfhhhhh?' WHERE `entry`=9441;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Floon hace una serie de ruidos y chasquidos extraños.>' WHERE `entry`=9442;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Hablas en serio? ¡Ya me ha matado! ¿Qué más podría querer?' WHERE `entry`=9443;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gracias de nuevo, $N, por ayudarme a descubrir lo que pasó en el matorral.' WHERE `entry`=9444;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mi trabajo consiste en ocuparme de estas pobres almas. Acepto la ayuda de cualquier corazón bondadoso, ya sea Aldor o Arúspice.$B$BNecesitamos a tanta gente que no le diría que no ni a un ogro si supiera cómo hacer un torniquete. La política de la ciudad me importa bien poco.' WHERE `entry`=9451;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No puedo creerlo. ¡Este etéreo no tiene ni un solo ojo de basilisco!$B$B¡Y su líder tiene la desfachatez de llamarse \'el Príncipe Contrabandista\'!' WHERE `entry`=9452;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida;, $N. Somos los Aldor, sacerdotes de la luz y siervos de Sha\'tar.$B$BPuedes quedarte con nosotros todo el tiempo que quieras.' WHERE `entry`=9457;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; al Bastión Allerian, $C.' WHERE `entry`=9471;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cuidado, $C. El bosque está lleno de peligros ocultos y de enemigos.' WHERE `entry`=9472;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Esto es sencillamente horrible.$B$BNo me importan nada nuestras propias incomodidades aquí, ¡pero debemos hacer algo para aliviar el dolor de los muertos de El Vertedero de Huesos!' WHERE `entry`=9477;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Estás aquí para ayudar a los Aldor?' WHERE `entry`=9478;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si esperas algo parecido a una bienvenida oficial, siento decepcionarte, $C; aquí no seguimos demasiado las tradiciones. No nos gustan los artificios.' WHERE `entry`=9481;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Una nota destaca de los demás pedazos de pergamino que están clavados en este cartel de "Se busca". Un llamamiento para matar a los Atizahuesos de El Vertedero de Huesos.' WHERE `entry`=9482;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No, que no te sepa mal. Me pasa mucho.$B$B<Taela parece entre divertida y molesta.>$B$BSoy una ALTA elfa, no una elfa de sangre. No te preocupes, no voy a chuparte toda la magia.' WHERE `entry`=9484;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Atizahuesos... ¡Los detesto a todos!$B$BSi no hubiera perdido mi ojo ni me salieran a veces esos hechizos tan débiles, estaría ahí fuera cargándomelos a todos.' WHERE `entry`=9485;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay un gran bosque ahí fuera, $C. Deja que el chamán juegue con los espíritus y el guerrero con las espadas. En Terokkar haré fortuna.' WHERE `entry`=9487;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Malukaz no para de hablar de la importancia histórica de este bosque, pero yo no puedo permitirme el lujo de preocuparme por la historia hasta que no se arregle el presente.$B$BNo digo que su trabajo aquí no sea importante, pero alguien tiene que preocuparse de que funcionen las defensas y la logística del fuerte.' WHERE `entry`=9488;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hasta que viajé por Nagrand y llegué al bosque, pensaba que una gran parte de nuestra cultura original se había perdido. Me alegra comprobar que estaba equivocado.$B$B¡Todavía queda mucho por hacer aquí!' WHERE `entry`=9489;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La apertura del Portal Oscuro, trajo noticias de la suerte que corrió mi pueblo. En cierto modo, mi exilio evitó que compartiese su caída, pero ver a los Errantes darlo todo por Kael\'thas...$B$BNunca imaginé que los que fueron mis hermanos podrían hacer algo así.$B$BLa vuelta a casa con la que tanto soñé nunca tendrá lugar. Este bosque es el único hogar que me queda.' WHERE `entry`=9490;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cuado llegué aquí, esperaba estar a la altura de la reputación de la ciudad.$B$B<Ros\'eleth mira a su alrededor.>$B$BLa mayoría de los jóvenes superan la fase de "quiero ser un Errante" en cuanto alguien los acepta como aprendices. Entonces pasan a la fase "yo quiero ser magister".$B$BPara serte sincera, yo todavía estoy superando lo de "quiero ser Errante". Nadie quiere ser costurera de mayor, pero alguien tiene que hacer lo que se ponen los Errantes para no andar desnudos. Eso es lo que solía decirme mi madre.' WHERE `entry`=9492;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Has visto cómo me guiña el ojo Ros\'eleth cada vez que voy a recoger ropa o algún sombrero? Cuando las cosas se calmen por aquí, me la voy a llevar de paseo al bosque.$B$BAhora sí que echo de menos todas esas tabernas de Ventormenta.' WHERE `entry`=9493;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Kaide te hace callar.>$B$B¡Silencio, o atraerás a los orcos y a la Alianza hacia nosotros, torpe patán!$B$BExacto, asiente con la cabeza, y cuando acabemos de hablar te irás y olvidarás que me viste aquí.' WHERE `entry`=9494;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puede que estemos juntos en esto, pero eso solo hace que sea más necesaria la discreción. Te lo repito: ten cuidado con lo que dices y con lo que haces.$B$BTe garantizo que no somos los únicos aquí fuera.' WHERE `entry`=9495;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sí, ¿qué quieres? ¿No ves que estoy ocupada? ¡El Consejo de la Sombra no me paga por hora de trabajo!' WHERE `entry`=9497;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué parece que estoy haciendo? Estoy arreglando las cosas para que podamos instalarnos. Y yo sola, además.$B$BCon una base de operaciones en el bosque será más fácil seguir la pista a lo que traman los druidas de Shattrath, Allerian, Rompepedras y Cenarion. Nuestra base en Auchindoun es demasiado poco práctica para eso.$B$BEspera un momento, ¿te conozco?' WHERE `entry`=9498;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Sí? Rayos. Ahora voy a tener que empezar desde el principio.' WHERE `entry`=9502;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Entrenando! Pronto seré lo suficientemente buena como para unirme a los otros invocadores y entrar en el Laberinto de las Sombras de Auchindoun.$B$B¡Espero poder invocar algo grande! ¡Quizás algo tan grande como Murmur, aunque no perdería el control sobre él!' WHERE `entry`=9503;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Y qué es exactamente lo que puedo hacer por ti? ¿No te das cuenta de que estoy muy ocupada discutiendo cuestiones importantes con nuestro ilustre líder?$B$BDate prisa en hablar o haré que te den una paliza, gusano.' WHERE `entry`=9505;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Bueno, yo solo estaba informando a nuestro líder sobre los avances y sobre como todo sale según lo previsto. Solo tardaremos unas semanas más en tenerlo todo arreglado.$B$BPuede que nos convenga que el Consejo de la Sombra contrate a orcos para el pueblo. Así alegaremos derecho ancestral y podremos trabajar en la sombra.$B$BUn momento. ¿Te conozco? ¿Cómo te llamas?' WHERE `entry`=9506;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, joven $R.' WHERE `entry`=9507;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, $Gquerido:querida;. Soy Geyah, matrona de los Mag\'har.$B$B<La Abuela Geyah tose.>$B$BSi estás aquí, deduzco que es porque el portal se ha abierto. La mayoría de los orcos son demasiado jóvenes o están demasiado cansados como para acordarse del Portal Oscuro.$B$B<La Abuela Geyah señala a los orcos de su alrededor.>$B$BMuchos de ellos eran huérfanos o estaban a punto de convertirse en huérfanos. Sí, Puño Negro no querría que fuese de otra forma. Aparta al sano del enfermo. Solo sobrevivirán los fuertes... Eso dicen.' WHERE `entry`=9508;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Han crecido aquí. Los protegemos del peligro. Los hemos curado y alimentado lo mejor que hemos podido. Muchos murieron, pero muchos, muchos otros han crecido sanos y fuertes. Como Garrosh...$B$B<La Abuela Geyah suspira.>$B$BPerdió su corazón, ¿sabes? Me dice que soy su equilibrio. Que sin mí, la sed de sangre que consumió a su padre hará con él lo mismo.' WHERE `entry`=9509;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me gustaría saberlo, $Gquerido:querida;. No te escuchará. Igual que no me escucha a mí. En realidad, él es la razón por la que sigo aquí. Por eso no me fui con madre Kashur. Debe encontrar su corazón. Nuestro pueblo no sobrevivirá al invierno sin un líder.' WHERE `entry`=9511;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Abuela Geyah sonríe.>$B$BSoy vieja. Pronto llegará mi hora. Cuando ya no esté, mi espíritu pasará al reino de los espíritus. Allí me encontraré con mis ancestros. Desde allí protegeré a nuestro pueblo.$B$BCuando Garad vivía, el deber de madre Kashur era el que tengo yo hoy.' WHERE `entry`=9512;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El deber de una madre del clan es escuchar. Escuchar el viento, el agua, el fuego y la tierra. Conseguir información para el clan del mismo modo que otros consiguen pieles para calentarnos en invierno. Yo me comunico con los espíritus... En mis sueños, madre Kashur me habla. Cuando deje este mundo, hablaré con Drakia y Celestine de este mismo modo.$B$B<La Abuela Geyah sonríe.>' WHERE `entry`=9513;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aquí hay más artefactos incendiarios, $N. Ten cuidado con ellos. Si no los usas adecuadamente, pueden provocar una catástrofe.' WHERE `entry`=9515;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Voy a averiguar lo que ese siniestro grupo está haciendo el Poblado Grangol\'var!' WHERE `entry`=9517;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hay que averiguar lo que ese siniestro grupo está haciendo en el Poblado Grangol\'var!' WHERE `entry`=9520;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tenemos una misión que cumplir, $Gamigo:amiga;: no dejarnos sorprender por nadie.$B$B¡Y eso significa que hay que actuar antes que ellos!' WHERE `entry`=9521;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='He tenido que hacer algo mal en algún sitio para que me destinaran aquí.' WHERE `entry`=9522;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Quién te crees para aparecer por aquí y hablarme sin más? Soy el líder del Consejo de la Sombra en este pueblo. ¡Fuera de mi vista!$B$BSi te vuelvo a ver, seré la última persona que veas, ¿entendido?$B$BQuizás te gustaría que te enterraran en el laberinto de Auchindoun, o te sacrificaran a Murmur.' WHERE `entry`=9524;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Encontrarás a nuestro instructor alquimista en El Arca de las Luces. Ve con la Luz.' WHERE `entry`=9527;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestros maestros de batalla están en el Arca de las Luces. También encontrarás a ""Miglik Borrón"" junto al cuidador Breel; dice ser el representante del círculo de lucha de este mundo.' WHERE `entry`=9531;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué instructor buscas?' WHERE `entry`=9533;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Druida? Ah... Shalannius. Buscas a Shalannius. Encontrarás a ese elfo de la noche fuera de El Exodar, al lado del atracadero de Valaar.' WHERE `entry`=9534;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestros encantadores están en la Sala de Cristal, enfrente de los joyeros. Qué la luz de acompañe.' WHERE `entry`=9535;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Así que quieres ayudarnos a reparar el gran Exodar? He oído que pasarán años antes de que El Exodar vuelva a estar operativo. Tienes dos horas. Es broma. Nuestros ingenieros están en las partes bajas de la Grada de los Mercaderes. Ve con la Luz.' WHERE `entry`=9536;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Por la Luz! Es una bendición encontrar a alguien que se preocupe por el bienestar de los demás. En un momento como este, necesitamos todas las manos posibles. Nus te puede adiestrar en primeros auxilios. Está cerca de la cueva de cristales de la Sala de Cristal.' WHERE `entry`=9537;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Encontrarás a Erett el instructor de pesca en la Sala de Cristal, cerca de las aguas bendecidas por la Luz.' WHERE `entry`=9538;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Funaam, el maestro de la hermandad, está cerca de la entrada de la Grada de los Mercaderes.' WHERE `entry`=9539;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestro herborista, Cemmorhan, está cerca del muro trasero de El Arca de las Luces.' WHERE `entry`=9543;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Tienes el nivel suficiente para aprender lo que te puedo enseñar?' WHERE `entry`=9546;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Encontrarás a nuestros instructores de minería en la parte trasera de la Grada de los Mercaderes.' WHERE `entry`=9552;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué tipo de instructor de profesión estás buscando?' WHERE `entry`=9555;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay varios instructores de chamanes en la Sala de Cristal. Puede que quieras ver al clarividente Nobundo. Muéstrate $Gcompasivo:compasiva; con los Tábidos. Que la Luz te acompañe.' WHERE `entry`=9556;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedes encontrar a Arthaid, nuestro maestro de establos, fuera del Centro de Cuidadores. Se encuentra en la planta superior de la cabina central, cerca de la entrada principal a El Exodar.' WHERE `entry`=9558;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hace mucho tiempo, llevábamos monos de una sola pieza. Gracias a nuestros modernos sastres, ya no tenemos que vestir algo tan poco práctico. Puedes encontrar a esos diseñadores de vanguardia en la Grada de los Mercaderes.' WHERE `entry`=9559;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Quieres ser un gran guerrero? La guerra no te hace grande. Aun así, encontrarás nuestros instructores de guerreros en el Bancal del Guerrero, por encima del resto de la Grada de los Mercaderes.' WHERE `entry`=9562;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; a al Banco Real de Cambio. ¿Qué deseas?',`Text1_0_loc6`='Saludos. ¿En qué puede ayudarte el Banco del Intercambio Real?' WHERE `entry`=9563;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gracias por escoger el Banco de Lunargenta. ¿En qué puedo ayudarte?',`Text1_0_loc6`='$GBienvenido:Bienvenida; al Banco de Lunargenta. ¿Quieres acceder a tu cuenta?' WHERE `entry`=9564;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Quieres mejorar tu habilidad con las armas? El maestro de armas está en nivel más elevado de la Grada de los Mercaderes, en el Bancal del Guerrero.' WHERE `entry`=9565;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sí, $Tciudadano:ciudadana;, ¿qué asunto importante puede precisar mi atención?' WHERE `entry`=9566;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola. ¿Puedes ayudarme a salir de aquí?' WHERE `entry`=9568;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Sí? ¿Qué quieres?' WHERE `entry`=9574;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Supongo que no traes noticias de las minas, ¿verdad?' WHERE `entry`=9575;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No hay nada que ver aquí. Circula.',`Text1_0_loc6`='Esto no te concierne. Sigue tu camino.' WHERE `entry`=9577;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No te sorprendas tanto por verme aquí, $R. Kurz el Revelador predijo la llegada de los draenei y su alianza con mi pueblo.' WHERE `entry`=9583;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Cuando saludas al embajador Frasaboo en el idioma fúrbolg, sonríe.>$B$BTú debes de ser de quien habla la profecía. Es un gran honor conocerte.$B$BSoy el embajador Frasaboo, representante del gran jefe Semprepino en Avanzada de Sangre.' WHERE `entry`=9584;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Durante mis años como miembro de la Liga de Expedicionarios, he viajado por todo Azeroth, pero nunca he visto nada como Bruma de Sangre.' WHERE `entry`=9586;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Por favor, no me digas nada. No quiero tener más problemas con ellos.' WHERE `entry`=9587;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Acabamos de llegar aquí y ya hemos enterrado a demasiados amigos.' WHERE `entry`=9591;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si no has venido a ayudar a la causa, vete de aquí.' WHERE `entry`=9595;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Con tu ayuda, ¡quizás podamos ganar!' WHERE `entry`=9596;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Molino Tarren está más allá de estos árboles. La conmoción de Durnholde debería darnos algo de tiempo para encontrar a Taretha antes de que todo el país esté inundado de soldados humanos. Echemos un vistazo a ese granero que está cerca y luego dirijámonos al pueblo.' WHERE `entry`=9597;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedo instruirte en el oficio del desollador. ¿Te interesa?' WHERE `entry`=9599;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gracias por ayudar a Thrall a escapar, amigos. Ahora, espero que también podáis ayudarme a mí. Me desperté esta mañana atrapada en esta prisión mágica. Un extraño zahorí me dijo que me quedaría un tiempo aquí.' WHERE `entry`=9610;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sí, amigos. Ese hombre no era un zahorí de Dalaran. Sus ropas y acento no me resultaban familiares. Todo lo que decía es el tiempo esto, el tiempo lo otro... Tendréis que encontrar al zahorí y convencerle para que me suelte.' WHERE `entry`=9613;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me alegro de que Taretha siga viva. Debemos encontrar la forma de liberarla de sus ataduras mágicas.' WHERE `entry`=9614;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Como representante del Bajo Arrabal, estoy aquí para exponer nuestro caso, por el que Auchindoun debe ser evacuada de su infestación del Consejo de la Sombra. Esto es, ¡si queremos albergar esperanzas de tener un futuro y evitar la posibilidad de un cataclismo mayor que la creación de El Vertedero de Huesos!$B$BTodas nuestras gentes han sufrido ya demasiado. ¡Estoy aquí para encargarme de que ese sufrimiento se detenga!' WHERE `entry`=9615;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Si quieres descansar aquí, solo tienes que preguntar!' WHERE `entry`=9618;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gracias por tu ayuda con el bálsamo, $N. Cuando me haya calmado el dolor, probaré lo lejos que consigo llegar con la pierna así...' WHERE `entry`=9619;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:bienvenida; a Garadar, $Gforastero:forastera;. ¿Buscas un lugar donde descansar?' WHERE `entry`=9620;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ah... ¿Otro chamán que busca la iluminación? ¿Acaso los espíritus no hablan claro? ¿Quieres que te ayude a ver lo que te espera en tu camino?' WHERE `entry`=9621;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Soy Skaltesh, guía de los chamanes que buscan la iluminación... pero incluso aquellos que siguen diferentes caminos, pueden aprender un par de cosas. ¿Deseas ver?' WHERE `entry`=9622;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Bebe de este vial y la niebla se disipará durante un rato. Ve con los espíritus, $Gamigo:amiga;.' WHERE `entry`=9623;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡El Gran Padre Invierno deja juguetes para todos bajo el árbol de Forjaz durante el festín del Festival de Invierno!' WHERE `entry`=9626;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mi hermana Silanna es muy ingenua, no entiende el peligro creciente que habita en estos bosques. Pero de todas formas, en cuanto cargue el carro, nos vamos a la ciudad. $B$BParece ser que tengo más bolsas de las que necesito. Quizás a ti te vengan bien.' WHERE `entry`=9630;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No tengo mucho tiempo para hablar, $R. A menos que quieras ayudarme con un problemilla que tengo.' WHERE `entry`=9631;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te agradezco muchísimo tu ayuda con las minas. Ahora, solo hay que convencer a los mineros para que vuelvan al trabajo.' WHERE `entry`=9632;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Isfar suspira.>$B$BUn par de casas en los árboles no hacen un hogar, pero ha sido una gran mejora en las Salas Sethekk.$B$BAunque en Shattrath estaba perfectamente bien, no soy capaz de regresar a mi verdadero hogar en Skettis sin mi hermana. Está atrapada en las Salas Sethekk. La tiene prisionera nuestro hermano mayor.' WHERE `entry`=9634;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ahora es nuestra oportunidad! ¡Sácame de aquí!' WHERE `entry`=9636;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Círculo de los Retos era antaño un lugar de honor y gloria. Pero entonces llegó el Cártel Bonvapor con sus carros llenos de oro y sus plumas más afiladas que hachas de orco...' WHERE `entry`=9637;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los Sethekk se creen los verdaderos y más fieles seguidores del maestro de todos los arakkoa. No me preguntes por el maestro, $R, porque sus misterios son solo para los alados.$B$BLos Sethekk interpretaron la destrucción de Auchindoun como la llegada de nuestro maestro a este planeta. Partieron hacia las ruinas llevándose las reliquias de nuestro gran héroe, Terokk.$B$BSiguen buscando al maestro en las ruinas del templo, pero he conseguido ver la verdad que se esconde tras sus mentiras. Ya no soy uno de ellos.' WHERE `entry`=9638;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La última vez que estuve en las Salas, el Rey Garra Ikiss llevaba la pluma de Terokk con él, como símbolo de autoridad.$B$BMi hermano, Tejeoscuro Syth, llevó la máscara de Terokk durante nuestros rituales.$B$BY, con respecto a la saga de Terokk, no hay forma de saber exactamente dónde está, pero generalmente la tienen en el centro de uno de sus círculos de runas en las grandes salas selladas.' WHERE `entry`=9639;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Rey \'Garra Ikiss\' es uno de los líderes de los Sethekk, junto con mi hermano, Tejeoscuro Syth.$B$BFueron ellos quienes guiaron el éxodo de los Sethekk desde Skettis hasta las Ruinas de Auchindoun. Al principio, Ikiss era un líder carismático, pero la locura se ha apoderado de él.$B$B¡Ahora se proclama a sí mismo como la reencarnación de Terokk! Realmente espera que los arakkoa se arrodillen ante él como su verdadero gobernante. ¿Te puedes creer su arrogancia?' WHERE `entry`=9640;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es el héroe más glorioso entre los arakkoa.$B$BDicen que, en la antigüedad, construyó Skettis con sus propias garras. Era nuestro mayor campeón y defensor, pero nos dejó cuando dejamos de venerar a Rukhmar.$B$BEmpezamos a adorar a un nuevo maestro, uno que nos prometió increíble poder y estatus en el cosmos.$B$BAlgunos dicen que Terokk y Rukhmar son uno y que nos abandonó cuando nosotros le abandonamos a él.' WHERE `entry`=9641;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Mucho brazo y poco seso! Así son los peones. Trabajan bien cuando eres capaz de motivarlos, pero de vez en cuando necesitan un poco más de "motivación", ¿entiendes?' WHERE `entry`=9642;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Te gusta algo de lo que ves, $C?' WHERE `entry`=9643;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si tienes dinero, tengo lo que necesitas.' WHERE `entry`=9644;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Echa un vistazo y avísame si te interesa algo.' WHERE `entry`=9645;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tómate tu tiempo, mira bien las armas. También reparo armaduras, si necesitas ese servicio.' WHERE `entry`=9646;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tengo justo lo que necesitas para lanzar tu hechizo.' WHERE `entry`=9647;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Estás buscando algún componente o suministros de veneno? ¡Pues has venido al lugar apropiado!' WHERE `entry`=9649;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vendo lo que los artesanos necesitan. Solo objetos básicos, nada especial.' WHERE `entry`=9650;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Sabes dónde estás? La mayoría de los mercaderes están cerca del centro de la ciudad.' WHERE `entry`=9652;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo traigo fino y lo traigo gordo. Bueno, casi todo gordo, más bien.' WHERE `entry`=9653;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Me estoy volviendo loco! ¡No consigo alcanzar esos malditos huevos!' WHERE `entry`=9654;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es lamentable que, entre nuestros muchos enemigos en la Península del Fuego Infernal, se encuentren los orcos de la Ciudadela del Fuego Infernal.$B$BPero... ¿quién mejor para desafiar a la Horda que... los orcos?' WHERE `entry`=9657;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Me alegro de verte de nuevo, $N! ¡Unos pocos ajustes a la trayectoria del salto y será perfecto!' WHERE `entry`=9659;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedes encontrar al Gran Padre Invierno justo delante de la ribera de Forjaz. El árbol que está junto a él es donde se entregan los regalos.' WHERE `entry`=9686;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ve a la Sala Mística de Forjaz y habla con los druidas elfos de la noche de allí. Te podrán transportar a Claro de la Luna para celebrar con ellos el Festival Lunar.' WHERE `entry`=9688;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La celebración del Festival Lunar está en pleno apogeo en el Claro de la Luna. Puedes ir caminando, pero los druidas del Enclave Cenarion están ofreciendo transporte.' WHERE `entry`=9692;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='He viajado por muchísimos mundos, pero nunca había visto nada tan entrañable como las celebraciones del festín del Festival de Invierno. Deberías ir a Forjaz, la gran ciudad de los enanos, y verlo con tus propios ojos.' WHERE `entry`=9694;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los druidas de Azeroth están celebrando una gran fiesta en el Claro de la Luna. Si encuentras a los juerguistas del Festival Lunar en la ciudad, podrán llevarte allí.' WHERE `entry`=9696;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si encuentras a juerguistas del Festival Lunar en Lunargenta, podrán transportarte a Claro de la Luna para que participes en la fiesta.' WHERE `entry`=9700;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Encontrarás al Gran Padre Invierno en el centro del Valle de la Fuerza. Busca el árbol.' WHERE `entry`=9702;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La celebración del festín del Festival de Invierno es fuente de regocijo en los largos meses fríos. Su corazón está en Orgrimmar.' WHERE `entry`=9706;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Todavía estamos celebrándolo, pero parece que la fiesta del festín del Festival de Invierno ya se ha terminado. En Orgrimmar es donde tienen la verdadera celebración.' WHERE `entry`=9710;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Para muchos Renegados, la luna representa lo que en algún momento representó el sol. Pero si quieres unirte a las celebraciones, ve a Claro de la Luna, en Kalimdor. En la sala del trono, encima de Entrañas, hay druidas que te pueden transportar hasta allí.' WHERE `entry`=9712;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tú... ven aquí. ¿No te parece que estas espadas son de gran calidad? Seguro que quieres comprarme alguna. No encontrarás armas forjadas con tanta artesanía ni dedicación en ningún otro lugar, $R.' WHERE `entry`=9713;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué miras, $R?' WHERE `entry`=9714;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ve a la Sala Mística de Forjaz y habla con los druidas elfos de la noche de allí. Te podrán transportar a Claro de la Luna para celebrar con ellos el Festival Lunar.' WHERE `entry`=9715;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La mejor fiesta del Festival Lunar se celebra en Claro de la Luna. No vayas hasta allí andando; los druidas del Valle de la Sabiduría te pueden llevar.' WHERE `entry`=9716;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedes encontrar al Gran Padre Invierno justo delante de la ribera de Forjaz. El árbol que está junto a él es donde se entregan los regalos.' WHERE `entry`=9717;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Encontrarás al Gran Padre Invierno en el centro del Valle de la Fuerza. Busca el árbol.' WHERE `entry`=9718;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las agrestes praderas de Nagrand son el mejor lugar para ir a cazar.' WHERE `entry`=9729;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Te gusta el pescado, $R? Tengo pescado. Si te gusta el pescado fresco, fresquísimo, ven a ver lo que tengo.' WHERE `entry`=9730;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nunca mires a esos Tábidos directamente a los ojos. ¡Creo que te pueden succionar el alma si quieren!' WHERE `entry`=9733;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Quítate de en medio, $Gniñato:niñata;! ¿No ves que estoy entrenando? Es cierto; el tuyo será el siguiente campeón de Mazo de Guerra.$B$BSí, estoy listo para El Círculo de Sangre de las viejas Ruinas Riecráneos. Ni lo intentes, $Gniñato:niñata;. Eres demasiado débil. Gurgthock, el comentarista del círculo no te dejará ni acercarte al foso. Y no digamos luchar.$B$BY ahora, golpea antes de que te golpee.' WHERE `entry`=9734;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vaya, vaya. Qué grande y fuerte eres... ¿Crees que eres lo suficientemente fuerte como para entrar en El Círculo de Sangre y enfrentarte al campeón Mazo de Guerra?$B$B<El luchador del foso se ríe.>$B$BNo lo creo, pero si alguna vez quieres poner a prueba tu fuerza, El Círculo de Sangre está en el norte, justo en las montañas que dividen Nagrand y la Marisma de Zangar. Gurgthock es quien se encarga del espectáculo en las viejas Ruinas Riecráneos y también es quien entrega los premios. Y, hablando de premios, ¡he oído que los de esta temporada son increíbles!' WHERE `entry`=9735;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El interrogador está intentando averiguar lo que han venido a hacer estos elfos de sangre a Nagrand. Por lo que he oído, buscan polvo de cristal de Oshu\'gun, la montaña diamante del oeste.$B$BSeguro que es para algún plan retorcido o para algún brebaje...$B$BSi quieres ayudarnos a detener a esos elfos de la sangre, ve hacia el noroeste, a Halaa. ¡Los soldados de la Mano de Argus se están enfrentando a los elfos de sangre! ¡Vete y demuéstrales lo que vales!' WHERE `entry`=9738;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Los draenei han vuelto! Este grupo se enfrentó a una patrulla con dos de mis mejores caballeros lobo. Por supuesto, salieron victoriosos de esa escaramuza.$B$BNo sé muy bien lo que está pasando, pero según mis patrullas, todo Halaa está lleno de draenei y elfos de sangre. No me gustan mucho los elfos de sangre, pero si hay algo que de verdad odio, son los draenei. Si ves a draenei o a alguno de sus aliados en Halaa, asegúrate de patearles el culo de mi parte.$B$BHalaa está al oeste. Por si querías ir ahora, digo.' WHERE `entry`=9739;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ya tenemos el control de las Ruinas de las Agujas Gemelas! Salid ahí afuera y defended lo que es nuestro.' WHERE `entry`=9749;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ya tenemos el control de las Ruinas de las Agujas Gemelas! Salid ahí afuera y defended lo que es nuestro.' WHERE `entry`=9750;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El cazador de las Sombras Denjai usa este tablón para publicar anuncios importantes y noticias de recompensas sobre enemigos de Zabra\'jin.' WHERE `entry`=9752;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ahh, ¿no has podido resistirte, eh? ¿Quieres ser mejor, más fuerte y más $Grápido:rápida;? Pues eso es lo que hacen cada uno de estos amuletos. ¡Pasa y compra una de estas pequeñas maravillas! ¡No encontrarás nada más barato!' WHERE `entry`=9755;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida;. ¿Descansarás aquí? ¿Te apetece comer algo?' WHERE `entry`=9766;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay que descubrir lo que se esconde tras el misterio del cristal gigante que cayó de los cielos en Cresta Cielo Estrellado.' WHERE `entry`=9769;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Algunas veces, un enfoque más directo es la solución.' WHERE `entry`=9770;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si te captura la Legión, diles \'Xar il romath da tidesbi.\' Te matarán enseguida por haber insultado a su dios y te librarás de una tortura horrible o de algo peor...' WHERE `entry`=9771;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estamos aquí para proteger los intereses de Gadgetzan en Un\'Goro y eso no te incumbe.' WHERE `entry`=9774;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Península del Fuego Infernal es un reino devastado y despiadado con sus habitantes. Repara y haz acopio de víveres y material necesario antes de salir de expedición.' WHERE `entry`=9775;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Habla bajo y mantén los ojos abiertos, $N. Estamos rodeados de enemigos.' WHERE `entry`=9777;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sigue tu camino; a menos que tengas algo que ver con la flota.' WHERE `entry`=9801;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es una preciosidad, ¿a que sí? Cielo\'ree, el fiel grifo de nuestro comandante. Kurdran ha desaparecido, ¿sabes? No está muerto, no; eso no lo sabremos hasta que encontremos el cuerpo. Y todavía entonces, habrá quienes no lo crean. Mientras tanto, Cielo\'ree está con nosotros, esperando a que vuelva.' WHERE `entry`=9805;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='\'La Tormenta Abisal\' no ha sido siempre como lo que ves ahora.$B$BHubo un tiempo cuando esta tierra destrozada se conocía con el nombre de las Llanuras de Farahlon. Muy poco de lo que quedaba de esos días ha sobrevivido al mal que se ha instalado aquí.' WHERE `entry`=9826;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$C, no ataques! Es solo un disfraz. Soy una espía que trabaja para la coalición del Bajo Arrabal fuera de la Ciudad de Shattrath.$B$B¡Espero que estés aquí para ayudar!' WHERE `entry`=9827;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El archimago Vargoth me confió su bastón cuando me apresuró a huir del ataque de los elfos de sangre a la Torre Violeta. Hice lo que me pidió, esperando poder refugiarme en el viejo asentamiento draenei de Arklon.$B$BPero no fue así. Había dado de pleno con una fuerza de la Legión Ardiente. Los demonios me robaron el bastón, aunque conseguí escapar con vida.$B$BAunque mi maestro nada sabe de mi fracaso, sigue siendo un motivo de gran vergüenza para mí.' WHERE `entry`=9828;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vaya. Mira qué han traído los vientos de la nada. Entra, siéntate y descansa. no cobramos mucho; seguro que puedes permitírtelo.' WHERE `entry`=9832;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hace mucho tiempo, en una dimensión del cosmos inconcebible para la mente humana, nació -si es que se puede usar este verbo- un ser con un poder tal que su entrada en la existencia resquebrajó la realidad a su alrededor.$B$BConocido como Murmullo, era la esencia del sonido. ¡Oírlo hablar significaba conocer la muerte eterna!' WHERE `entry`=9834;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las mohosas páginas del códice parecen girar por arte de magia y centran tu atención en un capítulo concreto sobre horrores pasados que ningún ojo mortal debería percibir.$B$BEl libro se abre en una página que, de alguna forma, parece ser la \'correcta\', aunque al mismo tiempo la equivocada.' WHERE `entry`=9836;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Y cómo los poderosos caían temblando a sus pies, temerosos de que pudiera dirigir sus palabras hacia ellos.$B$BY, aun así, esos charlatanes, esos falsos adoradores de una religión no existente, pretendían negarse a lo inevitable, en un vano intento de controlar a su \'dios\'. Indiferente y, probablemente sin darse cuenta de su presencia, Murmullo bostezó, y ellos cayeron en la inconsciencia.$B$BPero, de alguna forma, uno de ellos consiguió sobrevivir y, en su locura, encontró la forma de traer a Murmullo al mundo.' WHERE `entry`=9837;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un mortal con un increíble conocimiento arcano y oscuro que nadie podía superar; desarrolló un método para aumentar su invocación mediante la captura y utilización de almas.$B$BCivilizaciones enteras fueron exterminadas mediante este dispositivo de almas para alimentar el ritual mediante el que él y sus conspiradores traerían a Murmullo a su mundo.' WHERE `entry`=9838;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Y sí, invocaron a Murmullo.$B$BSe utilizaron poderosas magias para contenerle y silenciarle, gracias al constante goteo de almas de las que se alimentaban. Y, ni aun así, fueron capaces de controlar a la bestia, ni de someterla a su voluntad.$B$BEstos mortales comenzaron a pensar en diferentes estrategias que, una tras una, fracasaron. Y, al hacerlo, debilitaron los rituales, dando a Murmullo, sin querer, un atisbo de libertad.$B$BEra todo lo que necesitaba.' WHERE `entry`=9839;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Solo este libro sobrevivió a la catástrofe que destruyó el mundo en el que se encontraba. Aquellos hombres orgullosos e inconscientes pensaron que controlaban a un ser cósmico de poder inimaginable.$B$B¿Acaso tú, dueño del códice, eres como ellos?$B$B¿Has tramado algún plan para invocar a Murmullo o a alguna otra entidad de las mencionadas en esas páginas impías?$B$BYa lo veremos.' WHERE `entry`=9840;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Eh! Me alegro de ver caras nuevas. No tengo muchas visitas y los técnicos no son muy habladores.$B$BApuesto a que te diriges al Área 52, ¿verdad?' WHERE `entry`=9841;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Jefe de cohetes Fuselaje, comandante de B.U.M.: Baqueteados Ultraorbitales Mecanizados, a tu servicio.$B$B¿Están todos los sistemas en marcha? Parece que el cohete abisal X-52 todavía necesita mucho trabajo.$B$BVamos, chicos... ¡Estamos a menos CERO segundos!' WHERE `entry`=9844;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:bienvenida; al Bastión Allerian, $N.' WHERE `entry`=9845;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Se trata de un trabajo muy delicado, ¿sabes? No puedo estar sometida a presión, o algo acabará rompiéndose.$B$B¡Como la cabeza de Fuselaje!$B$B¿Te estás preguntando sobre mis robots? Siempre estoy trabajando en el nº I. El nº. II y yo conseguimos la capacidad de sentir durante un milisegundo antes de que su condensador cerebral explotase y se volviese loco.$B$BDecidí saltarme el III y IV, y me fui derecha al nº. V. ¡Mi ojito derecho!' WHERE `entry`=9846;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ten cuidado en las ruinas, $C. Los espíritus del Kirin Tor que encontrarás, no te reconocerán como $Gamigo:amiga;.$B$BEl custodio y yo somos los únicos cuyos espíritus mantenían un vínculo tan fuerte con este lugar como para conservar nuestra identidad en la muerte.' WHERE `entry`=9847;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Kirin\'Var sobrevivió a 20 largos años de incontables asaltos orcos, pero no pudo sobrevivir a las fuerzas de Kael\'thas. Un hombre que se hacía llamar miembro del Kirin Tor nos asesinó a todos, menos a uno.$B$BEl archimago Vargoth sobrevivió al ataque, así que Kael\'thas le encarceló en la Torre Violeta y protegió la Torre con una magia que prohibía el paso a cualquier miembro del Kirin Tor.' WHERE `entry`=9848;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Lees el cartel de Se busca para Grek?$B$B¡Grek quiere ayudar!' WHERE `entry`=9853;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué quieres? Siéntate y escoge uno.$B$BSoy Botas, mixólogo jefe de B.U.M. y barman extraordinario.$B$BSi lo que necesitas es un toque mágico, el viejo Botas te lo dará.' WHERE `entry`=9863;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Eres el corresponsal de la Voz de Gadgetzan? Si me sigues, te enseñaré mi nuevo invento...$B$B<Le dices a la maga que no eres el corresponsal que esperaba.> Está bien. Si necesitas componentes, búscalos. Rapidito. Estoy esperando a alguien.' WHERE `entry`=9868;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡¿Radiaciones?!$B$BNo, eso no tiene que preocuparte. Rayos, hay que ver las cosas que se cree la gente hoy en día.$B$BEntonces, ¿qué te parecen nuestras operaciones ahí abajo? ¡Es una obra de arte! Ver para creer.' WHERE `entry`=9869;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay dos notas enormes en el tablón; una es de Papá Ruedas y, la otra, del jefe de cohetes Fuselaje.$B$BLa de Papá parece ser un anuncio en el que solicita la pieza de una máquina. ¡La del jefe de cohetes guarda relación con una recompensa por la cabeza de un enorme gigante montés!$B$BLos detalles de ambas están escritos con caligrafía goblin muy pequeña, que, por supuesto, es prácticamente ilegible.' WHERE `entry`=9872;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay varias notas destacadas en el tablón.$B$BSin embargo, la letra pequeña indica que no estás $Gpreparado:preparada; para ninguna de las tareas. ¡Definitivamente deberías volver más tarde!' WHERE `entry`=9873;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡¿Trituradoras?! Ya no sirven. ¡Están muy pasadas de moda!$B$BSi vamos a construir cohetes... si vamos a hacer que el X-52 despegue... ¡necesitamos la mejor tecnología!' WHERE `entry`=9875;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Soy Khadgar, antiguo aprendiz del guardián caído, Medivh. Seguro que has oído su nombre antes.$B$BYo fui quien descubrió que Medivh había estado poseído por el Maligno, Sargeras, y que había abierto El Portal Oscuro entre este mundo y Azeroth. Si bien honraba a mi maestro, me vi obligado a matarle y desterrar su alma torturada.' WHERE `entry`=9876;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo luché junto a Lord Anduin Lothar cuando conseguimos expulsar a la Horda de nuestras tierras, en Azeroth. Como heredero del legado de Medivh, no podía hacer otra cosa.$B$BCuando finalmente llegó el momento de invadir este sombrío mundo, y acabar con el mal de los orcos para siempre, di un paso al frente junto a mis amigos Turalyon, Alleria, Danath y Kurdran.' WHERE `entry`=9877;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mis camaradas y yo nos hicimos llamar los Hijos de Lothar, en honor al hombre más bondadoso y desinteresado que jamás conocimos. Hicimos todo lo que pudimos por derrotar a Ner\'zhul y poner fin a la locura.$B$BCuando este mundo condenado se derrumbó a nuestro alrededor, destruimos El Portal Oscuro para evitar que algún otro mal pudiese caer sobre nuestro amado Azeroth.' WHERE `entry`=9878;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Atrapados en ese erial, hicimos lo que pudimos por sobrevivir. Con el tiempo, sentimos que una nueva maldad nos rodeaba desde El Vacío Abisal. Peor que los orcos malignos... Era la Legión Ardiente que pretendía tomar... Terrallende.' WHERE `entry`=9879;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mientras que mis camaradas aguantaban en el límite de la creciente oscuridad, utilicé mi magia para llegar a la Gran Oscuridad del Más Allá. Allí, pude sentir seres con una gran Luz y de insondable pureza.$B$BY encontré a A\'dal y a sus naaru. Fue como... un milagro.' WHERE `entry`=9880;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué deseas, $R?' WHERE `entry`=9886;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué es un "todólogo?" Mejor sería preguntar qué no es un "todólogo." ¡Significa que lo estudio todo! De hecho, soy tan experto en "todo" que te aseguro que soy el etéreo más inteligente que jamás hayas visto, ¡te apuesto lo que quieras!$B$BPerdona. Está hablando el comerciante en mí...' WHERE `entry`=9894;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No soy flaco ni oscuro. De hecho, ambas referencias me parecen insultantes.$B$BY ahora, sal de mi vista antes de que te clave el bastón en el culo.' WHERE `entry`=9895;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El panel de control cambia y gira sobre sí mismo.' WHERE `entry`=9899;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<La cabeza de la imagen se vuelve borrosa mientras se funde con lo que la rodea.>$B$BSi hubieras visto las Llanuras de Farahlon, no te creerías que es este mismo lugar. Hace solo unos meses, estos campos no habrían desentonado en los Páramos de Poniente.$B$BYa no queda nada. ¿Cómo ha sido? ¿Y por qué?',`Text1_0_loc6`='La tierra está en peor estado de lo que me había imaginado. Si se sigue dañando a este ritmo, Farahlon, eh..., Tormenta Abisal no será más que un erial cósmico.$B$B¿Cuándo parará? Puede que la pregunta correcta sea ¿parará algún día?' WHERE `entry`=9901;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='<La imagen del archimago parece atravesarte con la mirada en lugar de mirarte.>$B$BGalones y corrientes de energía arcana emanan y flotan como si la zona estuviera hundida en un mar de poder mágico. Ni siquiera el primer cataclismo causó tanto daño.',`Text3_0_loc6`='¡Me vengaré de Kael\'thas por esta traición al Kirin Tor! Una vez caminó entre nosotros como un hermano, pero ya no somos hermanos.$B$BLlegará la hora de la verdad.$B$B<El archimago asiente, sin que le entusiasme el panorama.>$B$BKael\'thas es mucho más que un simple pícaro mago. Cuando esté liberado de esta torre, Ravandwyr y yo ayudaremos a quien quiera que se oponga a él.' WHERE `entry`=9901;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hubo un tiempo cuando el orbe que ves detrás de mí era capaz de ver lo que sucedía en Azeroth.$B$BLa maldición de Kael\'thas cambió todo eso, por supuesto, pero durante nuestros años de aislamiento, muchos de los magos encontraron alivio en sus imágenes.$B$BAlgunos las miraban para recordar, otros para olvidar.',`Text1_0_loc6`='<El archimago Vargoth te sonríe irónicamente.>$B$BHay mucho sitio en la Torre Violeta.$B$BAquí me encontrarás en cualquier momento del año.' WHERE `entry`=9902;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='La noche en que Kael\'thas arrasó el pueblo, yo no pude hacer nada para ayudar. Su magia ya me había encerrado en la torre, exacerbando mi tormento al obligarme a ver las muertes de los míos.$B$BY todavía, sus voces me llaman en la distancia, despertándome en mitad de la noche.' WHERE `entry`=9902;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si te han enviado a matarme, coge un número y espera tu turno.' WHERE `entry`=9903;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Habla bajo! No quiero que me vean aquí.$B$B¡Ooh, esto me pone furioso! ¿Pero quién se creen que son esos etéreos?$B$B¡Ahora me toca a mí!' WHERE `entry`=9904;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Por favor, déjame sola con mi tristeza.' WHERE `entry`=9905;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si trabas amistad con el Bajo Arrabal, puede que hagamos negocios.' WHERE `entry`=9918;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿No te parece increíble esta tecnología etérea?$B$BYo me conformo con que me dejen acercarme hasta aquí. Voy a aprender mucho... ¡Puede que consiga alguno de estos para mí!' WHERE `entry`=9919;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Dale caña, $Gchico:chica;! Tengo asuntos que atender...' WHERE `entry`=9920;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Se dice que aquellos bendecidos por los espíritus de Auchindoun poseen la habilidad de capturar almas poderosas en forma de fragmentos de espíritus. Me he cruzado con algunos de estos fragmentos de espíritus en mis viajes, pero todavía debo desvelar sus secretos. $B$BLo único que he podido percibir es que parecen originarse en las profundidades de la misma Auchindoun. Si por casualidad te cruzas con alguno de estos fragmentos de espíritus, estoy seguro de que te valdría la pena permitirme que te los quite de las manos...' WHERE `entry`=9921;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este constructo de cristal zumba con energía. Ante ti, se proyectan extraños glifos que se desplazan arriba y abajo con rapidez.' WHERE `entry`=9922;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ocupado, ocupado! ¡Tanto por hacer, tan poco tiempo!$B$BBueno, ¿dónde he puesto esa pirollave? Sé que está por aquí... ¡me he quemado los dedos con ella hace solo un momento!' WHERE `entry`=9923;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Viene bien saber un poco de magia para ese tipo de trabajos. Las ilusiones no pueden confundir a todo el mundo, pero casi siempre funcionan bastante bien.' WHERE `entry`=9925;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué haces aquí? ¿No deberías estar trabajando en la tubería de energía?$B$BHemos aumentado a más del doble la cantidad de energía que enviamos a El Castillo de la Tempestad y no podemos permitirnos que nadie gandulee. No queremos que suceda otro Ultris aquí.' WHERE `entry`=9926;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡La forja de maná Ultris, por supuesto! ¿Dónde has estado?$B$BLa forja de maná se sobrecargó y creó una gran explosión. Dejó un desgarrón en la realidad y ahora las criaturas del vacío han estado entrando por ahí. Se las puede ver por todas partes hacia el noreste.' WHERE `entry`=9927;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aquí no deberías tener miedo, hemos mejorado la seguridad de estas instalaciones después de los ataques a Forja de Maná Duro.' WHERE `entry`=9929;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Forja de Maná Duro son las instalaciones más cercanas a El Castillo de la Tempestad, así que se ocupa de la mayor parte de la carga.$B$BComo deberías saber, si estas forjas de maná se sobrecargan se producen derrames en los conductos y a veces no solo se derrama maná. Hemos tenido plagas de criaturas de maná en muchos puntos en que ha habido derrames; la Forja de Maná Duro fue la peor parada, hace un tiempo.$B$BPor lo menos no explotó como la Forja de Maná Ultris.' WHERE `entry`=9930;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Trabajar con esta tecnología me da dolores de cabeza. Todo esto está construido de forma rarísima.' WHERE `entry`=9931;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hace muchos años, mis hombres y yo seguimos al mago Khadgar y al paladín Turalyon hasta este mundo. Nuestro objetivo era aniquilar a toda la horrible raza orca. Pero algo salió mal en el último momento.$B$BAl final, el mundo oscuro estalló en pedazos y mis amigos se perdieron en el vacío.' WHERE `entry`=9932;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿No os enseñan nada a los nuevos reclutas? Estas forjas de maná se construyeron a partir de partes de El Castillo de la Tempestad pensadas para succionar energía del vacío.$B$BLos naaru usaron esta tecnología para impulsar su nave durante sus viajes. Algunos dijeron que la tecnología puede que no sea segura para su uso en tierra. Se les hizo callar de inmediato.' WHERE `entry`=9934;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Otro soldado de camino al Bastión del Honor... ¡por la barba de Kurdran, podrán hacer uso de la ayuda!' WHERE `entry`=9935;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es mi pasión, sabes. En la vida y en la muerte, la artesanía de armas es lo que amo. Por favor, trátame como si fuera un draenei vivo, $Gextranjero:extranjera;. Deja que vuelva a saber lo que es sentir... Permíteme el honor de reparar tus objetos.' WHERE `entry`=9936;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡El Consorcio se vengará de los traidores Zaxxis!' WHERE `entry`=9937;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$N, los hijos de Lothar han luchado aquí, en la península del Fuego Infernal, durante dos décadas. Nos vemos acosados por todos lados por orcos indígenas y demonios infernales de la Cruzada Ardiente y, aunque nuestros corazones siguen latiendo con fuerza, años de guerra han reducido nuestros números.$B$B¡Tú, y el influjo de la nueva generación de héroes de Azeroth, sois, sin lugar a dudas, una visión bien acogida!' WHERE `entry`=9939;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Orcos. Creo que me he pasado la vida luchando contra ellos.$B$BHace veinte años, dividimos a la Horda y destruimos los muros de su Ciudadela del Fuego Infernal. ¡Dimos una buena lección a esos pieles verdes! Después de eso, a parte de alguna que otra escaramuza, los orcos ya no eran una amenaza.$B$BHasta ahora.' WHERE `entry`=9941;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hace unos veinte años, la Alianza envió la primera Expedición a este mundo con la esperanza de erradicar a los orcos para siempre. Fue un... conflicto terrible. Muchas almas valientes perecieron para asegurar la seguridad de Azeroth.$B$BPero algunas almas permanecen... en combate eterno.' WHERE `entry`=9942;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los hombres y mujeres de Bastión del Honor han derramado mucha sangre y han perdido a sus hijos en esta horrible tierra... ¡Y a pesar de eso, siguen luchando con la moral alta!$B$BAhora, tú y tus seguidores de Azeroth podéis ayudar a que cambien las tornas. Gracias a ti ha vuelto la esperanza de regresar a nuestras tierras.' WHERE `entry`=9944;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mi hermano menor es la nueva promesa del Consorcio. Por supuesto, es demasiado modesto para reconocerlo.' WHERE `entry`=9948;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cuando dice "hermano menor", olvida mencionar que somos gemelos. En realidad solo es mayor por un par de minutos.$B$BNo dejéis que su historia de la "nueva promesa" os engañe. Nunca seré un mercader de la misma magnitud que él.' WHERE `entry`=9949;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te saludo, $R. Qué hago aquí es una historia para otro momento. Uf, es una de las malas.$B$BMi hermano y yo estamos atrapados aquí, entre nuestro tiempo y el presente. No osamos marcharnos por miedo de causar una paradoja temporal. Esperamos pacientemente hasta que los dragones nos devuelvan a nuestra auténtica línea temporal.$B$B¿Cuánto tiempo hace, preguntas? En realidad no estoy seguro. Pregúntaselo a mi hermano.$B$B¿Te apetece algo de queso?' WHERE `entry`=9951;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sí, has oído bien. Tengo la voz de una joven orco. Pero escucha, $Gchico:chica;, los viajes en el tiempo tienen estas cosas.$B$BTengo 250 años, por cierto.' WHERE `entry`=9953;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sí, he estado atrapado aquí desde que puedo recordar.$B$BEn mi vida anterior, era armero. ¿Quizás pueda repararte algo?' WHERE `entry`=9954;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Esta plataforma de teletransporte etérea está cubierta de botones e interruptores de colores vivos, pero parece haber caído en desuso.' WHERE `entry`=9958;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Todo en venta, $Gamigo:amiga;! Grande y pequeño, sea lo que sea lo que necesitas, Hazzin te lo proporcionará.$B$BSi valoras tu vida, no te acerques a la forja de maná Ultris sin reabastecerte. Hazzin es la última oportunidad que tienes.' WHERE `entry`=9959;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los druidas están disfrutando de una gran celebración del Festival Lunar en el Claro de la Luna. Si te parece demasiado tiempo para ir andando, están teletransportando a gente desde las grandes ciudades.' WHERE `entry`=9964;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nunca había visto nada igual a esos dracos abisales. Parece que han heredado el temperamento de su señor, pero, al mismo tiempo, son algo totalmente diferente.' WHERE `entry`=9968;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El suelo de este lugar todavía está caliente de sangre recién derramada. ¡Ah, habría dado mi propio ojo para estar aquí cuando los demonios entraron por ese portal y se enfrentaron a los héroes de Azeroth! ¡Menuda batalla!$B$B¡Pero ahora, al otro lado hay una batalla aún más feroz!' WHERE `entry`=9969;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si has venido hasta aquí, debe de ser porque osas aventurarte cerca de la forja de maná Ara.$B$B¿Quieres comprar o vender antes de seguir hasta allí?' WHERE `entry`=9970;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Solo hay tres tipos de personas en este mundo: competidores, clientes y empleados. Los competidores se deben aplastar implacablemente, a los clientes se les debe satisfacer, y a los empleados, supervisar estrictamente.$B$BLos contratistas independientes son un mito. Quieren que se les pague mejor que a los empleados, pero que se les trate como a clientes. Nunca confíes en nadie que use ese título para sí.' WHERE `entry`=9971;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cuidado, mortal. tras esta sala hay peligros desconocidos en tu mundo. Camina con cuidado...' WHERE `entry`=9977;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me alegro de verte de nuevo, $N. ¿Quieres que te lleve a la guarida del maestro?' WHERE `entry`=9978;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Cuenca de Arathi está ocupada por Los Rapiñadores. ¿Puedes ayudarnos, $N?' WHERE `entry`=9979;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me alegro de ver a un amigo de Aldor por estos recónditos rincones. Me temo que no soportaré mucha más cháchara de esos elfos insolentes.' WHERE `entry`=9980;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$N, ve a la Cuenca de Arathi! ¡Mata! ¡Mata como nunca habías matado antes!' WHERE `entry`=9981;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tienes ante ti la entrada a la Garganta Grito de Guerra. ¿Te enfrentarás a la odiosa Alianza allí?' WHERE `entry`=9982;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C. Incluso ahora, la Horda ataca la garganta Grito de Guerra. ¿Lucharás junto a la Alianza allí?' WHERE `entry`=9983;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡¿Qué haces aquí?! ¡Muévete al Valle de Alterac, donde se te necesita!' WHERE `entry`=9984;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡MIERDA DE JABALÍ! ¿Por qué tengo que quedarme aquí sirviendo de guía para el Valle de Alterac pudiendo estar luchando? ¡Tú! ¿Has venido a luchar contra la Alianza?' WHERE `entry`=9985;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué es lo que quieres? No me van las charlas.' WHERE `entry`=9988;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ahora mismo estoy un poco ocupada negociando un soborno. Quizás quieras volver más tarde.' WHERE `entry`=9989;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='He organizado tu vuelo a la forja de maná Coruu. ¿Estás $Gpreparado:preparada;, $N? Te dejarán detrás de las filas enemigas, así que asegúrate de llevar todo lo que necesitas.' WHERE `entry`=9990;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Otro soldado de camino al Bastión del Honor... ¡por la barba de Kurdran, podrán hacer uso de la ayuda!' WHERE `entry`=9991;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; a la Flecha de la Tormenta, $C.$B$BQue no te disuada que esté presente a través de este dispositivo de imágenes. Tengo muchos enemigos a los que les gustaría verme muerto, así que es solo por precaución.$B$BTe aseguro que estás perfectamente $Gseguro:segura; aquí.' WHERE `entry`=9993;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Hay algo que desees comprar? ¿Hay algo que desees vender?$B$BPor eso estamos aquí, $Gamigo:amiga;.$B$BPor favor, dime qué deseas.' WHERE `entry`=9994;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Throm\'ka, $Ghermano:hermana;! $GBienvenido:Bienvenida; a Terrallende. La Horda necesita toda la fuerza que pueda reunir en este mundo destrozado. Te prometo que, si es una batalla gloriosa lo que andas buscando, aquí tendrás hasta hartarte.' WHERE `entry`=9996;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hazlo rápido, $C – tenemos una guerra que librar aquí, ¿o no te habías dado cuenta?' WHERE `entry`=9997;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ah, bienvenido de nuevo, héroe. ¿Has vuelto para volver a proteger al Guardián? ¡No olvides llevarte una cronobengala contigo!' WHERE `entry`=10001;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $N. Ten cuidado...' WHERE `entry`=10002;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Throm\'ka, $C. Debes de ser $Gnuevo:nueva;. Pareces joven y no tienes aspecto de haber vivido los horrores de estas tierras.$B$B<El sargento Trizacráneo escupe en el suelo>$B$BEso va a cambiar.' WHERE `entry`=10005;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestra expedición logró cruzar el Portal sin incidentes y estableció una nueva base llamada Thrallmar. Pero, como puedes ver, la Legión Ardiente se instaló y nos aisló de nuestros hermanos. Claramente, los demonios esperan retomar el Portal Oscuro y evitar que reunamos refuerzos de Azeroth.' WHERE `entry`=10006;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Creo que, con algo de ayuda, Taerix puede estar cerca de un avance.' WHERE `entry`=10008;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mis hermanos de Ventormenta me dijeron que no me uniera al ejército... ¡me alegro de no haberlos escuchado!' WHERE `entry`=10009;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los halcones zancudos son criaturas nobles. Tratad bien al vuestro; hay pocas cosas más peligrosas que un halcón zancudo enfadado.' WHERE `entry`=10011;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estoy segura de que, con tu ayuda, la botánica Taerix podrá hallar soluciones a nuestros problemas.' WHERE `entry`=10012;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Te diriges a Terrallende? Visita a Vixton Silbapellizco en el Área 52 de Tormenta Abisal. Como vendedor oficial de la arena del circuito de lucha de Bonvapor, tiene todo tipo de artículos increíbles para aquellos que dispongan de suficientes puntos de Arena.' WHERE `entry`=10018;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Te apetece ayudarme a destruir el portal de distorsión del campamento que la Legión Ardiente tiene hacia el sur?' WHERE `entry`=10019;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Esta gente llegó de Shattrath hace algunas noches y no han parado de discutir desde entonces.$B$BMe alegro de ver que no te han asustado. Estaba empezando a temer que ahuyentaran a mis clientes.',`Text1_0_loc6`='Espero que esa gente de Shattrath deje de luchar pronto. Están empezando a ponerme los nervios de punta.$B$B¿En qué puedo ayudarte?' WHERE `entry`=10022;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las fuerzas draenei y sus aliados de la Alianza se enfrentan a la Horda en el Ojo de la Tormenta, en Terrallende. ¿Quieres unirte a ellos para que la balanza se incline a nuestro favor?' WHERE `entry`=10025;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Horda no debe permitir que los draenei y su entrometida Alianza controlen las células de maná de Terrallende. ¿Quieres acompañar a los elfos de sangre en su ataque al Ojo de la Tormenta?' WHERE `entry`=10026;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola, $C! ¿Vienes por la misión de vuelo?' WHERE `entry`=10027;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si has estado en la marisma de Zangar, puede que te hayas encontrado con un tipo llamado Daniels. Ya sabes – lleva un casco extraño, solo responde al nombre de "el Especialista."$B$BPor supuesto, le enseñé todo lo que sabe.$B$BNo te dejes engañar por la ropa elegante. En el fondo sigo siendo un pícaro. Daniels y yo pasamos mucho tiempo juntos en las trincheras, pero eso fue antes de que yo me retirara. Por lo que sé, sigue ahí fuera, desatando el infierno.' WHERE `entry`=10031;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Día tras día, lo único que hacemos es matarnos trabajando en esta mina. El elfo de sangre no fue hecho para tales tareas manuales.' WHERE `entry`=10032;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$N, comunica a Gahruj nuestro éxito.' WHERE `entry`=10039;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El emisario Taluun me trajo consigo esperando que pudiera enseñar a nuestros nuevos aliados cosas sobre los elementos de este mundo... pero nadie escucha.' WHERE `entry`=10040;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Déjame, $C. Debo atender mis obligaciones con el embajador Penasol, por más aburridos que sean.' WHERE `entry`=10041;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aunque la atención de los Sha\'tar permanezca en el valle Sombraluna e Illidan, no podemos ignorar la amenaza que Kael\'thas el Caminante del Sol supone.' WHERE `entry`=10042;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El emisario Taluun me trajo consigo esperando que enseñara a nuestros nuevos aliados cosas acerca de los elementos de este mundo. Hasta ahora, se niegan a escuchar, pero, ¿quizás tú lo harías?' WHERE `entry`=10043;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me aburro de proteger al embajador Penasol, $N. ¿Quieres practicar un poco de lucha? Quizás uno de los dos aprenda algo.' WHERE `entry`=10044;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='A medida que te acercas a la consola, el cristal de acceso que tienes contigo comienza a zumbar suavemente y a emitir una luz parpadeante.$B$BUn torrente de información fluye hasta tu mente y, de repente, eres consciente de los niveles de producción de energía de la forja de maná, de su estabilidad y de su producción a diferentes tuberías. La consola aguarda tu orden.' WHERE `entry`=10045;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡No toques la capa! Sí, sí, ya sé que quieres una. Todo el mundo quiere una capa mía. Créeme, persona de clase baja y moral distendida, algún día me haré rico gracias a la venta de mis capas. Y todos, desde aquí hasta Forjaz, habrán oído hablar de Bartolo Ginsetti. ¡La marca Ginsetti será codiciada por todos!' WHERE `entry`=10047;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Celador de la Luz tiene una tarea doble. Proteger a la hermandad a cualquier precio en primer lugar. Y en segundo lugar, destruir a los enemigos de la Luz dondequiera que estén.' WHERE `entry`=10051;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Por la barba de Muradin, $C. Esta tierra está al borde de la aniquilación. No me puedo imaginar que alguien sobreviviera aquí desde el final de la segunda guerra.' WHERE `entry`=10052;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Throm\'ka, $C. ¿Es esta tierra destrozada lo que esperabas ver? No se parece en nada a las historias que me han contado. Es difícil creer que mi pueblo llegara a prosperar en este lugar desierto.' WHERE `entry`=10053;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Busco alguien que me ayude con una tarea que debería haberse acabado hace tiempo.$B$BPareces capaz.' WHERE `entry`=10054;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gracias a lo que hiciste por mí en las Ruinas de Farahlon ya no voy retrasado en mi plan de trabajo. Estoy reuniendo a mi equipo para salir.$B$BGracias, $N.' WHERE `entry`=10055;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Legión está por todas partes... ¡hay que ocuparse de ellos!' WHERE `entry`=10056;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gracias a ti, podremos seguir con nuestros planes en las Ruinas de Farahlon.' WHERE `entry`=10057;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Etereum se ha vuelto oscuro... retorcido. La venganza se ha convertido en su único recurso y, cualquiera que se interponga en su camino o no se una a su causa se considera enemigo. Muchos vieron que El Etereum caía en picado hacia la locura y se marcharon. Varios de los príncipes-nexo del consejo Etereum los abandonaron para formar otros grupos y facciones con diferentes objetivos. El Consorcio es un ejemplo de un grupo disidente. El Protectorado es otro...' WHERE `entry`=10060;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola. Soy Vixton Silbapellizco, vendedor oficial de la Arena para el circuito de lucha de Bonvapor. Si tienes al representante, tengo los artículos que te harán la boca agua.' WHERE `entry`=10062;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si estás $Gmareado:mareada;, habla con Pestle. Puede que tenga algo para los mareos.' WHERE `entry`=10063;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El aprendiz mira de vez en cuando por encima del hombro, aparentemente reaccionando ante sonidos que tú no puedes oír.>$B$BBuenos días, $N. Discúlpame si parezco distraído, pero me encuentro en medio de un trabajo para el granjero Natin. Quiero poner herraduras a todos sus caballos para cuando vuelva del concejo. ¿En qué puedo ayudarte?' WHERE `entry`=10064;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Al agente Ya-six y a mí nos asignaron una misión de prioridad alta desde el mando del Protectorado: erradicar toda señal de infestación de criaturas del vacío de Ultris. Tenían que ser ataques tácticos. Nuestro último ataque antes de volver a la Atalaya del Protectorado tenía que caer sobre un señor del vacío llamado Arconus, que había estado engordando gracias a los seres de carne de la zona.$B$BPor desgracia, llegamos un poco tarde. Ya-six y yo nos separamos en el tiroteo con las bestias de carne. Ahora está atrapado dentro, inmovilizado tras líneas enemigas.' WHERE `entry`=10065;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si la Luz no puede guiarte, $Gamigo:amiga;, quizás yo pueda ayudarte. ¿Qué necesitas?',`Text1_0_loc6`='Esta isla puede ser extraña, ¿verdad? ¿Hay algo que pueda hacer por ti?' WHERE `entry`=10066;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Asegúrate de visitar a Vixton Silbapellizco en el Área 52 de Tormenta Abisal. Como vendedor oficial de la arena del circuito de lucha de Bonvapor, tiene todo tipo de artículos increíbles para aquellos que dispongan de suficientes puntos de Arena.' WHERE `entry`=10069;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los hipogrifos están bien cuidados cerca del mismo Exodar. Deberías viajar hasta allí si deseas volar hasta la Avanzada de Sangre.' WHERE `entry`=10071;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Funaam es nuestro registrador del gremio. Su oficina se ha establecido dentro de El Exodar. Sigue el camino; la encontrarás enseguida.' WHERE `entry`=10073;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestra humilde posada está situada al norte de la Avanzada Azur; Chellan cuidará bien de ti.' WHERE `entry`=10074;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La buena Tullas es una mujer noble y una defensora incondicional de la Luz. Sus lecciones se instruyen en la misma posada.' WHERE `entry`=10083;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En la pequeña, pero buena posada de Chellan, puedes encontrar al sacerdote Guvan.' WHERE `entry`=10084;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Eres uno de los chamanes? Mm. Supongo que eres libre de escoger tu propio camino... sea cual sea. Tuluun tiene un pequeño campamento aislado al noroeste de la Avanzada Azur.' WHERE `entry`=10085;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ruada instruye a jóvenes combatientes en la parte oriental de la Avanzada Azur. Si lo que buscas es instrucción en el arte práctico de la guerra, ella te ayudará.' WHERE `entry`=10086;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay varios mercaderes diestros esparcidos por Bruma Azur. ¿A quién buscas?' WHERE `entry`=10087;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Daedal conoce el arte de elaborar pociones. Trabaja estrechamente con Fateema en el extremo oriental de la Avanzada Azur.' WHERE `entry`=10088;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No tenemos herrería establecida en la Avanzada Azur. Sin embargo, he visto a varios herreros jóvenes venir de aquel campamento extraño en la costa sur. Quizás deberías investigar.' WHERE `entry`=10089;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No teníamos cocineros de sobras, pero es un poco raro... hay algunos aromas deliciosos en el viento del sur. Sigue tu nariz, ¿eh?' WHERE `entry`=10090;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestros encantadores se pasan el día en El Exodar, trabajando en varios proyectos. Nahogg iniciará tu instrucción.' WHERE `entry`=10091;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El artificiero local, Daelo, puede enseñarte las bases de la ingeniería. ¡Quizás podrías ayudar a nuestro técnico a reparar el dispositivo de comunicaciones que funciona mal!' WHERE `entry`=10092;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Fateema es nuestra anacoreta, y ha sido inestimable para cuidar a las víctimas del accidente. Practica su oficio en el extremo occidental de la Avanzada.' WHERE `entry`=10093;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GUn:Una; aspirante a pescador... bueno, Diktynna vive en la costa del vado Ammen, y usa su red y su palo para mantenernos abastecidos a todos. Estoy segura de que tu ayuda le iría bien.' WHERE `entry`=10094;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Heur ha estudiado la flora local desde nuestro aterrizaje. Podrá guiarte. Puedes encontrarle trabajando con Daedal en el margen occidental de la Avanzada Azur.' WHERE `entry`=10095;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestros nuevos aliados, los fúrbolgs de Semprepino, han mostrado su interés en llevarse todas las pieles que encontremos. Viven al norte; yo acudiría a ellos en busca de conocimiento.' WHERE `entry`=10096;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Dulvi es la mujer que quieres. Instruye a los nuevos en medio de la Avanzada.' WHERE `entry`=10097;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Viaja al Bastión Semprepino, $Gamigo:amiga;. Los fúrbolgs que hay allí han sido muy útiles, y puede que tengan justo la pericia que necesitas.' WHERE `entry`=10098;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No tenemos sastres en la Avanzada Azur. Pero quizás quieras probar en el campamento de la playa – parecen tener varios mercaderes del otro lado del mar.' WHERE `entry`=10099;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡¿Que quieres qué?! He oído cosas sobre uno de esos extraños draenei en el Valle de los Héroes. Puede que ella sepa algo; yo no sé nada.' WHERE `entry`=10101;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El espíritu de agua nos ha dejado. Nuestra tribu está condenada.' WHERE `entry`=10103;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los vientos ya no hablan con nuestra tribu, $N. Mi pueblo está perdido sin ancianos que les traigan los espíritus.' WHERE `entry`=10104;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estas cadenas que me atan me impiden llevar el espíritu del fuego a mi pueblo, $N. Las almas sin fuego son almas que no se pueden rebelar contra sus maestros.' WHERE `entry`=10105;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nosotros no gusta paladín. Elfa viene a ciudad. Barrio Real. Tú habla ella y aprender cosas de paladín.' WHERE `entry`=10107;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Paladín? ¿Has estado tomando pociones raras del boticario o algo así? He oído rumores de que un destacamento elfo ha visitado a la Dama Oscura en Entrañas. Puede que alguno de ellos sepa algo.' WHERE `entry`=10108;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tu ayuda ha sido inestimable, $Gamigo:amiga;.$B$BTe aseguro que la expedición Cenarion hará lo que pueda para luchar contra cualquier cristal máculo que caiga de los cielos enviado por Illidan.' WHERE `entry`=10109;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Está por aquí... en algún lugar... lo sé. En algún lugar de esta biblioteca está el conocimiento que estoy buscando...' WHERE `entry`=10124;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Esta torre es una auténtica locura. Esto no es una biblioteca, es un enorme pajar, ¡y ni siquiera creo que exista la aguja! Bueno, ¿qué estaba haciendo?' WHERE `entry`=10125;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tu presencia aquí interfiere con un trabajo muy importante. Por favor, márchate de inmediato. ¿Por dónde iba?' WHERE `entry`=10126;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ah, qué lugar tan extraño para buscar posibles clientes!' WHERE `entry`=10127;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, ser de carne. Soy Navuud, jefe de investigación de la Avanzada del Protectorado.' WHERE `entry`=10152;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Dejar a Kael\'thas fue lo mejor que pudimos hacer. Sus seguidores están demasiado ciegos o demasiado asustados para darse cuenta de adónde les está conduciendo.$B$BSin embargo, nosotros no.' WHERE `entry`=10153;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Están aquellos que intentan mentir y tergiversar la realidad. Los videntes, por otra parte, llevan la verdad a la luz.$B$BEl fin de esta biblioteca, $N, es convertir a todo el mundo en profeta.' WHERE `entry`=10178;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Entre los etéreos, hay quienes tratan con carne y con seres de carne; son conocidos como los tratantes de carne.' WHERE `entry`=10179;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Buscas algo en nuestras bellas tierras?' WHERE `entry`=10180;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si quieres registrar una hermandad, te sugiero que busques en Lunargenta.' WHERE `entry`=10182;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Algunos forestales son incapaces de cuidar de su material. Lo mejor es dejarlo en algún sitio en la ciudad. Anathos se ocupa de esas cosas. Vive y trabaja en la Plaza Alalcón, al lado de la posada.' WHERE `entry`=10184;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hannovia se ocupa de adiestrar a los forestales. Se encuentra en el edificio nororiental de la Plaza Alalcón.' WHERE `entry`=10186;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Garridel enseña las artes Arcanas en la Plaza Alalcón. Se encuentra en la segunda planta de la posada.' WHERE `entry`=10187;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Caballero de sangre Noellene te espera en el edificio nororiental de la plaza.' WHERE `entry`=10189;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si buscas la sabiduría de un sacerdote, habla con Ponaris. Está con los demás en la segunda planta de la posada de Delaniel.' WHERE `entry`=10190;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay un edificio al noreste de la Plaza Alalcón. En él, es muy probable que encuentres a Tannaria. Su especialidad es permanecer oculta.' WHERE `entry`=10191;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Celoenus es nuestro experto residente en invocación de demonios. Está en la posada de la Plaza Alalcón, en la segunda planta.' WHERE `entry`=10192;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Conozco a un alquimista local. Creo que se llama Sheynathren. La última vez que lo vi, se dirigía al Refugio de Saltheril. Seguramente para unirse a esas ridículas fiestas.',`Text0_1_loc6`='Conozco a un alquimista local. Creo que se llama Sheynathren. La última vez que lo vi, se dirigía al Refugio de Saltheril. Seguramente para unirse a uno de esos ridículos grupos.' WHERE `entry`=10193;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los Errantes tienen un herrero en su retiro al este de Canción Eterna. Arathel podrá ayudarte.' WHERE `entry`=10194;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Delaniel ha contratado a una cocinera excelente. Se llama Quarelestra. Seguro que puedes aprender mucho de ella. Está en la posada de la Plaza de Alalcón, claro.' WHERE `entry`=10195;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si buscas a Eredania la encantadora, ve a la pequeña fiesta de Saltheril. Recibió su invitación hace unos días.' WHERE `entry`=10196;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Una profesión tan impredecible y peligrosa no puede desempeñarse en nuestros tranquilos bosques sin consecuencias desastrosas. Inténtalo en Lunargenta. Sus muros son lo suficientemente resistentes como para aguantar los impactos.' WHERE `entry`=10197;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Para los que prefieran métodos más caseros de curar las heridas, Kanaria tiene un puesto en el interior de la posada de la Plaza Alalcón.' WHERE `entry`=10198;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay un pescador en nuestra gran ciudad. Está bien si te gusta pasar la tarde con repugnantes cosas resbaladizas y húmedas.' WHERE `entry`=10199;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Quién mejor que el gran botánico Tyniarrel para los arreglos florales de una fiesta? Estoy segura de que Saltheril se lo paga en vino.' WHERE `entry`=10200;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ameer debe de estar preocupado por las actividades de El Etereum, pero yo debo concentrarme en la verdadera amenaza: Dimensius.' WHERE `entry`=10202;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sathein cree que los pellejos de lince son perfectos para sus creaciones. Deberías hablar con él. Está con Mathreyn al suroeste de la Puerta Magister.' WHERE `entry`=10204;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mathreyn es competente en la técnica bárbara del desuello, si es lo que te interesa. Es el proveedor de Sathein. Los encontrarás a ambos al suroeste de la Puerta Magister.' WHERE `entry`=10206;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La buena costurera está muy ocupada últimamente en el Refugio de Saltheril. Su habilidad con la aguja y el hilo es muy apreciada allí.' WHERE `entry`=10207;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué buscas? Habla.' WHERE `entry`=10208;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En otro tiempo y en otro lugar habría sido considerado como un cachorro. Y aquí estoy ahora; un luchador con experiencia y listo para morir por la gloria de los naaru.$B$BDiez de entre nosotros llegaron a Forja de Maná Ara. Los demás cayeron ante la Legión.' WHERE `entry`=10209;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Llegó el momento de enfrentarse a Socrethar, $N. ¿Estás $Glisto:lista;?' WHERE `entry`=10210;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Te enviaron los Arúspices? Tú no pareces un Arúspice.' WHERE `entry`=10211;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Habla rápido. No tengo tiempo que perder en charlas. Los Renegados son ya una fuerza considerable, pero todavía hay demasiadas cosas por hacer. Esta ciudad no se gestiona sola.$B$B¿Qué quieres?' WHERE `entry`=10212;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No tienes lo que hace falta para luchar en el Círculo de Lucha Bonvapor, $C. Deja de hacerme perder el tiempo.' WHERE `entry`=10214;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Está bien, $C. ¿Crees que tienes lo que hay que tener para entrar en las arenas del Círculo de Lucha Bonvapor?' WHERE `entry`=10215;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los maestros de batalla están cerca de la mesa de guerra de la Sala de Armas.' WHERE `entry`=10216;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los maestros de batalla están reunidos en el Bancal del Guerrero.' WHERE `entry`=10217;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los maestros de batalla están en la sala de guerra del Castillo de Ventormenta.' WHERE `entry`=10218;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ve al alto de los cazadores. Allí encontrarás a los maestros de batalla.' WHERE `entry`=10220;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Encontrarás a los maestros de batalla en el Bastión de los Valientes en el Valle del Honor.' WHERE `entry`=10221;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ve al Barrio Real. Todos allí. En círculo en la sala.' WHERE `entry`=10222;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo encontrarás en el centro de cuidadores, en el Trono de los Naaru. Que la Luz sea contigo.' WHERE `entry`=10223;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Sí, tengo bollos!' WHERE `entry`=10224;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ser de carne, soy el capitán Saeed del protectorado y estos soldados que están a mi lado son mis vengadores. Esperamos órdenes del ser de carne que destruyó el conducto del vacío. Cuando él lo ordene, nos adentraremos en la guarida de Dimensius y esperaremos para el ataque final.$B$BHa pasado demasiado tiempo... ¡El señor del vacío probará nuestras fuerzas combinadas!' WHERE `entry`=10229;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El draco abisal te dirige una mirada de aprobación.' WHERE `entry`=10230;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ya está, $N. ¡Atacamos cuando tú digas! ¡Da la orden y mis hombres y yo cargaremos!' WHERE `entry`=10232;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida al Ecodomo de la Tierra Media, $Gviajero:viajera;. Tengo que pedirte que tengas cuidado al tratar con ese goblin, Shauly. Es la única persona que conozca que usa la palabra "colega" como signo de puntuación.' WHERE `entry`=10233;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Bastión Allerian está rodeado de enemigos por todas partes. Es hora de plantarles cara.' WHERE `entry`=10234;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si vas a venir aquí fuera, haz algo útil, porque el acechasombras de ahí no está haciendo mucho.' WHERE `entry`=10236;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Batalla del Monte Hyjal es uno de los acontecimientos más protegidos de esta línea temporal. Si algún intruso modificara su resultado, se reflejaría en todos los momentos de la historia dependientes.$B$BComo mano derecha del Aspecto, soy más sensible al flujo del tiempo que otros dragones de bronce. He sentido una perturbación en el tiempo de los acontecimientos de la Batalla del Monte Hyjal. Alguien o algo está intentando cambiar esa línea temporal y hay que impedirlo.' WHERE `entry`=10237;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué buscas? Habla. No voy a venderte un elekk hasta que demuestres amistad y dedicación a mi pueblo.' WHERE `entry`=10239;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestro trabajo en la Arboleda Viviente no debe ponerse en peligro. Nuestras propias vidas dependen de su crecimiento y su salud.' WHERE `entry`=10245;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si todavía no lo has hecho, asegúrate de ofrecer tu ayuda a Rina Lunabril.' WHERE `entry`=10246;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si he entendido bien, ya estás haciendo un buen trabajo para Sylvanaar.$B$BSigue así y es posible que podamos conservar nuestro hogar aquí.' WHERE `entry`=10247;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Descansa, vagabundo. Nuestros fuegos son tuyos.' WHERE `entry`=10249;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aquí fue donde Gul\'dan cortó el vínculo entre los orcos y los espíritus elementales. Su insaciable sed de poder no se conformaba con la aniquilación de los draenei. Tenía que destruir Draenor; arrasar la tierra y absorber su energía para usarla en la guerra.$B$BAhora todo lo que queda son jirones de su locura.$B$BMira el altar, $R. Esta tierra quedará marcada para siempre...' WHERE `entry`=10250;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Honro a mis ancestros en la tradición chamanística. Nunca hay que olvidar los sacrificios que hicieron para que pudiéramos estar aquí.$B$BNosotros, a cambio, debemos hacer los sacrificios necesarios para que nuestros descendientes tengan un futuro.$B$B¿Qué te trae a mí, $C?' WHERE `entry`=10251;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No ha pasado tanto tiempo desde que tomamos el Bastión Señor del Trueno de manos de los ogros.$B$B¿Estás aquí para ayudarnos a defenderlo? Sé que Gor\'drek busca ayuda.' WHERE `entry`=10252;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué te parece la guerra, $N? Tú recuerda por qué estamos aquí... Eso hace que luchar y morir sea mucho más fácil.' WHERE `entry`=10253;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Punta de Expedición es uno de los peores lugares donde te pueden destinar, pero es vital para la defensa de Bastión del Honor. La información que conseguimos aquí y los combates que libran nuestros soldados contra la Legión y los orcos de Zeth\'Gor son muy útiles para la campaña de Fuego Infernal de la Alianza.' WHERE `entry`=10255;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='He oído que has decidido echar una mano por aquí.$B$BBien. Tenía miedo de que fuera necesario \'convencerte\' para que nos ayudaras.' WHERE `entry`=10256;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La piel tensada entre los huesos es una llamada a la lucha contra los defensores de la Alianza de Sylvanaar y la Arboleda Viviente al oeste.' WHERE `entry`=10257;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Saludos, soy Azimi! Suelo dar una vuelta por aquí fuera para vender a los guerreros fruta fresca y agua. ¿Tienes hambre? ¿Y sed?' WHERE `entry`=10258;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Bah! ¿Te parece que tengo tiempo para cháchara?$B$B¿Te lo parece?$B$BDate prisa en explicarme lo que quieres. ¡Hay ogros que matar!' WHERE `entry`=10259;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me alegro de ver a $Gun:una; $R como tú ayudando para que Sylvanaar sea un lugar mejor. Hemos hecho mucho en muy poco tiempo, pero las fuerzas que nos rodean amenazan nuestra forma de vida.$B$BMe gustaría escuchar lo que tienes que decir. Habla, $C.' WHERE `entry`=10264;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; al Refugio Cenarion, $N. Tengo órdenes de ofrecerte material básico si lo necesitas.' WHERE `entry`=10266;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Estás $Glisto:lista; para luchar contra la Guardia Pico Tormenta en el Valle de Alterac, $N?' WHERE `entry`=10269;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Como representante de la Liga de Expedicionarios, Bronwyn y yo estamos aquí por petición de la Alianza para estudiar estas montañas.$B$BEntre tú y yo..., cuando volvimos de Silithus necesitábamos unas vacaciones. Este parece un buen sitio para relajarse.' WHERE `entry`=10270;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puede que Borgrim tenga razón; es posible que un descanso nos venga bien.$B$BPero ahora que estamos aquí, estoy dispuesta a mancharme las manos y estudiar lo que puede ofrecernos esta tierra.' WHERE `entry`=10271;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$N, me alegro de verte. Nuestros aliados enanos necesitan nuestra ayuda en el Valle de Alterac. ¿Los ayudarás?' WHERE `entry`=10272;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los Hijos de Lothar llevan mucho tiempo luchando en una guerra que nunca termina. Es asombroso que nuestro equipo se haya conservado tan bien.' WHERE `entry`=10275;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En estos momentos, no-muertos libres luchan contra la Liga de Arathor en la Cuenca de Arathi. ¿Ayudarás a los Rapiñadores a derrotar a los humanos y a sus aliados de la Alianza?' WHERE `entry`=10276;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Throm\'ka, $N. En la Garganta Grito de Guerra hacen falta héroes cómo tú.' WHERE `entry`=10277;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestros amigos de la Alianza se están uniendo para luchar junto con los centinelas Ala de Plata en la Garganta Grito de Guerra. ¿Quieres ir a ayudarlos?' WHERE `entry`=10279;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $Gviajero:viajera; Soy Oronok. Siéntate al lado de mi fuego y caliéntate.$B$B¡Ten cuidado con las hambrientas criaturas que hay por debajo de nosotros!' WHERE `entry`=10280;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No lo olvides, $C, la guerra sigue en Azeroth. ¿Quieres volver a ayudar a la Horda en el Valle de Alterac?' WHERE `entry`=10282;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Throm\'ka, $C. ¿Sabías que la batalla contra los centinelas Ala de Plata continúa en la Garganta Grito de Guerra? Tu ayuda allí será bienvenida.' WHERE `entry`=10283;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué tal? ¿Ya has tenido tu dosis de Terrallende y quieres volver al Valle de Alterac a darle lo suyo a la Horda?' WHERE `entry`=10285;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los centinelas Ala de Plata siguen luchando contra la Horda en la Garganta Grito de Guerra, $C. ¿Volverás a echarles una mano?' WHERE `entry`=10286;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vine a Terrallende en busca de artefactos mágicos para ayudar a la Liga de Arathor contra los Rapiñadores en la Cuenca Arathi..., pero lo único que encontré es este fantástico taparrabos mágico, un ridículo ayudante gnomo y un perseguidor no-muerto maniaco convencido de que he robado su calavera gris o algo así.$B$BDe cualquier manera, ¿querrías unirte a la Liga en su lucha mientras sigo con mi búsqueda?' WHERE `entry`=10287;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Da la impresión de que Oric está aquí solo para hacer gracias y que no conviene hacerle mucho caso para no alentarlo.>' WHERE `entry`=10288;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Es un gato gigante con armadura, riendas y silla de montar. ¿No te parece ya lo suficientemente gracioso? ¿También quieres que hable?>' WHERE `entry`=10290;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Solo un mago puede aprender la magia de los portales, $C.' WHERE `entry`=10291;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un mago... ¿Quieres aprender la magia de los portales?' WHERE `entry`=10292;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Sociedad Real de Boticarios de Entrañas está dispuesta a ayudar a nuestros aliados orcos aquí, en Terrallende. Es una oportunidad de estrechar nuestros lazos y de encontrar nuevos especímenes y compuestos para nuestros estudios.$B$BCompuestos como... venenos.' WHERE `entry`=10293;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Campamento del Tramo Blanco es una parada que no estaba en mis planes, por decirlo suavemente. Mis clientes esperan que haga las entregas a tiempo y estoy empezando a pensar que va a ser imposible con todas estas paradas imprevistas.' WHERE `entry`=10299;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Eh, no abras las jaulas!$B$BEstos ogros me tienen justo donde quiero.' WHERE `entry`=10300;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tengo entendido que el polvo y el calor de la Península del Fuego Infernal son muy irritantes... No estoy seguro, pero confiaré en mis queridos y todavía vivos aliados.' WHERE `entry`=10301;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Olvidar la peletería elemental no es algo que deba tomarse a la ligera. Si lo haces, olvidarás también todas las recetas que requieran peletería elemental.' WHERE `entry`=10302;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No podrás comprar ningún halcón zancudo hasta que demuestres tu dedicación por mi pueblo.$B$B¡No vuelvas hasta que no te consideren exaltado! ¡Vete!' WHERE `entry`=10305;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si tienes dinero suficiente, puedo enseñarte a montar un halcón zancudo.' WHERE `entry`=10308;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es necesario que los elfos de sangre te consideren $Gun exaltado:una exaltada; para que pueda enseñarte una habilidad de equitación, $C.' WHERE `entry`=10310;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Estás $Glisto:lista; para escuchar la historia de la Clave de Condenación, $N?' WHERE `entry`=10312;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Muy bien. Las palabras que buscas tienen un nombre, $N. Son la Clave de Condenación. Son las palabras pronunciadas por Gul\'dan para destruir este mundo. El mismo hechizo que cortó el último vínculo que unía a los orcos con los elementos de Draenor.$B$B<Oronok asiente con la cabeza.>$B$BSí, esa clave es lo que debes reconstruir y son sus palabras las que debes pronunciar.' WHERE `entry`=10313;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Clave de Condenación permaneció en los aposentos privados de Gul\'dan en Karabor durante veinte años. Ah, claro... Tú no debes de saber lo que es Karabor, ¿verdad?$B$BEl Templo Oscuro...$B$BSí; ahora es propiedad de Illidan Tempestira.$B$BCuando Illidan tomó el control del Templo Oscuro, descubrió la clave. Se dio cuenta de que el daño a este mundo ya estaba hecho, pero pensó que sería mejor guardarla en un lugar seguro para usarlo en el futuro.' WHERE `entry`=10314;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Desde El Altar de Condenación ves los espíritus de los condenados, arrodillados, orando. Lo que no ves, soy yo al lado de Gul\'dan.$B$B<Oronok asiente con la cabeza.>$B$BTe dije que en mi vida he sido muchas cosas, ¿verdad? Una de ellas era lugarteniente de Gul\'dan.$B$BHe hecho cosas terribles en mi vida. He matado a hombres, mujeres y niños. He bailado sobre la sangre derramada de draenei... pero...$B$B<Oronok se atraganta.>' WHERE `entry`=10315;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me sacudió una náusea irresistible. Tras el ritual, volví corriendo a ver a mis hijos. Recogí lo que pude y me fui con ellos. Nos escapamos a las colinas y nos escondimos. No teníamos elección...$B$BNo pasa un día sin que recuerde el corte. Hemos tardado veinte años en volver a conectar con los elementos. Han sido necesarios veinte años para que los elementos empezaran a curarse.' WHERE `entry`=10317;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Como dije, Illidan encontró la clave cuando tomó el Templo Oscuro. Dividió el tomo en tres partes y se lo entregó a sus esbirros para que lo protegieran.$B$BMis hijos... Mis hijos han crecido. Cuando supe lo que había hecho Illidan con el código, les pedí que fuesen a recuperarlo.$B$BEncuéntralos, $N. Encuéntralos y recupera la Clave de Condenación.' WHERE `entry`=10318;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vine aquí para estudiar la primitiva cultura del clan Señor del Trueno.$B$BEran débiles y sucumbieron al poder que los transformó en orcos viles a través de la sangre de Mannoroth.$B$BHe leído todos sus textos. Me interesa especialmente recuperar un artefacto; un tambor, una flecha y una tableta que se perdieron cuando aplastamos a los ogros Aguja del Filo y los expulsamos hasta su bastión.' WHERE `entry`=10322;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El cuerpo sin vida de Ar\'tor permanece suspendido en el aire por la magia demoníaca. Siguen absorbiéndolo... incluso muerto.' WHERE `entry`=10323;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me congratula ver que has decidido hacer tuya nuestra causa. Como premio, impregnaré un anillo con los restos del Pozo de la Eternidad que me has traído.$B$BTienes varias formas de despertar su poder. Elige bien, $N. Las arenas del tiempo fluyen fácilmente en una dirección, pero no en la otra.' WHERE `entry`=10324;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='He... muerto.$B$B¿Por qué sigo aquí? ¿No hay ningún terreno de caza en el que pueda pasar la eternidad?$B$BNo... Antes debo terminar mi tarea. Antes de dejar este mundo, debo recuperar la segunda parte de la Clave de Condenación.' WHERE `entry`=10327;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Olvidar la forja de espadas no es algo que deba tomarse a la ligera. Si lo haces, olvidarás también todas las recetas que requieran forja de espadas.' WHERE `entry`=10329;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Como desees, $N. El espíritu cazador está preparado.' WHERE `entry`=10338;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='He pasado mi tiempo en las trincheras y muchos años entrenando a los cachorros. Me alegro de estar en casa. No importa en lo que se haya convertido, este es mi hogar.',`Text1_0_loc6`='Ocuparme de esas bestias intrépidas, que se interponen entre mis compañeros de clan y la muerte, es un honor.' WHERE `entry`=10339;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los ogros, me entierran hasta el cuello aquí y después se olvidan de volver.$B$BSí, muy gracioso. Ríete. Hay una cabeza de fantasma aquí.$B$BDe acuerdo, si quieres ayudarme, quizás puedas servirme.$B$BPero no, sigue riéndote.' WHERE `entry`=10341;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Nuainn Alatormenta niega con la cabeza.>$B$BCon la Legión a un lado y los esbirros de Illidan al otro, vamos a necesitar todo lo posible para defender el bastión, ¡y lo haremos! Todo auténtico Martillo Salvaje preferiría morir a renunciar a nuestra tierra.' WHERE `entry`=10349;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ponte $Gderecho:derecha;, $Gbruto:bruta;! Ahora estás bajo las órdenes de la Guardia de Kor\'kron.' WHERE `entry`=10350;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Esta tierra todavía porta las cicatrices del pasado. Me pregunto si alguna vez sanarán.' WHERE `entry`=10352;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los Feropantanos que habitan en la Marisma de Zangar tienen cestas de mimbre con cerraduras inteligentes. Sin duda pondrían a prueba hasta tu habilidad.' WHERE `entry`=10354;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los baúles de la Fortaleza Mata\'penas de Nagrand sería un reto considerable incluso para $Gun pícaro:una pícara; de tu notable habilidad.' WHERE `entry`=10355;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Pisa con suavidad, $R. Más allá de este puente se encuentra el infierno.' WHERE `entry`=10356;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡La Estación de Toshley es la mejor! ¡La soledad y el suministro de fuentes de poder extrañas de Terrallende son un marco ideal para experimentos extremos!' WHERE `entry`=10359;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Has hablado con mi clon –quiero decir... mi gemelo?' WHERE `entry`=10360;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Gracias por sacarme de ahí, te debo una!',`Text1_0_loc6`='Ahora debería poder salir de aquí. ¡Gracias!' WHERE `entry`=10373;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Solo un pequeño libro de canciones que pensé que te gustaría tener. Contiene la letra de una canción conocida como El lamento de los Altonato; la que Lady Sylvanas conjuró del aire.' WHERE `entry`=10378;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay maestros de establos justo fuera de las posadas de los draenei y los elfos de sangre, en Alto Aldor y la Grada del Arúspice.' WHERE `entry`=10387;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los maestros de guerra de la Alianza se suelen encontrar en un hueco de la escalera al noreste del centro de la ciudad.' WHERE `entry`=10389;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los maestros de batalla de la Horda se suelen encontrar en un hueco de la escalera al sureste del centro de la ciudad.' WHERE `entry`=10390;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué tipo de instructor buscabas?' WHERE `entry`=10391;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Quién osa interrumpir al Gran Comandante?' WHERE `entry`=10401;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Encontrarás buzones de correos dentro o justo fuera de los bancos y posadas de los Aldor y los Arúspices.' WHERE `entry`=10403;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay maestros de establos justo fuera de las posadas de los draenei y los elfos de sangre, en Alto Aldor y la Grada del Arúspice.' WHERE `entry`=10404;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Escucho.' WHERE `entry`=10405;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Oh? Por supuesto, tienes documentos que documentan esta solicitud.' WHERE `entry`=10406;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Escúchame, peón. Tenemos un proceso para la entrega y custodia del fragmento de la Clave. El enviado entrega la misiva, que ha sido sellada por Lord Illidan. Sin misiva, no hay traslado.' WHERE `entry`=10407;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Ruusk traga saliva.>$B$BYo... ¿Qué quieres decir?' WHERE `entry`=10408;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Espera. ¿Dónde vas? Necesitabas un traslado, ¿no es correcto? Pues acabemos con ello, ¿de acuerdo? ¿Zuluhed en Faucedraco? Así se hará. Haz saber a Lord Illidan que sus órdenes se llevaron a cabo con la entera colaboración de los eclipsianos.$B$B¡Enviaré a Ruul el Ensombrecedor para que entregue personalmente el fragmento!$B$BMe has oído bien - Ruul...' WHERE `entry`=10409;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Con la ayuda del ingeniero Lapforge, de Gadgetzan, he construido un potente transportador gnómico aquí, en la Estación de Toshley! Ayudaría de buena gana a cualquiera ingeniero gnómico con la pericia suficiente a construir su propio localizador de transporte personal.' WHERE `entry`=10410;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Inessera es una mujer astuta que lleva varias gemas, así como suministros de joyería. Puedes encontrar su tienda en la parte norte de Alto Aldor.' WHERE `entry`=10411;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El elemental de agua está listo para elevarse.>' WHERE `entry`=10413;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El destrero se alza con orgullo, ansioso por que comience la batalla.>' WHERE `entry`=10414;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El sumo encantador Bardolan se puede encontrar en la Biblioteca del Profeta, en la Grada del Arúspice. Es un gran maestro del arte y está rodeado de sus estudiantes: maestros, artesanos, expertos y oficiales.' WHERE `entry`=10415;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El clérigo te mira por el rabillo del ojo.>' WHERE `entry`=10416;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El conjurador se alza fiero, con la mirada distante.>' WHERE `entry`=10417;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<La auténtica sabiduría y fuerza de Llane brillan con fuerza, incluso en este parecido del rey.>' WHERE `entry`=10418;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Seymour, el principal desollador de Shattrath y gran maestro del comercio, se puede encontrar en el mercado al aire libre de la parte baja de la ciudad.' WHERE `entry`=10419;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lucharemos cuando estés $Gpreparado:preparada;, $N.' WHERE `entry`=10421;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='DOMINO MÁS DE DOS IDIOMAS: ENÁNICO, GNOMÓTICO Y LA LENGUA COMÚN DE LOS HUMANOS. ¡PERO NO TENGO NI IDEA DE QUÉ ESTÁS DICIENDO!$B$B¡HABLA MÁS ALTO: NO TE OIGO CON EL CHIRRIDO DE MIS ENGRANAJES!' WHERE `entry`=10422;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sí, sí... $Gbienvenido:bienvenida; a mi estación.$B$BEntonces, ¿qué te parece? Es bastante ingenioso lo que hemos hecho aquí, ¿no crees?$B$BMe alegro de haberme librado de esos elfos estirados de Sylvanaar, de cualquier modo.$B$BBusca algo útil que hacer por aquí; hay muchos experimentos y todas esas cosas con las que ayudar.' WHERE `entry`=10423;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gloria a los naaru, $N. La Luz triunfará pronto sobre sus enemigos.' WHERE `entry`=10424;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Al bruto se le hace la boca agua, al esperar el derramamiento de sangre.>' WHERE `entry`=10425;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El belcebú invocado se mueve nervioso, luchando contra las reglas de juego de Medivh. ¡Quiere tu alma!>' WHERE `entry`=10426;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El enano parece haber sido descubierto y asesinado en medio de una misión de espionaje. Los guardas de la Forja Muerta han dejado el cadáver al aire libre, quizás como aviso para otros entrometidos en potencia.' WHERE `entry`=10427;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Magia oscura emana ondulante del necrólito.>' WHERE `entry`=10434;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Las temibles fauces congeladas del lobo orco te provocan escalofríos.>' WHERE `entry`=10439;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Tras los ojos del brujo arde una malevolencia profunda.>' WHERE `entry`=10440;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El Jefe de Guerra Puño Negro domina el campo.>' WHERE `entry`=10442;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Soy un científico que estudia el tiempo y el sonido, así que no estoy muy seguro de cómo se supone que tengo que pensar o encontrar el tiempo para hacer nada de trabajo aquí, con todo este ruido!$B$B¿Por qué no van esos estúpidos a volarse a otra parte?$B$BAh, bueno, al menos tengo buen aspecto. Dime, $Gmuchacho:preciosa;, ¿te he visto antes por aquí?' WHERE `entry`=10444;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El cadáver del orco se ha apartado a un lado para no ensuciar el camino.' WHERE `entry`=10446;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gran parte de mi pueblo me conoce como un traidor... como el perrito faldero de Illidan. Es mejor que lo sigan pensando por ahora.' WHERE `entry`=10447;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Todas las recompensas por servicios prestados son administradas por el Clamaguerras Sardon Cortecertero.$B$BNo colguéis facturas.' WHERE `entry`=10448;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; a la posada de la Estación de Toshley. ¿Qué puedo hacer por ti?' WHERE `entry`=10450;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Te interesa aprender las complejidades de la joyería?' WHERE `entry`=10451;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué deseas saber acerca de la joyería?' WHERE `entry`=10454;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hasta que no domines el nivel de aprendiz de joyería no volveremos a hablar.' WHERE `entry`=10455;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Compartiré contigo mis conocimientos de joyería.' WHERE `entry`=10456;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La creación de joyas y gemas es para lo que vivo. ¿Puedo compartir mi pasión contigo?' WHERE `entry`=10457;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Desearía tener más tiempo para enseñar a todos los que se dirigieron a mí, pero así no es como funcionan las cosas. Hasta que no hayas aprendido todo lo que Driann tiene que enseñarte, no puedo ayudarte.' WHERE `entry`=10458;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es una bendición poder ayudar a alguien con tus habilidades a aprender más sobre la joyería. ¿Qué deseas saber?' WHERE `entry`=10459;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estoy muy ocupada, $R.$B$B¡Tenemos un problema terrible en nuestras manos del que debemos ocuparnos de inmediato!' WHERE `entry`=10460;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El alcance de Illidan sobre esta tierra se deteriora. ¿Lo notas, $N?' WHERE `entry`=10465;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cuando corras con los Jinetes Férreos de Razak, aprenderás un par de cosas sobre seguir órdenes.$B$BLo primero es que luchas hasta la muerte. Lo segundo es ver lo primero.' WHERE `entry`=10466;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Necesitas que te afilen la espada, o quizás un arma nueva? Más vale prevenir que curar...' WHERE `entry`=10467;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si buscas un martillo, has venido al lugar apropiado.' WHERE `entry`=10468;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$G Hijo : Jovencita;, no voy a mentirte, las cosas no van bien por aquí. ¡Estamos bajo ataques constantes de los bichos y nuestra misma forma de vida, nuestra libertad, está en juego!$B$B¿Vas a comportarte de forma responsable y ayudar?' WHERE `entry`=10469;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Teron Sanguino? Sí... he oído hablar de él.' WHERE `entry`=10470;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Espléndido! ¡Maravilloso! ¡Una actuación excelente!' WHERE `entry`=10471;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cuando era joven me formé con dureza para convertirme en una anacoreta en el sacerdocio. Prácticamente no se había oído de las exarcas femeninas.$B$BHoy en día, el sexo importa poco – es su coraje y convicción lo que marca la diferencia.' WHERE `entry`=10473;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Dar forma al flujo del tiempo es una tarea muy difícil y bastante delicada, pero hoy has realizado una actuación admirable.' WHERE `entry`=10475;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Estás aquí para ayudar con los disturbios?' WHERE `entry`=10491;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Como recompensa por sus años de servicio, Illidan permitió a Kael\'thas que le enviara cinco guerreros para instruirlos bajo su mando. Sería la primera vez que se permitía a elfos de sangre instruirse como cazademonios.$B$BLa instrucción fue brutal. Illidan hizo pasar a los guerreros de Kael por pruebas que vencerían a los cazademonios más expertos.$B$BTres de los elfos de Kael murieron durante la instrucción – otro se volvió loco.$B$BUno lo logró.' WHERE `entry`=10492;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tras un año de instrucción bajo el mando de los tres mejores cazademonios de Illidan, Varedis los había superado.$B$BAl no estar ya satisfecho con la instrucción que podían ofrecerle, Varedis se infiltró en el Consejo de la Sombra, donde conoció la existencia de una reliquia poderosa conocida como el Libro de Nombres Viles. Leyó este libro en secreto, memorizando cada pasaje.' WHERE `entry`=10494;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Se rumorea que saber el verdadero nombre de un demonio proporciona cierto poder sobre ese demonio.$B$BSe dice que el Libro de Nombres Viles contiene el verdadero nombre de cada demonio que ha existido. Al memorizar los pasajes del libro, Varedis había encontrado una fuente de poder que rivalizaba con el acto de beber del cráneo de Gul\'dan del propio Illidan.$B$BPor petición de Illidan, Varedis dirige ahora el patio de armas de las Ruinas de Karabor – junto con los tres maestros que instruyeron a Varedis.' WHERE `entry`=10495;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Vete, mortal! Tu calaña no es bienvenida aquí...' WHERE `entry`=10496;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No es sorprendente que el potencial de poder como cazademonios de un elfo sea tan alto. Después de todo, los sin\'dorei no poseen la aversión natural a la magia demoníaca que tienen los elfos de la noche.$B$BSin embargo, no espero que muchos de ellos lo logren. La mayoría, incluido Varedis, buscan el poder en sí. Ese poder los consumirá, pues no les impulsa el fuego puro que arde en el interior del corazón de un auténtico cazademonios.' WHERE `entry`=10497;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedes alejar a un hombre de los hijos de Lothar, pero no puedes sacarle a los hijos de su interior.$B$B<El herrero se encoge de hombros.>$B$BEs difícil de explicar, pero no siento que deba estar en el Bastión del Honor ahora mismo, eso es todo.$B$BQuizás esté destinado a formar parte de algo más grande.' WHERE `entry`=10500;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mumman ha establecido su humilde tienda cerca de la entrada principal de la ciudad, justo después de pasar por la posada. Es un cocinero excelente.' WHERE `entry`=10501;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La sastrería de Keelen, en la esquina noroeste del Bazar, tiene un telar como el que describes.' WHERE `entry`=10502;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='"Es demasiado pronto para que me controles, pues has dejado el juego recientemente."' WHERE `entry`=10505;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Para cruzar mi sala, debes vencerme en un juego de habilidad! Controla a tu rey cuando estés $Gpreparado:preparada; para comenzar...' WHERE `entry`=10506;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Cualquier consejo que pueda dar para ayudarte en tu lucha contra los ogros, lo tendrás!' WHERE `entry`=10508;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los detalles de este anuncio exigen la ejecución de una loba enorme. Parece que tiene su guarida al sur de la Cresta Dentada, cerca de la fortaleza de la Horda.' WHERE `entry`=10509;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Encontrarás al maestro de establos cerca del centro de cuidadores.' WHERE `entry`=10527;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C – soy un oficial de Honor que actúa en nombre de la ciudad de Lunargenta. Mi deber es ayudar a los aventureros que hayan recibido sellos de mención de honor de la Horda.$B$BAcepto sellos en diferentes cantidades, pero el intercambio más beneficioso para ti es entregarlos en series de diez a la vez. Introduciré tus hazañas en nuestros registros cuando entregues tus sellos. Como resultado, obtendrás reconocimiento de la ciudad de Lunargenta por tu deber y servicio.',`Text0_1_loc6`='Saludos, $C – soy un oficial de Honor que actúa en nombre de la ciudad de Lunargenta. Mi deber es ayudar a los aventureros que hayan recibido sellos de mención de honor de la Horda.$B$BAcepto sellos en diferentes cantidades, pero el intercambio más beneficioso para ti es entregarlos en series de diez a la vez. Introduciré tus hazañas en nuestros registros cuando entregues tus sellos. Como resultado, obtendrás reconocimiento de la ciudad de Lunargenta por tu deber y servicio.' WHERE `entry`=10530;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C – soy una oficial de honor que actúa en nombre de El Exodar. Mi deber es ayudar a los aventureros que hayan recibido sellos de mención de honor de la Alianza.$B$BAcepto sellos en diferentes cantidades, pero el intercambio más beneficioso para ti es entregarlos en series de diez a la vez. Introduciré tus hazañas en nuestros registros cuando entregues tus sellos. Como resultado, obtendrás reconocimiento de El Exodar por tu deber y servicio.' WHERE `entry`=10531;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Negociaciones? ¡NEGOCIACIONES!$B$BPermíteme dejar totalmente clara nuestra postura. Somos un pueblo libre, capaz de hacer lo que queramos y movernos como nos plazca. La Expedición de Cenarion no es dueña de esta cadena montañosa.$B$B¡No habrá negociaciones! Ahora vuelve con los druidas y diles que seguiremos haciendo lo que nos plazca.' WHERE `entry`=10533;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos venido a la Foresta Ruuan para ver si podemos restablecer el equilibrio de la naturaleza en estas montañas.$B$BSin embargo, la intromisión de los arakkoa y el Culto Vermis está dificultando la tarea.$B$BAfortunadamente, hay gente como tú y Samia, dispuesta a echarnos una mano.' WHERE `entry`=10534;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Jaja, ¿has vuelto? No esperarás en serio que cambie de opinión acerca de ti, ¿verdad?$B$BPiérdete.' WHERE `entry`=10535;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Detecto una carga en tu región paranarbulátrica! Debes de haber utilizado el Cefirium Capacitorium últimamente – no puedes volver a usarlo hasta que se agote la carga...' WHERE `entry`=10537;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estoy aquí a instancias de mi maestro, el Barón Sablecrín, para ayudar a la Expedición de Cenarion con sus problemas con el Culto Vermis.$B$BSoy bastante experta en estas cosas.' WHERE `entry`=10538;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuaar, ¿te encuentras bien? Hoy no pareces tú.' WHERE `entry`=10539;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cierto, la reunión. Vayamos al grano, entonces.$B$BEl hecho es que la madera y el ganado que se está reuniendo de la Foresta Ruuan, de lo que tú eres responsable, ha disminuido sobremanera.$B$B¡Necesitamos esos recursos, pero has permitido que los druidas de la Expedición de Cenarion se interpongan en nuestro camino!' WHERE `entry`=10541;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡No quiero excusas, exijo resultados!$B$B¿Crees que lo estamos haciendo aquí es una broma? Si no lo hacemos bien, no solo vendrán los llamados bienhechores, sino que también se nos considerará indignos para la elevación en las filas de Alanegra.$B$B¿Es eso lo que quieres?' WHERE `entry`=10542;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Al menos podrías intentar sonar algo convincente.$B$BMira, Nuaar, no iba a decirte esto, pero quizás sea lo mejor, porque también va a involucrarte a ti. Maxnar planea un ataque total a los druidas de la Foresta Ruuan. Y pretende aniquilarlos.$B$BYa es lo malo que hayamos estado luchando contra los ogros Peña\'mok, así que no podemos permitirnos tener que encargarnos de otro frente. He organizado una tregua temporal con los arakkoa.$B$BBueno, ¿qué opinas?' WHERE `entry`=10543;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estamos organizando los preparativos definitivos mientras hablamos. El resto dependerá de con qué rapidez puedas organizar tus tropas en la Foresta Ruuan.$B$B¿Crees que puedes ocuparte de eso y tenerlo hecho rápidamente?' WHERE `entry`=10544;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Esa es la actitud!$B$BMe alegro de que hayamos tenido esta breve reunión. Me siento mucho mejor con respecto al ataque ahora. Como líderes como tú en el frente, ¿cómo vamos a perder?$B$BBueno, ya tienes tus órdenes de marcha. ¡Ahora vuelve a la Foresta Ruuan y haz que suceda!' WHERE `entry`=10545;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Karazhan no fue siempre tan... siniestro. El oscurecimiento de Karazhan llegó solo con la muerte de Medivh. O con lo que creemos que fue la muerte de Medivh.' WHERE `entry`=10546;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tengo que decir que la Bahía del Botín tiene la mejor sopa de almejas que se pueda probar en los Mares del Sur. Ah, ¿no te interesa la cocina? Pues deja que sea el primero en darte la bienvenida al atracadero de La Fantasía de la Doncella, el mejor barco de pasajeros que hay para navegar entre los Reinos del Este y Kalimdor. Pronto volverá para realizar otro viaje a Trinquete, así que ponte cómodo hasta que llegue.' WHERE `entry`=10548;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Has llegado hasta aquí, bruto. Ya no puedes volver atrás.' WHERE `entry`=10549;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mi hermano dijo que podíamos hacer mucho dinero con esta ruta comercial, pero no me dijo que yo sería el que viviría a este lado. Si vuelves por este camino en el zepelín, ¿podrías traerme algo de comer? Ellos venden algunas de las mejores piezas de carne de todo Orgrimmar y estoy seguro de que podría aprovechar algo que no lleve muerto varias semanas.' WHERE `entry`=10553;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Esos druidas han explotado una tremenda fuente de energía! Se pueden convertir en animales... proyectar relámpagos y luz lunar intensa... ¡Debo descubrir cómo lo hacen!$B$BQuizás llevan cargas bajo la toga...' WHERE `entry`=10554;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El arakkoa te dedica una mirada fija y encantada, y alterna entre mirarte y mirar a través de ti.>$B$B¿Qué han provocado nuestros esfuerzos, aparte de una abominación mayor que la que intentábamos evitar?' WHERE `entry`=10556;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En este embarcadero puedes encontrar un pasaje a Theramore. Haz caso de lo que digo, esas tierras no son un lugar seguro para la Alianza. Cuidado con dónde pisas y vigila tu espalda.' WHERE `entry`=10557;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En Terrallende nuestras tropas luchan contra la Horda para controlar el Ojo de la Tormenta. Es peligroso luchar entre las fieras energías del Vacío. ¿Tienes lo que hay que tener para unirte a nosotros?' WHERE `entry`=10558;
UPDATE `locales_npc_text` SET `Text1_0_loc6`='Me entristece ver que los fúrbolgs están ya tan corruptos. Solían ser amigos nuestros.',`Text1_1_loc6`='Me entristece ver que los fúrbolgs están ya tan corruptos. Solían ser amigos nuestros.' WHERE `entry`=10560;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este vuelo te lleva al punto remoto de Cresta Canto. ¡Muy peligroso!$B$BPor esto, voy a tener que necesitar una renuncia firmada...' WHERE `entry`=10561;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Siempre codiciamos más datos de vuelo! ¿Dónde quieres ir?' WHERE `entry`=10562;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Entonces, qué es? ¿No ves que soy un enano ocupado?' WHERE `entry`=10563;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aunque explorar estas nuevas tierras es, sin duda, importante, ¡no debes abandonar Azeroth! Te necesitamos más que nunca en la lucha contra los Rapiñadores. ¡Hay que proteger esos recursos!' WHERE `entry`=10565;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Tú, $C! ¡Necesitamos tu ayuda! Los toscos orcos Grito de Guerra están destruyendo las maravillas y la belleza de Vallfresno, y necesitamos tu fuerza para enfrentarnos a ellos en el campo de batalla. ¡Vuelve con nosotros a Azeroth y lucha por el honor de los centinelas Ala de Plata! ¿Te unirás a nosotros?' WHERE `entry`=10566;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay un viejo dicho, "Dale un pescado a un hombre; lo has alimentado un día. Enseña a pescar a un hombre y lo habrás alimentado toda una vida." Por supuesto, querrás cocinar ese pescado, y ahí es donde entro yo.' WHERE `entry`=10568;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola, $N! ¿Cómo van tus aventuras? Mis experimentos progresan bien...$B$B¡Asegúrate de volver conmigo más adelante! ¡Quizás te tenga más oportunidades de probar vuelos!' WHERE `entry`=10569;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Fuerza y honor, $C. Necesitamos soldados con ganas de aprender como tú en el Valle de Alterac; ¡el territorio de los Lobo Gélido debe ser defendido! ¿Volverás a Azeroth para prestar tu ayuda?' WHERE `entry`=10570;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$R! ¡Sí, tú! ¿Por qué pierdes el tiempo en esta ciudad en ruinas cuando deberías estar en la Cuenca de Arathi? La Liga de Arathor ataca despiadadamente; ¡se necesita tu ayuda, y se te recompensará!' WHERE `entry`=10571;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$C, en Azeroth, los centinelas Ala de Plata obstaculizan nuestro avance por el bosque. ¡Te necesitamos en el frente de batalla para hacerlos retroceder! Conseguirás un gran honor y reputación entre los escoltas Grito de Guerra por ayudarnos - ¡únete a nosotros ahora!' WHERE `entry`=10572;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Círculo de Lucha goblin Bonvapor comienza pronto. Celebran combates de práctica en la arena ahora, pero los combates de clasificación comienzan en $4262d.' WHERE `entry`=10576;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Círculo de Lucha goblin Bonvapor ha comenzado. Puedes encontrar organizadores de combates vendiendo estatutos de equipos de arena en Gadgetzan y fuera de la arena de Nagrand y de las Montañas Filospada.' WHERE `entry`=10578;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Círculo de Lucha goblin Bonvapor ha comenzado. Puedes encontrar organizadores de combates vendiendo estatutos de equipos de arena en Gadgetzan y fuera de la arena de Nagrand y de las Montañas Filospada.' WHERE `entry`=10579;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Círculo de Lucha goblin Bonvapor ha comenzado. Puedes encontrar organizadores de combates vendiendo estatutos de equipos de arena en Gadgetzan y fuera de la arena de Nagrand y de las Montañas Filospada.' WHERE `entry`=10595;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El gladiador de sienes plateadas habla en lengua común vacilante.> ¿Qué? ¿Tienes algún problema conmigo? Llévalo a la arena. No pierdo el tiempo con vosotros, pequeños de la "Horda" y de la "Alianza."' WHERE `entry`=10597;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los gladiadores obtienen el derecho a llevar equipamiento como el mío ganando encuentros de arena puntuados.' WHERE `entry`=10598;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El gladiador veterano habla en lengua orca vacilante.> ¿Qué? ¿Tienes algún problema conmigo? Llévalo a la arena. No pierdo el tiempo con vosotros, pequeños de la "Horda" y de la "Alianza."' WHERE `entry`=10599;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los gladiadores obtienen el derecho a llevar equipamiento como el mío ganando encuentros de arena puntuados.' WHERE `entry`=10600;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Eh! ¿Has oído hablar del Círculo de Lucha de Bonvapor?$B$BPuedes encontrar organizadores de combates vendiendo estatutos de equipos de arena en Gadgetzan y fuera de la arena de Nagrand y de las Montañas Filospada.' WHERE `entry`=10601;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola. ¿Te tomas un descanso, sobrestante? Ya no te vemos demasiado por aquí desde que te reasignaron a la Foresta Ruuan.$B$BEcha un vistazo a lo que tengo que ofrecer. Puede que no haya cambiado demasiado desde que hablamos por última vez, pero tengo en abundancia.$B$B¡Y no seas $Gun extraño:una extraña;!' WHERE `entry`=10602;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Mi aversión a las interrupciones solo se ve eclipsada por mi odio hacia Gruul el Asesino de Dragones y sus siete hijos!' WHERE `entry`=10603;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los encuentros de arena puntuados comienzan en $4262d. Los aspirantes a campeones ya están ocupados perfeccionando sus habilidades y adquiriendo el mejor equipo que pueden. ¿Estarás $Gpreparado:preparada;?' WHERE `entry`=10604;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Debes... liberar... a mis hijos...' WHERE `entry`=10605;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡No lo pueden mantener arriba! Al final, sus alas cederán...' WHERE `entry`=10606;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ah, $Gotro:otra; $C que quiere ser $Ggladiador:gladiadora;, ¿eh? ¡Bien!$B$BVendo los estatutos de equipos de arena que necesitarás para participar en los combates de clasificación del Círculo de Lucha de Bonvapor.' WHERE `entry`=10608;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡La Avanzada Rompeloma es un lugar genial en el que estar destinado! ¡Las aberraciones del Foso Sangrante están tan cerca que se puede oler su infecta sangre! ¿Qué mejor lugar para vengarse de un clan que ha traicionado a sus hermanos y ha hecho un pacto con demonios?' WHERE `entry`=10610;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, mortal.' WHERE `entry`=10613;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En los tramos orientales de los Campos de Ala Abisal se encuentra la Fortaleza Faucedraco. Es allí donde el cruel clan de orcos Faucedraco tortura a los dracos que capturan intentando doblegarlos a su voluntad. Muchos han perdido la esperanza y ceden a la voluntad de los Faucedraco. Los demás... se niegan a desembarcar por miedo a que las partidas de guerra Faucedraco los capturen y los lleven de vuelta a la fortaleza.' WHERE `entry`=10614;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo... No tenemos ningún poder. Illidan encontró y entregó a los Faucedraco a su líder - Zuluhed. Este orco era el único responsable de la captura y encarcelamiento de Alexstrasza. ¿Cómo podríamos luchar contra tal poder? ¡Capturó a un Aspecto!$B$BY con igual facilidad, se llevó a mi compañera.' WHERE `entry`=10615;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La lucha contra Kael\'thas e Illidan continúa en todos los frentes. La derrota no es una opción.' WHERE `entry`=10625;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me doy cuenta de que hace poco que te has unido a nosotros y, por eso, puede que no conozcas nuestras tradiciones. En Claro de la Luna, está teniendo lugar una celebración estupenda. Es una época del año en la que honramos a nuestros ancestros, y desde luego, ¡estás $Ginvitado:invitada;! ¡Viaja hasta una de nuestras ciudades si deseas unirte a nosotros!' WHERE `entry`=10631;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡El Festival Lunar está totalmente en marcha en Azeroth! ¡Vuelve allí y honra a tus ancianos en esta maravillosa celebración! Los principales festejos se pueden encontrar en Claro de la Luna, pero hemos proporcionado un transporte cómodo desde Ventormenta, Forjaz, Darnassus, Cima del Trueno, Orgrimmar, y Entrañas. ¡Esperamos verte allí!' WHERE `entry`=10634;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En este lugar yacen misterios que nosotros, los orcos, ansiamos descubrir. Misterios de nuestro pasado, misterios de nuestro futuro y misterios que ofrecen fugaces vistazos a nuestro interior.$B$BMi esperanza es desvelarlos... ¿Me ayudarás a conseguirlo?' WHERE `entry`=10636;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay problemas en el bosque. ¡Debemos descubrir su origen antes de que sea demasiado tarde!' WHERE `entry`=10637;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Pronto echaré raíces y dormiré. Antes de descansar hay cosas que se deben hacer.' WHERE `entry`=10640;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Trajiste el verde a mi tierra durante un breve tiempo. Es suficiente. Estoy en paz. Esto listo para mi largo sueño.' WHERE `entry`=10641;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Éxito! Me he ajustado a la Ciudadela del Fuego Infernal y a la magia que hay en su interior. ¡Ah, tanta energía!$B$BHay innumerables orcos viles en aquel lugar... ¡pero eso no es todo! También siento poder demoníaco dentro de esos muros...$B$BSi te abres, podré comprender tu mente -- brevemente --, tomar pluma y pergamino y registrar mis hallazgos para ti...' WHERE `entry`=10643;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestros enemigos nos rodean. Me gustaría que los Mok\'Nathal tuvieran una vida mejor, pero las tropas contra las que luchamos han escogido otro camino para nosotros.$B$BSi quieres demostrar tu valía a los Mok\'Nathal, ayúdanos a eliminarlos.' WHERE `entry`=10645;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Te asombra lo que ves, $Gextraño:extraña;?$B$BElogiamos a los espíritus de nuestros ancestros manteniendo viva la llama de su recuerdo y rindiéndole homenaje.' WHERE `entry`=10647;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedo instruirte en alquimia. ¿Te interesa?' WHERE `entry`=10649;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedo instruirte en ingeniería. ¿Te interesa?' WHERE `entry`=10650;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedo instruirte en encantamiento. ¿Te interesa?' WHERE `entry`=10651;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedo instruirte en sastrería. ¿Te interesa?' WHERE `entry`=10652;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedo instruirte en peletería. ¿Te interesa?' WHERE `entry`=10653;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedo instruirte en herrería. ¿Te interesa?' WHERE `entry`=10654;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me alegro de verte de nuevo, camarada.' WHERE `entry`=10655;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es agradable caminar y estirar las piernas. ¿Hay algo en lo que pueda ayudarte, $C?' WHERE `entry`=10657;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C. Si se me permite el atrevimiento, te recuerdo que, mientras me ajusto a la magia de la Ciudadela del Fuego Infernal, sin duda habrá celadores del Fuego Infernal pisándome los talones.$B$BAsí que te pido que seas un miembro honesto de tu Horda y actúes como el héroe... ¡y me protejas!' WHERE `entry`=10659;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La búsqueda de mi pueblo. Nuestra lucha contra la Alianza. La guerra contra los ogros y sus maestros gronn.$B$BTodas estas cosas son un gran peso sobre mis hombros, $C.$B$BHasta a mí me viene bien algo de ayuda de vez en cuando.' WHERE `entry`=10661;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Forja Muerta está dirigida por el Consejo de la Sombra, bajo el ojo vigilante de la Legión.' WHERE `entry`=10664;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Abajo se encuentra la Forja Muerta, que tiene un nombre acertado. Si se le dejara hacer lo que pretende, el Consejo de la Sombra suministraría a la Legión con una multitud interminable de infernales.' WHERE `entry`=10666;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sabemos que los cultores del Culto Vermis se congregan al otro lado del túnel. Resistiremos aquí.' WHERE `entry`=10667;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida;, $N. Tengo pericia en el arte de trabajar con tela lunar primigenia. Los sastres interesados en ser especialistas en tejido lunar me piden a menudo que les enseñe nuestras artes.' WHERE `entry`=10669;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me temo que hay poco que pueda enseñarte, $C. Eres libre para echar un vistazo a mi mercancía, por supuesto, pero no la encontrarás útil.' WHERE `entry`=10670;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Encantada de conocerte! Soy Gidge, la experta en sastrería con fuego de hechizo residente en Shattrath. No es que haya competencia, pero si te interesa aprender a ser $Gun sastre:una sastra; fuego de hechizo y no te has decidido por una especialidad, puedo ayudarte.' WHERE `entry`=10671;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si no eres sastre, no creo que te interese ninguno de mis patrones, ¡pero puedes echar un vistazo si quieres!' WHERE `entry`=10672;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si buscas patrones o conocimientos sobre la sastrería con tejido de sombra, te ayudaré con mucho gusto.' WHERE `entry`=10673;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo siento, $C, pero mis mercancías son adecuadas para aquellos que saben por qué extremo de la aguja se introduce el hilo.$B$BTe aseguro que la confección es mucho más que bordar pañuelos.' WHERE `entry`=10674;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estas lágrimas de la diosa han sido bendecidas por Elune, y su energía te ayudará a combatir la magia vil de Archimonde. Usa bien su energía.' WHERE `entry`=10675;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Temen mis palabras. Hacen que sea un ejemplo. Una parodia del único dios verdadero, Neptulon.$B$B<Skar\'this escupe.>$B$BSe acerca el jusssto castigo, mortal. La gloriosa mano del Cazamareas se extenderá hasta los no creyentes como una guadaña contra una nueva cosecha.' WHERE `entry`=10676;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estas ruinas están marcadas por una magia terrible. Debemos hacer lo posible por curar esta gran herida.' WHERE `entry`=10677;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Puerta de la Muerte es un portal que se abre al mundo que la Legión Ardiente utiliza para alimentar a sus canes manáfagos y a otros horrores de tipo canino. Cuando veas a alguna de esas criaturas, de ahí es de donde proviene, y primero pasa por aquí.$B$BÚltimamente nos hemos dado cuenta de que la Legión Ardiente ha aumentado sus convocatorias de estas criaturas a través del portal.$B$B¡Debes cerrar la Puerta de la Muerte antes de que se convierta en una marea inexorable y todo esté perdido!' WHERE `entry`=10679;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vine a esta tierra para purificar espíritus purificados, pero, después del ataque, me temo que mi misión ha fracasado. Se han perdido tantos... por favor, debes ayudarlos.' WHERE `entry`=10680;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Que la Luz te guíe, $N.' WHERE `entry`=10682;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Llevo a cabo mis servicios al Bastión del Honor con gran orgullo.' WHERE `entry`=10683;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cuando establecí mi academia para pícaros en Shattrath, no estaba tan abarrotada. Entonces, los refugiados comenzaron a venir en avalancha y los naaru necesitaron todo edificio disponible para alojarlos.$B$BHe cerrado las puertas de mi academia por ahora, pero no es permanente. Buscaré un nuevo lugar en el que establecerme.$B$BEntonces, una nueva generación de pícaros aprenderá cosas tan básicas como gubia y sonreír, los combates de saqueos, y culpar a otro por romper polimorfos. Y quién podría olvidar la regla principal de la picardía: los pícaros de verdad no amagan.',`Text1_0_loc6`='El truco para utilizar la evasión es darse cuenta de que los mejores quince segundos de tu vida a menudo son los últimos.' WHERE `entry`=10686;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Espadas, garras, dagas, por supuesto – he instruido a pícaros en el uso de todas ellas. Pero las mazas no las pienso tocar, ni instruiré a ningún pícaro al que se le meta en la cabeza blandir una.$B$BNo hay nada sigiloso en una maza. Si vas por ese camino habrá una muerte ruidosa y una limpieza larga.',`Text2_1_loc6`='Este lugar está infestado... Bueno, tendremos que salir de aquí con las reliquias tan rápido como nos sea posible.',`Text3_0_loc6`='Mis primeras clases siempre están llenas de chicos con ojos brillantes. Pero pocos aprueban la primera prueba.$B$BMe he cansado de gritar "cuando grito desaparecer, tenéis que desaparecer". No escuchan nunca. Ahora, la prueba simplemente mata al inepto.' WHERE `entry`=10686;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='Nunca salgo de casa sin suministro abundante de partículas explosivas, especialmente cuando trabajo con otros. Te garantizo que a alguien se le acabará cada vez.$B$BCuando las opciones son repartir material o dejar que el pobre inocente muera, ayudo... casi siempre.' WHERE `entry`=10686;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Soy Zarevhi del Consorcio. Mis habilidades como transmutador son conocidas en mil mundos.' WHERE `entry`=10687;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Oh, no! ¡Mi Fei Fei nunca cogería las cosas de otra persona! Es un buen perro... solo necesita un poco de atención a veces. ¡Si le das un regalito, verás!' WHERE `entry`=10689;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Solía divertirme aceptando retos de cualquiera que me los ofreciera, pero hace tiempo que me he cansado de victorias tan fáciles.$B$BPor ahora, me estoy tomando algo de tiempo para que mi negocio de componentes despegue y para escribir un libro sobre mis experiencias en Martillo Crepuscular.' WHERE `entry`=10691;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestro alquimista residente, Lorokeem, se trajo su equipamiento con él, de hecho. Puede ser ligeramente diferente de lo que acostumbras a usar – es un después de todo – pero debería satisfacer tus necesidades. Lleva el mando en la parte noreste de la parte baja de la ciudad.' WHERE `entry`=10696;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay dos mercaderes de gemas residentes: uno trabaja estrechamente con los Arúspices, y el otro es leal a los Aldor. ¿A quién buscas?' WHERE `entry`=10697;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Inessera es una mujer astuta que lleva varias gemas, así como suministros de joyería. Puedes encontrar su tienda en la parte norte de Alto Aldor.' WHERE `entry`=10698;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lisrythe puede ser un poco... intimidante al principio, pero tiene lo que buscas. Vende su mercancía en la parte oriental de la Grada del Arúspice.',`Text0_1_loc6`='Lisrythe puede ser un poco... intimidante al principio, pero tiene lo que buscas. Vende su mercancía en la parte oriental de la Grada del Arúspice.' WHERE `entry`=10699;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El constructo hace un gesto hacia la parte oriental de la Grada.>' WHERE `entry`=10702;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No te voy a instruir hasta que seas $Gexaltado:exaltada; a ojos de los draenei.' WHERE `entry`=10705;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Oh, gracias! ¡La negrura que había en mi interior se ha disipado! Veo de nuevo... ¡y veo la cara de quien me ha salvado!' WHERE `entry`=10706;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Vete, $C! ¡Déjanos en paz o entraremos en ti y te mostraremos lo que es la auténtica desesperación!' WHERE `entry`=10707;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No te preocupes. ¡El Puesto Devastación es de lo más sólido! Pero, por si acaso, prepárate para saltar sobre un grifo si la plataforma comienza a romperse...' WHERE `entry`=10717;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ahora que me habéis superado, poned a prueba vuestras habilidades el uno contra el otro! Tomad control de cualquiera de los reyes para comenzar a jugar.' WHERE `entry`=10718;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Deseas hablar conmigo, $R?' WHERE `entry`=10720;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='He visto muchos peligros en estos residuos. ¿Quién sabe qué criaturas acechan en el polvo para atacar al desprevenido?' WHERE `entry`=10721;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No tienes suficiente reputación con los Aldor o no les has jurado lealtad.' WHERE `entry`=10722;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No tienes suficiente reputación con los Arúspices o no les has jurado lealtad.' WHERE `entry`=10723;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Taberna del Descanso del Caminante se encuentra entre el Camino de los Ancestros y el Bazar. Puedes llegar hasta ella desde cualquier dirección.' WHERE `entry`=10727;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los conflictos en Terrallende son los más cruentos que nunca haya visto. Y las víctimas más trágicas son los niños que quedan atrás.$B$BAhora, con la amenaza contra Shattrath de los arakkoa de Skettis, surgirán aún más huérfanos a los que cuidar. Estos niños a menudo viven sin la esperanza de un futuro.$B$BPero tú puedes hacer algo al respecto. Es la Semana de los Niños, $C, y te ruego que trabajes como voluntario para llevar alegría e ilusión a la vida de un niño.' WHERE `entry`=10729;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me siento solo. A veces me gustaría que hubiese más niños de mi edad por aquí. A veces echo de menos el orfanato de Ciudad de Shattrath.' WHERE `entry`=10730;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Hemos llegado ya?',`Text1_0_loc6`='Si los demonios cayeron, ¿de dónde cayeron?' WHERE `entry`=10732;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Creo que necesito un baño. *sniff* ¡Creo que tú también necesitas uno!',`Text2_1_loc6`='Este lugar está infestado... Bueno, tendremos que salir de aquí con las reliquias tan rápido como nos sea posible.',`Text3_0_loc6`='¿Podría un paladín Tauren ser un toro bravo?' WHERE `entry`=10732;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='¿Me hace parecer gorda este vestido?',`Text5_0_loc6`='Seguro que, en el fondo, Illidan es un tío majo. Solo necesita una mamá.' WHERE `entry`=10732;
UPDATE `locales_npc_text` SET `Text6_0_loc6`='¡Apuesto a que los helados serían más ricos si estuvieran tallados!',`Text7_0_loc6`='¡Los chicos tienen gérmenes y las chicas tienen belleza!' WHERE `entry`=10732;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Soy libre! Pero ahora el secreto... peligra. ¡Debo advertir a Akama!' WHERE `entry`=10733;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Por qué no llaman Draenor a Terrallende ya?' WHERE `entry`=10734;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Morthis observa el cielo.>$B$BEn parte, envidio la libertad que disfrutan Grajo y Corvax. En efecto es una gran bendición estar libre de ataduras terrenales.$B$BEl futuro me verá volar de nuevo, pero antes me debo ocupar de una tarea.' WHERE `entry`=10735;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Aran ha sido vencido? Sí... Siento que su puño se ha debilitado, aunque solo un poco. Lo suficiente para permitirme hacer mi trabajo como portero. ¿Quieres que te transporte hasta la Biblioteca del Guardián, $N?' WHERE `entry`=10741;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me alegro de verte de nuevo, $C $N. Dornaa ha estado preguntado por ti.' WHERE `entry`=10742;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los conflictos en Terrallende son los más cruentos que nunca haya visto. Y las víctimas más trágicas son los niños que quedan atrás.$B$BAhora, con la amenaza contra Shattrath de los arakkoa de Skettis, surgirán aún más huérfanos a los que cuidar. Estos niños a menudo viven sin la esperanza de un futuro.' WHERE `entry`=10744;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El druida parece estar en un sueño profundo, totalmente ajeno a lo que le rodea.>' WHERE `entry`=10745;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te lo ruego, $R, date prisa. ¡No dejarán de perseguirme! ¡No podré escapar de ellos! ¡Ni siquiera aquí!' WHERE `entry`=10746;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ilyenia Fuegolunar, elfa de la noche maestra de armas que reside en el Bancal del Guerrero en Darnassus podrá entrenarte en el uso de los arcos.' WHERE `entry`=10748;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ilyenia Fuegolunar, elfa de la noche maestra de armas del Bancal del Guerrero en Darnassus, o Buliwyf, el enano maestro de armas de Armas Mil Maderas en Forjaz podrán entrenarte en el uso de las armas de puño.' WHERE `entry`=10750;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estoy a quí a instancias del príncipe-nexo Haramad. Están ocurriendo cosas extrañas en La Flecha de la Tormenta.' WHERE `entry`=10752;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El maestro de armas enano Buliwyf te puede entrenar tanto con hachas de una mano como de dos manos. Lo encontrarás en Armas Mil Maderas en Forjaz.' WHERE `entry`=10754;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Woo Ping es el maestro de las armas de asta. Entrena a sus pupilos en el Arsenal de Weller en Ventormenta.' WHERE `entry`=10756;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tanto Woo Ping, el maestro que se encuentra en el Arsenal de Weller en Ventormenta, como Ilyenia Fuegolunar que entrena a los elfos de la noche en el Bancal del Guerrero de Darnassus podrán formarte en el arte de los bastones.' WHERE `entry`=10757;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tanto Ilyenia Fuegolunar en el Bancal del Guerrero en Darnassus como Bixi Tambaleapié de Armas Mil Maderas en Forjaz te podrán enseñar a lanzar las armas arrojadizas correctamente.' WHERE `entry`=10759;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ilyenia Fuegolunar, elfa de la noche maestra de armas que reside en el Bancal del Guerrero en Darnassus podrá entrenarte a usar un arco.' WHERE `entry`=10760;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Bixi te ayudará a aprender a lanzar las armas arrojadizas. Si Darnassus es más tu estilo, habla con Ilyenia Fuegolunar, que está en el Bancal del Guerrero.' WHERE `entry`=10762;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si buscas instrucción en las armas, yo la ofrezco.' WHERE `entry`=10767;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ah, así que quieres entrenarte lejos de aquí. Te guiaré. Dime qué arma ha despertado tu interés.' WHERE `entry`=10768;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tanto Hanashi como Sayoc podrán entrenarte en el uso de las hachas de una y dos manos. Se encuentran en el Valle del Honor en Orgrimmar.' WHERE `entry`=10770;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Archibald es el maestro de la ballesta. Es el maestro de armas de Entrañas y se encuentra en el Barrio de la Guerra.' WHERE `entry`=10772;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sayoc, el maestro de armas de Orgrimmar te enseñará cómo usar las armas de puño. Pasa sus días en el Valle del Honor.' WHERE `entry`=10774;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestro instructor de armas de fuego es Ansekhwa, el maestro de armas tauren que se encuentran en la elevación media central de Cima del Trueno.' WHERE `entry`=10775;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ansekhwa enseña sobre las mazas y se encuentra en la elevación menor central de Cima del Trueno.' WHERE `entry`=10776;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tanto Hanashi, el maestro de armas trol del Valle del Honor de Orgrimmar como Ansekhwa, el maestro de armas tauren que entrena en la elevación menor central de Cima del Trueno podrán formarte en el arte de los bastones.' WHERE `entry`=10778;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sayoc y Hanashi, los maestros de armas de Orgrimmar, son auténticos artesanos de las armas arrojadizas. Entrenan a pupilos obsecuentes en el Valle del Honor. Claro que si buscas una astucia imbatible, ve a ver a Ileda de la Plaza del Errante de Lunargenta.' WHERE `entry`=10780;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Archibald es el maestro de la ballesta. Es el maestro de armas de Entrañas y se encuentra en el Barrio de la Guerra.',`Text0_1_loc6`='Archibald es el maestro de la ballesta. Es el maestro de armas de Entrañas y se encuentra en el Barrio de la Guerra.' WHERE `entry`=10781;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Así que te gusta dar puñetazos. Bueno, entonces habla con Sayoc. Él te enseñará.' WHERE `entry`=10783;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestro instructor de armas de fuego es Ansekhwa, el maestro de armas tauren que se encuentran en la elevación media central de Cima del Trueno.' WHERE `entry`=10784;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ansekhwa enseña de mazas y está en la elevación menor central de Cima del Trueno.' WHERE `entry`=10785;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hanashi conoce bien los bastones. Si buscas un instructor más robusto, ve a Cima del Trueno. Ansekhwa te enseñará más cosas, está en el alto central inferior.' WHERE `entry`=10788;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si no estás aquí para entrenar, vete y déjame trabajar.' WHERE `entry`=10789;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No entiendo por qué quieres aprender otra cosa... pero supongo que puedo indicarte adónde ir. ¿Qué tipo de instrucción te interesa?' WHERE `entry`=10790;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cuidado con El Etereum que nos rodea por todas partes.' WHERE `entry`=10791;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ven y yo te mostraré cómo montar las mejores bestias que jamás hayas visto. Tú... tendrás suficiente oro para pagar por mis servicios... Supongo.' WHERE `entry`=10792;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Bienvenido a Soto Eterno, $C. Timeon y yo llevamos algún tiempo estudiando a los arakkoa de las Montañas Filospada y hemos encontrado algunas diferencias alarmantes entre ellos y sus primos de Terokkar.' WHERE `entry`=10793;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Perdona que te interrogue, hijo mío. Mi vista ya no es lo que era, pero el Cuervo me ha bendecido con una vida larga.$B$BPronto llegará el momento de que ocupes mi lugar. Te he enseñado todo lo que sé. Tan solo me arrepiento de no haber sido digno de recuperar el sagrado Libro del Cuervo.' WHERE `entry`=10795;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Los verdaderos creyentes hemos vivido avergonzados desde el día que nuestros traidores primos de Skettis nos robaron el libro, rompieron la tablilla y enterraron los fragmentos en su despreciable ciudad!$B$BReza para que el Cuervo te elija para restaurarlo, hijo mío. Ten fe y recuerda la profecía: "De los sueños de sus enemigos surgirá el Cuervo en el mundo"' WHERE `entry`=10796;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Grok busca ayuda. ¡$GPequeño:Pequeña; $R ve a las montañas y ayuda al Machacasangre!' WHERE `entry`=10797;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Vas a ver a Mog\'dorg?' WHERE `entry`=10798;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Busco un $G héroe : heroína; de gran habilidad. $G Él : Ella; reunirá a los demás para salvar al Clan Machacasangre y a todos los ogros de estas montañas de la tiranía de nuestros opresores gronn.$B$BVuelve a mí cuando hayas averiguado todo lo que puedas. Pero, $C, mantén esto en secreto hasta que llegue el momento o los gronn se enterarán de nuestro movimiento clandestino.' WHERE `entry`=10799;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Los Hijos de Gruul han de ser derrotados si pretendemos librarnos de nuestros grilletes y volver a vivir como ogros libres! $B$BDebemos trabajar silenciosamente pero rápido si queremos que nuestra resistencia clandestina tenga alguna posibilidad de éxito.' WHERE `entry`=10800;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Temo que solo sea posible salvar al Coronel Jules mediante un peligroso ritual: un exorcismo.' WHERE `entry`=10801;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Acaso me engañan mis ojos? ¿Eres realmente tú, hermano Rokkaram, después de todo este tiempo?$B$BSi estás buscando a Sai\'kkal, suele caminar entre los cristales en la zona oeste de La Cumbre del Vórtice. Parece preocupado por algo que él describe como una presencia bruta y primitiva en ese lugar, aunque no he visto nada así.' WHERE `entry`=10807;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puede que sepas un par de cosas acerca de montar animales pero ¿has despegado alguna vez? Los Martillo Salvaje sabemos como cabalgar el viento con los mejores. Si te atreves, te enseñaré como Dios manda por un módico precio.' WHERE `entry`=10808;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué es lo primero que te viene en mente cuando piensas en los Martillo Salvaje?¡Jinetes de Grifos si estás en tus cabales! Sombraluna no es Pico Nidal, pero también tenemos buenas mercancías para ti, eso es, buenas mercancías. ¡Echa un vistazo a mis bellezas a ver si alguna te llama la atención!' WHERE `entry`=10809;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Parece que sabes manejarte con una bestia que corre por la tierra pero si quieres mejorar, necesitarás entrenamiento y dedicación. Y oro. Tienes oro, ¿verdad?' WHERE `entry`=10817;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Son buenas bestias, ¿verdad? Los dracoleones que criamos aquí están entrenados para soportar las condiciones climatológicas extremas y duras de Draenor. Verás que son fiables y robustos.' WHERE `entry`=10818;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No me importa quién eres ni qué haces fuera de la Guardia del cielo, pero mientras estés aquí harás lo que se te diga.' WHERE `entry`=10819;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Cuando sea mayor quiero ser curtidor!' WHERE `entry`=10821;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos recibido algunas quejas acerca de este... individuo. Me han comentado que es de carácter sombrío. Estoy aquí para determinar si su negocio es o no legal... y para tomar medidas si es necesario.' WHERE `entry`=10830;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si consigues completar una baraja de Tormentas, te recompensaremos con un naipe superior de la Luna Negra de la Cólera. Esto aumentará tus posibilidades de asestar un impacto crítico con hechizos o ataques físicos hasta que consigas uno.' WHERE `entry`=10831;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si consigues completar una baraja de Locuras, te recompensaremos con un naipe superior de la Luna Negra de la Demencia. El poder de la demencia te llenará cada vez que mates a un enemigo.' WHERE `entry`=10832;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si consigues completar una baraja de Bendiciones, te recompensaremos con un naipe superior de la Luna Negra de la Cruzada. Esto aumentará el daño que inflijas con tus hechizos y ataques a medida que sigas luchando.' WHERE `entry`=10833;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si consigues completar una baraja de Furias, te recompensaremos con un naipe superior de la Luna Negra de la Venganza. Mientras poseas este naipe cualquier jugador que te ataque tendrá una posibilidad de sufrir Venganza Sagrada.' WHERE `entry`=10834;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tened cuidado, amigos. El Traidor medita en el patio de ahí al lado' WHERE `entry`=10835;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Shhh! Habla más bajo. Los bebés están durmiendo.' WHERE `entry`=10837;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Será mejor que te despiertes rápido, novato. Esto no es nuestro cómodo patio trasero en Skettis, ¡esto son las Montañas Filospada! ¡Un paso en falso y será caer y arder!' WHERE `entry`=10841;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedes encontrar al maestro Pyreanor entre los consejeros de Thrall en el Fuerte Grommash, en el Valle de la Sabiduría.' WHERE `entry`=10843;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedo enseñarte joyería. ¿Te interesa?' WHERE `entry`=10846;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué querer? Oler raro para ser orco vil...' WHERE `entry`=10847;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tú no sentir como en tu casa. ¿Quizá querer comer algo?' WHERE `entry`=10848;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='A veces esos detestables elfos de sangre vienen por aquí y necesitan algún reactivo inane o algún veneno. Tengo que mantener mis reservas de esos productos llenas todo el tiempo para que no le lleguen rumores a Lord Illidan de que los Faucedraco no están colaborando...$B$B<Rumpus traga con dificultad>$B$BNo queremos que pase eso...' WHERE `entry`=10849;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡OH SÍ! ¿Quieres probar estas pistolas, gusano? ¡Entra!',`Text1_0_loc6`='¿Cómo compites contra estas serpientes pitón? ¡Oooooooooh uaaaaaaa, $Ghermano:hermana;!' WHERE `entry`=10850;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¿Has oído eso? ¿Has oído ese canto, $Ghermano:hermana;? ¡Están animando al Julkster!',`Text3_0_loc6`='¡¿¿¿Qué harás cuando la Julkomanía te atrape???!' WHERE `entry`=10850;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$G Hermano:Hermana;, a partir de ahora se te considera $G unido:unida; por la sangre al Ala Abisal. ¡El Ala Abisal al completo está detrás de ti ahora! ¡Ha llegado el momento de atacar al corazón de nuestros enemigos! Debemos reclamar nuestra herencia perdida.' WHERE `entry`=10854;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Has sido de gran ayuda para nuestra causa, $N, pero no podrás seguir avanzando hasta que domines el arte del vuelo. Solo los jinetes más hábiles pueden ayudar a los Ala Abisal.' WHERE `entry`=10855;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Debes haberte preguntado por qué hablo con tanta claridad para ser un ogro. Es un efecto secundario de los cristales que crecen en el bancal que rodea Ogri\'la. Los ogros de allí me han bendecido con esta vara, y su cristal ha bendecido mi mente.$B$BA través de tus continuas acciones arriba en Ogri\'la, espero poder llevar esta bendición a mis hermanos ogros, algún día. Te agradezco todo lo que has hecho y harás, mi $G rey: reina;.' WHERE `entry`=10860;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Otro, eh? Nunca lo conseguirás...' WHERE `entry`=10862;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Recuerda que solo puedes escoger un trabajo de recolección cada día. Además de eso, siempre buscamos cristales. Hasta el peón más insignificante puede traerlos al campamento.' WHERE `entry`=10863;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No tengo nada nuevo para ti, chico. Las labores manuales se te han quedado pequeñas...' WHERE `entry`=10864;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El control de Illidan sobre mi tribu es fuerte, $N. Pronto acabará, de un modo u otro.$B$B¿Quieres seguir adelante, $N? El futuro de mi pueblo está en nuestras manos.' WHERE `entry`=10866;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Guarda silencio sobre lo que oigas y veas aquí, $R.' WHERE `entry`=10877;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Grok oír que tú es $Gel nuevo rey:la nueva reina; de los gros. Enhor...Enohra...rabuena, $Gpequeño:pequeña; $R.' WHERE `entry`=10879;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿En qué puedo ayudarte, $C?' WHERE `entry`=10887;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este montón de calaveras apesta a vileza. Temes que los arakkoa hayan añadido algo al montón recientemente.' WHERE `entry`=10888;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Si buscas artilugios, chismes, trastos, cachivaches, chirimbolos, cacharros o cosas, has venido al lugar adecuado!' WHERE `entry`=10889;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me resultas familiar...' WHERE `entry`=10892;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mis visiones están llenas de eventos que suceden por el mundo y de algunos que pueden llegar a suceder. Muchos de ellos son desalentadores, pero no debemos creer que no puedan cambiar.' WHERE `entry`=10893;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si necesitas reactivos, has venido al lugar adecuado. No encontrarás una selección mejor en todo Shattrath.' WHERE `entry`=10894;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El sobrestante jefe Limo limpia sus botas.>' WHERE `entry`=10896;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Firme, soldado!' WHERE `entry`=10901;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Te maldigo mil veces, $R! ¡Esta prisión no me retendrá durante mucho tiempo!' WHERE `entry`=10903;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Piérdete antes de que te dé una paliza con mi botarang.' WHERE `entry`=10904;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Oh, ¿te gusta mi parche, verdad? Bueno, asegúrate de no tener que llevar uno $G chico : chica;.' WHERE `entry`=10906;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Huh? ¿Qué? Estoy un poco ocupada, $Gamigo:amiga;.$B$BClaro que si has venido para ayudar, tengo todo el tiempo del mundo.' WHERE `entry`=10907;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La forja de cristal vil puede usarse para transformar tus fragmentos de ápices en frascos inestables de la bestia. Estos frascos vuelven al que los bebe más ágil, fuerte y feliz.$B$BElige, abajo.' WHERE `entry`=10910;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Al colocar los fragmentos de ápices en la forja de cristal vil y tirar de la palanca, el aparato hace un ruido de otro mundo mientras los convierte en polvo. Unos instantes después tu frasco sale de su "boca".>' WHERE `entry`=10911;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Al colocar los fragmentos de ápices en la forja de cristal vil y tirar de la palanca, el aparato hace un ruido de otro mundo mientras los convierte en polvo. Unos instantes después tu frasco sale de su "boca".>' WHERE `entry`=10912;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La forja de cristal Bash\'ir puede usarse para transformar tus fragmentos de ápices en frascos inestables del hechicero. Estos frascos vuelven al que los bebe más ágil y feliz y aumentan la efectividad de sus hechizos de ataque y sanación.$B$BElige debajo.' WHERE `entry`=10913;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Cuando colocas tus fragmentos de ápices en el hueco de la forja de cristal Bash\'ir y tiras de la palanca, el aparato, literalmente, los desintegra. Unos momentos después tu frasco aparece a tus pies.>' WHERE `entry`=10914;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Esperamos que puedas librarnos de nuestros dolores de cabeza. ¡Intenta tener diez hijos, los diez intentando ser algo para lo que no están destinados!$B$BAun así, como padre, hacemos lo que debemos.' WHERE `entry`=10916;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Tienes hijos, $C?$B$BDeja que te digamos algo, creemos que éramos mucho más felices antes de venir a Ogri\'la y que los cristales nos hicieran más conscientes. Antes los habríamos golpeado en la cabeza para callarlos. Ahora, todo lo que hacen es darnos la lata con el Guardia del cielo Sha\'tari.$B$B¡La ignorancia es felicidad!' WHERE `entry`=10917;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ya que eres relativamente nuevo en Ogri\'la, no has estado expuesto a los cristales el tiempo suficiente para obtener todos sus beneficios. Además, solo tienes una cabeza.$B$BSin embargo, nosotros, como puedes imaginar, sabemos mucho de bastantes cosas.$B$BÚltimamente, nos hemos interesado en los demonios de los Campamentos Forja. En concreto, hemos diseñado una forma de deshacernos de ellos para siempre.$B$BAl menos en teoría.' WHERE `entry`=10919;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nos gustaría poder crear más de una runaoscura diaria, pero sería una pérdida de tiempo. Simplemente, no es físicamente posible. Además, ni siquiera sería fiable crearlas a ese ritmo.$B$BY confía en nosotros, si hubiera alguien lo suficientemente inteligente para ayudar, sería reclutado y tendríamos todas las runaoscuras que quisiéramos.' WHERE `entry`=10920;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La forja de cristal Bash\'ir puede usarse para transformar tus fragmentos de ápices en frascos inestables del hechicero. Estos frascos vuelven al que los bebe más ágil y feliz y aumentan la efectividad de sus hechizos de ataque y sanación.$B$BSin embargo, parece que no tienes los 10 fragmentos necesarios para comprar un frasco.' WHERE `entry`=10921;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La forja de cristal vil puede usarse para transformar tus fragmentos de ápices en frascos de la bestia. Estos frascos vuelven al que los bebe más ágil, fuerte y feliz.$B$BSin embargo, parece que no tienes los 10 fragmentos necesarios para comprar un frasco.' WHERE `entry`=10922;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gahk nuevo en Ogri\'la como $Gpequeño:pequeña; $R. Ser como el cielo del que hablan los ogros abajo. Pero a Gahk le duelen las cabezas porque el cristal hacer a Gahk más listo.$B$B<Las dos cabezas de Gahk asienten.>$B$BNosotros aprender a hacer una runaoscura de cristal forjado. ¡Eso ayudar a Ogros a machacar a los demonios!' WHERE `entry`=10923;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Gahk ser listo ahora! ¡Nosotros saber convertir la runaoscura en runaoscura de cristal forjado!' WHERE `entry`=10924;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$N machacar bien puerta de distorsión, pero los demonios volver a hacerse fuertes.$B$B¡$N traer a Gahk más runaoscuras y Gahk hacer runaoscura de cristal forjado especial para $N!' WHERE `entry`=10925;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; a Ogri\'la, $C.$B$BLos ogros aquí hemos conseguido gran agudeza mental gracias a las emanaciones mágicas de los cristales que nos rodean. Aunque no acabamos de entender su funcionamiento, hemos logrado apreciar nuestro nuevo hogar y solo queremos vivir en paz.$B$BPor desgracia, los demonios y etéreos y, en especial, el Vuelo Negro, no nos dejan alcanzar esa paz.' WHERE `entry`=10926;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tú no. No eres lo suficientemente fuerte para participar en partidas de clasificación. Puedes buscar a un maestro de batalla de arena y practicar. Nunca es demasiado pronto para empezar a entrenar.' WHERE `entry`=10929;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Has venido a hacerme daño? No tengo nada que ver con la guerra, $R.$B$B¡Solo soy un erudito enfermizo, por favor déjame tranquilo!' WHERE `entry`=10930;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El huevo parece fuerte tal y como debería ser un huevo del Vuelo Negro. ¿Quizá si colocaras muchos fragmentos de ápices cerca, sus vibraciones harían que el huevo se rompiera?$B$BUna lástima que no tengas los 35 fragmentos necesarios para llevar a cabo la tarea.' WHERE `entry`=10932;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Angela arrulla de nuevo al manáfago antes de dirigirse a ti.>$B$BJir\'ve siempre ha sido más pequeño que los otros manáfagos, pero eso nunca le ha preocupado. Y Terrallende va a cambiar eso, ¿verdad Jir\'ve?$B$BEl angelito de mamá va a crecer para convertirse en un manáfago grande y fiero. ¡Eso es!' WHERE `entry`=10934;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Expedición Cenarion me salvo de una muerte segura dentro de las minas. ¡Les debo mi vida! Ofrezco mis servicios de reparación y venta de reactivos a todos los aliados de la Expedición Cenarion.' WHERE `entry`=10936;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aquí encontrarás mucho que hacer, $N.' WHERE `entry`=10937;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ack, has estado $Gocupado:ocupada;, ¿verdad? No puedo agradecerte lo suficiente que hayas bombardeado a los demonios y que nos hayas reabastecido de monturas.$B$BEstás haciendo que nuestro trabajo sea más fácil. ¡Sigue así $Gchico:chica;!' WHERE `entry`=10938;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Debemos restaurar nuestro poder en estas minas!' WHERE `entry`=10939;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El sobrestante Sangreoscura gruñe.>' WHERE `entry`=10940;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Debo marcharme de aquí de una vez!' WHERE `entry`=10947;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La reliquia de ápices brilla, revelando la inteligencia oculta en su interior.' WHERE `entry`=10948;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El monumento de ápices amenaza desde arriba.' WHERE `entry`=10949;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Firme, Capitán!' WHERE `entry`=10950;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Unos ojos ardientes dentro del prisma te llenan de una sensación de pavor... Especialmente al saber que no tienes los 35 fragmentos de ápices necesarios para invocar a este demonio.' WHERE `entry`=10951;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Eres una pequeña cría, ¿verdad? Mis ojos me decepcionan.$B$B¿Supongo que estás aquí para comprar alguno de mis escritos exóticos?' WHERE `entry`=10953;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Olvida las baratijas de Vixton. ¿Quieres material bueno? Habla con Krixel aquí. Tengo justo lo que necesitas, suponiendo que tengas la reputación adecuada. Ya sabes, en la arena. Este material no es para nenazas.' WHERE `entry`=10957;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estamos probando la Batalla del Monte Hyjal. Para participar debes tener, como mínimo, reputación HONORABLE con El Ojo Violeta.$B$BEsa es la reputación que ganas la hacer Karazhan.' WHERE `entry`=10961;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ahora que estás preparado para el Monte Hyjal puedo teletransportarte a las Cavernas del Tiempo. Cuando estés preparado, házmelo saber.' WHERE `entry`=10962;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ahora que tienes tu llave de El Templo Oscuro (Medallón de Karabor), puedo teletransportarte directamente a El Templo Oscuro. Avísame cuando estés $Glisto:lista;.' WHERE `entry`=10964;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$GBienvenido amigo:Bienvenida amiga;! ¿Qué te gustaría ver?' WHERE `entry`=10965;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos $Ghermano:hermana;. ¿Cómo podemos servirte?' WHERE `entry`=10966;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Cómo podemos ayudarte?' WHERE `entry`=10967;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestra comida debería saciar incluso el hambre más voraz.' WHERE `entry`=10973;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Aún estás $Ghambriento:hambrienta; $Ghermano:hermana;? ¿Cómo podemos ayudar?' WHERE `entry`=10974;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tenemos un buen festín para tu amigo.' WHERE `entry`=10975;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué delicias podemos ofrecerte?' WHERE `entry`=10976;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola $Gseñor:señora;! Si quieres, tengo una raya abisal esperando para llevarte a la Avanzada de la Guardia del Cielo, en lo alto de las montañas Filospada.$B$BTan solo házmelo saber.' WHERE `entry`=10979;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Buenos días. Cuando hayas subido un poco más en las filas de la Guardia del cielo Sha\'tari, podré ofrecerte un vuelo directo a la Avanzada de la Guardia del Cielo, nuestro campamento en lo alto de las Montañas Filospada.' WHERE `entry`=10980;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿No tienes nada mejor que hacer que molestarme, $Gchico:chica;?$B$B¡Ahora vete de aquí y demuestra tu entereza!' WHERE `entry`=10988;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ese es el espíritu, $Gchico:chica;! ¡Estamos muy orgullosos del trabajo que estás haciendo para nosotros!$B$BY ya sabes, si quieres un viaje rápido a las Montañas Skethyl, baja a hablar con la cuidadora de la Guardia del cielo Irena. Ella se encargará.' WHERE `entry`=10989;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Veo que has estado invirtiendo tu tiempo por aquí, $C.$B$BInvierte más, aún quedan demonios, novato.' WHERE `entry`=10992;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tu nombre parece estar en los labios de todo el mundo en la avanzada. ¡No dejes que se te suba a la cabeza; yo sé que aún puedes hacerlo mejor!$B$BTe estaré vigilando, novato.' WHERE `entry`=10994;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vale, lo admito, has demostrado lo que vales y más, $N.$B$BSi antes he sido dura contigo, ha sido porque he visto la chispa de un auténtico Guardia del cielo en ti. ¡Me atrevería a decir que eres el mejor as que tenemos!$B$BCuando quieras ayudar, estaré más que orgullosa de entregarte otro montón de bombas.' WHERE `entry`=10995;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola $C!$B$B<Khatie te mira astutamente.>$B$B¡Cuando quieras domar más rayas de éter, asegúrate de venir a verme!' WHERE `entry`=10997;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué buscas?' WHERE `entry`=11000;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ya sabes, $N, es un gran honor trabajar contigo.$B$BCuando no estés domando, quizá podamos $Gsalir a cenar : juntarnos para una noche de chicas;.$B$B¡Oh! ¿He mencionado que ahora podemos llevarte a nuestro campamento en las Montañas Skethyl realmente rápido? Si quieres, hablaré con la cuidadora de la Guardia del cielo Irena acerca de eso.' WHERE `entry`=11001;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Así que parece que habéis andado mucho juntos pero seguís echando de menos quedar juntos después del trabajo.$B$B¿Soy yo?$B$B<Khatie se alegra a propósito.>$B$BDe todos modos, solo quería decir que yo... nosotros apreciamos mucho todo lo que has hecho por la Guardia del cielo.' WHERE `entry`=11002;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Espero que no creas que soy una acosadora ni nada de eso, $N.$B$BQuiero decir... sé que ahora eres muy $Gfamoso:famosa; entre la Guardia del cielo y eso y bien... Estoy segura de que no querrás andar con una humilde currante como yo.$B$B¡Pero si alguna vez quieres quedar para estar juntos o domar algunas rayas más, pásate por aquí! Estaré aquí.$B$BTe echo de menos...' WHERE `entry`=11004;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las reliquias son, en verdad, un gran misterio. Quizá si las estudias de forma regular puedas adquirir un mejor conocimiento de sus capacidades.' WHERE `entry`=11007;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El acero no es fuerte, exaltado, la carne lo es más. El acero toma su fuerza de quien lo empuña. Pero basta de eso... ¿Cómo podemos ayudarte, exaltado?' WHERE `entry`=11008;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<La cabeza izquierda de Chu\'a\'lor dice, >$B$B¡Tu entusiasmo nos parece de lo más interesante...$B$B<Su cabeza derecha termina,>...y queremos que sepas que realmente apreciamos tus esfuerzos!' WHERE `entry`=11009;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tu nombre comienza a ser venerado por aquí.$B$B<El ojo izquierdo de Chu\'a\'lor te perfora con su pétrea mirada.>$B$BSigue así, $N.' WHERE `entry`=11010;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Primero te convertiste en $Grey:reina; de los ogros de abajo y ahora te has engrandecido entre nosotros, en Ogri\'la.$B$BEsto era inimaginable para $Gun:una:r; $R y, sin embargo, tú eres la prueba viviente. ¡Te lo agradezco, $N y todos nosotros te debemos más de lo que jamás podremos pagarte!' WHERE `entry`=11011;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aquí en Ogri\'la tenemos tiempo para considerar el acertijo del acero. Pero basta de eso, ¿cómo podemos ayudar?' WHERE `entry`=11016;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Tienes acero que necesite ser moldeado?' WHERE `entry`=11017;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No podemos agradecerte lo suficiente todo lo que has hecho para proteger a nuestra familia, $N.$B$BY te das cuenta de que bromeábamos con lo de venderte a uno de nuestros hijos, ¿verdad? No, en serio... aunque podemos sentir que vamos a tener un par de jaquecas más.$B$B¡Júnior, ¿quieres estarte quieto?!' WHERE `entry`=11018;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Pequeño $N empezar a gustarnos! ¡Tú machacar demonios muy bien!' WHERE `entry`=11019;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Nosotros ahora verte por aquí todo el tiempo! $B$B¡Chu\'a\'lor decir que $Gpequeño:pequeña:r; $R ser $Gnuestro héroe:nuestra heroína;!$B$B$N ser de los nuestros ahora. ¡Pero Gahk estar confundido porque $N necesitar ser más grande para eso!' WHERE `entry`=11021;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola! ¿Qué podemos hacer por ti?' WHERE `entry`=11025;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estamos aquí para ayudar.' WHERE `entry`=11026;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tu altruismo nos hace sentirnos humildes, $N. Si en el pasado parecimos un poco vanidosos, ten en cuenta que hemos cambiado de actitud siguiendo tu ejemplo.$B$BTe lo agradecemos y esperamos que sigas ayudándonos en nuestra lucha contra los campamentos Forja.' WHERE `entry`=11028;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Lo estás pasando bien con nosotros en Ogri\'la?$B$BNuestros ogros están fabricando muchas cosas, noche y día. ¡Guardamos las mejores para vendérselas a nuestros invitados más enrollados, como tú!$B$BAsí que mientras andes por ahí haciendo lo que haces y convirtiendo las montañas en un lugar más seguro, pásate por aquí de vez en cuando para ver qué tenemos.$B$BSuerte ahí fuera $Ghombre:mujer;.' WHERE `entry`=11029;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué quieres?' WHERE `entry`=11030;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El señor supremo Mor\'ghor saluda.>' WHERE `entry`=11032;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Oye $N, recuerda que dejaremos que compres estás cosas a medida que vayas realizando buenas acciones. Es como el karma, o algo así.$B$BAsí que ayuda a quién puedas, incluidos nuestros amigos de la Guardia del cielo en el norte.$B$B¡Genial... gracias!' WHERE `entry`=11035;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Chu\'a\'lor ha estado hablando bien de ti, $N. Si te interesan, tenemos un par de brebajes especiales ogros en venta.$B$B¡Están muy ricos!' WHERE `entry`=11036;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué podemos decir, $N? $GTío:tía;, puedes comprar cualquier cosa que tengamos en la tienda.$B$B¡Adelante!' WHERE `entry`=11038;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vete. No tengo nada para ti.' WHERE `entry`=11042;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué misterios del comercio puedo ayudarte a desvelar hoy?' WHERE `entry`=11045;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ven, ven. Si has preparado el cuero adecuadamente, te enseñaré a hacer objetos útiles.' WHERE `entry`=11049;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='He oído hablar de un herrero realmente hábil, al otro lado del mar, llamado Saru Furiacerada. Si quieres ofrecerle un trato, debes dirigirte a la herrería de Orgrimmar, $C. Buena suerte.' WHERE `entry`=11051;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ven, $R. ¿Qué fórmula te interesa hoy?' WHERE `entry`=11055;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vamos a asaltar el Alto Bash\'ir para estudiar su forja de cristal. ¡Ayúdanos si puedes! ¡Nuestro técnico de éter de la Guardia del cielo saldrá en aproximadamente $4581d.' WHERE `entry`=11056;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Una energía oscura terrible emana de este montón de cráneos.' WHERE `entry`=11058;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Necesitas provisiones apra el campo?' WHERE `entry`=11059;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Necesitas requisar algunas provisiones?' WHERE `entry`=11061;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $Gviajero:viajera;.' WHERE `entry`=11064;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestro técnico de éter de la Guardia del cielo y su compañía se dirigen al Alto Bash\'ir. ¡Únete a ellos allí y ayúdales con su misión!' WHERE `entry`=11065;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestra técnico de éter de la Guardia del cielo y su compañía están estudiando la forja de cristal Bash\'ir. ¡Necesita ayuda! ¡Ve al Alto Bash\'ir, encuentra a la técnico y mantenla a salvo!' WHERE `entry`=11066;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Han caído todos lo tenientes de Illidan, $N. Si estas preparado, el camino está despejado.' WHERE `entry`=11077;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Siento que has despejado el camino para mis hermanos. Mi conexión con ellos y con el templo aún es fuerte. ¿Quieres adentrarte más?' WHERE `entry`=11081;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mis hermanos estuvieron aquí hace poco. Mi conexión con ellos se ha vuelto débil. Una presencia fuerte en el patio vigila el camino frente a nosotros.' WHERE `entry`=11082;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida;, $N. Tráeme las marcas que Illidan otorga a sus esbirros más poderosos y te permitiré acceder a mis bienes alquímicos.' WHERE `entry`=11085;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los dracos son mi negocio, $N. tengo autorización para vendérselos a nuestros oficiales de mayor rango.' WHERE `entry`=11090;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué necesitas, $C?' WHERE `entry`=11091;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Salgamos de aquí!' WHERE `entry`=11093;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Los focos de cristal? Te lo explicaré, ya que solo tienes la mitad del cerebro necesario para imaginarlo.$B$BUn foco de cristal mermado puede combinarse con diez fragmentos de ápices, creando un foco de cristal cargado. Estos focos cargados tienen propiedades curativas, pero lo más importante es que pueden usarse con los demonios poseídos.$B$BMira, el foco cargado puede usarse para permitir que un demonio poseído presente poderes especiales. Cuando pase algo de tiempo, Gahk tendrá una misión para ti que consistirá en eso.' WHERE `entry`=11094;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Pareces audaz, $C. ¿Recuerdas los días gloriosos de Theramore, antes de que Jaina nos hiciera abandonar la lucha contra la Horda? ¿Estás harto de tener que guardar tu espada mientras los orcos siguen provocándonos?' WHERE `entry`=11136;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡El Jinete decapitado ataca el pueblo! ¡Alguien debe salvar a los niños y detenerle!' WHERE `entry`=11145;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='De momento, hemos acabado con el terror de El Jinete decapitado. Podemos descansar en paz, pero solo hasta que llegue la oportunidad de vencerle a él y su cabeza, ya que volverá.' WHERE `entry`=11147;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La verdad, ahora mismo no estoy seguro de dónde está el zepelín...' WHERE `entry`=11163;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El zepelín debería haber llegado a Orgrimmar hace poco.' WHERE `entry`=11165;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El zepelín debería haber llegado a Grom\'gol hace poco...' WHERE `entry`=11167;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El zepelín debería haber llegado a Orgrimmar hace poco...' WHERE `entry`=11169;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El zepelín debería haber salido de Orgrimmar hace poco...' WHERE `entry`=11170;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El zepelín debería haber salido de Grom\'gol hace poco...' WHERE `entry`=11172;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El zepelín debería haber llegado a Entrañas hace nada.' WHERE `entry`=11173;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El zepelín debería haber salido de Orgrimmar hace nada.' WHERE `entry`=11174;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El zepelín debería haber salido de Entrañas hace nada.' WHERE `entry`=11175;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡El Jinete decapitado ataca! ¡El fuego amenaza con consumir el pueblo por completo! ¿Qué vamos a hacer?' WHERE `entry`=11177;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El zepelín debería haber llegado a Entrañas hace nada.' WHERE `entry`=11179;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El zepelín debería haber llegado a Grom\'gol hace nada.' WHERE `entry`=11180;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El zepelín debería haber salido de Grom\'gol hace nada.' WHERE `entry`=11181;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El zepelín debería haber salido de Entrañas hace nada.' WHERE `entry`=11182;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me paso el día buscando piezas para el zepelín y Frezza no tiene ni la decencia de pagarme por horas. Pero tú no has venido por eso, ¿verdad? Apuesto a que quieres saber dónde está ahora el zepelín. ¡Estás de suerte, tengo mi dispositivo de seguimiento de zepelines aquí!' WHERE `entry`=11189;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El mercader conocido como Griftah ha sido exiliado de esta ciudad por sus actividades fraudulentas. Tenemos intención de revisar estos pequeños chismes con mucho cuidado. Por favor, no toques este puesto y déjanos hacer nuestro trabajo.' WHERE `entry`=11196;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No encontrarás una cerveza mejor en todo Azeroth.' WHERE `entry`=11209;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los Cebatruenos nos enorgullecemos de ser la mejor destilería de todos los reinos.' WHERE `entry`=11210;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Guau! ¿Realmente quieres comprarnos a nosotros?' WHERE `entry`=11211;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡No ves que tengo sitios a los que ir y gente que visitar! ¡Aparta campesino!' WHERE `entry`=11215;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No puedo más que dar por hecho que sabes quien soy. ¿Y aun así insistes en molestarme?$B$B¿Qué puedes querer que requiera mi atención?' WHERE `entry`=11217;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vamos, ¡no seas ridículo! ¡Yo soy el único Budd Magallenas!$B$BHe recorrido toda esta tierra, desde lo alto del Monte Hyjal hasta las profundidades del Templo Sumergido. ¡De hecho, yo fui el primero en llamarlo el Templo Sumergido!$B$BSí, fui yo. Pero no lo pienses demasiado.' WHERE `entry`=11219;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te recomiendo encarecidamente que visites a Sayge para que te lea tu fortuna. Lo sé, lo sé... es un gnoll. ¡Pero es un gnoll muy especial con el poder de la adivinación!$B$B¡Y continuamente ampliamos la feria con todo tipo de atracciones nuevas!' WHERE `entry`=11220;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sí, bien, escucha $C. Soy mayor de lo que aparento y muy sabio para mi edad. Harías bien en no hacerme demasiadas preguntas.$B$BMira, no tienes que creerme. Lee lo publicado en cualquier tablón de anuncios. ¡Estoy en todas partes!$B$BPero hazte un favor e ignora esas en las que pone "SE BUSCA" en el título. Leves malentendidos, te lo aseguro.' WHERE `entry`=11221;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida;, $C. Estoy aprendiendo el arte de manejar grifos con Baldruc. Espero tener mi propio establo de grifos algún día.' WHERE `entry`=11224;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola $Gguapito:guapita;! ¿Qué puedo hacer por ti?' WHERE `entry`=11226;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué quieres, cariño?' WHERE `entry`=11230;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Oh, ¿yo cocinera? Supongo que es cierto. Una camarera tiene que trabajar en la cocina de vez en cuando y yo he aprendido alguna que otra receta a lo largo de los años. Nada especial, pero son lo suficientemente abundantes como para llenar la considerable barriga de Grok.' WHERE `entry`=11231;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo siento, $N, pero ya has aprendido todo lo que sé acerca de cocinar. Por supuesto, nunca se sabe cuando prepararán algo nuevo nuestros cocineros, así que no seas tímido y visítanos de vez en cuando.' WHERE `entry`=11233;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mi hermano Krixx cree que trabajar en Orgrimmar es malo. Nada se parece a la peste de este lugar. ¡Al menos el jefe me paga un plus de peligrosidad!$B$B¿Qué? ¿Quieres saber dónde está ahora el zepelín? ¡Estás de suerte, tengo mi dispositivo de seguimiento de zepelines aquí!' WHERE `entry`=11234;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Manteneos en guardia. Somos la primera línea de defensa de Theramore contra sus numerosos enemigos de la marisma.' WHERE `entry`=11243;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ahora que los desertores han tomado Punta Perdida, Torre de la Punta Norte es la más lejana de las avanzadas de Theramore.' WHERE `entry`=11245;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué puede hacer un tipo como yo por $Gun:una; $R como tú en un momento como este?',`Text1_0_loc6`='¿Puedo hacer algo por ti, $C?' WHERE `entry`=11256;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¡Si estas aquí para tomar parte en los grandes desechos, ponte en la cola!$B$BMi parte viene con retraso.' WHERE `entry`=11256;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me supera, chico. Soy nuevo en el equipo.$B$BPuedo decirte que he matado montones de trols y bebido mucho grog. Lo único que me falta por ver es el tesoro que Budd está venga a prometer...' WHERE `entry`=11257;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Te refieres al equipo de Budd? Sí, no respeta a las personas. Cogería a cualquiera que supiera usar una pala.$B$BAcepta no-muertos. Budd los odia, es probable que les tenga miedo.',`Text1_0_loc6`='Sí, nunca se sabe quien va a ser el siguiente en unirse, o el siguiente en morir.$B$B¿Mi consejo? No te encariñes con nadie.' WHERE `entry`=11258;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='De acuerdo. Budd ha estado recogiendo gente por todo el mundo.$B$BParece que cuando hay bebida gratis la gente está dispuesta a dejar sus diferencias de lado.' WHERE `entry`=11258;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Se huele a sí mismo y hace una mueca de asco>$B$B¡Ese es el olor de la aventura, $C!',`Text1_0_loc6`='<hip>Esscucha esto, $R. Passha un par de díassh luchando con trols salvajes y desssspuésh cuéntame cómo hueles.' WHERE `entry`=11259;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Oh, eso. Verás, últimamente no ha habido más que grog orco por el campamento.$B$BEl grog orco no parece sentarme bien, tú me entiendes...' WHERE `entry`=11259;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; al Alto del Centinela, $C.' WHERE `entry`=11264;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Escucha tío!$B$BTe diré todo lo que sé, pero primero tienes que salvarme de estos salvajes y sus osos asesinos.$B$B¿Trato hecho?' WHERE `entry`=11279;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Yuju! ¡Tanzar es libre, tío! ¡Qué suerte, todo eso y ni siquiera un colmillo roto!$B$BBendecido, tío. Eso es lo que estoy. No tiene sentido negarlo.' WHERE `entry`=11283;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nunca he experimentado un frío como este, $R. Incluso sin hielo ni nieve, los fríos baldíos de Rasganorte son extremadamente agotadores.' WHERE `entry`=11285;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tu cara me suena. ¿Nos conocemos?',`Text1_0_loc6`='¿Te conozco?' WHERE `entry`=11287;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Deja que te dé un consejo: nunca te fíes de un gnomo.' WHERE `entry`=11287;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vale tío, un trato es un trato...$B$BEra de noche cuando nos metimos en Zul\'Aman para echar un vistazo.$B$BTodo estaba tranquilo hasta que oímos un susurro, como el viento entre los árboles. ¡Antes de que nos diéramos cuenta estábamos rodeados de un montón de salvajes y sus mascotas! Al día siguiente me levanto aquí, metido en una jaula.$B$BDía y noche mataban a los prisioneros, uno a uno. Cada vez que mataban a uno el trol-oso parecía más fuerte.$B$B¡Eso es todo lo que sé sobre este lugar y no quiero saber nada más!' WHERE `entry`=11289;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ese hombre no es más que un loco! ¿Fama... fortuna...? ¡Puff! ¡Este lugar no es más que una trampa mortal, tío!$B$BHe visto todo tipo de trols en mi vida, pero estos son diferentes: escurridizos, fuertes, tramposos.$B$BMe daba la sensación de que sabían que veníamos...' WHERE `entry`=11295;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Budd gira los ojos al ver que te acercas>$B$B¡Ahh, sí, $N, quien pronto conocerá la fama!$B$B¿A qué debo el placer de tu compañía?' WHERE `entry`=11299;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vamos, vamos, $N, relájate y respira hondo. Necesitas aprender a confiar en mí.$B$BTu falta de experiencia en estas situaciones está haciendo que te asustes y el miedo siempre exagera la realidad. ¿Ves? No hay ninguna crisis.$B$BAhora que hemos aclarado esto, tengo que pedirte que dejes de ser tan negativo. No es bueno para la moral del equipo...' WHERE `entry`=11302;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si necesitas hablar con el aprendiz Garion, lo encontrarás dentro de la casita con Tabetha. Por lo menos allí no puede robar los componentes.' WHERE `entry`=11309;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Desearía que tuviéramos un sacerdote o un druida aquí. No puedo continuar mis estudios hasta que me haya recuperado de mis heridas.' WHERE `entry`=11311;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Espera, no me lo digas... $N, ¿verdad?$B$B¿Qué puedo hacer por ti?' WHERE `entry`=11313;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ah, bueno, eso depende de a qué socios te refieras. A lo largo de los años muchos han ido y venido, siempre hay alguien ansioso por aprovecharse de la leyenda que es Budd Magallenas.$B$BPuedes descansar tranquilo sabiendo que la mayoría se han jubilado siendo ricos. Pero ese no es el camino que yo he elegido. Yo siempre he sentido que mi destino es desvelar los mayores misterios del mundo. Claro, si no lo hago yo, ¿quién lo hará?' WHERE `entry`=11314;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Escucha, $N. A estas alturas ya deberías saber que cuando la fortuna llama a tu puerta, primero abres y luego preguntas. ¡No todos los días se presentan oportunidades como esta!$B$B¿Te apuntas o no, $C?$B$B<Budd no espera una respuesta>$B$B¡Pues claro que sí! Vamos, hay mucho trabajo y de momento solo he visto mucha palabrería pero poca acción. Más vale que me demuestres algo antes de que encuentre un sustituto.$B$B¡Te aseguro que mucha gente mataría por una oportunidad como esta!' WHERE `entry`=11315;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ah, ellos. Una triste historia.$B$BHan desaparecido tras los muros de Zul\'Aman. Hace días que no sé nada de ellos.$B$BLo he visto cientos de veces; esa necesidad desenfrenada de obtener fama y fortuna. Intenté detenerlos, les dije que no estaban preparados, pero ellos decidieron correr el riesgo...' WHERE `entry`=11316;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola! Esos inútiles piratas nos están bloqueando en la bahía.$B$B¿Crees que puedes ayudarnos lanzándoles unas cuantas bombas?$B$BNuestro zepelín robado está aquí, ¡salta a bordo antes de que zarpe!' WHERE `entry`=11320;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola! Esos inútiles piratas nos están bloqueando en la bahía.$B$B¿Crees que puedes ayudarnos lanzándoles unas cuantas bombas?$B$BNuestro zepelín robado está a menos de $3078w minutos de aquí. ¡Asegúrate de estar a bordo cuando zarpe!' WHERE `entry`=11322;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola! Esos inútiles piratas nos están bloqueando en la bahía.$B$B¿Crees que puedes ayudarnos lanzándoles unas cuantas bombas?$B$BNuestro zepelín robado casi está aquí... Creo que lo oigo. ¡Asegúrate de estar a bordo cuando zarpe!' WHERE `entry`=11324;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Soy un prisionero!$B$BMe derribaron junto a mi precioso zepelín. ¿Y por qué? ¿Por transportar hombres y material para la Horda? ¡Eso no es nuevo!$B$BAhora me obligan a dar vueltas a la bahía con él para bombardear a los piratas. ¡No puedo seguir así! ¡Apenas lo puedo mantener íntegro a base de saliva y alambre!$B$BNo me hago responsable de lo que te pueda ocurrir, pero deberías subir a bordo antes de que zarpe.$B$B¡Te digo que es un crimen!' WHERE `entry`=11328;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Soy un prisionero!$B$BMe derribaron junto a mi precioso zepelín. ¿Y por qué? ¿Por transportar hombres y material para la Horda? ¡Eso no es nuevo!$B$BAhora me obligan a dar vueltas a la bahía con él para bombardear a los piratas. ¡No puedo seguir así! ¡Apenas lo puedo mantener íntegro a base de saliva y alambre!$B$BSi estás seguro de que quieres ayudarles, volverá a atracar en el puerto en menos de un minuto.$B$B¡Te digo que es un crimen!' WHERE `entry`=11330;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Paciencia, $N. No me convertí en el conocidísimo Budd Magallenas a base de apresurarme.$B$B¡Se están llevando a cabo las preparaciones finales y pronto los secretos y tesoros de Zul\'Aman serán míos!$B$B<Budd se detiene y te mira durante un instante, entonces se ríe.>$B$B...Quiero decir nuestros, por supuesto.' WHERE `entry`=11338;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Paso el día y la noche aquí sentado haciendo munición y encordando ballestas.$B$B¿Supongo que tu también quieres que haga algo para ti?' WHERE `entry`=11352;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué está pasando?',`Text1_0_loc6`='¡Quiero volver al orfanato!' WHERE `entry`=11356;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¡Matrona! ¿Nos va a comer ese hombre malo?',`Text2_1_loc6`='¡Matrona! ¿Nos va a comer ese hombre malo?',`Text3_0_loc6`='¡Si mi padre estuviera vivo, apagaría los fuegos!' WHERE `entry`=11356;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='¡Si mi madre estuviera viva, apagaría los fuegos!' WHERE `entry`=11356;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Se ha ido? ¡Hurra!',`Text1_0_loc6`='¡El Jinete decapitado daba miedo, pero a mí no! ¡Yo no estaba asustado!' WHERE `entry`=11358;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¡Hemos asustado a El Jinete decapitado!',`Text2_1_loc6`='¡Hemos asustado a El Jinete decapitado!',`Text3_0_loc6`='Si vuelve, ¿vas a luchar contra él de nuevo?' WHERE `entry`=11358;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='¡Ojala tuviera un $Gpadre:madre; como tú!' WHERE `entry`=11358;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Halloween es una fiesta aterradora, ¡pero me gusta!',`Text1_0_loc6`='¿Has visto a El Jinete decapitado? La Matrona dice que si le miro, me convertirá en una calabaza.' WHERE `entry`=11359;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Puedes llenar un cubo en la barrica de agua. ¡Es divertido salpicar!',`Text2_1_loc6`='Puedes llenar un cubo en la barrica de agua. ¡Es divertido salpicar!',`Text3_0_loc6`='Antes he visto soldados de Ventormenta apagando un fuego. ¡Eran héroes!' WHERE `entry`=11359;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='El Jinete decapitado vive en el Monasterio Escarlata. A veces viene y asusta a los mayores, pero a mí no.',`Text5_0_loc6`='Antes me han dado dulces en la posada. ¡No puedo esperar a que me den más!' WHERE `entry`=11359;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los representantes de Bonvapor de Piñón de Barro usan este tablón para colgar información sobre trabajo y recompensas.' WHERE `entry`=11365;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Cierto! Les dieron todas las ventajas en las que puedo pensar. Simplemente no puedo imaginar qué fue mal.$B$BAquí, $C, toma uno. He hecho un par de modificaciones, pequeños refinamientos. ¡Ahora no hay excusas para el fracaso!' WHERE `entry`=11370;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puede que quieras apartarte. Limpiar peces es un trabajo sucio.' WHERE `entry`=11376;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Sociedad Real de Boticarios te da la bienvenida a Rasganorte, $C. Y bien, ¿qué vas a hacer para colaborar a la causa de Sylvanas?' WHERE `entry`=11384;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡La situación allí es peor de lo que pensaba! ¡El zepelín es poco más que un amasijo de hierros retorcidos y combustible ardiendo!' WHERE `entry`=11403;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida a Theramore, $C.' WHERE `entry`=11406;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si no es urgente, no tengo demasiado tiempo para hablar. Las puertas de Theramore son mi mayor responsabilidad.' WHERE `entry`=11424;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Gracias a la Luz que los Defias no tienen ni idea de bucear!' WHERE `entry`=11432;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida a Theramore.' WHERE `entry`=11457;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Encantado de conocerte, $C.' WHERE `entry`=11469;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Existen ocho tipos de cartas de la Luna Negra: Bestias, Bendiciones, Elementales, Furias, Locuras, Tormentas, Portales y Señores de la Guerra. Cada palo tiene ocho cartas que van del as al ocho. Si reúnes las ocho cartas de un mismo palo, puedes convertirlas en una baraja que podrás entregar al profesor Thaddeus Paleo a cambio de un poderoso abalorio.' WHERE `entry`=11472;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡La mente de El Jinete decapitado está apestada de demencia! Durante Halloween, puede atacar este poblado en cualquier momento.' WHERE `entry`=11473;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡El Jinete decapitado ha incendiado el pueblo! ¡Debemos proteger a los niños!' WHERE `entry`=11474;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡El Jinete decapitado ataca! ¡Rápido, ayudad a vuestros hermanos de la Horda a defender el pueblo!' WHERE `entry`=11475;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='De momento, hemos ahuyentado a El Jinete decapitado. Podemos recuperar el aliento, pero no debemos bajar la guardia.' WHERE `entry`=11476;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Halloween es una fiesta aterradora, ¡pero me gusta!',`Text1_0_loc6`='¿Has visto a El Jinete decapitado? ¡Si viene aquí, lucharé con él y le robaré su calabaza!' WHERE `entry`=11477;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Me gusta jugar en la barrica de agua, pero me han dicho que no es para niños.',`Text2_1_loc6`='Me gusta jugar en la barrica de agua, pero me han dicho que no es para niños.',`Text3_0_loc6`='Dicen que la barrica y los cubos son para apagar fuegos. ¿Eres bombero?' WHERE `entry`=11477;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='El Jinete decapitado vive en el Monasterio Escarlata con los demás fantasmas y humanos aterradores.',`Text5_0_loc6`='¡Están dando dulces y regalos en la posada! La guardiana Macabro dice que si no nos dan un regalo... ¡podemos gastarles una broma!' WHERE `entry`=11477;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida a Theramore, $C.',`Text0_1_loc6`='Te doy la bienvenida a Theramore, $C.',`Text1_0_loc6`='Me entristece ver que los fúrbolgs están ya tan corruptos. Solían ser amigos nuestros.',`Text1_1_loc6`='Me entristece ver que los fúrbolgs están ya tan corruptos. Solían ser amigos nuestros.' WHERE `entry`=11492;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aconsejo al Capitán en los asuntos que decide compartir conmigo, pero tengo la sensación de que preferiría escucharte a ti.' WHERE `entry`=11496;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Todas estas bocas que alimentar y ¿quién tiene que hacerlo? ¡Yo!$B$B¡Cocinar, limpiar y todo lo demás!' WHERE `entry`=11498;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Jamás en mi vida creí que vería una frontera tan espectacular.$B$BOjalá no estuviera contaminada por el mal y por un saber que está mejor oculto.' WHERE `entry`=11502;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Has alcanzado el límite de mi saber, pero aún te queda mucho por aprender. Cruzando el mar, en el Barrio de los Magos de la Ciudad de Ventormenta, habita un gran sastre. Su nombre es Georgio Bolero y creo que te entrenará. Ve a hablar con él.' WHERE `entry`=11551;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La tierra... está helada. Somos presas fáciles aquí. Estos carros de despojos no van a ir a ningún sitio.' WHERE `entry`=11562;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué parece? ¡El suelo está helado como un témpano!$B$BLas ruedas de estos carros de despojos fueron construidas para avanzar sobre barro como mucho. Vamos a necesitar una abominación y algunas cadenas para moverlos.' WHERE `entry`=11564;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nunca llamaré al Poblado Murohelecho mi hogar. Hacer eso es abandonar la esperanza de reclamar las tierras que nos pertenecen.' WHERE `entry`=11567;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Bueno, supongo que debo darte las gracias, $R.$B$BLa verdad es que, hasta que has llegado, creía que me había tragado mi última jarra de cerveza.' WHERE `entry`=11578;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Esa es una historia triste y un poco humillante.$B$BMi gente es conocida por tener buen gusto para la bebida, ya sabes. Y yo no soy una excepción. Parece que tengo más sed de lo normal.$B$BAsí que ya ves, me encontré con el viejo Budd y me ofreció todo el grog que pudiera beber, si me unía a su equipo, algo de lo que me he arrepentido.$B$BUna noche, después de beber como para tumbar a un kodo, me engatusaron para que siguiera al equipo de inadaptados de Budd a Zul\'Aman.$B$BEso es lo último que recuerdo antes de encontrarme en esta jaula.' WHERE `entry`=11580;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Oh, muchas gracias por sacarme de esa jaula porque llevo tanto tiempo ahí y olía a carne de oveja picada que ha estado al sol durante una semana y, para empezar, no me gusta la carne de oveja, porque cuando era pequeña, bueno más pequeña, me convirtieron en oveja durante un minuto, así que sé como es ser una oveja y no quiero que alguien me coma, sobre todo si es muy grande y va vestido de gatito...$B$B< Ashli respira hondo>$B$B¿Qué tal tengo el pelo?' WHERE `entry`=11582;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los marineros varados de la Alianza saben que van a morir, pero aun así luchan contra nosotros con todas sus fuerzas.$B$BImpresionante y patético al mismo tiempo.',`Text1_0_loc6`='Es cuestión de horas que aplasten a los Alianzas de Playa Derelicta. Están rodeados y son inferiores en número.' WHERE `entry`=11586;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Por la voluntad de Sylvanas, nuestros enemigos caerán.' WHERE `entry`=11586;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Esos dos cañones de la pared son lo que nos está ralentizando. Sin su protección, nuestras fuerzas podrían asaltar su fortificación sin sufrir ninguna baja.$B$BO eliminamos esos cañones o esperamos a que se queden sin munición. Elección de Anselm, supongo.' WHERE `entry`=11588;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Jinete decapitado, que una vez fue un caballero de la Mano de Plata y un héroe entre los paladines, está maldito. Se volvió loco dentro del Monasterio Escarlata y cree que él está vivo y nosotros muertos.$B$BAhora su fervor ya no está al servicio de la Luz. Con la llegada de Halloween lleva la penumbra y el fuego a los pueblos de Azeroth.' WHERE `entry`=11590;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Libertad! La vida de Kraz había terminado.$B$BGracias, $C.' WHERE `entry`=11602;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Kraz nunca debió haber venido a este maldito lugar... Debería haber sabido que el humano no era de fiar. Fui un ciego insensato, $C, y tú también lo serás si sigues su consejo.$B$BMi hijo, perdido durante dos lunas, fue visto por última vez con el humano, Budd. Me dijo que podría encontrarlo dentro de las murallas de Zul\'Aman...$B$BYo seguí a su gente hasta aquí y me capturaron junto a los demás. Sin tu ayuda, estaría condenado a morir.$B$BGracias.' WHERE `entry`=11604;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ven, resguárdate del frío y descansa tus agotados huesos un rato. Nuestra hospitalidad es tuya.' WHERE `entry`=11607;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Camina en la Luz, $Ghijo:hija;.' WHERE `entry`=11614;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$G Señor : Señora; estoy aquí para $Gayudarle y defenderle:ayudarla y defenderla;.$B$B¿Sus órdenes?' WHERE `entry`=11622;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Durante años estuve perdido. Ahora, por fin, veo la oscuridad que había anidado en mi alma...' WHERE `entry`=11625;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La cabeza calabaza arde y deja un mal presentimiento...' WHERE `entry`=11645;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Date prisa, $C!$B$BEsta crisis está de todo menos bajo control.' WHERE `entry`=11655;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Buscas transporte a algún lugar, $N? Mis murciélagos te llevarán allí en un abrir y cerrar de ojos.' WHERE `entry`=11662;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Estos soplagaitas de la Alianza son duros! Embarcarse en destructores tripulados por Renegados... eso sí que es comportamiento suicida.' WHERE `entry`=11671;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mis hermanas y yo te damos la bienvenida, $R.' WHERE `entry`=11673;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mantente alerta, $N.$B$BAún no estamos a salvo...' WHERE `entry`=11676;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estoy aquí para ayudar y defender.$B$B¿Cómo vamos a atacar al enemigo?' WHERE `entry`=11689;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Acércate, joven, mis oídos ya no son lo que eran y no me gusta levantar la voz.' WHERE `entry`=11691;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida a mi taberna, $Gfatigado viajero:fatigada viajera;. ¿En qué puedo ayudarte?' WHERE `entry`=11714;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Tantos usos para la peste y tan poco tiempo!' WHERE `entry`=11724;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Cómo me alegro de verte!$B$BHay trabajo que hacer y no tenemos demasiado tiempo.' WHERE `entry`=11738;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gorth no tiene miedo a morir. Los Renegados me volverán a montar.',`Text1_0_loc6`='A veces Gorth está confuso. A Gorth se le da bien matar gente, así que a Gorth lo hacen sargento. ¡Pero ahora Gorth ya no puede matar a nadie!' WHERE `entry`=11742;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Vrykuls son más grandes y fuertes que nos. ¡Pero nos es más guapo!' WHERE `entry`=11742;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Ultracañón Pimpampum 5000 es un artefacto único para el lanzamiento de personas que proyecta su carga a una velocidad cercana a la del vuelo de un dragón. Este cañón es el orgullo de la familia Pimpampum, conocida en todo el mundo por sus derroches en el campo de la aeronáutica.$B$BSi quieres probar el cañón y volar como un pájaro, busca a Maxima Pimpampum en el Bancal de la Luz en la Ciudad de Shattrath. Te lanzará a algún lugar desde un pequeño estanque fuera de los muros de la ciudad.' WHERE `entry`=11743;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mantén la voz baja, $C. No queremos que nos oigan.' WHERE `entry`=11746;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='He pasado mi vida entera en el ejército. He luchado en muchas guerras, bajo muchos estandartes, pero siempre han sido las guerras de otros.$B$BEsta vez no es así. Cada soldado Renegado que ves aquí ha venido por un único motivo. Arthas ha de morir.' WHERE `entry`=11796;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Hambriento? También puedes dormir aquí.' WHERE `entry`=11797;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mantente alerta, $R, estamos rodeados de muertos andantes.' WHERE `entry`=11799;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los pesteadores tendemos a ser ligeramente más prácticos que el boticario medio. Al fin y al cabo nos encargamos de la logística de distribuir la peste.$B$BEso no quiere decir que nuestro trasfondo teórico no sea sólido como una roca. Simplemente preferimos... estar manos a la obra.' WHERE `entry`=11827;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tienes mi gratitud, $C.$B$BSi hay algo que pueda hacer por ti, tan solo dilo.' WHERE `entry`=11851;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Fue todo un detalle por parte de la Liga de Expedicionarios permitirme ir con ellos. De todas formas, con los riesgos de El Bosque Hendido y alrededores, parece que venir sin ayuda habría sido una locura.' WHERE `entry`=11858;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Voy a echar de menos el Campamento Pezuña Invernal, pero comprendo por qué ha tomado esta decisión el Cabecilla Tótem de Ceniza. El hogar es una comodidad que no siempre podemos permitirnos.' WHERE `entry`=11874;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Oye, ¿no vessh que esshtoy intentando beber?' WHERE `entry`=11880;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Huesos... carne... músculos... tendones... cartílagos... todo se junta bajo mi sierra.' WHERE `entry`=11884;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aquí tienes, $Gchico:chica;. ¡Haz que valga la pena!' WHERE `entry`=11898;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Claro. Lo encontré en el cadáver de un enano aquí cerca.' WHERE `entry`=11910;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Claro.' WHERE `entry`=11912;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Déjame adivinar, otro grupo de gentuza de Budd.$B$BFuera de aquí, $R, antes de que os hagáis daño o incluso algo peor...' WHERE `entry`=11943;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hey! Que sea capaz de montar en carnero más rápido que nadie, no quiere decir que no vaya a ayudar a los destiladores. Puedes ayudar si quieres.$B$B¡Pero espera que aún hay más!$B$BAyudar en la Fiesta de la Cerveza te da la posibilidad de montar en algunos de los carneros más rápidos de los reinos!' WHERE `entry`=11945;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Jaja, ¿ves esto? ¡Griftah ha VUELTO! ¡De nuevo en mi puesto, con mis antiguas mercancías y listo para hacer tratos contigo!$B$B¿Quieres ser mejor, más rápido, más fuerte? Cada uno de estos amuletos hace lo que te estoy contando. ¡Anímate y compra uno de estos adornos milagrosos! ¡A precio de ganga!' WHERE `entry`=11947;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los tiempos oscuros dan lugar a extrañas amistades. Puede que valga la pena visitar la vieja Lordaeron para ver como los Renegados queman su pequeño hombrecillo de mimbre.' WHERE `entry`=11956;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sé que esta fiesta no es una creación de nuestros aliados, sino de la Horda. Lo que no sé es por qué hacemos lo mismo.' WHERE `entry`=11957;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Celebramos que quién se liberó de qué? Bah ¿A quién le importa? ¡Más caramelos!' WHERE `entry`=11958;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Todos estos caramelos gratis son un mal negocio, te lo digo yo. O... a lo mejor solo son un engañabobos... te llevan a la posada y después ya eres suya. ¡Es brillante!' WHERE `entry`=11960;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cuando acabe Halloween destrozaremos estas calabazas. ¡Será el mejor día de mi vida!' WHERE `entry`=11961;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Celebrar el nacimiento de otro enemigo mortal es extraño, por decir algo. Aun así, nos uniremos a las festividades de nuestros aliados.' WHERE `entry`=11962;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los Renegados hacen bien en celebrar su libertad. ¿Acaso hay algo más importante?' WHERE `entry`=11963;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No entiendo qué significado tiene que los Renegados quemen una gran efigie de mimbre cada noche, pero seguro que a ellos nuestros rituales les parecen igual de raros.' WHERE `entry`=11964;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Durante Halloween celebramos el día en que la Reina alma en pena nos liberó de las garras del Rey Exánime y de la Plaga. No te pierdas el Festival del Hombre de Mimbre que se celebra cada noche a las afueras de Entrañas.' WHERE `entry`=11966;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Muchos son objetos poco comunes y muy preciados que busca mi clientela.$B$BTú podrías traérmelos, $C. A cambio te ofreceré lo que codicies.$B$B¿Hablamos de eso que me gustaría poder obtener hoy?' WHERE `entry`=12044;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Una orden es susurrada por los vientos abisales y Mah\'duun escucha.$B$B$G Un hombre:Una mujer; se presenta ante Mah\'duun y hablan sobre aquellos a los que el viento quiere que maten hoy.' WHERE `entry`=12046;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos preparado esta zona para practicar las técnicas de apagado de fuegos. ¡Nunca se sabe cuando estará en peligro la ciudad!' WHERE `entry`=12049;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te crees muy duro ¿eh? Puede que tenga algo para ti si superas la arena. Buen material, no como el de Krixel. Eso es de la temporada pasada ¿sabes?$B$BOh, y que tengas un buen día.' WHERE `entry`=12052;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Siempre hacen falta más barriles en la Fiesta de la Cerveza!$B$BTe prestaré las riendas de uno de mis carneros personales.$B$BPor cada barril que me traigas te daré unas fichas. ¡Hasta dejaré que te quedes con el carnero un ratito más por cada barril! $B$B¡Presta atención! Una vez que empieces, no podrás repetirlo hasta mañana.$B$B¿Estás seguro de que estás preparado?' WHERE `entry`=12076;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿No es preciosa? ¿Quieres llevarla a dar una vuelta?' WHERE `entry`=12082;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola! ¿Te interesa aprender más sobre los carneros de carreras y las carreras de carneros?' WHERE `entry`=12083;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cuando aceptes una de estas tareas, te prestaremos un carnero de carreras. Es un préstamo, así que no durará para siempre. El carnero puede parecer lento, pero dale con las riendas y cogerá el ritmo.$B$BA nuestros carneros les encantan las manzanas. Hemos colocado algunos contenedores con manzanas por el camino, así que asegúrate de parar junto a ellos. Comer manzanas hará que tu carnero olvide lo cansado que está.' WHERE `entry`=12084;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cuando te dan un carnero, también te entregan las riendas. Pero estas no son las riendas de la abuelita, estas tienes que usarlas.$B$BUsa las riendas para conseguir velocidad. Sigue usándolas y ¡arre! ¡Estarás volando!' WHERE `entry`=12085;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Una vez que empieces a usar las riendas con el carnero, verás su velocidad cambiar. Comienza andando. Tira un poco de las riendas y se pondrá a trotar. Usa las riendas mientras trota para que vaya a medio galope. Tirar un poco más de las riendas hará que galope.',`Text0_1_loc6`='Una vez que empieces a usar las riendas con el carnero, verás su velocidad cambiar. Comienza andando. Tira un poco de las riendas y se pondrá a trotar. Usa las riendas mientras trota para que vaya a medio galope. Tirar un poco más de las riendas hará que galope.' WHERE `entry`=12086;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mantener al carnero a medio galope o galope durante demasiado tiempo hará que se canse. Si quieres que tu carnero se recupere un poco, deja que ande o trote. Si hay contenedores de manzanas cerca, llévalo junto a ellos.',`Text0_1_loc6`='Mantener al carnero a medio galope o galope durante demasiado tiempo hará que se canse. Si quieres que tu carnero se recupere un poco, deja que ande o trote. Si hay contenedores de manzanas cerca, llévalo junto a ellos.' WHERE `entry`=12087;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Montar en carnero es más fácil de lo que parece, pero hace falta habilidad para ser un experto. Usa las riendas, come manzanas si puedes y evita que tu carnero se canse.',`Text0_1_loc6`='Montar en carnero es más fácil de lo que parece, pero hace falta habilidad para ser un experto. Usa las riendas, come manzanas si puedes y evita que tu carnero se canse.' WHERE `entry`=12088;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Sí? ¿Qué ocurre?$B$B¡El tiempo no espera a nadie!' WHERE `entry`=12090;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='"Si consigues suficientes vales para la Fiesta de la Cerveza, el canjeador de vales, en el campamento de la Fiesta de la Cerveza, te dará un "Sello de "Cervecero Honorario"".$B$BEse sello me permitirá venderte un carnero de montar de la Fiesta de la Cerveza. ¡Pero recuerda que no son baratos!' WHERE `entry`=12092;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Amigo, lo que tengo aquí es una novedad sin precedentes! ¡Y está en venta a muy buen precio!$B$BVerás, estaba harto de gastar dinero en las tabernas buscando a mi media naranja, así que hice estas gafas y ahora TODO EL MUNDO es la viva imagen de la perfección. ¡No hay posibilidad de error!$B$BSi quieres probarlas, tendrás que acolchar mis bolsillos. ¡Ya que tienes tan buen aspecto te haré un descuento!' WHERE `entry`=12098;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te vi por el camino y esperaba que te pasaras por aquí. ¿Qué puedo hacer por ti, cielo?' WHERE `entry`=12100;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Actualmente, los perros normales ni se me acercan. Me quedé hecho polvo cuando lo descubrí... Me encantaban las bestias.$B$BPero, de todos modos, los canes de peste son buenos sustitutos. No son tan... bonitos. Pero bueno... ¡yo tampoco!' WHERE `entry`=12103;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No encontrarás una cerveza mejor en todo Azeroth.' WHERE `entry`=12104;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Te sientes en condiciones de probar la mejor cerveza de todos los reinos?' WHERE `entry`=12105;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aquí practicamos las técnicas para apagar fuegos. ¡Poder para los Renegados!' WHERE `entry`=12108;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡El accidente de Cerrotajo está entorpeciendo la llegada de la cerveza! ¡Y los ataques de los enanos Hierro Negro cada hora no están ayudando!$B$BAsí que si quieres ayudar, este es el trato: te prestaré unas riendas y uno de mis carneros.$B$BPor cada barril que me traigas te daré unas fichas. Hasta dejaré que te quedes con el carnero un ratito más por cada barril$B$B¡PERO una vez que empieces, no podrás repetirlo hasta mañana.$B$B¿Estás seguro de que estás preparado?' WHERE `entry`=12113;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Veo que inspeccionas los carneros que hemos obtenido de forma totalmente legal... Sé que quieres descubrir la sensación de montar en un carnero... Pero antes de jugar hay que trabajar.$B$BBueno, cuando se trata de estos carneros jugar y trabajar son lo mismo.' WHERE `entry`=12114;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ah del barco, asere! ¿Qué ocurre, perro de agua salada?$B$B¡Este lugal es todo extraño, cheche!',`Text1_0_loc6`='¡Tienes buen aspecto, compay! ¿Robaste a algún masetero? Será mejor que repartas las riquesas... ¡no seas un sucio perro agarrao, camarada!' WHERE `entry`=12120;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¡Mira tú allá, compay! ¡Ese tío con escorbuto está cabreando al guajiro! El muy terco va a tragar sal, yunta...  lo van a pasar pol debajo de la quilla y se va a quedar hecho un trapo, asere.',`Text3_0_loc6`='¡Es la verdad, compay! ¡No hay marinero más duro que los piratas del Mar del Norte! Mejor no enfadar a nadie, camarada. ¡Fíjate tú! ¡Todos los marineros estúpidos acaban siendo asotaos, compay!' WHERE `entry`=12120;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Y ahora te pones cheche! ¿Intentas ofenderme, pulgoso chucho de tierra firme?$B$BApártate, cobarde pillastre. ¡Voy en pira, t\'aluego!' WHERE `entry`=12121;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El Viejo Aleta Helada te mira con recelo, su ojo de pez parpadea mientras él mueve su cabeza hacia arriba y hacia abajo a modo de brusco rechazo.>' WHERE `entry`=12130;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡En ninguna otra fiesta se bebe y celebra como en la Fiesta de la Cerveza!' WHERE `entry`=12134;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Escucha estas sabias palabras y vuelve a tu casa.$B$BEste no es lugar para aventureros despreocupados. Te engullirá y escupirá tus huesos.' WHERE `entry`=12135;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Esta gran calabaza linterna se encuentra en el centro del poblado.' WHERE `entry`=12137;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La calabaza ha sido destrozada y ya no ofrece más tesoros.' WHERE `entry`=12138;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Al destrozar la calabaza aparece un tesoro escondido! ¡Este es el mejor día de tu vida!' WHERE `entry`=12140;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Te doy la bienvenida a la zona de la Fiesta de la Cerveza, colega! Hay mucho de lo que disfrutar aquí mientras celebramos el espíritu de la competición amistosa entre destilerías rivales. ¡Avísame si tienes alguna pregunta!' WHERE `entry`=12152;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Fiesta de la Cerveza consiste en disfrutar de la mejor comida y bebida de la zona ¡y no exagero! Prueba las cervezas caseras vigorizantes, las salchichas, los pretzels y los quesos. ¡No encontrarás nada tan bueno por tan poco dinero!' WHERE `entry`=12154;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Después de la cerveza y las salchichas, la parte más popular de la Fiesta de la Cerveza son los souvenirs. Desde las vestimentas tradicionales hasta mini-barriles de sabrosa cerveza, Belbi Tiratrastos tiene toda clase de objetos basados en la Fiesta de la Cerveza. ¡Pero asegúrate de que tienes suficientes fichas de la Fiesta de la Cerveza, ya que es la única forma de comprarlos! Puedes conseguir fichas ayudando en la zona de la Fiesta.' WHERE `entry`=12155;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las carreras de carneros son una de las grandes tradiciones de la Fiesta de la Cerveza. Si quieres más información, habla con Pol Semperámbar junto al establo de carneros. ¡Es muy divertido y puede ser una buena manera de conseguir fichas para la Fiesta de la Cerveza!' WHERE `entry`=12156;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola, te doy la bienvenida a la Fiesta de la Cerveza! Hay muchas formas de divertirse aquí, así que si tienes alguna pregunta, avísame. Si no, ve a comprar una cerveza y un pretzel.' WHERE `entry`=12157;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La comida y la bebida lo son todo en la Fiesta de la Cerveza y no hay nada mejor que lo que estamos sirviendo. ¿Crees que miento? Prueba las sabrosas cervezas, salchichas, galletas saladas y los quesos que tenemos. Ya lo verás: ¡lo mejor y lo más barato!' WHERE `entry`=12158;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='A todo el mundo le gustan esos carneros de carreras de la Fiesta de la Cerveza. ¿Quieres saber más? Habla con Driz Pataplaf abajo en el establo de carneros. Pásalo pipa y gana algunas fichas para la Fiesta de la Cerveza.' WHERE `entry`=12159;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Oye, no te olvides de llevarte algún souvenir de la Fiesta de la Cerveza! Tenemos de todo: desde las vestimentas tradicionales hasta mini-barriles de sabrosa cerveza. Habla con Blix Chirimbolo, pero asegúrate de que tienes suficientes fichas de la Fiesta de la Cerveza... ¡es la única manera de comprarlos! Puedes conseguir fichas ayudando en la zona de la Fiesta.' WHERE `entry`=12160;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, la Luz de Elune te acompaña.' WHERE `entry`=12162;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Me encantaría leértelo!$B$BNo permanezcas ante mi tumba llorando,$Bno estoy allí, no estoy descansando.$B$BEstoy en los miles de vientos que los árboles mueven,$Ben Rasganorte sobre la brillante nieve.$B$BSoy la suave lluvia que cae gentilmente$Ben los campos de Páramos de Poniente.$B$BEstoy en la calma matutina,$Bde la selva de Tuercespina.$B$BEstoy en el redoble magistral,$Bde las pezuñas en Nagrand al pisar.$B$BEn las cálidas y brillantes estrellas me muevo,$Bmientras soñando sobre Darnassus me elevo.$B$BEstoy en los pájaros cantores$By en las cosas que inspiran pasiones.$B$BNo permanezcas ante mi tumba llorando,$Bno estoy allí, no estoy descansando.' WHERE `entry`=12163;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El falcón grazna hambriento pidiendo una larva.>' WHERE `entry`=12168;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Será mejor que mantengas la cabesa gacha, "asere". Si entiendes lo que quiero desir.$B$B¡Yarrrrr!' WHERE `entry`=12170;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo llevo las peleas aquí. Si quieres apostar, haslo con confiansa; si encontramos a alguien amañando las peleas, lo pasamos pol la quilla.$B$B¡D\'eso m\'aseguro yo, cheche!' WHERE `entry`=12173;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo único que elll Loco Jonah Sterling quiele que haga es fregalll la\'h cubielllta\'h todo\' los días.$B$B¿Fregalll las cubielta\'h?$B$B¡Yo soy maga, yo NO friego cubiellltas! Al menos no de la manela mundana.' WHERE `entry`=12176;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Entonces, ¿qué va a ser? ¿Ron? ¿Cerveza?$B$B¿O quizá otra cosa?' WHERE `entry`=12179;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Vas a hacerle una de las tuyas a Jack delante de sus chicos?$B$BEso requiere mucha... estupidez. Bueno, será tu problema. Yo no voy a arriesgar mi vida por menos de un oro. ¡Y será mejor que esperes a que me haya ido antes de hacer nada!' WHERE `entry`=12180;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los enanos Hierro negro están irritados por algo. Vienen casi cada media hora, rompen todos los barriles y se van.$B$BTe estarás preguntando "¿y a mí qué?"$B$BTe lo explicare... ¡Los barriles que rompen evitan que regalemos cerveza gratis! Me has oído bien: cerveza gratis.$B$BAsí que, si estás por aquí cuando aparezcan esos enanos, dedica un minutos a ayudar a proteger los barriles.' WHERE `entry`=12182;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡La Fiesta de la Cerveza es la festividad perfecta! Pero tiene un inconveniente: los ataques de los enanos Hierro Negro. Tienen lugar casi cada media hora.$B$BLos atacantes se ceban con los barriles que dan cerveza GRATUITA. Y es una cerveza muy buena que regalan los maestros cerveceros.$B$BSi estás por aquí cuando aparezcan los enanos Hierro Negro, echa una mano y protege la cerveza GRATUITA.' WHERE `entry`=12183;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te damos la bienvenida de nuevo, $N.' WHERE `entry`=12185;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este maldito Jonah Sterling nos llevará a la perdición. Si pasara más tiempo fuera de su cueva, vería que atacar a armadas profesionales en tiempos de guerra es una locura.' WHERE `entry`=12186;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Nota, está muy disgustado por su alfombra, ¿sabes?' WHERE `entry`=12190;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Buenas, colega.' WHERE `entry`=12191;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Su... su... supongo que quieres volver a la avanzada. ¿Yarp?' WHERE `entry`=12193;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El mismísimo Manitas Mayor Mekkatorque viene a la zona de la Fiesta de la Cerveza para la ceremonia de apertura del barril dos veces al día, a las seis y cuarto de la mañana y a las seis y cuarto de la tarde. ¡No te la pierdas!' WHERE `entry`=12195;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El mismísimo cazador de las Sombras Vol\'jin viene a la zona de la Fiesta de la Cerveza para la ceremonia de apertura del barril dos veces al día, a las seis y cuarto de la mañana y a las seis y cuarto de la tarde. ¡No te la puedes perder!' WHERE `entry`=12196;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este barril de cerveza está protegido por Jarven Cebatruenos. Mientras él esté en el sótano, ningún barril será molestado.' WHERE `entry`=12198;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡El capitán Alijo es el MEJOR!',`Text1_0_loc6`='¡Yo friego las cubiertas!' WHERE `entry`=12207;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¡El Lady Mehley es el mejor barco DE TODOS!',`Text3_0_loc6`='¡Fregar cubiertas me curó el raquitismo!' WHERE `entry`=12207;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los tiempos cambian, $N. La guerra ha llegado.$B$B¿Se alzará nuestra gente en esta ocasión y ayudará a nuestros aliados... o nos quedaremos al margen?' WHERE `entry`=12208;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No tengo tiempo para hablar, lo siento.' WHERE `entry`=12209;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿No ves que estoy trabajando $Gchico:chica;? Lárgate antes de que Kowalski me vea charlando.' WHERE `entry`=12210;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Torgoley nos hace trabajar duro, pero es un buen capitán.' WHERE `entry`=12212;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No quedamos muchos, $N. El regreso de nuestro viejo enemigo es motivo de gran preocupación.' WHERE `entry`=12213;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Debo prestar atención al barco y a la tripulación, $Gbuen hombre:buena mujer;. Perdona que no me pare a hablar contigo.' WHERE `entry`=12214;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Lady Mehley pasa tanto tiempo en Theramore que nos han encargado que cuidemos de sus pasajeros.' WHERE `entry`=12215;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sí, ese es su nombre y es un buen barco.$B$BHay mucho que aprender de ese tipo de naves, ya sabes. Un gran poeta dijo una vez: Los barcos pasan en la noche y se comunican con solo pasar, solo una señal y una voz lejana en la oscuridad. Así que, en la vida en el océano, pasamos y nos comunicamos con solo una mirada y una voz, la oscuridad de nuevo y el silencio.' WHERE `entry`=12217;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si deseas sustento, la jefa de galera Grace puede ayudarte.' WHERE `entry`=12218;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El señor Combs, el ingeniero e intendente del barco puede proporcionarte cualquier suministro que necesites. También es un gran manitas si necesitas reparar tu equipo.' WHERE `entry`=12219;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ah, sí, eso... el nombre. Bueno... dicen que un buen nombre, como la buena voluntad, se consigue mediante muchas acciones y se pierde con solo una. No vale la pena entrar en detalles de cómo o por qué, digamos, sin más, que así es como me llaman algunos.' WHERE `entry`=12220;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si queremos tener éxito al crear un portal a la Isla de Quel\'Danas, y a La Fuente del Sol que se encuentra en su interior, debemos adquirir una fuente de energía con potencia suficiente para encender el portal.$B$BNuestros esfuerzos están completados al $3269w %. ¿Nos ayudarás, $C?' WHERE `entry`=12226;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este portal no es como los demás que llevan de vuelta a Azeroth. Para mantenerlo debemos conseguir más células de maná.$B$BO corremos ese riesgo o perdemos nuestro acceso a la Isla de Quel\'Danas y a La Fuente del Sol.' WHERE `entry`=12227;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $R.$B$BAl fin está abierta mi tienda. ¡Espero que te guste lo que hay!' WHERE `entry`=12237;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Buenos días, $C.$B$BHay mucho que hacer para ayudar aquí. Por desgracia, no puedo hacer mucho por ti hasta que consiga todos mis suministros.$B$BPor ahora tengo el $3223w %de las raíces de Rajaespina que necesito para empezar. Te aseguro que cualquier ayuda que me puedas prestar merecerá la pena.' WHERE `entry`=12238;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este es el portal que usaron los ejércitos de Kael para retirarse a La Fuente del Sol. Algunos de sus perritos falderos continúan aquí, la Legión los ha recompensado de una forma muy... poco habitual.' WHERE `entry`=12239;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Has visto los centinelas dañados que vagan por la isla? Unas cuantas modificaciones en los cristales que controlan su comportamiento nos permitirán controlarlos y usarlos para asegurar posiciones tácticas.$B$BEl Sagrario de Tramo del Sol será nuestro en un abrir y cerrar de ojos. Hemos completado nuestra meta en un $3244w %.' WHERE `entry`=12240;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos logrado tomar el Sagrario. Podemos seguir convirtiendo a los centinelas y usarlos como guardias en otras posiciones tácticas.' WHERE `entry`=12241;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Debemos asegurarnos de no perder más almas a manos del mar.' WHERE `entry`=12243;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Perdóname $C, pero he de concentrarme en mi trabajo.' WHERE `entry`=12244;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Muchas almas se han perdido a manos de los mares. Yo y los fuegos fatuos tenemos la misión de encontrarlas.' WHERE `entry`=12245;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los fuegos fatuos y yo vigilamos los mares en busca de almas perdidas.' WHERE `entry`=12246;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los mares son un lugar peligroso, $C. Debemos estar alerta.' WHERE `entry`=12247;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me temo que no puedo hablar contigo ahora.' WHERE `entry`=12248;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No hay mucha acción en mi trabajo, servir tan cerca de las primeras líneas en una zona de combate es un cambio agradable.' WHERE `entry`=12252;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Capturar el Sagrario de Tramo del Sol era el primer paso de nuestro plan de batalla. Nuestras órdenes son tomar el control del arsenal a continuación.$B$BHemos completado nuestra meta en un $3233w %.' WHERE `entry`=12255;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos tomado el control del arsenal, pero nuestro trabajo en esta isla aún no ha terminado.' WHERE `entry`=12256;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mis órdenes son crear una distracción en La Cicatriz Muerta para que nuestras fuerzas puedan tomar el Arsenal de Tramo del Sol. Puede que no sea tan glamuroso como matar demonios cara a cara, pero si nos ayuda a ganar esta batalla yo me apunto.$B$BHemos completado nuestra meta en un $3233w %.' WHERE `entry`=12257;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos capturado el arsenal, $N. Pero no nos durmamos en los laureles. La Legión no lo hará.' WHERE `entry`=12258;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestros esfuerzos han dado su fruto. Hemos capturado el Sagrario de Tramo del Sol, $N. Ve a ver si hay alguien a quien puedas ayudar.' WHERE `entry`=12259;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Armar los resguardos en el sagrario nos ayudará a asegurarlo antes. No queremos que ningún desdichado se interponga en nuestro camino cuando desatemos un ataque a gran escala contra la Legión y Kael.$B$BHemos completado el $3244w %de nuestro objetivo.' WHERE `entry`=12260;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La tripulación de El Valentía hará todo lo que esté en sus manos para que tu viaje sea tan rápido y agradable como sea posible.' WHERE `entry`=12262;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo siento, pero tendremos que hablar en otro momento. Adiós.' WHERE `entry`=12263;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; a El Valentía, $Gseñor:señora;. Ahora, si me disculpa...' WHERE `entry`=12264;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un nombre poco apropiado para un marinero, ¿no te parece?' WHERE `entry`=12267;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si te ruge el estómago, habla con el Jefe de galera Tripacero, pero, entre tú y yo, quizá prefieras esperar. Se llama así por algo.' WHERE `entry`=12273;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida a bordo del Transbordador de Plumaluna. Viajamos entre el Bastión Plumaluna y La Costa Olvidada buscando a quienes se perdieron en el mar. Este mar puede ser un lugar peligroso si no estás preparado.$B$BSi necesitas prepararte, habla con nuestros proveedores. Están más que dispuestos a ayudarte.' WHERE `entry`=12275;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ten cuidado a bordo del Bendición de Elune, $R. Aunque Elune nos ha bendecido con el descubrimiento de nuestros nuevos amigos, también hemos sufrido la dureza del mar.$B$BSin embargo, recuperar almas perdidas en el mar o en un nuevo mundo es una auténtica bendición de Elune.' WHERE `entry`=12277;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Elune nos ha bendecido muchas veces con los descubrimientos que hemos realizado. Pero debemos permanecer atentos, $C.' WHERE `entry`=12278;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Discúlpame, $R, he de concentrarme en mi trabajo.' WHERE `entry`=12279;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; a bordo de La Fantasía de la Doncella. Debería ser una travesía tranquila hasta el puerto.' WHERE `entry`=12281;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es increíble lo que puedes aprender de un gnomo si lo estrujas con fuerza suficiente: trucos astutos sobre cómo librarse de unas ataduras, hacer galletas deliciosas e, incluso, cómo construir una máquina voladora.' WHERE `entry`=12282;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Siempre que puedo. Es un ejercicio magnífico y me mantiene informado sobre lo que traman, que sé por experiencia, que nunca es nada bueno.' WHERE `entry`=12283;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Urrrk... si ese avaricioso jefe de galera te ofrece su especial, hazte un favor y tíralo al océano. Puede que mate algunos makruras en vez de tus ganas de vivir.' WHERE `entry`=12284;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, $R.$B$B¡Pongámonos manos a la obra! No tiene sentido esperar unos suministros que podrían no llegar nunca.$B$BNecesito mi yunque para poder equipar a las tropas debidamente. Ahora mismo está completado en un $3228w %.$B$BY cuando el yunque esté terminado, habrá muchos más objetos poderosos que podré forjar para ti.' WHERE `entry`=12285;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, $N.$B$BAhora que he terminado de crear mi yunque, puedo comenzar a equipar a nuestras tropas. Es un gran paso, pero aún queda mucho por hacer.$B$BLo que más debería importarte a ti es que ahora podré crear cosas nuevas que podrían interesarte.' WHERE `entry`=12286;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos tomado el puerto y este barco es un bonito extra. Una pena que aún no se pueda llevar al mar.$B$BMientras, se me ha ordenado que supervise el desbaratamiento de los nagas en la Costa Branquia Verde.$B$B¿Crees que puedes ayudarme?' WHERE `entry`=12287;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Te doy la bienvenida a bordo de El invocador del trueno!$B$BNo prestes atención a esa gente que se queja sobre explosiones y cosas así. Lo digo en serio, todo el mundo comete errores. Hay que tener un poco de confianza.$B$BMira, lo entendería si un capitán hubiera hundido su barco 22 o 23 veces... pero menos que eso no es digno de mención.' WHERE `entry`=12288;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Refunfuña> Creo que esas montañas han aparecido de la nada... ¡las cinco veces!',`Text1_0_loc6`='<Refunfuña> ¿Crees que estrellar un zepelín contra el océano es difícil? ¡Más difícil sería hacerlo 4 veces!' WHERE `entry`=12289;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='<Refunfuña> Me gustaría poder decir que nunca nos hemos estrellado contra un lago. Me gustaría poder decir que solo nos hemos estrellado contra un lago una vez... ¡Pero nos hemos estrellado en 4 lagos diferentes, incluido el Lago Lordamere!',`Text3_0_loc6`='Me impresiona que las ramas de los pinos no dañen demasiado nuestra nave.$B$BClaro que eso no es excusa para las 3 veces que nos hemos estrellado contra un bosque.' WHERE `entry`=12289;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='Jamás pensé que llegaría a ver Teldrassil... Y te juro que jamás pensé que nos estrellaríamos contra Teldrassil.',`Text5_0_loc6`='¡Hemos conseguido atravesar Cima del Trueno dos veces!' WHERE `entry`=12289;
UPDATE `locales_npc_text` SET `Text6_0_loc6`='<Refunfuña> ¡Aún no sé cómo hemos logrado atravesar Las Mil Agujas sin un arañazo, para acabar estrellándonos y ardiendo en El Desierto de Sal!' WHERE `entry`=12289;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Si te gustan las emociones fuertes, nuestro Capitán es el mejor! ¡Adoro este trabajo!' WHERE `entry`=12290;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No preguntes...' WHERE `entry`=12291;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No puedes culparme por estos contratiempos... ¡Ni siquiera puedo ver por dónde vamos!$B$BAunque quizá eso signifique que sí puedes culparme...$B$B¡No! ¡Espera! Puedo atracar esta nave perfectamente... ¡Deben de ser las órdenes del Capitán! Quizá esté forzándonos al límite y examinando nuestra capacidad de supervivencia y reparación.' WHERE `entry`=12292;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Solían llamarme Iolol hasta que se hizo una votación secreta en la ciudad que dio como resultado que tuviera que cambiarme el nombre. Era eso o seguir los pasos de Corki. Tú habrías hecho lo mismo. ¿Qué te parece mi nuevo nombre? Un gran avance, ¿verdad?$B$BPor cierto, mira el tablón de anuncios si buscas trabajo.' WHERE `entry`=12294;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me alegra que me lo preguntes. Nuestros esfuerzos por tomar el arsenal están al $3233w %de lo planeado.$B$BSé que la maga de batalla Arynna y el presagista Inuuro necesitan tu ayuda. Búscalos dentro del Sagrario de Tramo del Sol, en la Isla de Quel\'Danas.' WHERE `entry`=12300;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las últimas noticias que tuve de Hauthaa indicaban que estábamos al $3228w %.$B$BNo puedo expresar lo necesario que es para nuestros esfuerzos que los creemos. Nuestros hombres y mujeres van a necesitar mejores armaduras y armas, y el yunque y la forja son la clave para ello.$B$BEncontrarás a la herrera detrás del Arsenal de Tramo del Sol, $N.' WHERE `entry`=12301;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No, por desgracia aún no hemos tomado el puerto. Sin embargo, los informes indican que estamos al $3238w %.$B$B$N, si quieres ayudar busca al magister Ilastar y al vindicador Kaalan en el Arsenal de Tramo del Sol, en la Isla de Quel\'Danas.' WHERE `entry`=12302;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El laboratorio de alquimia aún no está preparado, $N. Pero Mar\'nah dice que lo tiene montado al $3223w %.$B$BSi quieres ayudarle, la encontrarás dentro de la posada en el Puerto de Tramo del Sol.' WHERE `entry`=12303;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aún estamos intentando derribar Agamath, la primera puerta. ¡Por favor, $C, ve a la Isla de Quel\'Danas y ayuda en lo que sea posible!' WHERE `entry`=12304;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos derribado la primera puerta, Agamath, y ahora tenemos que destruir a dos de las tenientes de Kil\'jaeden, Lady Sacrolash y la bruja suprema Alythess.$B$BReúne a tus amigos, $C, y encargaros de ellas. Rohendor, la segunda puerta, es nuestro próximo objetivo.' WHERE `entry`=12305;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si Kil\'jaeden atraviesa La Fuente del Sol, nuestro mundo se verá envuelto en una guerra sin precedentes en los últimos 10000 años.' WHERE `entry`=12309;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='"El Águila de Hierro." Bah. ¡Es un barco de pasajeros, no una nave de guerra! Pero bueno, un trabajo es un trabajo y mientras el Príncipe siga pagando, tendré contento al capitán "Pretencioso”.$B$BDe todas formas, te doy la bienvenida a bordo.' WHERE `entry`=12314;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un poco ocupada.',`Text1_0_loc6`='¿Qué? Estoy ocupada.' WHERE `entry`=12315;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Estoy de servicio. VETE. DE. AQUÍ.',`Text2_1_loc6`='Estoy de servicio. VETE. DE. AQUÍ.' WHERE `entry`=12315;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Protegemos esta nave en su trayecto de Orgrimmar al Campamento Grom\'gol en la Vega de Tuercespina.' WHERE `entry`=12316;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ahora no, $Gtío:cielo;... Tengo cosas que hacer. Nos vemos.' WHERE `entry`=12317;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Casi hemos consolidado nuestro control en Tramo del Sol. Si evitamos que el puerto consiga refuerzos tendremos éxito.$B$BHemos completado el $3238w %de nuestra meta.' WHERE `entry`=12319;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo hemos logrado, $N. Con Tramo del Sol bajo nuestro control estamos a un paso de la victoria absoluta.' WHERE `entry`=12320;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estoy recolectando fondos para construir un monumento en honor de aquellos que murieron en la guerra. Es importante que no olvidemos los sacrificios que hemos hecho para obtener la victoria.$B$BHemos completado esta meta en un $3275w %.' WHERE `entry`=12322;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿No es precioso el monumento? Es importante recordar los momentos de nuestra historia en los que se hicieron grandes sacrificios para no olvidar que todas las guerras tienen un precio, incluso las que se ganan.' WHERE `entry`=12323;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida a bordo, $C. Relájate y disfruta del viaje. A bordo del "Princesa púrpura" queremos que te relajes. Tenemos máquinas de refrescos y comida abajo por si necesitas algo mientras estas a bordo.' WHERE `entry`=12327;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La relajación es la clave para conseguir una tripulación eficiente. Deja que la tripulación se relaje y trabajará mejor.' WHERE `entry`=12328;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Por favor, permíteme descansar unos segundos.' WHERE `entry`=12330;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Déjame dormir cinco minutitos más...' WHERE `entry`=12331;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ahora estoy ocupada.' WHERE `entry`=12332;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='No tengo tiempo para hablar ahora.',`Text2_1_loc6`='No tengo tiempo para hablar ahora.' WHERE `entry`=12332;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El vuelo desde Grom\'gol a Entrañas es precioso, pero muy tranquilo. Aunque eso no es excusa para quedarse dormido en el trabajo.' WHERE `entry`=12333;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Entra y líbrate de la ceniza y la mugre. Sécate con un hechizo.' WHERE `entry`=12334;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los Tauren Chieftains se quedarán en Lunargenta mientras preparo su próxima gira. Cuando no están ensayando, suelen estar en el balcón que hay sobre mí. No te cortes y saluda... les encanta la atención.$B$BPor ahora no estamos contratando gente ni para el espectáculo ni para el personal, pero puedes volver a comprobarlo más tarde. ¿Quién sabe qué puede pasar?' WHERE `entry`=12335;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No luches contra el enemigo sin estar preparado, $N. Puedo proporcionarte objetos muy poderosos... siempre y cuando nos hayas demostrado lo que vales.' WHERE `entry`=12338;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vamos a enseñar una humillante lección a los lacayos de Kael. Aplastaremos al ejército Hojalba y les arrebataremos el Puerto de Tramo del Sol.$B$BHemos completado el $3238w %de nuestra meta.' WHERE `entry`=12339;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tramo del Sol es nuestro. Los perros de Kael están huyendo... ¡Es hora de acabar con su sufrimiento!' WHERE `entry`=12340;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estoy al mando de los trabajadores que acompañan a los Tauren Chieftains.$B$BYa sabes: cargar, conducir y descargar los vagones, montar y desmontar el escenario, colocar la pirotecnia y ese tipo de cosas.' WHERE `entry`=12341;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Schweitzer es un viejo amigo mío... una cosa de gran valor. Me refiero a la amistad. Creía que había olvidado esa sensación hasta que volvimos a encontrarnos en la Ciudad de Shattrath.$B$BYa no pasa tanto tiempo en Tirisfal, así que se me ocurrió traer el Orbe de traslado aquí para saludarle.' WHERE `entry`=12342;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Mmrrglglglg? Glbmr brmmrggl glmrrbmrgl.$B$B¡¡¡Grglgrlbrr mrgl!!!' WHERE `entry`=12343;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La mejor defensa es un buen ataque; todo el mundo lo sabe.' WHERE `entry`=12347;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un arma fuerte no sirve de nada si no vives el tiempo suficiente para blandirla, $C.' WHERE `entry`=12348;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Gawurgggl! ¡Blrgl-brgl grgl!' WHERE `entry`=12349;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No estoy ciego, ¿sabes? ¡Veo lo que está pasando aquí!' WHERE `entry`=12352;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Blurg awrurgle mrr?' WHERE `entry`=12360;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Mis hermanos y hermanas se han vuelto locos! ¿Crees que puedes ayudarme a salir de aquí?' WHERE `entry`=12361;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Glug bmrgl grggl!',`Text1_0_loc6`='¡Mawurrgggl, gurgl lur!' WHERE `entry`=12362;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¿Glumrggl?',`Text3_0_loc6`='¡Brggl-brggl Mrgl-Mrgl! ¡Gluk!' WHERE `entry`=12362;
UPDATE `locales_npc_text` SET `Text6_0_loc6`='Mrglrglrglrgl... ¿glrgl?',`Text7_0_loc6`='¡Mrglmrgl Glrggl!' WHERE `entry`=12362;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡No tengo tiempo para charlas, hay trabajo que hacer!',`Text1_0_loc6`='¿Tú puedes entenderme? ¡Yo puedo entenderte!' WHERE `entry`=12363;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='$R, ¿por qué iba a querer hablar contigo? ¡Probablemente fue tu gente la que provocó este desastre!',`Text3_0_loc6`='Hay almejas que recoger y orcas que matar. ¡Ayuda o apártate!' WHERE `entry`=12363;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='Blurglgr murbrgl... es broma. Te entiendo.',`Text5_0_loc6`='¿Ya has averiguado por qué se han vuelto locos nuestros hermanos y hermanas del poblado?' WHERE `entry`=12363;
UPDATE `locales_npc_text` SET `Text6_0_loc6`='Sabía que no deberíamos haber entrado en esas cuevas. ¡No es natural!',`Text7_0_loc6`='Algo me huele a chamusquina en el nuevo rey Mrgl-Mrgl, pero no consigo averiguar el qué.' WHERE `entry`=12363;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Malditos sean Kael\'thas y sus sangrevil! Han destruido todo por lo que hemos trabajado, pero están locos si creen que no contraatacaremos. Por el momento, Lady Liadrin busca una nueva fuente de poder para nuestra orden. No nos destruirán tan fácilmente.' WHERE `entry`=12370;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<La conmoción que le rodea parece inquietar al dracohalcón.>' WHERE `entry`=12371;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Todo el mundo puede encontrar algo con lo que disfrutar en el Festival de Fuego del Solsticio de Verano!',`Text1_0_loc6`='¡Nuestras llamas arden fuertes y leales a través del territorio!' WHERE `entry`=12376;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¿Has bailado ya en el poste de las cintas?',`Text2_1_loc6`='¿Has bailado ya en el poste de las cintas?',`Text3_0_loc6`='Admiro la habilidad de los malabaristas de antorchas.' WHERE `entry`=12376;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Has llegado a una hoguera de la Alianza! ¡Profana esta hoguera y acaba con ella!' WHERE `entry`=12377;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tenemos que preparar esta avanzada para el ataque a la Cuenca de Sholazar.$B$BNo te quedes ahí $Gparado:parada:c; $C. ¡Ve y representa a la Horda derramando sangre y con honor!' WHERE `entry`=12382;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tengo que limpiar este desastre, pero lo haré sin quejarme.$B$B¡Si tuvieras un poco de orgullo de la Horda, me ayudarías!' WHERE `entry`=12384;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los espíritus hablan alrededor nuestro. ¿Estás escuchando, $R?' WHERE `entry`=12387;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Culto Crepuscular intenta deshacer todo lo que luchamos para preservar. Hemos jurado evitar que ocurra esta calamidad, $R.' WHERE `entry`=12390;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Soy una deshonra. Debí haber muerto con el clarividente en Magmoth.$B$BDéjame meditar.' WHERE `entry`=12394;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El elemento de aire está inquieto en este lugar. Cuidado... No conseguirás pasar indemne.' WHERE `entry`=12395;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me duele ver que mis ancestros son molestados en su descanso eterno.$B$BDebemos atacar al espíritu maldito del insensato comandante en jefe Quijacero, quien guió esta caravana a través de esta tierra sagrada.' WHERE `entry`=12403;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Solo tú puedes salvarme de Kajanishu, $N.' WHERE `entry`=12413;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos perdido a muchos de nuestros jinetes del viento en misiones de exploración al oeste. La Plaga no tiene piedad en sus ataques contra los posibles espías.$B$BPor suerte, he encontrado una solución que solo pone en peligro la vida del jinete. La tasa de supervivencia entre los que han sido transportados hasta las Llanuras de Nasam es del 30%. ¡Eso es un 30%más que cuando estábamos usando jinetes del viento!' WHERE `entry`=12417;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Soy una deshonra. Debí haber muerto con el clarividente en Magmoth.' WHERE `entry`=12419;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tanto Desembarco de Garrosh como Pal\'ea fueron barridos por la niebla. ¡Somos los únicos suficientemente locos como para quedarnos aquí, colega!' WHERE `entry`=12422;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo que ves en esta costa no es fruto de la Plaga. De hecho, creemos que la Plaga también teme a esta niebla... puede que ese sea el motivo por el que no vienen aquí.$B$BVerás, la niebla está viva, colega. Dentro de esos zarcillos de aire yace la muerte...$B$BSi entras en sus dominios, te desvanecerás. ¡La niebla te llevará!$B$BNunca hemos visto a nadie regresar de la niebla con vida.' WHERE `entry`=12424;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Desde que llegué aquí he visto horrores que antes solo habitaban en mis peores pesadillas, pero nada podría haberme preparado para lo que se oculta tras la niebla.$B$B<Mootoo asiente.>$B$BAl otro lado de esa niebla, a solo unos metros de aquí, se encuentran unas criaturas sedientas de sangre conocidas como Kvaldir. Nadie ha regresado de la niebla y pocos de los que habitaban aquí cuando se formó la niebla lograron huir. Desde entonces la zona ha sido declarada no habitable por Grito Infernal. Entra bajo tu propia responsabilidad...' WHERE `entry`=12426;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo...tú...$B$B<Mootoo tiembla.>$B$B¿Estás aquí para salvarme?' WHERE `entry`=12427;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Me alegro de verte! Estoy demasiado débil para romper los eslabones de esta cadena.$B$B¡Bueno no te quedes ahí! ¡Libérame!' WHERE `entry`=12433;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ahora mismo estoy muy ocupado, $C.$B$B¿Qué puedo hacer por ti?' WHERE `entry`=12435;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Habla despacio, $Gniño:niña;, tu extraño idioma es nuevo para mí.' WHERE `entry`=12440;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Baja la voz, $R. Hamat es mayor y necesita descansar.' WHERE `entry`=12441;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='De todas formas, tuvimos que bombear mucha arena y aceite para la maquinaria. Las charcas cercanas resultaron perfectas para ello.$B$BIncluso parte de la arena tenía propiedades mágicas... ¿podríamos hablar de beneficios? Pero esa es otra historia.$B$BAsí que drenamos la mayoría del agua y construimos la estación de bombeo justo en el medio. Todo iba como la seda hasta que un día la tubería de succión principal se atascó.' WHERE `entry`=12457;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ten en cuenta que esta parte la descubrí posteriormente, porque yo no estaba allí entonces.$B$BCuando la desmontaron, encontraron las piezas de un robot que parecía un gnomo. Por supuesto los muy insensatos trabajaron día y noche para reconstruirlo sin avisarnos.$B$BEs entonces cuando perdimos el contacto con la estación de bombeo.' WHERE `entry`=12458;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tras un par de días sin noticias, envié a una partida de reconocimiento a la plataforma. Jamás regresaron.$B$BEnvié a otro grupo al día siguiente con el mismo resultado y perdí un par de máquinas voladoras durante el reconocimiento aéreo.$B$BLlegados a ese punto, envié a alguien en busca de ayuda al sur y nos preparamos para lo peor. Concentramos todo nuestro esfuerzo en hacer armaduras, armas y robots para dirigirnos hacia allí con toda nuestra fuerza.$B$BCuando lo hicimos, hace un par de días, no podíamos creer lo que encontramos.' WHERE `entry`=12459;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Como te iba contando, no era posible explicar lo que vimos allí.$B$BMi gente había desaparecido y en su lugar había un auténtico ejército de robots y androides campando a sus anchas.$B$BLos androides tenían aspecto de gnomos y dijeron que habían estado esperándonos. De hecho, a su modo, actuaban como si nos conocieran. Nos rodearon y rápidamente nos llevaron a lo más alto de la estación de bombeo.$B$B¡Fue entonces cuando vimos a su líder y lo que estaba haciendo con los gnomos supervivientes!' WHERE `entry`=12460;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Se hace llamar el maestro de palancas Mecazod. ¡Cuando llegamos, estaba ocupado trasvasando los cerebros de los supervivientes a cuerpos mecánicos!$B$BNos saludó con afecto y nos explicó que él era uno de los primeros gnomos creados por algo llamado "el gran arquitecto", un vigilante titán del legendario Ulduar.$B$BPor lo visto, él era el obstáculo que mi equipo había absorbido accidentalmente del lugar en el que se había estropeado miles de años atrás. ¡Y nosotros tuvimos que construir nuestra estación de bombeo justo sobre él!' WHERE `entry`=12461;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los mecánicos de la estación lo habían reconstruido, devolviéndolo a la "vida".$B$BY ahora, como agradecimiento, nos iba a devolver el favor curándonos del mal que él denomina "la maldición de la carne".$B$BSegún Mecazod, es una dolencia que acaba afectando a todas las creaciones de los titanes. Dicho de otra forma, todos empezamos siendo robots de algún tipo y con el paso de los milenios poco a poco nos vamos volviendo seres de carne.$B$BLo sé, es absurdo.' WHERE `entry`=12462;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='A mí no me parecía una "cura" y no estaba dispuesto a quedarme parado escuchando a un robot demente mientras masacraba a mi gente.$B$BHicimos lo que cualquier gnomo en su sano juicio hubiera hecho... ¡corrimos! Bueno, de hecho, la mayoría de nosotros nos lanzamos en paracaídas desde la plataforma, pero te haces una idea.$B$BSolo unos pocos conseguimos regresar aquí y aún estamos bastante mermados, pues también nos tenemos que encargar de todo lo demás: el magnatauro al norte y la Plaga al este. ¡Por no mencionar que la Horda se ha establecido al noroeste!' WHERE `entry`=12463;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Eso explica nuestro estado actual, $N.$B$BNo podemos encargarnos de esto nosotros solos y, para serte sincero, me siento un poco perdido en este asunto. Jinky sabe lo que hay que hacer. Deberías ir a verla ahora.$B$BSolo espero que seas capaz de sacarnos de este lío con nuestros cuerpos intactos.' WHERE `entry`=12464;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las acciones de estos desertores nos ponen a todos en evidencia. Solo los que son testigos de su magnitud entienden la vergüenza que supone. Por eso Ug\'thor está aquí… Debe aprender.' WHERE `entry`=12472;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ten cuidado por aquí, $R. Los nativos son despiadados.' WHERE `entry`=12475;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Entonces, $N, ¿qué objeto te interesa?' WHERE `entry`=12494;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ah, rarezas y artículos poco comunes. Estamos especializados en los objetos totalmente únicos de Azeroth para los aventureros más exigentes.$B$BSupongo que si estás hablando conmigo es porque tienes una idea de lo que estás buscando.' WHERE `entry`=12495;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Sabías que los alquimistas pueden transmutar ciertas gemas? Mar\'nah y yo hemos estado hablando sobre esto.$B$BEstoy convencida de que con su destreza, en cuanto su laboratorio esté terminado, ella y yo podremos crear gemas que se creían perdidas para siempre.' WHERE `entry`=12496;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Bien hecho, $N. Mar\'nah ha aprendido a transmutar unas gemas muy especiales. Y yo he aprendido a trabajarlas.' WHERE `entry`=12497;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No temas, $R. Nuestro fin es el mismo.' WHERE `entry`=12498;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo soy Kalecgos del Vuelo Azul.$B$BLlevo tiempo observando los nacientes poderes de La Fuente del Sol...¡Pero ahora sé que su terrible poder podría provocar el fin de nuestro mundo!' WHERE `entry`=12500;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vencer a Kael\'thas es una tarea a la que tendrás que prestar toda tu atención.$B$BAdemás, si eres capaz de organizar un ejército, hay muchas más cosas de las que encargarse en La Fuente del Sol.$B$BQuizá volvamos a encontrarnos allí.' WHERE `entry`=12502;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C. ¿Has venido a honrar esta llama?$B$BRecuerda: las hogueras de nuestro festival arden en todas las regiones con un asentamiento de la Alianza. Saca tiempo para honrarlas a todas si puedes.$B$BLa Horda también tiene sus propios fuegos. Harías bien en profanar esos indignos esfuerzos cuando los veas.' WHERE `entry`=12504;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Observad las llamas del verano!',`Text1_0_loc6`='¡Cuidado, no te quemes!' WHERE `entry`=12506;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¿Disfrutando del Festival del Fuego?',`Text2_1_loc6`='¿Disfrutando del Festival del Fuego?',`Text3_0_loc6`='¡Te doy la bienvenida! ¿Has honrado ya la llama de esta tierra?' WHERE `entry`=12506;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este mundo ha sido dañado más allá de lo que nuestros ojos pueden ver. Incluso el paso del tiempo parece diferente en cierto modo.' WHERE `entry`=12507;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los maestros de batalla de arena se encuentran al sudeste del Bajo Arrabal, junto a la muralla.' WHERE `entry`=12510;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aunque hemos detenido las incursiones de Nesingwary en la vida salvaje de Rasganorte, solo es cuestión de tiempo que lo vuelva a intentar. ¡Debes permanecer alerta, $N!$B$BPero lo más importante es que no cedas, te ofrezca lo que te ofrezca... El demonio intentará engañarte. Cuando eso ocurra, deberás resistir.' WHERE `entry`=12511;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Solo me faltaba que la maldita Plaga nos vaya a echar de aquí! Los colonos lo dimos todo para comenzar nuevas vidas en Rasganorte.$B$BSerá mejor que creas que vamos a luchar con uñas y dientes.' WHERE `entry`=12521;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La piedra irradia un frío amargo, helando el aire a su alrededor y la plataforma sobre la que se asienta. En su interior una luz azul parpadea, esperando.' WHERE `entry`=12524;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Algunos intentamos evitar las tareas militares. Un buen pescador siempre viene bien... incluso los generales necesitan comer.' WHERE `entry`=12528;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Otro día más trabajando en la cocina. Después de un tiempo se vuelve un rollo.' WHERE `entry`=12529;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mi turno casi ha terminado. No puedo esperar para plegar la oreja, ¿sabes a qué me refiero?' WHERE `entry`=12530;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Necesitas oro? Aquí tienes 5000 monedas.' WHERE `entry`=12532;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hace tiempo Arthas fue un príncipe humano. El ejército que guió hasta Rasganorte sufrió un fatídico destino.$B$BMi hermano Thassarian era miembro de aquel ejército.' WHERE `entry`=12534;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Somos mineros, granjeros y artesanos, no soldados. Pero defenderemos nuestra tierra con los puños desnudos si es necesario.' WHERE `entry`=12538;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo siento, pero mis servicios solo son útiles para los cazadores.' WHERE `entry`=12539;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo siento, pero mis servicios solo son útiles para los druidas.' WHERE `entry`=12540;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Cómo puedo ayudarte, $C?' WHERE `entry`=12541;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedo borrar todos tus talentos para que puedas elegir otros nuevos.$B$BEste proceso es más caro cada vez que lo repites.' WHERE `entry`=12542;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo siento, pero mis servicios solo son útiles para los cazadores.' WHERE `entry`=12543;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo siento, pero mis servicios solo son útiles para los magos.' WHERE `entry`=12544;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo siento, pero mis servicios solo son útiles para los paladines.' WHERE `entry`=12545;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo siento, pero mis servicios solo son útiles para los sacerdotes.' WHERE `entry`=12546;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo siento, pero mis servicios solo son útiles para los pícaros.' WHERE `entry`=12547;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo siento, pero mis servicios solo son útiles para los chamanes.' WHERE `entry`=12548;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo siento, pero mis servicios solo son útiles para los brujos.' WHERE `entry`=12549;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo siento, pero mis servicios solo son útiles para los guerreros.' WHERE `entry`=12550;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Puedo traerte algo? Mi surtido es un poco limitado, pero era de esperar... teniendo en cuenta la situación.' WHERE `entry`=12553;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Eh?' WHERE `entry`=12554;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Morir por su país es el deber de un soldado. Pero si decir que esta guerra es una locura es traición, entonces soy un traidor.' WHERE `entry`=12555;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ahora estoy muy ocupada, $R. Por favor, déjame sola.' WHERE `entry`=12556;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Concéntrate, mortal. Esta es la línea de frente de una guerra como jamás has visto.$B$BNecesitarás el control total de tus facultades si quieres servirnos de algo.' WHERE `entry`=12566;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Estaremos preparados cuando Ahune salga a la superficie?' WHERE `entry`=12572;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Oh...$B$B¿Dónde estoy?' WHERE `entry`=12577;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C. ¿Has venido a honrar esta llama?$B$BRecuerda: las hogueras de nuestro festival arden en todas las regiones con un asentamiento de la Horda. Saca tiempo para honrarlas a todas si puedes.$B$BLa Alianza también tiene sus propios fuegos. Harías bien en profanar esos indignos esfuerzos cuando los veas.' WHERE `entry`=12582;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Entonces me alegro de que hayas venido. Ahora que ya has aprendido acerca de honrar nuestros fuegos... y acerca de apagar los de nuestros enemigos... puedes volver a verle para que te recompense.' WHERE `entry`=12587;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En una sola palabra: magia.$B$BLos irresponsables mortales con sus cortas vidas no piensan en los efectos a largo plazo de sus acciones.$B$BLas energías Arcanas han sido liberadas de forma demasiado liberal, desbaratando el equilibrio del mundo. Si esto sigue así, Azeroth pronto será aniquilado.$B$BAmbas partes están de acuerdo en esto. Pero antes de que se pudiera planear un concilio, Malygos puso en marcha su plan.$B$BNo hemos tenido más elección que enfrentarnos a él.' WHERE `entry`=12590;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estamos progresando, $R, pero demasiado despacio.' WHERE `entry`=12591;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Tenemos que lograrlo! El destino de Azeroth depende de ello...' WHERE `entry`=12592;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Malygos el Tejehechizos, el Señor de la magia, maestro del Vuelo Azul. Es el Aspecto dragón Azul, una de las criaturas más antiguas de Azeroth.$B$BDicen que se está recuperando de una locura que ha durado milenios, pero sus acciones recientes me hacen pensar que aún no está cuerdo.$B$BLa solución que propone para nuestro problema es buscar y matar a todos los mortales de Azeroth que usen la magia.$B$BAl ser el guardián de la vida, el Vuelo Rojo se ha visto obligado a entrar en acción.$B$BAhora un dragón lucha contra otro y el destino de nuestro frágil mundo depende del resultado.' WHERE `entry`=12593;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me alegra que estés aquí, $C. La situación militar en la Meseta de La Fuente del Sol es bastante desalentadora.' WHERE `entry`=12596;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La capitana Selana me ha ordenado que colabore con tus esfuerzos aquí, en la Meseta de La Fuente del Sol.' WHERE `entry`=12597;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Ofensiva Sol Devastado ha realizado un gran progreso en las zonas exteriores de la Isla de Quel\'Danas. Están rechazando a las tropas de Kil\'jaeden y, en medio del caos, he logrado guiar a una pequeña fuerza de exploradores dentro de la Meseta de La Fuente del Sol. Mis tropas avanzaron a través de la Plaza del Parhelio, avistaron a un grupo de elfos de sangre Filosol y se enzarzaron en una lucha contra el enemigo. Esta lucha no tardó en convertirse en un desastre.' WHERE `entry`=12598;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los defensores de Kil\'jaeden aquí, en La Fuente del Sol, son mucho más fuertes que cualquier cosa a la que nos hayamos enfrentado fuera de la isla. La Legión ha distorsionado nuestra tecnología de los guardianes Arcanos con energía vil para crear algo mucho más siniestro. Uno solo de esos protectores Filosol diezmó mis tropas y tuve que ordenar una retirada rápida. Esperaba que expulsaran a mi escuadrón de la isla, pero me sorprendieron dejando de perseguirnos.' WHERE `entry`=12599;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un amigo y yo hemos venido a rescatar a una joven mujer humana de las garras del Señor de la Legión, Kil\'jaeden, y sus infames esbirros.$B$BHemos descubierto que Kael\'thas está tratando de invocar al señor de la Legión, Kil\'jaeden, desde lo más profundo de la mismísima Fuente del Sol.$B$B¡Por el bien de este mundo, eso no puede suceder! Me he comprometido a evitarlo.' WHERE `entry`=12606;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Anveena no es una simple mortal, $R. Aquellos que reconocen su verdadera naturaleza harían todo lo posible para poseerla.$B$BEn una ocasión me rescató a mí y ahora tengo intención de devolverle el favor.' WHERE `entry`=12608;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Estás disfrutando del festival, $C?$B$BMientras los vigilantes de la Llama se ocupan de las hogueras del presente, yo soy más bien un historiador y me interesan mucho los festivales anteriores. Por supuesto, estoy documentando las celebraciones de este año. De momento, todo está saliendo bien, ¿no crees?$B$B¿Sabes, $N? Hay un poder inherente a todas las hogueras del festival que se prenderán durante estas fiestas. Nosotros cuidamos las nuestras pero estoy seguro de que en las ciudades enemigas arden hogueras sagradas...' WHERE `entry`=12609;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te damos la bienvenida a nuestro campamento, $C. Puedes descansar aquí si lo deseas.' WHERE `entry`=12610;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No queda nada para nosotros aquí.',`Text1_0_loc6`='Mi familia fue asesinada sin piedad. Incluso los jóvenes.' WHERE `entry`=12611;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Ojalá el Rey Exánime arda en el fuego infernal por lo que le ha hecho a esta tierra.',`Text2_1_loc6`='Ojalá el Rey Exánime arda en el fuego infernal por lo que le ha hecho a esta tierra.',`Text3_0_loc6`='Ahora somos un pueblo sin un lugar al que llamar hogar.' WHERE `entry`=12611;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='La Plaga es una intrépida máquina programada para acabar con la vida de este mundo.',`Text5_0_loc6`='Aquel a quien llaman Grito Infernal puede ser vuestra única esperanza.' WHERE `entry`=12611;
UPDATE `locales_npc_text` SET `Text6_0_loc6`='Solo la Horda puede salvarnos ahora.' WHERE `entry`=12611;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Budd se queda muy quieto, haciendo como que no te escucha. Inspeccionándolo de cerca puedes ver un poco de baba caer bajo su máscara.>',`Text1_0_loc6`='<Budd se balancea en cuclillas, farfullando algo que parece ser lengua trol.>' WHERE `entry`=12617;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Pírate, colega.',`Text3_0_loc6`='No es momento para hablar, colega. Necesito descansar...' WHERE `entry`=12617;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='¡Poder trol, colega!',`Text5_0_loc6`='¿Qué te pasa, colega? ¿Nunca has visto un trol?' WHERE `entry`=12617;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Me dará la Horda la posibilidad de luchar contra la Plaga?' WHERE `entry`=12618;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aceptaré tu juramento para vengar a mi gente, para eliminar el añublo que ha caído sobre nuestra tierra. Comprometeré todo lo que tengo y todo lo que soy para con la Horda.' WHERE `entry`=12619;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Lok\'tar ogar! Victoria o muerte: estas son las palabras que me unen a la Horda, ya que son las más sagradas y fundamentales verdades para cualquier guerrero de la Horda.$B$BEntrego mi cuerpo y sangre libremente al Jefe de Guerra. Soy el instrumento de los deseos de mi Jefe de Guerra. Soy el arma de las órdenes de mi Jefe de Guerra. ¡A partir de este momento y durante el resto de mis días vivo y muero POR LA HORDA!' WHERE `entry`=12620;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ey, colega! ¡Llévame con mis hermanos trols!',`Text1_0_loc6`='¡Es hora de jugar a pillar al trol, colega!' WHERE `entry`=12622;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¡Mi hermanos trol se van a divertir de lo lindo hoy, colega!',`Text3_0_loc6`='¡Je, je, je! ¡Esto va a ser divertido, colega!' WHERE `entry`=12622;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='¿Seguro que es por aquí, colega?',`Text5_0_loc6`='<snif, snif> Puedo olerlos, colega.' WHERE `entry`=12622;
UPDATE `locales_npc_text` SET `Text6_0_loc6`='Nada expresa “te quiero” como un toquecito en la cabeza.',`Text7_0_loc6`='Nada expresa “te quiero” como un toquecito en la cabeza.' WHERE `entry`=12622;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las tres puertas de la Meseta de La Fuente del Sol han sido derribadas, $N.$B$B¡Has de organizar tus tropas y evitar que Kil\'jaeden entre en Azeroth antes de que sea demasiado tarde!' WHERE `entry`=12623;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tralladón nunca volverá a su antigua gloria, pero... ¡Juro que esas escorias marineras pagarán por lo que han hecho!' WHERE `entry`=12626;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es nuestro deber proteger a estos taunka y ofrecerles las comodidades que nos sean posibles.' WHERE `entry`=12627;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Jamás comprendí pol qué algunos piratas entierran sus tesoros. A mí me gusta tenerlo a mano.' WHERE `entry`=12636;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puerto Moa\'ki ha sido bendecido con dos grandes tortugas marinas: Caminante de las Olas e Isla Verde. Nadan entre Unu\'pe y Komawa.' WHERE `entry`=12640;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tienes ganas de ir a Rasganorte, ¿verdad? El Kraken a vapor navega desde aquí hasta la Fortaleza Denuedo, en la Tundra Boreal.' WHERE `entry`=12641;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola! Echas de menos los Reinos del Este, ¿verdad? Bueno, si realmente quieres volver, el Kraken navega de vuelta a Ventormenta. ¡Yo agradecería un clima más cálido!' WHERE `entry`=12644;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestra bendita gran tortuga marina, Isla Verde, viaja desde aquí hasta Puerto Moa\'ki, en el Cementerio de Dragones.' WHERE `entry`=12645;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ey. ¿Quieres volver al viejo mundo? Vuela hasta los Claros de Tirisfal con estilo a bordo del Besanubes.' WHERE `entry`=12646;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Vas a casa, $C? El Viento Poderoso te llevará hasta allí si tu hogar está en Kalimdor. Vuela directamente desde aquí hasta Durotar.' WHERE `entry`=12650;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Se busca MUERTO por traición y otros actos traicioneros contra la Horda!$B$BTodas las recompensas serán pagadas por el capitán Gort.' WHERE `entry`=12657;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Buenas, colega! Siéntate y echa un trago.' WHERE `entry`=12659;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Todo lo que deseaba era volver a servir a mi rey. Por la humanidad, contra un enemigo común.$B$BPero sin embargo descubro que hay inmundos cultores infiltrados entre nosotros... y todas las señales apuntan hacia Naxxanar como origen de esta podredumbre.' WHERE `entry`=12663;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='De aquí es de donde vienen las órdenes de los cultores. Su líder aparecerá pronto.$B$BNo dejemos pasar esta oportunidad.' WHERE `entry`=12664;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Eh? ¿Qué? Lárgate.' WHERE `entry`=12666;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si consigo crear una nueva máquina de guerra, sé que conseguiré un buen dinerito por ella en la Puerta de Cólera.$B$BAlgunas de nuestras anteriores acciones no han sido del todo... estables, pero mi máquina voladora debería ser suficiente si consigo desarrollar un sistema de bombardeo efectivo para ella.' WHERE `entry`=12667;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sientes una extraña presencia mientras permaneces frente al ídolo.' WHERE `entry`=12669;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En el lugar donde el ojo de los profetas descansó durante milenios solo queda una hornacina vacía.' WHERE `entry`=12670;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Te doy la bienvenida! Si necesitas un lugar para descansar, puedo solucionarlo.' WHERE `entry`=12673;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Buscas un maestro de batalla de arena? Puedes encontrarlos en las carreteras entre las ciudades o en las capitales.$B$BPuedes vincularte aquí, conmigo. También puedo teletransportarte a una de esas capitales.' WHERE `entry`=12684;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$GBienvenido:Bienvenida;, $C! ¡Espero que no te den miedo las alturas!$B$BTengo un paracaídas de prácticas para ti. Estos se usan para aterrizajes rápidos. Si quieres caer suavemente, usa una montura. Si quieres acción y un subidón de adrenalina: tírate y abre el paracaídas lo más tarde posible. Para eso están. ¡Acción rápida! ¿Te apetece un poco de acción rápida?$B$BEl salto solo dura unos segundos. Así que tendrás que calcular bien el tiempo. No tengo claro que seas capaz de hacerlo, $C. A ver si me demuestras lo contrario.' WHERE `entry`=12687;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Será mejor que te marches de aquí si no quieres acabar dentro de una de esas jaulas...' WHERE `entry`=12694;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gracias por hacerme llegar la carta de mi hermano, $C.$B$BPuedes estar seguro de que su muerte no habrá sido en vano. El Vuelo Azul pagará por esto$B$B¡Me encargaré de ello personalmente!' WHERE `entry`=12696;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Haz lo que puedas para mantener a la Plaga a raya, soldado! Informa de cualquier actividad sospechosa y mata primero, ¡ya preguntarás después!' WHERE `entry`=12698;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Recuerda mis palabras, estos insensatos Renegados nos conducirán a la muerte a todos.',`Text1_0_loc6`='Si me preguntas a mí, y lo has hecho, creo que deberíamos matar a todos los boticarios antes de que sea demasiado tarde.' WHERE `entry`=12699;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='La Horda da mucha importancia al honor y a la guerra. Estoy seguro de que no hay nada de honorable en lo que están haciendo los boticarios.',`Text3_0_loc6`='Estoy vigilando a esos idiotas Renegados de ahí abajo. ¡Ninguna precaución está de más con esa gente!' WHERE `entry`=12699;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='Si tienes tratos con el doctor de allí, ten cuidado. Los que se traen entre manos no es natural.' WHERE `entry`=12699;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Colega, los baldíos al este de eh\'te lugar están llenos de cuerpos. ¡Aléjate de eh\'ta zona si quieres vivir!' WHERE `entry`=12701;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mis responsabilidades en el Martillo de Agmar van más allá del cuidado de los animales y los establos.' WHERE `entry`=12703;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Morir? ¿Yo? Je, ni por asomo, chico. Eso solo ha sido un rasguño.' WHERE `entry`=12705;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si tuviera dinero suficiente, podría conquistar el mundo...' WHERE `entry`=12708;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Esta consola se encuentra delane de unos escombros envueltos en vapor. Debajo se oye el sonido de un motor.' WHERE `entry`=12709;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Quieres volver abajo, $C?$B$BPuedo hacer que uno de estos dracos te lleve a salvo hasta donde quieras ir.' WHERE `entry`=12714;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Déjame en paz, $Ginsensato:insensata;!' WHERE `entry`=12715;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué? ¿Quieres entrenar conmigo, $C? Más vale que tengas ganas de trabajar.' WHERE `entry`=12718;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedes dormir en los barracones, si quieres. Pero no despiertes a mis hombres o te tiraré a la nieve yo mismo.' WHERE `entry`=12720;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gracias por rescatarme, $N. Nunca olvidaré tu valor y determinación.' WHERE `entry`=12721;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Será un honor luchar junto a ti como hermano de la Horda, $N. Acepto tu oferta.' WHERE `entry`=12722;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No serás $Gun verdadero:una verdadera; colega hah\'ta que hayas mata\'o un dragón... colega.' WHERE `entry`=12725;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, $R. Siento que la primera vez que veas nuestra tierra sagrada sea en este estado.' WHERE `entry`=12727;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola. Si necesitas algo, házmelo saber.' WHERE `entry`=12732;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si tienes hambre, tengo comida y puedes usar mi tienda si necesitas descansar.' WHERE `entry`=12734;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puede que no esté nevando ahí fuera, pero las noches aquí pueden helarte el alma. Los camastros están en la parte trasera, pero no seas un incordio.' WHERE `entry`=12735;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Je. No tengo nada en contra tuyo, pero quizá quieras buscar otro sitio donde dormir esta noche. No me gustaría que esa piel tan delicada acabara llena de moratones.' WHERE `entry`=12736;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Acércate al fuego y descansa, $R. Espero que no te moleste el olor a pescado.' WHERE `entry`=12738;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos luchado a través de los valles y cimas del Monte Hyjal, a través de las arenas movedizas de Silithus, contra los temibles ejércitos de la Legión. Somos los hijos sin nombre ni rostro de la Alianza. Por la Luz y por el poder de la Alianza, el primer golpe nos pertenece y el último golpe es todo lo que ven nuestros enemigos.$B$BSomos la Séptima Legión.' WHERE `entry`=12739;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Saludos! ¿Necesitas algo?' WHERE `entry`=12740;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mi G.U.A.O. es más que un objetivo al que lanzarle jarras, es un recurso muy valioso para los entrenamientos. ¡Si puedes acertar al G.U.A.O., podrás acertar a cualquier cosa!$B$BPero procura no golpearme a mí.' WHERE `entry`=12744;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Todas las recompensas serán pagadas por el sumo ejecutor Wroth después de realizar las comprobaciones pertinentes.$B$BLas personas interesadas pueden obtener más información dentro.$B$B¡ABSTENERSE ABOGADOS!' WHERE `entry`=12750;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El colmillarr te mira con recelo.>$B$BNo te quedes demasiado. No entiendes a nuestra gente y no quiero problemas.' WHERE `entry`=12755;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El cielo no es seguro, colega. ¡Mantente alerta!' WHERE `entry`=12756;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ten cuidado por dónde pisas en el jardín de Ysera, mortal. Las cosas no siempre son lo que parecen.' WHERE `entry`=12758;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Necesitas algo? ¿Quizá que reparen tu arma o armadura? ¿Ehmmm?' WHERE `entry`=12759;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En tiempos desesperados son necesarias medidas desesperadas, $C.' WHERE `entry`=12770;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La belleza natural de estas Colinas oculta una gran oscuridad bajo ellas. Ten cuidado, colega.' WHERE `entry`=12773;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ten cuidado ya que los tentáculos apestados del Rey Exánime acechan de cerca.' WHERE `entry`=12774;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La letrina parece estar ocupada en este momento.' WHERE `entry`=12775;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Que la Luz purgue tu infección, valiente $N.' WHERE `entry`=12776;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mi padre me ha regalado esta armadura de batalla y esta hacha. Pronto les daré buen uso.' WHERE `entry`=12777;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Por suerte, la letrina está vacía.' WHERE `entry`=12779;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tenemos poca comida y bebida. Me temo que no tengo nada que ofrecerte.' WHERE `entry`=12780;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gracias por tu ayuda, $N. Estos gelifructus son, literalmente, el fruto de tu trabajo.' WHERE `entry`=12781;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hace tiempo caminaba por las Colinas Pardas, pues era hijo del gran Ursoc, quien reinaba en esta tierra.$B$BPero ahora... ahora estoy condenado a pasar el resto de mi vida en esta cueva.' WHERE `entry`=12793;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No lo entiendes. Si quisiera, podría salir de esta cueva luchando.$B$BEs mi padre, Ursoc. Ha regresado de entre los muertos... pero ya no es el mismo.$B$BDio su vida durante la Guerra de los Ancestros hace miles de años. Durante siglos, los fúrbolgs que le rendían pleitesía intentaron encontrar un modo de resucitarlo.$B$BFinalmente, decidieron que iban a intentar que el fallido Árbol del Mundo, Vordrassil, volviera a crecer y usar su magia para resucitar a mi padre.' WHERE `entry`=12794;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Habla en voz baja, $C. Soy una espía de Rencor Venenoso disfrazada.' WHERE `entry`=12795;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El esqueje que crearon tenía un fruto. Al principio pareció funcionar. Ursoc volvió y, durante un breve instante, me llené de alegría.$B$BA medida que se iba recuperando, era evidente que no era el mismo. Tampoco lo eran los fúrbolgs.$B$BEl mal que hizo que los druidas destrozaran a Vordrassil volvió a manifestarse en el nuevo árbol y los convirtió a todos en monstruos sedientos de sangre.$B$BEsa cosa que camina ahora por las Colinas Pardas no es mi padre y ha de ser devuelto a su descanso eterno.' WHERE `entry`=12796;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Soy Kodian, hija de Ursoc. Vigilo este santuario que en otro tiempo fue sagrado.$B$BNo he visto a ningún extraño venir aquí desde hace mucho tiempo.' WHERE `entry`=12797;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un fúrbolg llamado Tur Patafuria. No es uno de los nuestros.$B$BÉl viene de las tierras del sur. Pertenecía a la tribu Fauces de Madera.$B$BQuería ver a Ursoc. Parecía creer que la corrupción de Vordrassil podía ser purificada y utilizada para salvar a mi padre.$B$BEs una locura albergar tales esperanzas. Estarás de acuerdo conmigo cuando veas la oscuridad que le ha afectado.' WHERE `entry`=12798;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Crees que podrías sacarme de aquí, $C?' WHERE `entry`=12804;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ah bien, has venido a rescatarme. ¡Ahora, sácame de aquí!' WHERE `entry`=12805;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hey, $Gtío:señorita;. Te he visto por aquí.$B$B¿Te importaría sacarme de aquí?' WHERE `entry`=12806;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No sé qué pasó. ¡Acababa de llegar a Rencor Venenoso y, de repente, me desperté en esta jaula!$B$B¿Has venido a rescatarme?' WHERE `entry`=12808;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El temperamental protodraco del señor feudal Torvald Eriksson está encadenado aquí arriba.' WHERE `entry`=12811;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Sí? ¿Qué quieres?' WHERE `entry`=12812;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cada dos años los espíritus de competición se vuelven especialmente fervientes... ¡y ahora es ese momento! Llevamos este tabardo en los campos de batalla para honrarlos e intentamos derrotar a nuestros enemigos y ganar el favor de los espíritus.' WHERE `entry`=12819;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Solo tienes que enfrentarte a nuestros rivales en los campos de batalla. Si te quedas durante toda la batalla, aunque ganes o pierdas, se te enviará un tabardo de competidor por correo.' WHERE `entry`=12820;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tienes que derrotar a nuestros rivales en cualquier campo de batalla. Con cada victoria, es posible que se te envíe un medallón de oro por correo.' WHERE `entry`=12821;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Has oído hablar de los espíritus de competición, ¿verdad?$B$BCada dos años se acercan al reino de los mortales. En esos momentos, miembros tanto de la Alianza como de la Horda llevan tabardos de competidor en los campos de batalla para honrar a los espíritus e intentan derrotarse unos a otros y ganar el favor de los espíritus.$B$BYo acumulo recuerdos para esos momentos turbulentos. Si conoces la palabra adecuada, puede que me convenzas para que me deshaga de uno...' WHERE `entry`=12822;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En este baldío helado, estamos aprendiendo rápidamente el significado de la muerte, $N. El poder del Rey Exánime se extiende por cada centímetro de Rasganorte, penetrando en la propia tierra. Él vuelve a nuestros caídos contra nosotros.$B$BDesde el cielo, jinetes de grifos resucitados nos atacan con sus vengativos martillos de tormenta mientras los muertos andantes nos atacan en tierra.' WHERE `entry`=12823;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Supongo que Krenna duda de mis habilidades. Mientras nuestros guerreros de élite caminan para enfrentarse a la Alianza y sus aliados tramperos, o se dedican a causar estragos entre los trols, a mí me han ordenado eliminar a esos molestos vrykuls Desuelladragones.$B$BQuizá si unimos nuestros esfuerzos, podamos mostrarle lo que valemos a la conquistadora.' WHERE `entry`=12824;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Creo que lo han enjaulado cerca del aserradero.' WHERE `entry`=12827;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tienes que derrotar a nuestros rivales en cualquier campo de batalla. Con cada victoria, es posible que se te envíe un medallón de oro por correo.' WHERE `entry`=12828;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Solo tienes que enfrentarte a nuestros rivales en los campos de batalla. Si te quedas durante toda la batalla, aunque ganes o pierdas, se te enviará un tabardo de competidor por correo.' WHERE `entry`=12829;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Creo que está en el lateral sur de la abadía.' WHERE `entry`=12830;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cada dos años los espíritus de competición se vuelven especialmente fervientes... ¡y ahora es ese momento! Tanto la Alianza como la Horda llevamos este tabardo en los campos de batalla para honrarlos e intentamos derrotar a nuestros enemigos y ganar el favor de los espíritus.' WHERE `entry`=12831;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Creo que la tienen cerca de la horca.' WHERE `entry`=12832;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Creo que los vi llevarlo hacia la parte norte de la abadía, cerca de los objetivos de tiro con arco.' WHERE `entry`=12833;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C.$B$B¿Quieres que Darrok te envíe a Bahía Ventura?' WHERE `entry`=12838;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estoy a punto de descifrar esta mena. ¡En cualquier momento tendré la solución!' WHERE `entry`=12844;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola, $C!$B$B¿Estás en condiciones de ir a Bahía Ventura y romper unas cuantas cabezas de la Horda?' WHERE `entry`=12845;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Sí, $G hijo mío : hija mía;?' WHERE `entry`=12846;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El sumo abad te estudia un instante y sonríe.>$B$BMuy bien, conozco un lugar, $Ghijo:hija;. Tú besa mi anillo y avísame cuando te sientas en condiciones.' WHERE `entry`=12848;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Entonces, únete a mí en la entrada de la abadía. Estaré allí en seguida.' WHERE `entry`=12849;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='He jugado mi papel destruyendo civilizaciones enteras... pero tener que rebajarme a tratar con los desdichados miembros del Culto de los Malditos me repugna.' WHERE `entry`=12850;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vale, ¡pero ten más cuidado esta vez!' WHERE `entry`=12851;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Habla en voz baja. ¡No queremos llamar la atención de estas bestias!' WHERE `entry`=12852;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡No me molestes con nimiedades!' WHERE `entry`=12853;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡No ves que estoy ocupado! ¡Nunca conseguiremos que esta base esté lista cuando regrese el Príncipe!',`Text1_0_loc6`='Si esas bestias salvajes no hubieran quemado nuestros barcos, ya estaríamos en casa. ¡Ahora déjame en paz para que pueda seguir trabajando!' WHERE `entry`=12856;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un buen club puede marcar la diferencia en el mundo.' WHERE `entry`=12863;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola, colega! Veo que tienes algunas fichas de la Fiesta de la Cerveza. Resulta que yo aún tengo algunos objetos de la Fiesta de la Cerveza a la venta.' WHERE `entry`=12864;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Una buena cerveza es una cerveza fuerte.' WHERE `entry`=12867;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mola ser miembro del Club de la "Cerveza del Mes".' WHERE `entry`=12869;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿No has oído hablar del Club "de la Cerveza del Mes?" A esos tipos les encanta estar de fiesta. ¡Si fuera por ellos, la Fiesta de la Cerveza nunca acabaría! A veces, la del año pasado parecía no haber acabado.$B$BPero ahora han llegado a la conclusión de que la mejor manera de mantener vivo el espíritu de la Fiesta de la Cerveza es permitir que la gente se una a su club.$B$B¡Puedes unirte por solo unas fichas! Todos los meses se te enviará cerveza y podrás comprar esa misma cerveza si te gusta. Se trata de material de máxima calidad y es una forma de asegurarse de que el espíritu de la Fiesta de la Cerveza no se vaya nunca.' WHERE `entry`=12878;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿No has oído hablar del Club "de la Cerveza del Mes?" ¡Son un montón de tipos muy guapos que quieren celebrar la Fiesta de la Cerveza durante todo el año! Intentaron volver a traer la Fiesta de la Cerveza en el pasado, pero nunca duró demasiado.$B$BCuando te hayas unido al Club, se te enviará por correo una cerveza diferente cada mes. Si te gusta esa cerveza, puedes hablar con los vendedores de Forjaz para conseguir más.$B$BCreo que tienes belleza suficiente para unirte al Club. Tú me das unas cuantas fichas y yo te daré los formularios. ¡Quizá podamos celebrar la Fiesta de la Cerveza juntos durante todo el año!' WHERE `entry`=12880;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Lo lograste, $G chico:chica;! ¡Pensé que estabas en las últimas!' WHERE `entry`=12882;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué haces aquí todavía? ¡Vuelve a la ciudad!' WHERE `entry`=12883;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hey, $C, ¿has visto eso? Allí, oculto cerca de la superficie del agua...$B$BTú también lo has visto, ¿verdad?' WHERE `entry`=12895;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿El qué?$B$BBueno, yo las llamo obstrucciones flotantes indefinidas. Muchos niegan su existencia completamente.$B$B¡Pero yo soy más listo! ¡Yo las he visto de cerca! He pasado los últimos años construyendo este barco para encontrarlas, seguirlas, documentarlas y, si fuera necesario, erradicarlas.' WHERE `entry`=12896;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No te dejes engañar, $R. Son muy escurridizas. En un momento están aquí y, de repente, han desaparecido. ¡Es virtualmente inexplicable!$B$BEntre tú y yo, una vez tuve un trozo de una de ellas en mis manos. Por desgracia, desapareció antes de que pudiera realizar ninguna medición. Simplemente desapareció...' WHERE `entry`=12897;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mucho, ya sabes...$B$BHay quien dice que solo están observando, otros creen que están modificando la vida de nuestros mares, ¡y algunos piensan que han venido para conquistar Azeroth!$B$BCuando consiga destruir una con mis cabezas explosivas con propulsión a cohete, obtendré respuestas. ¡Eso es seguro!' WHERE `entry`=12898;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $R.$B$BTe estaba esperando.' WHERE `entry`=12899;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Muy bien. ¡Vamos!' WHERE `entry`=12900;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los grifos están a 10 pasos de aquí, colega. Ellos nos llevarán al Puesto de Thorson.' WHERE `entry`=12905;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No pienses ni por un momento que tus logros pasados te dan derecho a hacer lo que sea, $N. Porque no es así.$B$BSi quieres mi respeto, tendrás que ganártelo. Tendrás que seguir mis órdenes sin cuestionarlas y cumplirlas... sin excepción.' WHERE `entry`=12906;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida al Campamento Oneqwah.' WHERE `entry`=12912;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='G.U.A.O. es más que un objetivo al que lanzarle jarras, es un recurso muy valioso para los entrenamientos. ¡Si puedes acertar al G.U.A.O., podrás acertar a cualquier cosa!$B$BPero procura no golpearme a mí.' WHERE `entry`=12913;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La mayoría de mis hermanos desaprueban los medios "bárbaros" de la Horda y hubo un tiempo en el que yo era uno de ellos. Cuando me ordenaron venir a este húmedo y... aromático lugar, maldije mi suerte.$B$BPero, mientras llevaba a cabo mis estudios, he acabado apreciando lo que me rodea. Tiene cierto encanto, ¿no crees?$B$BAh y mis estudios, por supuesto, permitirán que otros magos vuelvan aquí con un hechizo. ¿Te lo enseño?' WHERE `entry`=12926;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='"La mayoría de mis hermanos desaprueban los medios "bárbaros" de la Horda y hubo un tiempo en el que yo era uno de ellos. Cuando me ordenaron venir a este húmedo y... aromático lugar, maldije mi suerte.$B$BPero, mientras llevaba a cabo mis estudios, he acabado apreciando lo que me rodea. Tiene cierto encanto, ¿no crees?$B$BNo creo que te interesen mis estudios ya que no eres $Gun mago:una maga;...' WHERE `entry`=12927;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sinu a\'manore. Si quieres aprender a invocar un portal a Theramore, puedo enseñarte.' WHERE `entry`=12933;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Fiesta de la Cerveza ha sufrido algunos cambios desde el primer año. Ahora entregamos fichas de la Fiesta de la Cerveza en vez de vales. Si tienes vales viejos, puedo cambiártelos.' WHERE `entry`=12934;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Vienes para viajar, $R?$B$B¿O te preocupa algo más?' WHERE `entry`=12937;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Escucha. Cuando buscas respuestas, no hay nada mejor que una buena explosión; cuánto más grande, mejor.$B$BPero ya basta de preguntas por ahora, $R.$B$BMis cabezas explosivas son material clasificado y el elemento 115 que creé para darles energía es aún más secreto.$B$BDe hecho, será mejor que regreses a tu rutina habitual y hagas como que no nos hemos conocido.' WHERE `entry`=12938;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida, $Gaventurero:aventurera;. Llegas justo a tiempo.' WHERE `entry`=12939;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Te inquieta estar ante la presencia de un dragón Negro?$B$BBien. Debería. También inquieta a estos dragones. Pero se aferran a sus ancestrales virtudes, no osarían atacarme aquí.$B$BInsensatos.' WHERE `entry`=12942;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Escucha, $R.$B$BSi buscas una trituradora que montar, tendrás que dirigirte hacia las colinas al norte.$B$BEsta está fuera de servicio.' WHERE `entry`=12943;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Al principio las reparamos para cosechar estos bosques, pero desde que llegó la Alianza y comenzó la lucha las usamos como máquinas de guerra.$B$BCréeme, $C. Estarías $Gloco:loca; si tuvieras una llave y no la usaras.' WHERE `entry`=12944;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las tiene Grekk.$B$BQuizá te entregue una si le ayudas. Asegúrate de hablar con él la próxima vez que estés en el norte, en el aserradero Cielo Azul.' WHERE `entry`=12945;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Pero?' WHERE `entry`=12946;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Vuelo Infinito está tratando de alterar el destino del príncipe Arthas Menethil.$B$BAhora lo conoces como el Rey Exánime, pero aquí y en este momento aún era un príncipe de Lordaeron que trataba de hacer lo que era mejor para su reino.$B$BHoy Arthas toma una decisión que marcará el destino, llevando a lo que los Vigilantes del Tiempo llamamos un punto de inflexión en la línea temporal.' WHERE `entry`=12949;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los Infinitos están intentando algo con una sutileza poco común en ellos. Están tratando de ocultar las pruebas que llevarán a Arthas a tomar la decisión de arrasar Stratholme.$B$BSus agentes han usado magia ilusionista para que los cargamentos de grano de los campos cercanos parezcan normales.$B$BNecesito que encuentres el grano y uses este disruptor Arcano en los envíos. Los hombres de Arthas están buscando grano apestado y lo encontrarán rápidamente cuando desaparezca la ilusión.' WHERE `entry`=12952;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No creo que podamos permitirnos dejar pasar la oportunidad de congraciarnos con el Vuelo Rojo. Con las batallas que se avecinan, tenerlos como aliados tendrá un valor incalculable.' WHERE `entry`=12953;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El Comandante detiene la clase y se gira: sus ojos te evalúan, te juzgan.>$B$BCreo que alguien como tú me sería de ayuda, $C.' WHERE `entry`=12958;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sí, $C, ¿qué ocurre?' WHERE `entry`=12960;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me alegro de verte de nuevo, $N.$B$B¿Has venido a poner al día tus conocimientos sobre tácticas de la Plaga?' WHERE `entry`=12961;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Querido, ¡moriría por esos zapatos! Literalmente. ¿A quién has matado para conseguirlos?' WHERE `entry`=12963;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida al Manantial de Granito, colega.$B$BSi consigues atrapar a alguno de esos trols de hielo vivo, avísame. Pagamos bien por ellos.' WHERE `entry`=12966;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Oye, veo que tienes algunas fichas de la Fiesta de la Cerveza. Si quieres, aún tengo algunos objetos de la Fiesta de la Cerveza a la venta.' WHERE `entry`=12968;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$N, me alegro de verte!$B$B¿O es la primera vez que nos encontramos?$B$BAhora mismo estoy en tantos lugares y momentos que a veces es difícil llevar la cuenta.' WHERE `entry`=12976;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Una vez abandoné una guerra para ocuparme de los problemas de mi hogar, en los Páramos de Poniente. Parece que fue hace siglos. Gracias a la Luz que los Defias ya no suponen una amenaza.$B$BAhora mismo estamos muy lejos de nuestro hogar, pero si no ganamos esta batalla, no tendremos donde volver.' WHERE `entry`=12982;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mis magos de guerra están volando de vuelta. Llegarán de un momento a otro.$B$BHabla conmigo de nuevo cuando hayan vuelto.' WHERE `entry`=12990;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Perdona, $R, pero estoy de viaje oficial y no puedo distraerme.$B$BDiscúlpame.' WHERE `entry`=12991;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Buen trabajo! Ahora Arthas está al corriente del grano apestado de Stratholme y está a punto de comenzar la matanza.$B$BPero algo no va bien. Siento una presencia extraña en esta línea temporal además de nosotros y eso debe de significar que el Vuelo Infinito anda por aquí.' WHERE `entry`=12992;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No lo sé y eso me preocupa. Haré todo lo que pueda para encontrar a los Infinitos.$B$BAhora necesito que te unas al ejército de Arthas para mantenerte cerca de él.' WHERE `entry`=12993;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Buena suerte y mantente a salvo. Aquí vienen Uther y Jaina.' WHERE `entry`=12995;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Drakuru extiende su ensangrentada mano a través de la jaula y te mira expectante.>' WHERE `entry`=12998;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hubo un tiempo en el que podría haberte arrancado el corazón solo por ver la expresión de tu rostro...' WHERE `entry`=12999;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Eh, colega!$B$BMe alegro de volver a verte... $N, ¿verdad? Sí, sí, claro.$B$BTú y yo vamos a ser muy buenos amigos. Drakuru sabe de estas cosas.$B$B<Una sonrisa comienza a extenderse por la cara pintada del trol.>' WHERE `entry`=13001;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Uy, colega!$B$BEsto no es lo que parece. Bueno, igual...$B$BEs complicado.' WHERE `entry`=13011;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ehh! ¿Nos conocemos?$B$B¡Sí, sí! Prigmon no olvida una cara.$B$B¿Acaso me estás siguiendo...? Sea lo que sea… ¡No es culpa de Prigmon!' WHERE `entry`=13012;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $R.$B$BSi te interesa colaborar en la defensa del templo, habla con Lord Afrasastrasz en el interior.' WHERE `entry`=13018;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida a mi campamento, $R.$B$BNo parece gran cosa, pero por ahora es un hogar.' WHERE `entry`=13019;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Floppy y yo salimos a dar un paseo, pero nos perdimos y yo tenía mucho miedo porque había lobos, ya sabes... y nos perseguían, pero entonces vimos esta torre y Floppy y yo nos escondimos aquí y después de un rato los lobos se marcharon y ha sido terrible y no sabemos volver a casa.' WHERE `entry`=13023;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Creo que debería marcharse, $Gseñor:señorita;. Está asustando a Floppy.' WHERE `entry`=13024;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las fuerzas de la Plaga intentan aniquilarnos. ¡No desesperéis y seguid luchando!' WHERE `entry`=13026;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Debemos resistir! ¡No perdáis la esperanza! ¡Quemad la hedionda peste allí donde la encontréis!' WHERE `entry`=13028;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Saludos, $C!$B$BEspero que no te moleste el trabajo duro. Nos vendría bien un poco de ayuda.' WHERE `entry`=13030;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los vientos y los árboles hablan en susurros de nuevos problemas. La tierra parece saludable, pero está sucediendo algo que no se ve a primera vista.' WHERE `entry`=13031;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Pellejos de la Horda! ¡Para nuestras tropas, solo lo mejor!' WHERE `entry`=13033;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Suministros para el camino? ¿Quizá algo para tomar mientras observas las luchas?' WHERE `entry`=13034;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los enemigos se acercan por todos los flancos y todos los ancestros hablan de abandonar este puesto. Yo digo que deberíamos quedarnos y luchar como los guerreros que somos.' WHERE `entry`=13035;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La llamaré el espíritu de Gnomeregan. ¿No es hermosa?' WHERE `entry`=13042;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡La aterradora capitana DeMeza ha amarrado en Bahía Botín y ha declarado que hoy es el Día de los Piratas!$B$BCualquiera que se atreva a compartir un trago con ella, será nombrado tripulante honorífico.' WHERE `entry`=13044;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tenemos un serio problema.' WHERE `entry`=13045;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Demos gracias a la Luz!',`Text1_0_loc6`='Eh, tú, $C. Estamos en plena batalla.' WHERE `entry`=13047;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¿Qué haces aquí?',`Text2_1_loc6`='¿Qué haces aquí?',`Text3_0_loc6`='¿Tú no eres $N? Había oído que habías muerto.' WHERE `entry`=13047;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='Que sea rápido... Luchar aquí es macabro y necesito mantenerme fuerte.',`Text5_0_loc6`='¿Eres un nuevo recluta? No llevas la armadura estándar.' WHERE `entry`=13047;
UPDATE `locales_npc_text` SET `Text6_0_loc6`='¡Oh, bien! ¡Refuerzos!',`Text7_0_loc6`='¿Tienes un mensaje para mí?' WHERE `entry`=13047;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Oh... Me siento grogui...' WHERE `entry`=13060;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡No me molestes, $C! ¡Tengo dos calderos enteros de brebaje burbujeante aquí y debo asegurarme de que sepa bien!$B$B¡Si no es así, el maestro Coren Cerveza Temible me despellejará y escaldará!' WHERE `entry`=13061;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Está cada vez mejor! ¡Calienta el estómago! ¡Y $N, debo decir que tienes mucho estilo vistiendo!',`Text1_0_loc6`='Sí, $Gseñor:señora;... ¡Menuda bebida! ¡MENUDA bebida!' WHERE `entry`=13063;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¡Cantemos algo!$B$B¡Birra, birra!$B¡Birra, birra!$B¡La vida de Hierro Negro!$B$B¡Ja, ja! $N, ¡qué bien me caes!',`Text3_0_loc6`='¿Por dónde iba...? Ah, sí... Así que le dije "¡Pero te necesito! ¡Te necesito!" ¡... Y ella simplemente se rió y se alejó deslizándose! Es la verdad, $N. ¡Los nagas pueden resultar tan crueles!' WHERE `entry`=13063;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Está bueno! Pero es un poco flojo. Yo le añadiría algo más de aliento de ajo...' WHERE `entry`=13064;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Menudo desastre.' WHERE `entry`=13070;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cualquier otro día no haría esto, pero... ¡tengo sed y odio beber a solas!$B$B¡Al centro!' WHERE `entry`=13074;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡BUM!$B$BEs lo último que recuerdo...' WHERE `entry`=13077;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<suspiro>...$B$B¿Qué tal un poco de ayuda?' WHERE `entry`=13078;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No me hagas caso. No soy más que un viejo que espera a un viejo... amigo.' WHERE `entry`=13079;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ohh, el dolor es... insoportable...' WHERE `entry`=13080;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Date prisa, $R.$B$BNo me queda mucho tiempo.' WHERE `entry`=13081;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Rápido, $Ghermano:hermana;. Estoy goteando...' WHERE `entry`=13082;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<suspiro>... ¿Crees que podríamos irnos, $C?$B$B¡Pierdo fluido con rapidez!' WHERE `entry`=13083;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué pasa? ¿No ves que estoy ocupado?' WHERE `entry`=13089;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mira, les dices a Schweitzer y a Roitau que aquí lo tenemos todo bajo control. No tienen que... ¿qué?$B$B¿No te envían ellos? ¿Entonces para qué me haces perder el tiempo?$B$B¡Tengo trabajo que hacer!' WHERE `entry`=13093;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Oye, ¿no ves que estoy ocupado?$B$B¿Quieres ver el espectáculo? El grupo toca todas las tardes a las ocho.' WHERE `entry`=13094;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $R.$B$BMe temo que tendrás que disculparme, pero estoy terriblemente ocupado en este momento.' WHERE `entry`=13097;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El tiempo es esencial aquí, $N. ¿Empezamos con la prueba?' WHERE `entry`=13098;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Agh, no debí haberme ofrecido como especialista. ¡Siempre nos piden que hagamos lo imposible!' WHERE `entry`=13118;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El crocolisco cautivo emite un gruñido grave cuando te acercas.>' WHERE `entry`=13120;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un día excelente para una incursión en el campo, ¿no le parece, $Gseñor:señora;?' WHERE `entry`=13121;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Bg\'grrml brgl brm!' WHERE `entry`=13134;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Sácame de aquí, $R!' WHERE `entry`=13135;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Alguna vez has visto dracos como los de aquí? Son perfectos para cazar.' WHERE `entry`=13147;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este puesto es seguro, $R.$B$BHace ya algún tiempo que se eliminaron a los enemigos de esta zona.' WHERE `entry`=13156;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Recupérate deprisa, debemos seguir adelante.' WHERE `entry`=13177;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Zim\'Torga puede proporcionarnos cierto nivel de protección, pero la presencia de la Plaga y las acciones de los Drakkari la han debilitado mucho.$B$BAhunae y yo nos ocuparemos del resto de nuestras defensas.' WHERE `entry`=13180;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Has arrebatado el control del Oculus a las fuerzas de Malygos. El Vuelo Rojo está en deuda contigo.' WHERE `entry`=13239;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Vuelo Bronce está en deuda contigo.' WHERE `entry`=13240;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Vuelo Verde está en deuda contigo.' WHERE `entry`=13241;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un draco rubí, con todo su poder, puede llevar a cabo Mártir, que protege a los objetivos amistosos redirigiendo toda la magia hostil hacia el draco rubí durante un breve periodo de tiempo. Maniobras evasivas se puede realizar mientras Mártir está activo.' WHERE `entry`=13255;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un draco esmeralda con todo su poder puede realizar Cauce de sueño, que transfiere la propia energía vital del draco a un objetivo amistoso y lo sana. Veneno parasitante se puede usar para restaurar la salud que se pierde al usar esta facultad.' WHERE `entry`=13259;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida al campamento, $C.' WHERE `entry`=13261;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Medita sobre lo que has visto hoy, $N.$B$BEl futuro ya está escrito. Solo te queda decidir qué papel vas a jugar en él, tron.' WHERE `entry`=13265;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No le pidas demasiado al viejo Nass. Ya no es tan joven y fuerte como antaño.' WHERE `entry`=13271;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Muévete. No hay nada que ver aquí.' WHERE `entry`=13289;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Rosangre te observa con curiosidad.>$B$BNo puedo evitar preguntarme por qué te mereces tanta atención.$B$BNo te ofendas...' WHERE `entry`=13290;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Por favor, date prisa, $N. Me duele mucho y se nos agota el tiempo.' WHERE `entry`=13293;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Etamos intentado volar este muro para que los hombres de Wick puedan trabajar en la nueva casa del guarda del puerto. ¡Sin embargo, el pesado este no tiene ni idea y cree que uso demasiada dinamita!$B$BPsé. Yo volaba cosas cuando él aún llevaba pañales. Como siga así voy a tirarlo al canal de una patada y terminar el maldito trabajo yo misma.' WHERE `entry`=13301;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vamos a construir otra carretera hacia el Puerto de Ventormenta en cuanto quitemos esta pared... ¡Pero esta enana loca ha traído dinamita suficiente para nivelar todo el barrio!$B$BNo sé en qué estaba pensando el maestro Alexston cuando la contrató... ¡es malhablada, egoísta y terca como una mula!' WHERE `entry`=13302;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Plaga es una mancha en estas prístinas tierras. Por el poder de los creadores, aplastaré hasta el último de sus decrépitos huesos bajo mi talón.' WHERE `entry`=13303;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Parece que has tenido la misma idea que yo. Cazando fruta, ¿no?' WHERE `entry`=13307;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡¿Loken?! Ya me estoy hartando... puede que hayamos neutralizado a los enanos férreos, ¡pero apostaría a que en otra parte hay otra máquina escupiendo un montón de esos vrykuls férreos!' WHERE `entry`=13308;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Busca la torre de aterrizaje de zepelines que está fuera de la ciudad en el sudeste.' WHERE `entry`=13311;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mi pobre máquina voladora. ¡No sobrevivió al golpe y sin piezas de repuesto nunca podré arreglarla!' WHERE `entry`=13322;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Pronto, $N!$B$BPronto probarás el auténtico poder. Pronto recibirás la recompensa por tu dedicación y servicio.' WHERE `entry`=13324;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida. ¿Puedo ofrecerte descanso o sustento?' WHERE `entry`=13328;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Te gusta el nuevo hogar que he tallado para mis llamados adoradores?$B$BEn mi lecho de muerte, los maldije a todos y los arrastré junto a mí a este inframundo especial que yo misma creé.$B$BCreo que me deleitaré con su tormento eterno, ¡pero lo que realmente ansío es vengarme de mi anterior profeta!' WHERE `entry`=13331;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Has venido para comprarle algo a Hargus?' WHERE `entry`=13342;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Otra persona suplicando los favores del Rey Exánime?$B$BSolo los dignos obtienen los dones oscuros...' WHERE `entry`=13348;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Espero que el archimago se dé cuenta de lo que he tenido que hacer para cumplir la parte del trato de Hemet.' WHERE `entry`=13352;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Deberías pegarte a Arthas ahora mismo!' WHERE `entry`=13353;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Kartak venir a por carne de lenguas-grandes. Corazón Frenético asegurarse de que haya bastante preparada.' WHERE `entry`=13364;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Todo bien?',`Text1_0_loc6`='¿Sí?' WHERE `entry`=13366;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Es agradable tener un buen compañero de caza.',`Text3_0_loc6`='¿Ocurre algo?' WHERE `entry`=13366;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola! ¿Cómo estar? Nosotros bien... sí, bien bien. ¡Hola!',`Text1_0_loc6`='¿Alguna vez tropezar con tus pies? Nosotros sí. Doler un poco. Si no hacer, no divertido. Nop.' WHERE `entry`=13367;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¡Hola! Eres fuerte y $Gguapo:guapa; y esas cosas. Me gustas. Seamos amigos, ¿vale?',`Text3_0_loc6`='¿Quieres que ayudemos en algo? ¿Con qué ayudar? Nosotros buenos ayudando.' WHERE `entry`=13367;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estaré en deuda contigo eternamente, $N.' WHERE `entry`=13368;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un buen día para volar, ¿verdad?' WHERE `entry`=13375;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Reconozco a un as en cuanto lo veo. ¡Gracias de nuevo por salvarme la vida!' WHERE `entry`=13376;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Mira lo que has hecho, caballero de la Muerte! ¡Has otorgado gloria a la Plaga!' WHERE `entry`=13382;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Déjame salir de aquí, tron!',`Text1_0_loc6`='Tú no eres de la Plaga. ¿Qué haces aquí, tron?' WHERE `entry`=13390;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¿Por qué te deja caminar por aquí la Plaga, tron?',`Text3_0_loc6`='Creo que estás perdido, tron...' WHERE `entry`=13390;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Tu Plaga no ser bienvenida en Zul\'Drak!',`Text1_0_loc6`='Zul\'Drak pertenece a los Drakkari, tron. ¡Siempre lo hará!' WHERE `entry`=13391;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Tu Plaga va a fracasar aquí, tron.',`Text3_0_loc6`='¡Nuestra gente va a matar hasta al último de vosotros, miembros de la Plaga!' WHERE `entry`=13391;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='¡Aléjate de mí, asqueroso miembro de la Plaga!' WHERE `entry`=13391;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Cómo te atreves? ¡Desaparece de mi vista!',`Text1_0_loc6`='Ese zoquete probablemente esté practicando ballet en su cuarto.' WHERE `entry`=13396;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¡JAMÁS menciones ese nombre delante de mí! ¡Considera este el primer y último aviso!',`Text3_0_loc6`='Puedo decirte donde no está...' WHERE `entry`=13396;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='¡Heigan está llevando a cabo el mantenimiento de ese estudio de baile lleno de babas y mugre que tiene!' WHERE `entry`=13396;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Que la peste llegue a Nuevo Avalon solo es cuestión de tiempo.' WHERE `entry`=13397;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Fineous tiene lo que necesitas, colega.' WHERE `entry`=13402;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$N! He estado esperando tu vuelta. ¿Qué te ha retenido tanto tiempo, tron?$B$BEs hora de completar la conversión de los Sangreañublo.$B$B¡Únete a mí y contempla la creación de nuestro ejército!' WHERE `entry`=13422;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo no me alisté para esto, pero oye... al menos aquí hace calor.' WHERE `entry`=13431;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Algún día, Rejek ser poderoso guerrero.' WHERE `entry`=13438;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C. Espero que me traigas buenas noticias sobre la Worldwide Invitational. Si es así, significa que tienes un código secreto para entregarme. A cambio del código, te daré un regalo, la empuñadura de Tyrael.$B$BSimplemente susúrramelo al oído cuando estés $Glisto:lista;.' WHERE `entry`=13441;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$GBienvenido:Bienvenida; a mi cocina! Aquí preparamos algunos de los mejores platos de Dalaran.' WHERE `entry`=13449;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Espero que les des su merecido a los lenguas-grandes. Ser hora de que ellos irse.' WHERE `entry`=13451;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No creo que poseas la agudeza mental necesaria para comprender la naturaleza de la magia de portales.$B$BSin embargo, puedes usar el cristal tras de mí para teletransportarte a las ruinas que se encuentran bajo Dalaran en el Bosque Canto de Cristal.' WHERE `entry`=13455;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Como mago, formas parte de un selecto grupo que puede invocar un portal para transportarte de vuelta a este lugar. Puedo enseñarte a hacerlo, si estas $Gpreparado:preparada;.$B$BY recuerda que siempre puedes usar el cristal que se encuentra detrás de mí para teletransportarte a las ruinas que hay bajo Dalaran, en el Bosque Canto de Cristal.' WHERE `entry`=13456;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C.' WHERE `entry`=13458;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$GBienvenido:Bienvenida; a la barbería, colega! ¿Has venido a cortarte el pelo? ¿A teñirte? ¿Quizá algo distinto?$B$BPodemos cepillar el vello facial, poner pendientes, dar forma a colmillos y cuernos, ¡e incluso modificar las características de los no-muertos!$B$BSiéntate y nos pondremos manos a la obra. Solo unas pocas monedas y pronto encontrarás al nuevo y más atractivo tú...' WHERE `entry`=13461;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El mayor de tus desafíos aún está por llegar. El Guardián-Ley Eregos es un dragón Azul de inmenso poder. Lo encontrarás sobrevolando el anillo superior.' WHERE `entry`=13468;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué haces aquí? ¡Tienes que volver a Stratholme inmediatamente!$B$B¿Te teletransporto a Stratholme?' WHERE `entry`=13470;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Busca esas cajas con grano plagado y usa tu disruptor arcano en ellas!$B$BEntonces Arthas tendrá las pruebas que necesita para comenzar la purga de Stratholme.' WHERE `entry`=13471;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué haces aquí?$B$B¡Arthas está en la entrada de Stratholme ahora mismo y tú deberías estar con él! ¡Vamos!' WHERE `entry`=13472;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Estás $Gseguro:segura;?$B$BUna vez que haya eliminado el conocimiento de los talentos de tu mente, no habrá vuelta atrás.' WHERE `entry`=13475;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Muy bien. Ya está.' WHERE `entry`=13476;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aquí es donde lo sacrifiqué todo para liberar a mi padre. Ahora vengo a destruirlo...' WHERE `entry`=13485;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aquí estás, $N.$B$BAhora vuelve a concentrarte en tu tarea...' WHERE `entry`=13501;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestros almacenes están vacíos. Hasta que no lleguen más suministros, no puedo vender nada.' WHERE `entry`=13502;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Gracias a que has logrado reponer algunos de nuestros suministros, puedo volver a cocinar. ¿Qué puedo hacer por ti?' WHERE `entry`=13503;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Por qué me miran todos como si hubiera estrellado la nave?' WHERE `entry`=13506;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mmm... tu cara me resulta familiar, $R. Dame un momento...$B$B¡Ajá! Te recuerdo de los Páramos de Poniente. ¡Eres $N! Me alegro de volver a verte. Pero esperemos que esta vez nos ayudes a terminar el trabajo.$B$BAhora estamos muy lejos de los Páramos de Poniente, pero si no ganamos esta batalla, puede que no haya un hogar al que volver.' WHERE `entry`=13510;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$N! Apenas te reconozco con esas ropas nuevas. ¿Todavía tienes esa túnica... o escogiste las albarcas... o fue el bastón? No lo recuerdo, fue hace tanto tiempo...$B$BBueno, me alegro de que estés aquí para ayudar de nuevo. Necesitamos toda la ayuda posible. $N, si actúas tal y como lo hiciste allá en los Páramos de Poniente con La Milicia Popular, tendremos una oportunidad.$B$BEstos son tiempos duros y si no ganamos esta batalla, puede que no haya unos Páramos de Poniente a los que volver.' WHERE `entry`=13511;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Con la bendición de la Luz y tu ayuda, $N, los Defias dejaron de ser una amenaza.$B$BPero ahora estamos muy lejos de los Páramos de Poniente y si no ganamos esta batalla, puede que no tengamos un hogar al que volver.' WHERE `entry`=13512;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, $Gamigo:amiga;.' WHERE `entry`=13519;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sí, el Rey Exánime caerá ante nuestro poder. Obtendremos nuestra venganza final.$B$B¡Pero primero nos aseguraremos de que la peste conocida como El Embate Escarlata conozca su final de una vez por todas!' WHERE `entry`=13525;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Muy bien, pero si yo fuera tú, prestaría atención y no asentiría ciegamente a todo lo que escuchase.$B$BEmpieza, tal y como empiezan todas las buenas historias, con una traición. No es que fuésemos traicionados... en absoluto. El Rey Exánime nos ascendió a los caballeros de la Muerte por encima de los demás.$B$BMás bien fuimos nosotros los que le traicionamos con la ayuda del alto señor Darion Mograine.$B$BEn la batalla final en la Capilla de la Esperanza de la Luz, cuando esos infelices estaban a punto de ser aniquilados, Tirion Vadín ayudó a Mograine a romper el control que el Rey Exánime tenía sobre él y de todos los que estábamos allí presentes.' WHERE `entry`=13526;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Con el Rey Exánime huyendo aterrado de Vadín, que estaba armado con la Crematoria, pudimos arrebatarle El Bastión de Ébano al resto de fuerzas de la Plaga.$B$BPero eso no arregló las cosas entre nosotros y la Cruzada Escarlata.$B$BTal y como los Renegados aprendieron de la forma más difícil, los Escarlatas no parecían hacer diferencias entre nosotros y la Plaga.$B$BPensamos por nosotros mismos, tenemos nuestras propias motivaciones, tenemos casi todas las cualidades de los vivos y aun así, para ellos no somos distintos de los esclavos sin mente del Rey Exánime.$B$BPiensan erróneamente que nos tumbaremos y nos haremos los muertos.' WHERE `entry`=13527;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El lord comandante te clava la mirada antes de continuar.>$B$BA eso iba. Cuando la Alta General Abbendis trajo a sus fuerzas a Rasganorte, cambió su nombre por el de El Embate Escarlata.$B$BEl almirante Barean Viento Oeste no tardó mucho en presentarse ante ella en Nueva Vega del Amparo en el Cementerio de Dragones. Por lo que parece, ella no le esperaba. Y tampoco se esperaba que minase su autoridad y le arrebatase las riendas de mando.$B$BProbablemente estuviste $Ginvolucrado:involucrada; en su caída, pero da igual. Una vez que ella murió, el almirante se fue de forma misteriosa y le hemos perdido el rastro.' WHERE `entry`=13528;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Como he dicho, le perdimos el rastro, pero sin duda es algo de lo que podrás encargarte por mí. Sospecho que está ahí abajo, en alguna parte, pero los espías que han vuelto no han sido capaces de localizarlo en la isla.$B$BLos Renegados en Rencor Venenoso hicieron un buen trabajo saboteando la Nueva Vega del Amparo, pero las fuerzas del Embate que están presentes ahí abajo son los veteranos de ese lugar. Por desgracia han conseguido mejor equipo por el camino y unos grifos que les habrán quitado a algunos idiotas.$B$BLo que intento decir es que esto no va a ser un paseo por el campo, $N, ni siquiera para ti.' WHERE `entry`=13529;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Empezaremos en un momento.$B$BSolo quiero ser muy claro: no estamos aquí para ablandarlos. No estamos aquí para hacerles mella. Estamos aquí para poner fin a esa enfermedad que se autodenomina El Embate Escarlata.$B$BSi no quieres formar parte de esto, si no tienes las agallas necesarias, vete volando ya. Quiero a alguien que esté con nosotros todos los días hasta terminar el trabajo.$B$B¿Eres tú esa persona, $N?' WHERE `entry`=13530;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El lord comandante hace una mueca, mirándote de arriba abajo con respeto.>$B$BEso es lo que quería oír.' WHERE `entry`=13531;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Piensa en todo el sufrimiento que han causado los Escarlatas a lo largo de los años. Las torturas, los asesinatos, el fanatismo.$B$BNo permitiré que eso continúe. ¿Y tú?' WHERE `entry`=13534;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El encuentro Hyldnir es sagrado. Quien obtiene una victoria se asegura toda una vida junto a Thorim.' WHERE `entry`=13540;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $N.' WHERE `entry`=13548;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C. Puedo ayudarte a guardar a tus mascotas o recuperar a compañeros perdidos.',`Text0_1_loc6`='Saludos, $C. Puedo ayudarte a guardar a tus mascotas o recuperar a compañeros perdidos.' WHERE `entry`=13557;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mi vida y mi alma han estado llenas con recuerdos de la insufrible Horda. Tiempos de dolor, desesperación y sacrificio. ¡La furia de la batalla jamás podría abandonar mi corazón!' WHERE `entry`=13574;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este es un portal para el campo de batalla del Valle de Alterac.' WHERE `entry`=13576;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este es un portal para el campo de batalla del Ojo de la Tormenta.' WHERE `entry`=13577;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este es un portal para el campo de batalla de la Garganta Grito de Guerra.' WHERE `entry`=13579;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué pasa?$B$B¿Has venido a comer, tron? ¿O quizás a aprender del mejor?' WHERE `entry`=13581;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Buscas un tabardo?',`Text0_1_loc6`='¿Buscas un tabardo?' WHERE `entry`=13583;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C. Yo puedo ayudarte a recuperar a tus compañeros perdidos.',`Text0_1_loc6`='Saludos, $C. Yo puedo ayudarte a recuperar a tus compañeros perdidos.' WHERE `entry`=13584;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Qué hay! ¿Quieres comprar suministros de pesca o recibir una lección o dos?' WHERE `entry`=13591;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me alegro de volver a verte, $N. ¿Cómo podría olvidar a la persona que salvó la vida de mi hermana?' WHERE `entry`=13609;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es estupendo volver a verte, $N.' WHERE `entry`=13610;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El explorador Natoescarcha intenta ponerse en pie mientras te acercas, pero vuelve a caer al suelo mientras se agarra una herida sin duda dolorosa en el costado.>' WHERE `entry`=13611;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No pensaba que esto fuera a suceder, $Gzagal:zagala;. He perdido mucha sangre... es imposible que pueda volver.' WHERE `entry`=13612;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Una manada de arpías llegó desde el valle. Las lideraba una grande llamada Sirana... No tenía ningún lugar al que escapar. Hacía mucho tiempo que no eran agresivas... No me lo esperaba... Bajé la guardia... Ha sido culpa mía.' WHERE `entry`=13613;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es un poco tarde... para eso... $Gmuchacho:muchacha;.$B$B<El explorador Natoescarcha tose algo de sangre sobre la nieve.>$B$BCoge... al joven que está al mando por mí.$B$B<El explorador Natoescarcha se desploma mientras exhala su último aliento.>' WHERE `entry`=13614;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sé $Gbienvenido:bienvenida; entre nosotros, $C.' WHERE `entry`=13637;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Escucha lo que tengo que contarte sobre nuestro enemigo antes de que empecemos. Querrás estar $Gpreparado:preparada; cuando estemos machacando a la Plaga, ¿verdad?' WHERE `entry`=13640;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<Brann te mira expectante.>' WHERE `entry`=13641;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Imagina todo el conocimiento y sabiduría que hay encerrados en Ulduar. ¡Allí podrían estar los mismos secretos de la vida!' WHERE `entry`=13642;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Boktar cree que este accidente ha sido mala suerte, pero yo creo que se supone que debemos aprender algo de ello.' WHERE `entry`=13646;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Estamos desbordados, $R! ¡Estate $Gatento:atenta;!' WHERE `entry`=13658;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Bienvenida, compañera de batallas. Puedo ayudarte a recuperar compañeros perdidos.' WHERE `entry`=13661;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vamos, compañera de batallas.$B$B¡Come y duerme para que puedas luchar con toda tu fuerza!' WHERE `entry`=13663;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedo ocuparme de mí mismo, $N.' WHERE `entry`=13673;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ahora eres $Gel campeón:la campeona;, colega! ¿Crees que tienes lo que hay que tener para defender tu título?' WHERE `entry`=13680;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vuelve al trabajo antes de que el capataz nos vea.' WHERE `entry`=13682;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ya no entreno a caballeros de la Muerte, me cansé de iniciados cretinos y de sus constantes preguntas.$B$BAhora, largo.' WHERE `entry`=13699;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Cómo puedo ayudarte?' WHERE `entry`=13702;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $N. Este es el taller del guardián Mimir.' WHERE `entry`=13703;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Vaya descubrimiento histórico! La Sociedad Real de Boticarios ha dedicado años a aprender los secretos de la plaga del Rey Exánime, ¡y al fin lo ha logrado!$B$BA pesar de este giro en la historia, aún queda mucho por descubrir. Tengo que realizar más experimentos, conseguir más sujetos para usarlos en pruebas...$B$BAl menos no tengo que preocuparme por la necrópolis de la Plaga. La Alianza se ocupa de ese problema. Esto me deja tiempo suficiente para profundizar en los secretos de la plaga y descubrir lo que pueda.' WHERE `entry`=13731;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué puedo hacer por ti?' WHERE `entry`=13734;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿En qué puedo servirte?' WHERE `entry`=13737;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué es lo que quieres?' WHERE `entry`=13738;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No confío en ti lo suficiente como para hablar de tales cosas.$B$BDeberás respetar las pruebas que ha soportado nuestro pueblo, las cargas que hemos aguantado...$B$BQuizá si acabas siendo un gran honor para Dun Niffelem estaría dispuesto a compartir contigo el saber de nuestro pueblo.' WHERE `entry`=13748;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, $R. ¿En qué puedo ayudarte hoy?' WHERE `entry`=13759;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo siento, colega. Solo oficiales certificados de la Horda pueden autorizar la compra de un vehículo.' WHERE `entry`=13761;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Me alegro de volver a verte!$B$BEl rey Cuoretomenta ha informado de que estará fuera una temporada... Parece ser que tiene cuentas que saldar relacionadas con un amargo recuerdo. Insistió en que no fuéramos con él, así que hemos respetado su voluntad, como siempre.$B$BYo soy el responsable de nuestro clan mientras tanto. Aún te damos la bienvenida entre nosotros, por supuesto.' WHERE `entry`=13764;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No eres un taunka. ¿Por qué has venido al sagrado cementerio de nuestros guerreros?' WHERE `entry`=13776;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Míralos... todo lo que les importa es ser mejor que el otro.' WHERE `entry`=13777;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Verás, Thorim no es como esas arpías. Es un ser digno de estatura parecida a la de los dioses.$B$BSirvió a los titanes como guardián de Ulduar y el Templo de los Creadores.$B$BEso fue hasta que su hermano asesinó a su mujer, Sif, en un acto de cobardía.' WHERE `entry`=13778;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Thorim se volvió loco. Arremetió contra sus más amados aliados, los gigantes de escarcha.$B$BEsa es una de las razones por las que hoy, esta tierra está dividida por la guerra entre los Hyldnir y Los Hijos de Hodir... y por la que Thorim se ha exiliado al Templo de las Tormentas.' WHERE `entry`=13779;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Y a mí qué me importa si estas lobas y los gigantes de escarcha se matan entre ellos cada día?$B$BNo, no... Quiero que Thorim salga de su estupor y reúna a sus aliados para retomar Ulduar.$B$B¡El mundo se partirá en dos si fracasa!$B$BY por supuesto, te recompensaré apropiadamente.' WHERE `entry`=13780;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hombres... no puedes vivir con ellos y no puedes vivir sin ellos. Bueno, sí que puedes... pero es muy aburrido.' WHERE `entry`=13783;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estas minas son un lugar terrorífico. Por suerte, no tengo intención de quedarme mucho tiempo.' WHERE `entry`=13784;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los osos son criaturas hermosas. Salvajes, fuertes y feroces en la batalla... muy parecidos a nosotras, hermana.' WHERE `entry`=13785;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Me ha llamado alguien...?' WHERE `entry`=13788;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola, hola!' WHERE `entry`=13790;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, encanto. ¡Me alegra tener compañía!' WHERE `entry`=13792;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola, $N! ¿Cómo puedo ayudarte hoy?' WHERE `entry`=13798;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedes encontrar al ancestro Celador del Cielo cerca del Martillo de Agmar, en el Cementerio de Dragones.' WHERE `entry`=13816;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedes encontrar al ancestro Chogan\'gada en el Pináculo de Utgarde.' WHERE `entry`=13819;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Lunaro está en las Ruinas de Tethys en las Colinas Pardas.' WHERE `entry`=13820;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puedes encontrar al ancestro Kilias dentro de la Fortaleza de Drak\'Tharon.' WHERE `entry`=13823;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ancestro Fargal vigila el Fuerte Escarcha desde Las Cumbres Tormentosas.' WHERE `entry`=13828;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No podemos permitir que la Horda utilice los secretos ocultos de la Playa de los Ancestros contra nosotros. ¿Te unirás a nuestros valientes soldados allí?' WHERE `entry`=13833;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No podemos permitir que la Alianza utilice los secretos ocultos de la Playa de los Ancestros contra nosotros. ¿Te unirás a nuestros valientes guerreros allí?' WHERE `entry`=13834;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Factoría de Carne es el corazón de la producción de necrófagos, abominaciones y gigantes de carne de la Plaga. Si podemos destruirla, debilitaremos al Rey Exánime de forma significativa.' WHERE `entry`=13838;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué necesitas?' WHERE `entry`=13841;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Servimos a Thorim, hermana.' WHERE `entry`=13843;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Siempre es un placer conocer a personajes de tu talla en esta tierra abandonada. ¿Puedo ofrecerte algún tipo de ayuda?' WHERE `entry`=13844;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, $C. Mi consejo para ti es este: cuando viajes por el mundo, ten cuidado con la magia porque puede acabar con el profano.' WHERE `entry`=13846;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Por supuesto! Ve con la bendición de Elune.' WHERE `entry`=13847;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No debemos descentrarnos. Si dejamos que la situación nos confunda, podríamos ponernos en peligro. Los soldados necesitan descansar de toda esta locura.$B$BDisfruta de la victoria, $N. De no ser por ti, hubiéramos perdido la Vanguardia.' WHERE `entry`=13851;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo que hago por la Cruzada Argenta no debería considerarse el cumplimiento de una obligación. Más bien un pago, si lo prefieres. El Alto Señor me liberó del Rey Exánime y puso ante nosotros el sendero a la salvación. Dicho esto, hay que reconocer que nuestros métodos difieren... enormemente. Los Caballeros de la Espada de Ébano y yo ayudaremos a la Cruzada Argenta a sentar las bases en Corona de Hielo y luego separaremos nuestros caminos. Así, el pago quedará satisfecho.$B$BA nosotros nos aguarda otro destino.' WHERE `entry`=13856;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No cabe duda de que esos cruzados están causando muchos quebraderos de cabeza, $G hermano:hermana;. Pero ¿qué es el sacrificio sin dolor? Tú estuviste conmigo en la Capilla de la Esperanza de la Luz. Viste muchos sacrificios. Y ahora, tenemos que pagar nuestra deuda con el Alto Señor. Pagarle por su sacrificio.$B$BCuando la Cruzada Argenta haya establecido un punto de apoyo dentro de las murallas de Corona de Hielo, partiremos. A nosotros nos aguarda otro destino.' WHERE `entry`=13857;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tomar el Pináculo de los Cruzados solo es el primer paso de un viaje largo y peligroso. Aún queda mucho por hacer antes de que podamos asaltar la Ciudadela de la Corona de Hielo.$B$BSolo será posible con tu ayuda y la de otros como tú, $Ghéroe:heroína;.' WHERE `entry`=13896;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Martillo de Orgrim está preparado para atacar Corona de Hielo. No encontrarás una nave mejor.' WHERE `entry`=13902;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La única razón por la que sigues con vida es porque tengo curiosidad.' WHERE `entry`=13905;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sea lo que sea lo que vas a pedir, hazlo deprisa.' WHERE `entry`=13908;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='A partir de ahora nos encargaremos nosotros. Os habéis ganado un respiro después de esa batalla.' WHERE `entry`=13910;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué es eso?' WHERE `entry`=13911;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Alba Argenta coordina la oposición contra la Plaga en la Capilla de la Esperanza de la Luz en las Tierras de la Peste del Este. Ve allí para recibir instrucciones.' WHERE `entry`=13915;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Muchos han caído dentro de Valhalas y sus cadáveres son el testamento de la fuerza de nuestros métodos.$B$B¿Quedarán también tus huesos esparcidos en este lugar de honor y batalla, $N?' WHERE `entry`=13918;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Debería matarte aquí mismo, $R!$B$B¡Continúa con tu estúpido balbuceo y acabaré contigo!' WHERE `entry`=13919;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Todavía no has demostrado ser $Gdigno:digna; de participar en un desafío dentro de Valhalas, $N.$B$BRegresa cuando te hayas labrado un nombre.' WHERE `entry`=13921;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Muchos han caído dentro de Valhalas y sus cadáveres son el testamento de la fuerza de nuestros métodos.$B$B¿Quedarán también tus huesos esparcidos en este lugar de honor y batalla, $N?' WHERE `entry`=13922;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Uhnhh... No te preocupes por mí.$B$BEs... el final...',`Text0_1_loc6`='Uhnhh... No te preocupes por mí.$B$BEs... el final...' WHERE `entry`=13930;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Reuniendo... información... sobre la Plaga.$B$BLlegaron tropas de la Alianza... $B$BNos escondimos... esperamos hasta que atacaron la puerta... entonces, atacamos por detrás.$B$BAtrapados entre nosotros... y la Plaga... demasiado para ellos...$B$B$GHermano:Hermana;... ha sido glorioso.' WHERE `entry`=13931;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo he dado todo... por la Horda...' WHERE `entry`=13935;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GCampeón:Campeona; $N, me alegro de volver a verte,$B$B¿Has regresado a Valhalas para ayudar a algún amigo en sus desafíos?' WHERE `entry`=13946;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Espera, $Ghermano:mi señora;... $B$BUna última cosa...' WHERE `entry`=13948;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Buen trabajo! ¡Arthas camina seguro hacia Rasganorte!' WHERE `entry`=13950;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Factoría de Carne no volverá a producir soldados para los ejércitos del Rey Exánime.' WHERE `entry`=13978;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Por favor, respeta las leyes de Dalaran mientras estés aquí, $Gforastero:forastera;.$B$B¿Te has perdido? ¿Puedo ayudarte a encontrar algo?' WHERE `entry`=14014;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay que detener a la Plaga cueste lo que cueste. No podemos permitir que saquen al campo de batalla el ejército que están construyendo aquí.' WHERE `entry`=14028;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestra senda es sagrada. Elegiremos la muerte antes de abandonar a Thorim y unirnos a las huestes del Rey Exánime.' WHERE `entry`=14033;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Por favor, $R.$B$BLibérame...',`Text1_0_loc6`='No pienses, $C.$B$BSolo... hazlo...' WHERE `entry`=14035;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Di a mi familia... $B$BQue en los últimos instantes pensé en ellos...',`Text2_1_loc6`='Di a mi familia... $B$BQue en los últimos instantes pensé en ellos...',`Text3_0_loc6`='Déjame seguir mi camino... Te lo ruego...' WHERE `entry`=14035;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='Hazlo, $C... Después... ¡véngame!',`Text5_0_loc6`='Por favor, $R...$B$B¡No dejes nada que la Plaga pueda resucitar!' WHERE `entry`=14035;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Apenas puedo creer que el Culto fuera tan descuidado como para dejarse esto.' WHERE `entry`=14037;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿En qué puedo ayudar?' WHERE `entry`=14040;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Con qué puedo ayudarte?' WHERE `entry`=14041;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Buenos días!' WHERE `entry`=14042;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Puedes pagarme para que haga algo?' WHERE `entry`=14043;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La batalla aérea ha estallado cerca, $N. Tanto nosotros como la Horda enviamos bombarderos a El Bombardeo y a El Valle de la Esperanza Perdida. ¿Participarás en la batalla?' WHERE `entry`=14046;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Estás $Gpreparado:preparada; para comenzar el asalto y recuperar Entrañas para la Horda, $N?' WHERE `entry`=14050;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La fuerza de los elementos está con nosotros, $N. Con su ayuda, haremos todo lo que esté en nuestras manos para detener a Varimathras y a Putress. ¡LOK\'TAR OGAR!' WHERE `entry`=14051;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Cielos! ¡Baja la voz, $N! ¿Qué? ¿Quieres que nos maten?$B$BClaro, ¡ya sé quién eres! ¿Hay alguien en la Alianza que no conozca tu nombre, $N?$B$BDime, ¿Qué te parece mi saltarrobot? Está basado en el diseño de los mecagnomos. ¡Elegante y aerodinámico!' WHERE `entry`=14052;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El camino que tenemos ante nosotros no es sencillo. Si tienes algo de fuerza, quédate por aquí hasta que uno de mis escuadrones esté preparado para subir a Ymirheim. Les vendrá bien toda la ayuda que puedan conseguir.' WHERE `entry`=14055;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vaya, ¡tú sí que eres feo! Aunque eso no quiere decir que no sepas luchar.$B$BUno de mis escuadrones estará equipado y listo para marchar dentro de poco... ¡Mete tu sucio trasero en la fila y escala con ellos!' WHERE `entry`=14061;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los encuentros de arena puntuados volverán a comenzar en $6318d.$B$BLos aspirantes a campeones ya están afinando sus habilidades y consiguiendo el mejor equipo posible. ¿Estarás $Gpreparado:preparada;?' WHERE `entry`=14062;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este portal lleva a las Cavernas del Tiempo, en las profundidades de la tierra bajo el lejano Tanaris.$B$BPero tengo que pedirte que no viajes allí hasta que seas más fuerte.' WHERE `entry`=14066;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Puedo oler la pólvora de todas las bombas y cohetes! ¿Quieres salir y unirte a la fiesta?' WHERE `entry`=14067;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Las voces me están hablando a mí.',`Text1_0_loc6`='Debo obedecer.' WHERE `entry`=14068;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¡Tienes que salir de aquí antes de que llegue hasta ti también!',`Text3_0_loc6`='¡Está por todas partes! ¡Está en mi cabeza!' WHERE `entry`=14068;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='La saronita... ¡hay algo en ella!',`Text5_0_loc6`='Aquí hay algo. ¡Está en las rocas y en la tierra!' WHERE `entry`=14068;
UPDATE `locales_npc_text` SET `Text6_0_loc6`='¡Tengo que salir de aquí! ¿Puedes liberarme?',`Text7_0_loc6`='¿Puedes oírla en tu mente?' WHERE `entry`=14068;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aún estamos con los preparativos. No estaremos listos para partir hasta dentro de un rato.' WHERE `entry`=14069;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ya casi estamos preparados para partir. Solo déjanos un poco más de tiempo para acabar nuestros preparativos.' WHERE `entry`=14070;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En mis viajes he aprendido el patrón de la capa de fuego fatuo.$B$BEsta capa es una capa épica de lanzamiento de hechizos con la que se puede comerciar que otorga aguante, intelecto y regeneración de maná. Estoy buscando a alguien digno a quien enseñar este patrón.$B$BAlguien que hubiera completado todas las mazmorras normales de Rasganorte y tuviera el logro "Maestro de mazmorra de Rasganorte" sería digno de aprender el patrón de la capa de fuego fatuo.' WHERE `entry`=14072;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me he aventurado en oscuros lugares y he aprendido el patrón de la capa Fríomortal.$B$BEsta capa es una capa épica para taumaturgos con la que se puede comerciar que otorga poder de hechizos, celeridad y golpe crítico. Estoy buscando a alguien digno a quien enseñar este patrón.$B$BAlguien que haya completado la mayoría de las misiones de Rasganorte y que tenga el logro "Maestro cultural de Rasganorte" sería digno de aprender el patrón de la capa Fríomortal.' WHERE `entry`=14074;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ya que eres un sastre de cierta habilidad, puedo decirte que conozco algunos patrones especiales. Conozco los patrones de las capas de fuego fatuo y Fríomortal.' WHERE `entry`=14076;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $Gamigo:amiga;. ¿Puedo hacer algo por ti?' WHERE `entry`=14087;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola! Espera un segundo. Veo que no llevas una montura propia.$B$BProbablemente no serás de mucha ayuda a otros goblins si no puedes moverte a tus anchas. Me han pagado para que preste mis monturas a gente como tú... y sin coste alguno.$B$B¿Te interesa?' WHERE `entry`=14088;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tengo una pista sobre la piedra angular de Norgannon, que guarda la entrada a los archivos de Ulduar, pero los titanes la dividieron en dos partes y las ocultaron.$B$BUna de esas partes, el esqueleto de la piedra angular, está en la Biblioteca del Inventor en la costa norte, al sur de Ulduar. Lo primero que tienes que hacer es recuperar los fragmentos de un disco de acceso de manos de los guardianes de la biblioteca.' WHERE `entry`=14089;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Con los tesoros, nunca se mira la basura de\'l de al lao. Lo robao siempre es mejor, ¿ve\'dad? Pero gratis es gratis, ¡lo demás no importa!',`Text1_0_loc6`='Siempre \'toy lih\'ta pa\' un buen descuento de un tré\' dedos. Roba\' ehtá todo en la muñeca... y cuando falla, entonces clavas algo afilao entre las costillas, y todo en la muñeca también.$B$BLas manos asesinas valen más que un pago forá\', ¿me oyes?' WHERE `entry`=14090;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='No me preocupa el Rey Exánime. He visto muchos como él ir y venir, siempre eh\'tropean algo. Poco tardan después en envíahlos a su lugar en los libros de historia. Este no será diferente.' WHERE `entry`=14090;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Vienes para ayudarnos en algo?' WHERE `entry`=14115;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El secreto de la ingeniería goblin no tiene nada que ver con hacer explotar cosas. Tiene que ver con que las cosas exploten exactamente donde uno quiere.' WHERE `entry`=14118;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo soy Xarantaur, el Testigo',`Text1_0_loc6`='¿Quieres oír una historia?' WHERE `entry`=14119;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¿Tienes una historia para mí?',`Text3_0_loc6`='Ah, sí, he leído sobre ti por ahí, $N.' WHERE `entry`=14119;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo fui uno de los primeros tauren druidas, de la última generación de aprendices de Shan\'do Cenarius, antes de la Legión y del Cataclismo, cuando el mundo aún era joven, antes de que todo cambiase.$B$BMientras mis hermanos cazaban bestias, yo cazaba historias. Me sentí atraído por las artes druidas por esta sed, este deseo de conocimiento. Durante un tiempo, saciaba mi sed contando a los árboles, a las rocas y a las bestias sus historias.$B$BPero después, cuando Cenarius no estaba entre nosotros, mis hermanos comenzaron a olvidar lo que nos había enseñado. Mas yo no lo olvidé y aún necesito aprender más sobre nuestro mundo.$B$BAsí que me despedí y viajé a través de las tierras de mi gente.' WHERE `entry`=14120;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Cómo puedo ayudarte?',`Text0_1_loc6`='¿Cómo puedo ayudarte?' WHERE `entry`=14126;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Cómo puedo ayudarte?',`Text1_0_loc6`='¿Cómo puedo ayudarte?' WHERE `entry`=14128;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¿Cómo puedo ayudarte?',`Text2_1_loc6`='¿Cómo puedo ayudarte?' WHERE `entry`=14128;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Tienes algo que tengas que apretar, $R?' WHERE `entry`=14134;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos perdido a algunas buenas personas ya. Empiezo a dudar si este lugar merece nuestra atención.' WHERE `entry`=14137;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La batalla final comenzará pronto, $N. ¡Estate atento!' WHERE `entry`=14142;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sé $Gbienvenido:bienvenida; aquí, $Gamigo:amiga; y $Galiado:aliada; de los Vuelos. Descansa en nuestras salas.' WHERE `entry`=14149;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ah, eres tú, $N! $GBienvenido:Bienvenida; a nuestro santuario. Descansa y recupera fuerzas, te lo has ganado.' WHERE `entry`=14150;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ha sido un día muy ocupado en el aire, ¿eh, $N? Quizás deberías descansar un rato antes de volver.' WHERE `entry`=14155;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cuidado, $N. ¿Dónde están tus togas de cultor?' WHERE `entry`=14159;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aquí estás. Entonces, ¿estás $Glisto:lista;? Una vez hayamos empezado, no habrá vuelta atrás.' WHERE `entry`=14160;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Tienes las agallas de enfrentarte a los dragones élites de Malygos en su propio reino, $R?' WHERE `entry`=14169;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Lo siento, colega. Solo oficiales certificados de la Alianza pueden autorizar la compra de un vehículo.' WHERE `entry`=14172;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Acaso no es solo bonito? ¿Puede verle el beneficio en potencia?',`Text1_0_loc6`='Khit\'rix el Maestro Lóbrego se apresura hacia Valhalas desde el sureste.' WHERE `entry`=14177;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, $Gpequeño:pequeña; $R.' WHERE `entry`=14179;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Solo porque algo no esté roto no quiere decir que no puedas arreglarlo. ¿Buscas algo más, cariño?' WHERE `entry`=14180;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El suministrador de D.E.D.I. te ruge expectante.' WHERE `entry`=14182;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Sueño Esmeralda se ha vuelto un lugar peligroso. Crecí preocupándome cada vez más por aquellos que habían sacrificado su vida en Azeroth con la esperanza de salvarlo.' WHERE `entry`=14198;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hazlo rápido, $C.' WHERE `entry`=14204;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Sí, $Gpequeño:pequeña;?' WHERE `entry`=14209;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Este gran y brillante libro de magia ayuda a hacer glifos para hechizos y habilidades. Siempre tendrás a un insctructor de inscripción cerca, en el Apothecarium.' WHERE `entry`=14213;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Rápido, $R. Estoy cansada y no tengo tiempo para galanterías…' WHERE `entry`=14215;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ahí estás!' WHERE `entry`=14218;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ah, la celebración del Jardín Noble. Es tan divertido ver cómo los demás tiran su dignidad a la basura para encontrar huevos de colores... Y, sin embargo, hay una curiosa aura mágica en torno a ello...' WHERE `entry`=14227;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Caramelos! A eso me refiero. ¡Ojalá no tuviera que andar correteando para encontrarlos!' WHERE `entry`=14229;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Oye, si la gente quiere corretear por ahí embadurnándose por unos huevos de colores, ¿quién soy yo para juzgarlos? Me llevo un pellizco de los beneficios de la pintura, y este año he invertido en pollos.' WHERE `entry`=14231;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Durante el Jardín Noble celebramos la primavera. El fundamento de la vida y todo eso.' WHERE `entry`=14234;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sí, colee\'hga, e\'te é el momento de la felisidá de viví y de la primave\'ra. ¿Ya has conseguío muchos huevos?' WHERE `entry`=14236;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='"Toda esta fiesta carece de sentido. Huevos de colores y "afirmación de la vida"... buá.' WHERE `entry`=14237;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C.$B$BCuando la Alianza controle la Fortaleza de Conquista del Invierno, abriré un portal para aquellos que deseen viajar hasta allí.$B$BLa batalla está teniendo lugar ahora. ¡Ve corriendo a Conquista del Invierno y ayuda a nuestras tropas!' WHERE `entry`=14240;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C.$B$BCuando la Horda controle la Fortaleza de Conquista del Invierno, abriré un portal para aquellos que deseen viajar hasta allí.$B$BLa batalla está teniendo lugar ahora. ¡Ve corriendo a Conquista del Invierno y ayuda a nuestras tropas!' WHERE `entry`=14241;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Con la ayuda de nuestros nuevos aliados taunka, Rasganorte pertenecerá a la Horda.' WHERE `entry`=14245;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Saludos, $C! Te doy la bienvenida a bordo de El Besanubes. Rasganorte puede ser un lugar muy arduo. Espero que hayas hecho las preparaciones necesarias.' WHERE `entry`=14248;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Entonces querrás hablar con Benik Cortapernos. Está en el corazón del Distrito de los Enanos, junto a los herreros.' WHERE `entry`=14249;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los hipogrifos de Vesprystus te conducirán rápidamente a tu destino.',`Text1_0_loc6`='Es a Vesprystus a quien buscas. Guarda a los hipogrifos que llegan y parten de la base de Teldrassil.' WHERE `entry`=14254;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Después de lo de las Tierras de la Peste y de la batalla por la Capilla de la Esperanza de la Luz, jamás habría pensado que veríamos algo más horrible.$B$BMe equivocaba.' WHERE `entry`=14286;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Con las ratas de madriguera se puede hacer un estofado muy sabroso.' WHERE `entry`=14313;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido señor:Bienvenida señora;. Este campamento es mucho mejor que el que solía tener.$B$BMe alegra tener compañía. Han sido amables y me han enseñado algunas de sus técnicas de lucha para que pueda aguantar contra la Horda.' WHERE `entry`=14315;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C. Nuestra posición está algo expuesta, teniendo en cuenta el aumento de fuerzas de la Horda al sur.$B$BToda la ayuda que puedas aportarnos será bienvenida.',`Text0_1_loc6`='Saludos, $C. Nuestra posición está algo expuesta, teniendo en cuenta el aumento de fuerzas de la Horda al sur.$B$BToda la ayuda que puedas aportarnos será bienvenida.' WHERE `entry`=14316;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Pensé que jamás me libraría de esa terrible maldición. Tienes mi gratitud, $Gforastero:forastera;.' WHERE `entry`=14319;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Acéptala con mi gratitud, $C.' WHERE `entry`=14320;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué puedo hacer por ti, $Gseñor:señora;?' WHERE `entry`=14324;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Estáis seguros de que queréis mi ayuda en la batalla contra Yogg-Saron?' WHERE `entry`=14325;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mortales, estoy en deuda con vosotros por liberarme de la terrible corrupción que acosa este lugar. Aunque todavía no me he recuperado del todo, puedo ayudaros a luchar contra Yogg-Saron.$B$BPuedo otorgarte a ti y a tus amigos Velocidad de invención, que aumenta vuestro daño y velocidad de movimiento. Además, puedo desestabilizar las matrices de ciertas formas de vida basadas en la saronita y reducir su velocidad de ataque y de lanzamiento.' WHERE `entry`=14334;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué quieres, $C?' WHERE `entry`=14344;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vaya vaya, nunca pensé que tendría la oportunidad de ver a Ulduar con mis propios ojos. Bueno, cuando Brann me pidió ayuda para organizar esta expedición, ¡¿cómo iba a negarme?!' WHERE `entry`=14362;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Pensé que no íbamos a escapar nunca de esa jungla de vapor! Ahora soy el ingeniero jefe de esta expedición.' WHERE `entry`=14363;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puede que estas preciosidades parezcan chatarra, pero pueden embestir a los enemigos. El espolón central ha sido sobrecargado con un híper condensador electroscópico que le permite propinar descargas eléctricas. $B$BIncluso la torreta ha sido mejorada con cohetes antiaéreos, algo que proporciona, definitivamente, una ventaja sobre esos malditos demoledores.' WHERE `entry`=14364;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No hay nada que escupa fuego mejor que estas preciosidades. Incluso hemos conectado un cañón secundario y un gancho prensil, y hemos añadido un sistema manual de inyección de combustible. Si puedes obtener pirita líquida de los mecagnomos, puedes usarla para arrojar explosivos de alta densidad, o para incrementar tu velocidad de movimiento. $B$B¡A ver si esos mequetrefes de la Liga de Expedicionarios pueden hacer algo mejor!' WHERE `entry`=14368;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Que os hayais presentado son buenas noticias! Los enanos férreos nos han hecho retroceder hasta este lugar, y apenas estamos consiguiendo mantener la posición. $B$BY, lo que es peor, han empezado a reforzar sus defensas mágicas. ¡Si no atacamos pronto serán capaces de aniquilarnos en un abrir y cerrar de ojos!$B$BNos las hemos apañado para reconstruir varias máquinas de asedio, demoledores y motocicletas de expediciones de la Alianza y de la Horda que estuvieron aquí antes que nosotros.$B$B¡Por favor, $N! ¡Eres nuestra última esperanza!' WHERE `entry`=14369;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Puedo hacer algo por ti, jefe?' WHERE `entry`=14372;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos.' WHERE `entry`=14375;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La consola parece antigua, aunque no hay signo alguno de mal uso o de deterioro. Una ranura parece hecha a medida de grandes discos circulares.' WHERE `entry`=14383;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nuestra será la gloria cuando ganemos este torneo en nombre de Lunargenta.' WHERE `entry`=14392;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El rey Magni espera que ganemos este torneo y enorgullecerse de nosotros. No le fallaremos.' WHERE `entry`=14396;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cuando empiece el torneo, todos verán como los Renegados salen victoriosos.' WHERE `entry`=14402;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La competición será despiadada, pero los campeones de Orgrimmar harán pedazos a todo aquel que se interponga en nuestro camino.' WHERE `entry`=14403;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Competimos no solo por la gloria, sino para honrar a nuestros ancestros y a nuestro pueblo.' WHERE `entry`=14404;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los Lanza Negra están listos para enfrentarse a la competición y al enemigo.' WHERE `entry`=14405;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Cuando estés preparad$Go:a; para ponerte a prueba para el rango de Valeros$Go:a;, llamaré a un miembro de la Cruzada Argenta.' WHERE `entry`=14408;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estas salas contienen secretos que van más allá de vuestros sueños más alocados.' WHERE `entry`=14420;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es un auténtico honor poder ayudar a Brann a explorar el antiguo Ulduar.' WHERE `entry`=14428;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Brann me enseñó todo lo que sé sobre explorar.' WHERE `entry`=14429;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Encantado, $C.$B$BEntreno a los Aspirantes en el arte del combate con montura.' WHERE `entry`=14431;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los objetivos cuerpo a cuerpo entrenan a los aspirantes tanto en el ataque como en la defensa sobre montura.$B$BAcércate al objetivo cuerpo a cuerpo y golpéalo con un ataque de Estocada. La fuerza del golpe a menudo hace que el objetivo gire sobre sí mismo con suficiente fuerza para herirte a ti o a tu montura. Ahí es donde entra en juego la defensa.$B$BAsegúrate de tener al menos una capa de Defender antes de atacar al objetivo, y continúa acumulando y renovando Defender durante el ejercicio de entrenamiento.$B$BLo mismo sucede en el verdadero combate sobre montura: Mantén un balance entre defensa y ataque, lanzando Estocada cuando tengas la oportunidad y manteniendo tus capas de Defender lo mejor que puedas.' WHERE `entry`=14434;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Eh, ¿qué tal estás?$B$B¿Vienes a aprender el arte de Cargar, $C?' WHERE `entry`=14436;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El secreto de una buena carga reside en medir bien la distancia y el tiempo. No puedes estar DEMASIADO cerca, o no alcanzarás suficiente velocidad. ¡Pero tampoco puedes estar demasiado lejos o tu montura se cansará! En cuanto al tiempo...$B$BTienes que asegurarte de que el objetivo de carga no tiene una capa de Defender activa antes de avalanzarte. Si es así, emplea tu Romper escudo para hacerlo vulnerable, y ¡ENTONCES utiliza Cargar!$B$BLo mismo pasa con el combate de verdad: ¡intenta reducir las defensas de tu oponente antes de cargar!' WHERE `entry`=14437;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Bien. Otr$Go:a; $C. ¿Vienes a aprender Romper escudo?' WHERE `entry`=14438;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El ataque Romper escudo a distancia es fácil de aprender pero difícil de dominar. Puede que por sí solo parezca débil, pero en conjunción con Estocada y Cargar, Romper escudo hace que ganes una justa.$B$BEncuentra un objetivo a distancia. Tómate tu tiempo para entender el alcance de Romper escudo... cuánto espacio necesitas, y a cuánta distancia puedes arrojar tu lanza.$B$B¿Después de eso? Bueno. Ponte a arrojar y no pares hasta que hayas roto hasta la última capa de Defender y tu objetivo sea vulnerable.$B$BEsta táctica te será útil en el auténtico combate sobre montura: destruye las defensas de tus oponentes... ¡y aplástalos!' WHERE `entry`=14439;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Todo a su tiempo, camarada. Pronto tendré más trabajo para ti...' WHERE `entry`=14441;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Desde luego, camarada. ¡Nuestras reservas de cristales de añublo son casi suficientes!' WHERE `entry`=14442;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ya no nos estorbará más la incompetencia de Darmuk! Paciencia, camarada. Pronto habrá más que puedas hacer...' WHERE `entry`=14443;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ahora ya lo sabes todo, camarada! Prepárate... ¡pronto atacaremos!' WHERE `entry`=14445;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Disculpa, $R, pero solo los Campeones de Cima del Trueno pueden comprar lo que tengo en venta.' WHERE `entry`=14458;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Largo de aquí, $C!$B$BSolo los Campeones de Entrañas pueden comprar lo que tengo en venta.' WHERE `entry`=14459;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Solo los Campeones de Orgrimmar pueden comprar lo que tengo en venta.' WHERE `entry`=14460;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Solo los Campeones de El Exodar pueden comprar lo que tengo en venta.' WHERE `entry`=14461;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Hola!$B$BPerdona, pero solo los Campeones de Gnomeregan pueden comprar lo que tengo.' WHERE `entry`=14462;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Samamba piensa que eh\'táh en el lugá\' equivoca\'o! $B$BLo que tiene eh\' solo p\'a los Campeones de Sen\'jin...' WHERE `entry`=14464;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ay!, solo los estimados Campeones de Ciudad de Lunargenta pueden comprar lo que tengo en venta.' WHERE `entry`=14465;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me alegro de verte, $N.' WHERE `entry`=14473;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hace falta mucha dedicación para domar y montar un Pellejo Venenoso.' WHERE `entry`=14474;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los jinetes de la Cruzada Argenta creen en el equilibrio y en la precisión, y combinan con destreza el ataque y la defensa.$B$BSin embargo, a menudo se expondrán a una carga al intentar tomar distancia contigo.$B$BReduce esa distancia empleando Cargar antes de que ellos hagan lo mismo, y después emplea Romper escudo mientras te das la vuelta para utilizar Estocada de nuevo.' WHERE `entry`=14476;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hoy ya has derrotado a uno de nuestros jinetes, así que no puedo aceptar tu desafío.' WHERE `entry`=14489;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hoy ya has derrotado a uno de nuestros jinetes, así que no puedo aceptar tu desafío.' WHERE `entry`=14492;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Activar los sistemas defensivos secundarios provocará el exterminio de formas de vida no autorizadas mediante mecanismos orbitales.$B$BEres una forma de vida no autorizada. Confirmación, por favor.' WHERE `entry`=14496;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Permitido control manual de seguridad. Sistemas defensivos secundarios activados.$B$BPuede accederse a la desactivación de emergencia de los sistemas secundarios mediante generadores individuales ubicados en la explanada.' WHERE `entry`=14497;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<La prole te mira como si intentara averiguar si eres o no su madre.>' WHERE `entry`=14527;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El cruel reinado del Rey Exánime ha impuesto grandes penurias a todos los habitantes de Rasganorte, en particular a los niños. Si eres amigo de los Oráculos o de la tribu Corazón Frenético, quizás puedas considerar cuidar de un huérfano durante una semana.' WHERE `entry`=14534;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los huérfanos que hemos acogido vienen de la Cuenca de Sholazar destrozada por la guerra. Puedes elegir hacerte cargo de una prole de Oráculo huérfana o de un cachorro Corazón Frenético. Ambos estarán felices de hacer $Gun nuevo amigo:una nueva amiga;, pero solo podrás cuidar de uno de ellos.' WHERE `entry`=14537;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Lo notas?$B$BEl mundo de los espíritus está más cerca de nosotros durante la Festividad de los Muertos. Por eso decoramos las tumbas de aquellos que partieron y que quisimos con flores, velas y ofrendas: Para darles la bienvenida, incluso aunque regresen por un breve tiempo.$B$BAsí que, ¡vamos! ¡Baila conmigo! Mostremos a la fantasmal presencia de aquellos que hemos perdido nuestro cariño y respeto, para que puedan comer y beber y sonreír y saber que son amados.' WHERE `entry`=14543;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Durante la Festividad de los Muertos, la gente se reúne en los cementerios para rendir homenaje y mostrar afecto a los espíritus de aquellos que han partido.$B$BLas celebraciones pueden encontrarse en los cementerios de cualquier ciudad importante, donde podrás cocinar, bailar, lucir tus atuendos y más.' WHERE `entry`=14544;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es bueno volver a estar con los amigos y la familia, pasarlo bien y compartir historias antiguas.' WHERE `entry`=14552;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Seguro que en algún momento de tu vida fuiste un estudiante, $C. Aprendiste de un instructor y, de algún modo, comenzaste a respetarlo, ¿verdad? Quizá no como persona, pero al menos como alguien que sabía más que tú y estaba dispuesto a enseñarte. Si no, usa tu imaginación... $B$B¿Qué harías si te dieras cuenta de que tu instructor podría no saber tanto como pensabas y que tú has encontrado un camino mejor?' WHERE `entry`=14560;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Esto es terrible! No debería estar aquí sentada, inútil, cuando hay tanto que hacer. Si no hubiera sufrido lesiones en el brazo en Rasganorte... ¡Pero no vale la pena lamentarse! Tahu me curará y seguiremos ayudando por aquí mientras.$B$BQue no estemos en el frente no significa que no podamos ayudar de algún modo.' WHERE `entry`=14561;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Otra cara nueva? Me alegro de conocerte. Me llamo Dellylah.',`Text0_1_loc6`='¿Otra cara nueva? Me alegro de conocerte. Me llamo Dellylah.' WHERE `entry`=14562;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Zippik nos llevaría directos contra la cima si yo no estuviera aquí. Estúpido.' WHERE `entry`=14614;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Oye. Estoy en mi descanso.' WHERE `entry`=14617;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, cariño. El zepelín que para aquí te llevará directamente a Cima del Trueno, sin coste alguno. ¿Quién necesita dracoleones, cuando nos tenéis a los goblins? ¿Verdad?' WHERE `entry`=14621;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué miras, $R? ¿Has venio a atormental a los condenados?' WHERE `entry`=14623;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El barón muestra sus grilletes.>$B$BEntonses, adelante, $C. ¿Confías en mí?' WHERE `entry`=14624;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hola, $N. Si buscas instrucción en cocina, has venido al lugar adecuado. Normalmente recibo un montón de nuevos estudiantes cuando se acerca la Generosidad del Peregrino.' WHERE `entry`=14627;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si te interesa saber más acerca del tradicional festín de la Generosidad del Peregrino, habla con cualquiera de las anfitrionas.' WHERE `entry`=14628;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Has venido a celebrar la Generosidad del Peregrino con nosotros?' WHERE `entry`=14630;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$GBienvenido:Bienvenida;! ¿Has venido para unirte a un festín de la Generosidad del Peregrino? Siéntate en una mesa generosa.$B$B¡Pruébalo todo! Estoy segura de que encontrarás algo que te guste.' WHERE `entry`=14633;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$GBienvenido:Bienvenida;! ¿Has venido para unirte a un festín de la Generosidad del Peregrino? Siéntate en una mesa generosa.$B$B¡Pruébalo todo! Estoy segura de que encontrarás algo que te guste.' WHERE `entry`=14634;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='"Generosidad del Peregrino". Hmm. Puedo comprender la impaciencia de los tauren por expresar su gratitud y su camaradería al Jefe de Guerra de la Horda. ¿Pero los renegados?$B$BPensaba que se habrían dejado de tradiciones sentimentales hace mucho.' WHERE `entry`=14635;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Generosidad del Peregrino es una época para reflexionar acerca de la suerte que tenemos y compartir cosas con los que nos rodean.$B$BEsperamos que nuestros nobles aliados se unan a nosotros en los festines y la celebración.' WHERE `entry`=14640;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Esta fiesta contrasta con el comportamiento típico de los humanos. ¿Quién hubiera pensado que una gente tan implacablemente voraz crearía una celebración basada en dar y compartir?$B$BEs agradable verlo. Darnassus debería hacer todo lo que sea posible para apoyarla y alentarla.' WHERE `entry`=14641;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En cada mesa generosa se sirven cinco comidas de la Generosidad del Peregrino, una delante de cada silla.$B$BMientras estés en una de esta sillas, podrás comer tanto como quieras, además de pasar raciones de esa comida a las demás sillas. ¡Come y comparte con los amigos!$B$BSi comes suficiente de una comida, obtendrás beneficios únicos de esa comida.$B$B¡Además, si comes suficiente de cada comida, obtendrás El espíritu de compartir!' WHERE `entry`=14647;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En cada mesa generosa se sirven cinco comidas de la Generosidad del Peregrino, una delante de cada silla.$B$BMientras estés en una de esta sillas, podrás comer tanto como quieras, además de pasar raciones de esa comida a las demás sillas. ¡Come y comparte con los amigos!$B$BSi comes suficiente de una comida, obtendrás beneficios únicos de esa comida.$B$B¡Además, si comes suficiente de cada comida, obtendrás El espíritu de compartir!' WHERE `entry`=14648;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay algunas recompensas que solo son accesibles a quienes han conseguido el título de Cruzado, exaltados tanto a los ojos de la Cruzada Argenta como a los de sus aliados.$B$BEl propio Tirion me ha encargado que encuentre a esos héroes y les otorgue las condecoraciones pertinentes.' WHERE `entry`=14661;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Esas bestias son unos especímenes increíbles. No deberíais avergonzaros porque os hayan derrotado.' WHERE `entry`=14667;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Excelente! Avísame cuando quieras volverlo a intentar.' WHERE `entry`=14668;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Isla de la Conquista está repleta de valiosos recursos que no podemos permitir que caigan en manos de la Horda. ¿Te unirás a la lucha?' WHERE `entry`=14670;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Isla de la Conquista está repleta de valiosos recursos que no podemos permitir que caigan en manos de la Alianza. ¿Te unirás a la lucha?' WHERE `entry`=14671;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Estás $Gpreparado:preparada; para luchar por la Horda, $C?' WHERE `entry`=14674;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Estás $Gpreparado:preparada; para luchar por la Alianza, $C?' WHERE `entry`=14675;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Bien hecho! ¡Has luchado contra esas bestias de forma espléndida! ¡He oído que el siguiente reto será más duro!' WHERE `entry`=14678;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='De momento estamos reteniendo a Lord Jaraxxus, pero no podremos mantenerlo amarrado mucho tiempo más.' WHERE `entry`=14679;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Pronto lo descubrirás. ¿Estás $Gpreparado:preparada; para el siguiente reto?' WHERE `entry`=14680;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Eso es genial, tan solo hazme una señal para que detenga los hechizos que lo retienen.' WHERE `entry`=14682;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Durante años he servido en este rincón remoto y olvidado del mundo, memorizando el terreno y esperando mi momento. Y por fin ha llegado. Todo el poder de Orgrimmar está concentrado ahora en Azshara, nuestra puerta de entrada a Vallefresno y al dominio de todo el continente. Te doy la bienvenida al frente, $C.' WHERE `entry`=14690;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida; a nuestra tienda. Tenemos todo lo que necesitas... si necesitas suministros de alquimia.' WHERE `entry`=14712;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No, yo soy la intelectual y no hay que tomarme a la ligera. Podría hacer una poción sanadora con agua de fregar y dientes de león, pero no es algo que vaya a mostrarte.$B$BDe todas formas, si quieres comprar algo, habla con Patricia.' WHERE `entry`=14713;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hoy has impresionado a todo el mundo con tus habilidades en la arena. Ya es hora de que todos te conozcan como $Gel campeón:la campeona; del torneo.' WHERE `entry`=14738;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El primer reto requiere que estés montado sobre un caballo de guerra Argenta. Encontrarás estas monturas a lo largo de los muros del Coliseo.$B$BMonta el caballo de guerra, habla conmigo y entonces podremos continuar.' WHERE `entry`=14757;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los magnatauros no son tan estúpidos como parecen. Retener a Gormok hasta que llegue su momento no es tarea fácil, pero está tan bien atado que no hay riesgo.' WHERE `entry`=14759;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo me encargo de estas criaturas de Gormok ya que él no puede. No estoy totalmente de acuerdo con esta matanza, pero al menos tendrá la oportunidad de luchar.' WHERE `entry`=14760;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El yeti es una criatura feroz y astuta, y este es uno de los más fuertes de su especie. Lo mantenemos dormido echándole hierbas en la comida.$B$BPero no creas que estará en letargo cuando salte a la arena.' WHERE `entry`=14761;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me uniré al asalto contra la Ciudadela de la Corona de Hielo cuando llegue el momento de atacar.' WHERE `entry`=14771;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C.$B$BActualmente la Horda controla la Fortaleza de Conquista del Invierno, pero la batalla ha comenzado. ¡Ve corriendo a Conquista del Invierno y ayuda a nuestras tropas!' WHERE `entry`=14777;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos estado encargándonos de estos gusanos desde hace algún tiempo. Espero que estén en forma a tiempo para el evento de la arena de esta organización. Después de todo debes enfrentarte a dos jormungar, no uno.' WHERE `entry`=14779;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C.$B$BActualmente la Alianza controla la Fortaleza de Conquista del Invierno, pero la batalla ha comenzado. ¡Ve corriendo a Conquista del Invierno y ayuda a nuestras tropas!' WHERE `entry`=14781;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C.$B$BActualmente la Alianza controla la Fortaleza de Conquista del Invierno. Cuando la batalla esté a punto de comenzar, habla conmigo para unirte.$B$BLa siguiente batalla comenzará en $4354k.' WHERE `entry`=14782;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Tú! ¡Sí, tú!$B$BMírame y aprecia este momento de encuentro, pues pronto el mundo conocerá y temerá el nombre y el severo rostro de Wilfred Chispobang. ¡En cuanto mi magnífica invocación se complete, nadie dudará de mi habilidad!' WHERE `entry`=14787;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C.$B$BActualmente la Horda controla la Fortaleza de Conquista del Invierno, pero la próxima batalla comenzará pronto. ¡Prepárate para ir a Conquista del Invierno y apoyar a nuestras tropas!$B$BLa siguiente batalla comenzará en $4354k.' WHERE `entry`=14790;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $C.$B$BActualmente la Alianza controla la Fortaleza de Conquista del Invierno. ¡La batalla por la Fortaleza de Conquista del Invierno está a punto de comenzar! ¿Quieres unirte a la batalla?$B$BLa siguiente batalla comenzará en $4354k.' WHERE `entry`=14791;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La justicar Mariel Corazón Veraz nos ha pedido que nos ocupemos del torneo. A los que se han ganado el derecho de alzarse como campeones y han demostrado su valía ante nuestros ojos se les pide que ayuden con estas tareas.' WHERE `entry`=14793;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La justicar Mariel Corazón Veraz nos ha pedido que ayudemos a administrar y defender el torneo ahora que el coliseo está completo. Quiere que trabajemos con esos despreciables chuchos de El Pacto de Plata, pero no hacen más que entorpecer todo.' WHERE `entry`=14794;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Puede que operemos en las sombras de la sociedad educada, pero allá afuera, en el frente de batalla, nuestros poderes al fin se respetan debidamente.' WHERE `entry`=14804;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Está claro que ese brujo no invocó a un señor demoníaco. Vuestro siguiente reto os llevará a luchar contra caballeros de la Cruzada Argenta. ¿Creéis que estáis a la altura?' WHERE `entry`=14813;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Entonces daré la señal a Tirion cuando estéis listos.' WHERE `entry`=14814;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Esa lucha ha sido salvaje y brutal. ¡No he visto una gran melé como esa en muchos años! ¡Increíble!' WHERE `entry`=14815;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡No puedo esperar para verlo! Parecen listos para otra ronda, así que preparaos.' WHERE `entry`=14816;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El trabajo en equipo de las Val\'kyr es impresionante. Ahora entiendo por qué tuvimos tantos problemas para capturarlas.' WHERE `entry`=14820;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Será mejor que lo creas! ¡Vas a enfrentarte a dos de las tenientes Val\'kyr más poderosas de Arthas!' WHERE `entry`=14821;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Bueno, espero que mejoréis rápido.' WHERE `entry`=14822;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El senescal del torneo nos ha pedido que les ayudemos con algunas de las tareas de la competición ahora que el coliseo está acabado. Se pide a los que se han alzado como campeones y han demostrado su valía ante nuestros ojos que ayuden con estas labores.' WHERE `entry`=14824;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Espléndido, hoy habéis demostrado ser grandes campeones. ¡Con vosotros luchando junto a la Cruzada Argenta, la victoria contra la Plaga es inevitable!' WHERE `entry`=14828;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es una alabanza bien merecida. Tirion querrá elogiaros él mismo dentro de un rato. Oye, ¿os habéis dado cuenta de que de pronto hace un poco de frío?' WHERE `entry`=14829;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡$GBienvenido, Campeón:Bienvenida, Campeona;! ¿Has reunido tus fuerzas como preparativo para la Prueba del Gran Cruzado? Solo los combatientes más poderosos y consumados pueden participar en estas pruebas. Para participar en estas pruebas debes haber completado la Prueba del Cruzado normal.' WHERE `entry`=14850;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Prueba del Gran Cruzado es una versión más difícil de la Prueba del Cruzado. Cada encuentro será más difícil que el anterior y no podrás intentar superar los encuentros un número ilimitado de veces hasta que ganes. Después de 50 intentos fallidos durante la prueba, no podrás volver a la Prueba del Cruzado esa semana. Tras completar la Prueba del Gran Cruzado con éxito, recibirás un cofre de tributo adicional con recompensas como compensación. ¿Estás a la altura de la prueba?' WHERE `entry`=14853;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Me alegro de volver a verte, $N.' WHERE `entry`=14882;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Te sorprende ver a un orco aquí? No es necesario.$B$BLos renegados necesitan vigilancia. Si hubiéramos prestado más atención desde el principio, quizá los acontecimientos de la Puerta de Cólera no hubieran sucedido.',`Text1_0_loc6`='Espero que no estés buscando a esas abominaciones. No se puede confiar en los boticarios y eso incluye a sus pequeños ensamblajes que patrullan los pasillos. Estamos aquí para mantener esto a salvo.' WHERE `entry`=14931;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Lok\'tar. No intentes nada sospechoso aquí. Si estos no-muertos van a ser parte de la Horda, tendrán que comportarse bajo nuestra vigilancia.',`Text2_1_loc6`='Lok\'tar. No intentes nada sospechoso aquí. Si estos no-muertos van a ser parte de la Horda, tendrán que comportarse bajo nuestra vigilancia.' WHERE `entry`=14931;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Date prisa, $R.' WHERE `entry`=14932;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué ocurre, saco de huesos? ¿Buscabas a otra persona? Yo soy lo único que tienes, así que si quieres algo, que sea deprisa.',`Text1_0_loc6`='Apuesto a que vosotros, podridos, pensasteis que fuisteis muy listos en la Puerta de Cólera, ¿verdad? Se acabaron los juegos. Estamos aquí para asegurarnos de que no intentáis nada.' WHERE `entry`=14933;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Te diré lo que necesites saber, pero no tiene por qué gustarme. Os estamos vigilando a ti y a los tuyos.',`Text2_1_loc6`='Te diré lo que necesites saber, pero no tiene por qué gustarme. Os estamos vigilando a ti y a los tuyos.' WHERE `entry`=14933;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Recuerdo cuando mi hermano y yo acampábamos en los bosques de Azshara. Mira cómo ha quedado.' WHERE `entry`=15021;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='$GBienvenido:Bienvenida;, $Ghermano mohicano:hermana mohicana;.' WHERE `entry`=15038;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué haces aquí, $C?' WHERE `entry`=15074;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ahí estás! Empezaba a pensar que te habían interceptado los Atracasol. ¿Estás $Gpreparado:preparada; para entregar el escrito a nuestros representantes en Corona de Hielo?' WHERE `entry`=15077;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No puedo retenerlo mucho tiempo, ¡debemos marcharnos!' WHERE `entry`=15081;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tu jormungar puede moverse si te has sumergido en la nieve, pero así no puede atacar. Cuando emerge, puede utilizar su poderoso ataque ácido para atravesar la armadura del coloso férreo. Prepárate para sumergirte para escapar de los demoledores ataques del coloso.' WHERE `entry`=15119;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Pero si eres $Gel:la:c; $C que estaba buscando! Estoy probando un nuevo dispositivo volador propulsado por cohetes y necesito tu ayuda.$B$BCoge uno de estos arneses y colócalo bajo tu armadura. Pulsa el botón del lado, y BUM, ¡allá vas!$B$BNo intentes robarme el trabajo, ¿eh? Estas bellezas tienen integrado un transponstador 8000 vinculado al de mi mano. Si te alejas demasiado, se desactivará el cohete principal y no podrás despegar hasta que vuelvas.$B$BVeamos, ¿dónde he metido los contratos de responsabilidad?' WHERE `entry`=15134;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ay, madre. Las cosas no pintan muy bien para Felix.' WHERE `entry`=15139;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Encantado, $C.' WHERE `entry`=15145;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo esperaré aquí hasta que lleguen refuerzos, pero vosotros debéis seguir.$B$BNo tenemos mucho tiempo antes de que Arthas se dé cuenta de que estamos aquí.' WHERE `entry`=15152;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Parece que estos son los restos de Thalorien Buscalba, el último portador de Quel\'Delar.' WHERE `entry`=15155;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No puedo retenerlo mucho tiempo, ¡debemos marcharnos!' WHERE `entry`=15157;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Veredicto Cinéreo es un grupo selecto de los artesanos con más talento de la Espada de Ébano y la Cruzada Argenta. Su creación simboliza la unión de nuestras fuerzas a medida que ascendemos hacia el asalto final contra el Rey Exánime y sus esbirros.$B$BEs gracias al esfuerzo del Veredicto Cinéreo que hemos descubierto como amoldar la saronita primordial a nuestra voluntad.' WHERE `entry`=15159;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Habla con el Alquimista Finklestein, $C. Puede guiarte por el buen camino.$B$BDemuestra tu lealtad y te revelará sus métodos.' WHERE `entry`=15160;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El Rey Exánime está cerca, $N. Su voz me provoca...$B$B¡Acallémoslo de una vez!' WHERE `entry`=15166;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo reuniré a los demás esclavos y los prepararé para luchar. Te alcanzaremos antes de que comiences a luchar contra el Señor de la Plaga Tyrannus.' WHERE `entry`=15172;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Perdonadme, héroes. Debí escuchar a Uther. Tenía… tenía que… verlo por mí misma. Poder mirarle a los ojos una última vez. Lo siento.' WHERE `entry`=15189;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Estamos a salvo… por ahora. Su fuerza se ha multiplicado desde nuestra última batalla. Necesitamos un ejército poderoso para destruir al Rey Exánime, mejor incluso que el que la Horda puede convocar.' WHERE `entry`=15190;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo esperaré aquí hasta que lleguen refuerzos, pero vosotros debéis seguir. Pronto Arthas se dará cuenta de que estamos aquí.' WHERE `entry`=15191;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Estáis seguros? ¿No necesitáis unos minutos más?' WHERE `entry`=15195;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No tenemos tiempo que perder, $C. ¿Qué haces aún aquí?' WHERE `entry`=15206;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No tenemos mucho tiempo, $Ghéroe:heroína;. ¿Qué necesitas?' WHERE `entry`=15207;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Esperas que recuerde eso así como si nada? Mira. No me pagaron lo suficiente como para aguantar esto y el contrato solo pedía mi silencio. Así que se me va a caer mi libro de contabilidad aquí en el suelo y tú lo vas a coger y a dejarme en paz.$B$B¿Entendido?' WHERE `entry`=15211;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Lo que necesites, Brazie Lopillas! ¿Lo pillas?$B$BY no solo hablo de las cosas "legales". ¡LO QUE NECESITES, $GAMIGO:AMIGA;! Lo que sea...' WHERE `entry`=15214;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Al estar tan cerca de la espada que puso fin a mi vida… el dolor… vuelve.' WHERE `entry`=15215;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Valiente soldado de la Horda, ¡te saludo! Sin duda, la nave de guerra de la Alianza nos habría destruido de no haber sido por ti y tus aliados.$B$BDescansa un momento antes de que comencemos el asalto a la parte superior de la ciudadela.' WHERE `entry`=15217;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Seguro, $N? Debemos enfrentarnos a la Plaga con todas nuestras fuerzas si queremos tener éxito. Si no necesitas descansar más, comenzaremos.' WHERE `entry`=15218;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='He oído rumores de que mi hijo está aquí. Que ahora es un poderoso caballero de la Muerte en servicio del Rey Exánime. Si es cierto, la línea de descendencia acaba en mí.$B$BMi tarea ahora es dirigir el Martillo de Orgrim a la Ciudadela de la Corona de Hielo y destruir toda la Plaga en el camino. Partimos cuando tus aliados y tú estéis listos.' WHERE `entry`=15219;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te escoltaré a La Fuente del Sol cuando estés $Glisto:lista;.' WHERE `entry`=15240;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Arrodíllate junto a mí, colega. Me dio una bala durante el último encuentro.$B$B<Gann gime de dolor mientras trata de cambiar de posición. Su mano izquierda aprieta una herida y la sangre se amontona entre sus temblorosos dedos.>$B$BLa primera vez que los enanos invadieron Los Baldíos, me contenté con hostigar a sus excavadores y sabotear su equipo. Debería haber asestado un golpe mortal cuando tuve la oportunidad.' WHERE `entry`=15246;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Tú no tenías paquetes que mover?' WHERE `entry`=15282;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Algo huele mal, y por una vez, no son mis calcetines.' WHERE `entry`=15284;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Es una espada increíble, ¿no crees? No es una espada forjada por los enanos, no, es mucho más antigua.$B$BEsos representantes de los Atracasol y El Pacto de Plata parecen decididos a descubrir el misterio. Ya estoy preparando un informe para Brann.' WHERE `entry`=15287;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hace mucho que espero este día. ¿Estáis preparados tú y tus aliados para ajusticiar al Rey Exánime? ¡Atacaremos a vuestra señal!' WHERE `entry`=15290;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Me has pillado en un momento crítico de mi investigación! No te preocupes, puedes unirte a mí en contemplación silenciosa... pero hago hincapié en "silenciosa", si no te importa.',`Text1_0_loc6`='¡Me has pillado en un momento crítico de mi investigación! No te preocupes, puedes unirte a mí en contemplación silenciosa... pero hago hincapié en "silenciosa", si no te importa.' WHERE `entry`=15296;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¡Me has pillado en un momento crítico de mi investigación! No te preocupes, puedes unirte a mí en contemplación silenciosa... pero hago hincapié en "silenciosa", si no te importa.',`Text2_1_loc6`='¡Me has pillado en un momento crítico de mi investigación! No te preocupes, puedes unirte a mí en contemplación silenciosa... pero hago hincapié en "silenciosa", si no te importa.',`Text3_0_loc6`='¡Me has pillado en un momento crítico de mi investigación! No te preocupes, puedes unirte a mí en contemplación silenciosa... pero hago hincapié en "silenciosa", si no te importa.' WHERE `entry`=15296;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='¡Me has pillado en un momento crítico de mi investigación! No te preocupes, puedes unirte a mí en contemplación silenciosa... pero hago hincapié en "silenciosa", si no te importa.',`Text5_0_loc6`='¡Me has pillado en un momento crítico de mi investigación! No te preocupes, puedes unirte a mí en contemplación silenciosa... pero hago hincapié en "silenciosa", si no te importa.' WHERE `entry`=15296;
UPDATE `locales_npc_text` SET `Text6_0_loc6`='¡Me has pillado en un momento crítico de mi investigación! No te preocupes, puedes unirte a mí en contemplación silenciosa... pero hago hincapié en "silenciosa", si no te importa.',`Text7_0_loc6`='¡Me has pillado en un momento crítico de mi investigación! No te preocupes, puedes unirte a mí en contemplación silenciosa... pero hago hincapié en "silenciosa", si no te importa.' WHERE `entry`=15296;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Una herida que me infligí, $Gamigo:amiga;. No podía soportar ver un mundo en el que yo había cometido tantos horrores bajo la presión del Rey Exánime.$B$BFue mi primer paso hacia la redención, y si mis pecados son imperdonables, entonces intentaré compensarlos durante el resto de mi vida.' WHERE `entry`=15297;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Un viento nauseabundo sopla desde el Este, $C.' WHERE `entry`=15302;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La Agonía de Escarcha: la espada que destrozó nuestro reino.' WHERE `entry`=15339;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tras los incontables fracasos de nuestros agentes para conseguir muestras de añublo de la Plaga aptas para el análisis, he venido a las líneas de frente para supervisar sus esfuerzos. Como dicen, si quieres algo bien hecho...' WHERE `entry`=15340;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Esto es increíble! ¡Estás $Gcubierto:cubierta; de un añublo potente, virulento y fantástico! Permíteme recolectar estas muestras inmaculadas. ¡También eliminaré cualquier infección restante de tus aliados y te recompensaré por tu valor y sacrificio en el nombre de la medicina! Avísame si estás $Gpreparado:preparada; para continuar.$B$B[Antes de continuar, asegúrate de que TODOS los miembros de la banda están cerca para poder recibir el crédito de tu logro.]' WHERE `entry`=15343;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mortales, me habéis salvado de un destino peor que la muerte. De haber continuado, los hechiceros del Rey Exánime habrían quebrado mi voluntad y me habrían condenado a una vida de no-muerte. Ante vosotros yace una que no tuvo tanta suerte: la otrora orgullosa consorte de Malygos, Sindragosa.$B$BHerida de muerte tras la traición de Neltharion y abandonada para morir sola en la nieve de Corona de Hielo, ahora es un ser de odio puro. Y aun así, bajo todas esas capas de maldad todavía se encuentra el noble espíritu de una reina de los dragones. Creo que todavía hay esperanza de que el alma de Sindragosa pueda descansar en paz.' WHERE `entry`=15379;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Eres $Gastuto:astuta; al preguntar, joven $C. Los escribas son capaces de inscribir cartas de tarot de gran poder, aunque no tienen total control sobre lo que hacen. El destino decide por ellos.$B$BSi consigues una baraja completa, tráemela y obtendrás una magnífica recompensa.' WHERE `entry`=15400;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hemos superado la barrera final que bloqueaba la entrada a Las Cámaras de Alaescarcha. $Gchico:chica;. ¡Solo Sindragosa se interpone entre el Rey Exánime y el castigo divino! ¿A qué estás esperando?' WHERE `entry`=15412;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Todas las barreras han caído, $N! Solo Sindragosa se interpone entre el Rey Exánime y nosotros. Se acerca la justicia...' WHERE `entry`=15415;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En cualquier sitio menos aquí...' WHERE `entry`=15432;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura tejeazote de Freya para druidas.' WHERE `entry`=15445;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura cazador de sangre de Ahn\'kahar para cazadores.' WHERE `entry`=15449;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Saludos, $Ghéroe:heroína;. Yo fabrico y vendo la armadura de señor Ymirjar para guerreros.' WHERE `entry`=15450;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El Guardia de la Muerte Simmer te mira expectante.>' WHERE `entry`=15512;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Simmer dice que Gordo tiene agallas pero le falta cerebro. ¿Qué quiere decir?',`Text0_1_loc6`='A Gordo le gusta recoger flores. Recoger flores es un poco como matar.',`Text1_0_loc6`='A Gordo le gusta recoger flores. Recoger flores es un poco como matar.' WHERE `entry`=15531;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='Aquí hay corriente.',`Text3_0_loc6`='Johaan dice que Gordo necesita un sastre para arreglar la barriga de Gordo.' WHERE `entry`=15531;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En nombre del ilustre y todavía neutral Cártel Bonvapor, te deseo que disfrutes de tu estancia.$B$B¡Recuerda, todo el mundo es bienvenido en Gadgetzan! Todo el mundo menos los piratas.' WHERE `entry`=15532;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Necesitas algo?' WHERE `entry`=15572;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El continuo sacrificio del Veredicto Cinéreo y de los héroes como tú nos acerca a todos a la victoria final. He venido a la Ciudadela de la Corona de Hielo para prestar mi ayuda a aquellos que luchan contra la Plaga. ¡Juntos acabaremos con la tiranía del Rey Exánime!' WHERE `entry`=15602;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Dices que podéis hacerlo solos? ¿Esto que veo ante mí es pura necedad o confianza infundada?$B$BNo, lo veo en vuestros ojos. Tenéis que saber. Tenéis que demostrar que podéis hacerlo solos. Bien. Me retiraré del campo de batalla si así lo deseáis.' WHERE `entry`=15603;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Os hará falta algo más que coraje para derrotar al Rey Exánime. Buena suerte.' WHERE `entry`=15604;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Habéis tomado una decisión. Ahora os corresponde vivir o morir por ella.' WHERE `entry`=15605;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ahora que la Ofensiva Grito de Guerra está en la Ciudadela de la Corona de Hielo, la victoria está a nuestro alcance! ¡La muerte del Rey Exánime dará gloria a la Horda!' WHERE `entry`=15606;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Una brava decisión! ¡Respeto vuestras ansias de gloria y honor! ¡Morid bien, $N!' WHERE `entry`=15607;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Procura no hacer caso de las firmes y emplumadas partes traseras de las arpías... ¡Esas criaturas son realmente mortales!' WHERE `entry`=15664;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Claro! ¡Sí que te había visto aquí antes! ¿Cómo he podido olvidarlo?$B$BPuedo teletransportarte a Stratholme ahora si quieres.' WHERE `entry`=15704;
UPDATE `locales_npc_text` SET `Text0_1_loc6`='Las tropas del ejército de los centinelas mantienen a raya a la Horda por todo Kalimdor. Mientras la guerra continúa en Vallefresno y Sierra Espolón, también aquí necesitan a nuestras tropas para proteger las tierras salvajes que quedan y evitar que nuestros enemigos avancen aún más.' WHERE `entry`=15719;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo estoy aquí por si alguien se desmaya. Los nuevos reclutas se emocionan y a veces se esfuerzan demasiado.$B$B...Bueno, eso y que a veces explota uno de los nuevos prototipos de mecanotanque. ¡Vaya!' WHERE `entry`=15779;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Yo he sío una de las primeras en reh\'ponder al llamamiento de Vol\'jin. ¿Vas a unih\'te a nosotros?' WHERE `entry`=15796;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si necesitas un murciélago, has venío al lugar indicao. Yo cuido de tós los murciélagos de los exploradores.' WHERE `entry`=15806;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Que los eh\'píritus te guíen, $C.' WHERE `entry`=15846;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Hay que detener al Señor de la Escarcha Ahune.$B$BEn estos mismos instantes está tomando forma al ser invocado de su prisión elemental por los cultores locos del Martillo Crepuscular.$B$BSi consigue entrar en nuestro mundo por completo, nos enterrará bajo el manto de un invierno perpetuo.' WHERE `entry`=15855;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡¿Qué?! ¡¿Qué has dicho?! ¡Cómo te atreves! ¡Mi cerveza puede soportar la comparación con cualquier porquería que sirvan en la Fiesta de la Cerveza!$B$B¡Pagarás por esta ofensa, $C!' WHERE `entry`=15859;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Te gustará Ciudad Manitas!' WHERE `entry`=15863;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='En este fragmento de hielo retumba el eco del estruendo profundo y sonoro de los glaciares que se desplazan. El frío transforma tus pensamientos en un invierno eterno y sin vida.' WHERE `entry`=15864;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='El tiempo de Zalazane se agota. ¡Las Islas del Eco volverán a manos de la tribu Lanza Negra!' WHERE `entry`=15865;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Los Lanza Negra vuelven a tener un hogar! Y no lo habríamos conseguido sin tu ayuda, $N. Ahora, ¡a celebrarlo!' WHERE `entry`=15866;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mi trabajo es asegurarme de que este asalto funciona como la maquinaria de un reloj.$B$BEl Manitas Mayor Mekkatorque está en el campo de batalla, pero tengo previsto que regrese en breve a por tropas de refresco.$B$BNunca suele retrasarse, así que quédate por aquí y podrás verle.' WHERE `entry`=15868;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Deprisa! ¡Deberías hablar con Mekkatorque! Si no lanzamos el asalto ya llegaremos tarde.$B$B¡Y a mi jefe no le gusta nada que lleguemos tarde!' WHERE `entry`=15870;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Vol\'jin me ha dicho que siga tocando el tambor hah\'ta que él regrese. Si le eh\'tás eh\'perando, volverá pronto pa recuperar las Islas.$B$B¡Eh\'pérale aquí y disfruta de la música!' WHERE `entry`=15873;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Eh\'tamos preparándonos pa recuperar las Islas del Eco. Zalazane lleva tiempo trabajando en su vudú, convenciendo a la gente de que eh\'tá mueh\'to y celebrando antiguos rituales oscuros.$B$BSabe que vamos a atacar, así que tenemos que eh\'tar seguros de ir preparados.' WHERE `entry`=15876;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Necesitamos la ayuda de nueh\'tros aliaos pa recuperar las Islas.$B$BZent\'abra lleva tiempo cuidando de los animales de esas Islas. Necesitamos su ayuda y la de esas criaturas.$B$BBwonsamdi es un Loa muy poderoso que controla los eh\'píritus de los mueh\'tos de las Islas. Su bendición es fundamental pa el éxito de nueh\'tro ataque.$B$BUna vez nueh\'tros aliaos se unan a nosotros, ¡Zalazane y su ejército de trols controlados mentalmente no tendrán ni una opoh\'tunidad! ¡Entonces las Islas del Eco volverán a ser de los Lanza Negra!' WHERE `entry`=15877;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Tú ké kiere?',`Text0_1_loc6`='¿Tú ké kiere?' WHERE `entry`=16157;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ay, ya es difícil mantener el orden por aquí sin que aparezcan de repente todos esos nuevos problemas! ¡Espero que traigas buenas noticias, $N...',`Text0_1_loc6`='¡Ay, ya es difícil mantener el orden por aquí sin que aparezcan de repente todos esos nuevos problemas! ¡Espero que traigas buenas noticias, $N...' WHERE `entry`=16211;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Incluso en este remoto rincón del mundo, debes saber que los ojos de Grito Infernal te vigilan.' WHERE `entry`=16335;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Ya estoy cansado de estar aquí sentado. Espero que el desgraciado chupatintas que esté impidiendo el regreso de Danath reciba un puñetazo en los huevillos.' WHERE `entry`=16390;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Arriba! ¡Abajo! ¡Al centro! ¡Y para dentro!$B$BY si tú no quieres... ¡TE FASTIDIAS! ¡Arriba! ¡Abajo!' WHERE `entry`=16418;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Guauuu! ¡Ahora sí que se están dando bien!' WHERE `entry`=16432;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Quieres comprar algunos objetos piratiles?' WHERE `entry`=16440;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida a Bahía del Botín, $R.' WHERE `entry`=16441;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Que la luz del Sol te proteja, $C.',`Text0_1_loc6`='Que la luz del Sol te proteja, $C.' WHERE `entry`=16491;
UPDATE `locales_npc_text` SET `Text0_1_loc6`='$GBienvenido:Bienvenida; a Dolanaar, $C. Avísame si necesitas recetas o ingredientes básicos de cocina.' WHERE `entry`=16510;
UPDATE `locales_npc_text` SET `Text1_1_loc6`='Cuando venía de Dun Algaz vi a un gnoll cazando trillanodontes en mitad de un lago. Si nunca has visto a un perro intentar usar una lanza, ¡te espera una sorpresa!' WHERE `entry`=16528;
UPDATE `locales_npc_text` SET `Text2_1_loc6`='Fui a la Excavación de Whelgar hace unos días para... eh... liberar... algunas reliquias de los titanes de los huesos de los raptores muertos, ¡y lo vi! ¡Sarldente está vivo!$B$BBueno, es un no-muerto, así que supongo que no está vivo, pero existe. ¡Sarldente existe!',`Text3_0_loc6`='Dicen que la matriarca raptor vive en lo más profundo de la Colina del Raptor, protegiendo sus huevos.' WHERE `entry`=16528;
UPDATE `locales_npc_text` SET `Text4_0_loc6`='Los Faucedraco intentan salir del Campamento Dentellada y ya están asaltando una caravana al este. Han llamado a dos poderosos guerreros que lideran la carga. Si te apetece un desafío, puede que los encuentres en el campamento planeando, maquinando y haciendo más fechorías propias de los orcos.' WHERE `entry`=16528;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Mantén los ojos abiertos, $C. Ni siquiera en Durotar estamos a salvo de nuestros enemigos.' WHERE `entry`=16535;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='He debido llevar una vida estupenda, $N. Volver al mundo como Renegado y como miembro de la Sociedad de Boticarios... No podría haber deseado un destino más apropiado.' WHERE `entry`=16583;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Debemos recuperar lo que nos pertenece. Nuestra supervivencia depende de ello.',`Text0_1_loc6`='Debemos recuperar lo que nos pertenece. Nuestra supervivencia depende de ello.' WHERE `entry`=16661;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='<El Mayor asiente con sequedad.>' WHERE `entry`=16719;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No encontrarás un arma de fuego mejor en todo Orgrimmar.',`Text0_1_loc6`='No encontrarás un arma de fuego mejor en todo Orgrimmar.' WHERE `entry`=16901;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Me buscabas?' WHERE `entry`=17038;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Deberías probar esta cerveza.' WHERE `entry`=17140;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué te trae a la compañía de unos viejos e inútiles perros del desierto como nosotros?' WHERE `entry`=17151;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Bendita sea la Luz, alguien ha respondido por fin a la llamada.' WHERE `entry`=17200;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Sé que lo harás, $N.$B$BYo misma soy una sacerdotisa aunque, al contrario de ti, hace mucho que me he ido de este mundo. Sin embargo, mi espíritu permanece aquí, al igual que los espíritus de los miles que fueron brutalmente asesinados por el príncipe Arthas Menethil y su legión de no-muertos.' WHERE `entry`=17203;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nunca dije que tuvieras que redimirte, $Gsacerdote:sacerdotisa;. Estás aquí para redimirnos a mí y a los inocentes asesinados mientras tratábamos de escapar de Stratholme.$B$BMi espíritu y el de aquellos que fallecieron, están atados a este lugar, condenados a revivir eternamente nuestras trágicas muertes.' WHERE `entry`=17204;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Quién eres?',`Text0_1_loc6`='¿Quién eres?',`Text1_0_loc6`='¡Sal de aquí, antes de que te vean los esclavistas!',`Text1_1_loc6`='¡Sal de aquí, antes de que te vean los esclavistas!' WHERE `entry`=17235;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='¿Has venido... a salvarme?',`Text2_1_loc6`='¿Has venido... a salvarme?' WHERE `entry`=17235;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Bienvenido al Puesto del Torio. Como supervisor, soy responsable de todas las actividades de la Hermandad del Torio. Aunque tales actividades suelen ser la minería, la herrería y algún que otro turno de guardia, últimamente nos hemos vuelto una organización más marcial... Por necesidad.' WHERE `entry`=17268;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Derak Ocaso, a tu servicio!' WHERE `entry`=17511;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te doy la bienvenida a Aldea Estrella Fugaz, $Gviajero:viajera;. ¿Has venido a ayudarnos?' WHERE `entry`=17586;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Nos alegramos de contar con tu ayuda.' WHERE `entry`=17772;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Qué?',`Text0_1_loc6`='¿Qué?' WHERE `entry`=17804;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Solo los más valientes y más honorables de la tribu tienen el honor de ser enterrados en la Roca Roja.' WHERE `entry`=17838;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='La vida de un cazador de tesoros es traicionera. No te engañes.' WHERE `entry`=17861;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Con eso de que las máquinas de asedio están casi terminadas, estoy empezando a aburrirme.$B$BNo te dedicarás a la ingeniería, ¿verdad? ¿Te apetece que comparemos planos...?' WHERE `entry`=18070;
UPDATE `locales_npc_text` SET `Text2_0_loc6`='No puedo seguir los trazadores para salir, hay demasiadas bestias de carne por el camino. Envía a alguien dentro... Seguid los trazadores... Ya-six, corto.' WHERE `entry`=20551;
UPDATE `locales_npc_text` SET `Text1_1_loc6`='Ameer, cambio y corto.' WHERE `entry`=20564;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Aquí fue donde Gul\'dan cortó el vínculo entre los orcos y los espíritus elementales. Su insaciable sed de poder no se conformaba con la aniquilación de los draenei. Tenía que destruir Draenor; arrasar la tierra y absorber su energía para usarla en la guerra.$B$BAhora todo lo que queda son jirones de su locura.$B$BMira el altar, $R. Esta tierra quedará marcada para siempre...' WHERE `entry`=21024;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tened cuidado, amigos. El Traidor medita en el patio de ahí al lado' WHERE `entry`=22990;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿$GSastre:Sastra;, eh? Muy bien. ¿Sobre qué te gustaría aprender, $Gsastre:sastra;?' WHERE `entry`=24401;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¿Eso es todo lo que saben hacer?$B$B<Omarion reúne fuerzas para reírse.>$B$BNo tenéis lo que hay que tener para enfrentaros a un ser tan monstruoso como Kel\'Thuzad. Caeréis antes sus esbirros, al igual que los que vinieron antes que vosotros...$B$BY ahora déjame morir. Me niego a regresar a un mundo que defiende a los de tu calaña.' WHERE `entry`=24404;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Pues claro que no tengo el fragmento. Sería de necios. ¡Estoy rodeado de salvajes en esta condenada tierra! No puedo dar dos pasos sin que un orco o un humano enloquecido intenten clavarme una estaca. Podrías perfectamente estar hablando con un fantasma.$B$BDe cualquier manera... Lo escondí.$B$BVenga... pregúntame dónde.' WHERE `entry`=25005;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Te va a encantar esta historia, $R. Escucha. Tan pronto como Malygos me encargó la protección del fragmento de cetro, supe que iba a ser la causa de un eterno sufrimiento y preocupación.$B$BYo lo único que quiero es estudiar estas costas mágicas y recoger los artefactos que puedan quedar del Cataclismo. ¿Sabes? ¡En su día hubo aquí una próspera civilización de los Altonato! No hace falta que me digas que me voy a encontrar con algunos objetos mágicos por aquí, $R. ¡Ya lo intuyo!' WHERE `entry`=25006;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Como iba diciendo, me aferré a ese fragmento de cetro durante unos quinientos años y solamente me trajo problemas. Llamaba la atención de todos, muy a mi pesar. Quiero estar tranquilo para poder investigar en lugar de tener cuenta del pasaporte hacia la gloria de alguien. Así pues... Se lo di a un pez.' WHERE `entry`=25007;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='No a un pez cualquiera, mortal. Se trataba de una sardinilla. Una sardinilla muy especial.' WHERE `entry`=25008;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Exacto!' WHERE `entry`=25009;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Qué genio, ya lo sé... Nunca nadie la encontrará pero, en caso de que así sea, le concedí a la sardinilla algunos poderes especiales.' WHERE `entry`=25010;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Los genios somos unos incomprendidos, $R. Entonces, en cuanto al fragmento de cetro... Si quieres ese fragmento pero no estás dispuesto a pasarte los próximos diez mil años buscándolo, tendrás que escuchar con atención.' WHERE `entry`=25012;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Tres palabras: boya de arcanita.' WHERE `entry`=25013;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Si no lo supiera pensaría que te estabas burlando de mí, mortal. Pero sí, es más o menos así. Aunque puedes dejarte la ropa puesta.' WHERE `entry`=25015;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Llévale este libro a un viejo conocido mío en Tanaris. Su nombre es Narain Sabelotodo. ¡Como físico es malísimo pero es un ingeniero increíble! Él será capaz de darle sentido a todo eso.$B$BNo me des las gracias, $N. Es lo menos que podía hacer.$B$B¡Que tengas un buen día!' WHERE `entry`=25016;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡QUE TENGAS UN BUEN DÍA, HE DICHO!' WHERE `entry`=25017;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='Debes de estar muy mal para vagar por estas Tierras Inhóspitas, $C. Tan mal como yo.$B$BO quizás estás aquí porque estás $Gloco:loca;. Tan $Gloco:loca; como yo.' WHERE `entry`=50010;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Tú ser $Grey:reina; de los Gordok! ¡El número uno!' WHERE `entry`=60000;
UPDATE `locales_npc_text` SET `Text0_0_loc6`='¡Ser jefe ahora, perro!' WHERE `entry`=60001;

SET NAMES 'latin1';
