UPDATE `locales_quest` SET `title_loc6`='Papiro corrupto',`details_loc6`='Ah, mientras estabas fuera llegó un papiro para ti, $N.$B$BSiempre que puedo procuro evitar el trato con brujos, pero creo que deberías leerlo cuando tengas tiempo. Si no me equivoco viene de parte del instructor que te acaba de ser asignado, Nartok. Querrá tener unas palabras contigo cuando estés $Glisto:lista;.',`objectives_loc6`='Lee el papiro corrupto y habla con Nartok en el Valle de los Retos.' WHERE `entry`=3090;
