UPDATE `locales_quest` SET `title_loc6`='Pergamino simple',`details_loc6`='Oye, mientras estabas por ahí ocupándote de la Plaga esa, llegó este pergamino para ti. Debe de ser importante porque parece que lleva el sello del instructor de guerreros Dannal. Yo me tomaría un momento para leerlo antes de ponerme en marcha otra vez.',`objectives_loc6`='Lee el pergamino simple, luego ve a Camposanto y habla con Dannal Stern.' WHERE `entry`=3095;
