UPDATE `locales_quest` SET `title_loc6`='Carta simple',`details_loc6`='$N, me han pedido que te diera esto en cuanto terminaras de encargarte de los espías Roca Negra. Parece una carta sellada con la insignia de Llane, nuestro instructor de guerreros. Si fuera tú, la leería antes de hacer cualquier otra cosa en la Abadía.',`objectives_loc6`='Lee la carta simple y habla con Llane Beshere en la Abadía de Villanorte.' WHERE `entry`=3100;
