UPDATE `locales_quest` SET `title_loc6`='Runa simple',`details_loc6`='$N, me fue confiada esta runa para que te la entregara. Cuando tengas la oportunidad, léela. Creo que es de Thran, el instructor de guerreros. Échale un vistazo y, cuando puedas, ve a buscar a Thran a Yunquemar.',`objectives_loc6`='Lee la runa simple, luego ve al Valle de Crestanevada y habla con Thran Khorman.' WHERE `entry`=3106;
