UPDATE `locales_quest` SET `title_loc6`='Memorándum glífico',`details_loc6`='Mientras me estabas ayudando, me fue confiado este memorándum para que te lo entregara. Cuando tengas la oportunidad, léelo. Creo que es de Marryk, el instructor de magos. Échale un vistazo y, cuando puedas, ve a buscar a Marryk a Yunquemar.',`objectives_loc6`='Lee el memorándum glífico, luego ve a hablar con Marryk Nurribit, dentro de Yunquemar, sobre el Valle de Crestanevada.' WHERE `entry`=3114;
UPDATE `locales_quest` SET `title_loc6`='Runa corrupta',`details_loc6`='Mientras me estabas ayudando, me fue confiado este memorándum para que te lo entregara. Cuando tengas la oportunidad, léelo. Creo que es de Saripal, el instructor de brujos. Échale un vistazo y, cuando puedas, ve a buscar a Saripal a Yunquemar.$B$BY ten cuidado, $N: los de tu clase no son bien recibidos por estos lares.',`objectives_loc6`='Lee la runa corrupta, luego ve a hablar con Saripal Cervezardiente que está en Yunquemar, sobre el Valle de Crestanevada.' WHERE `entry`=3115;
UPDATE `locales_quest` SET `title_loc6`='Sigilo simple',`details_loc6`='Un mensajero de nuestra instructora de guerreros, Alyissia, ha traído este sigilo. Parece que quiere hablar contigo. Léelo y luego llévaselo.',`objectives_loc6`='Habla con Alyissia dentro de Aldrassil, en Cañada Umbría.' WHERE `entry`=3116;
