UPDATE `locales_quest` SET `title_loc6`='Atrapado',`details_loc6`='¡Eh! ¡Oye, tú! ¡Ven aquí!$B$BAyúdame. Me escondí aquí para huir de los Hierro Negro. ¡Malditos sean los geólogos y sus trucos mágicos! Supongo que me vieron entrar y bloquearon la puerta.$B$B¡Dales una lección! Ah, y tráeme unos cuantos paños de seda para... Esto, para... Para nada.',`objectives_loc6`='Mata a 8 geólogos Hierro Negro y lleva 15 paños de seda a la persona encerrada en las letrinas de La Garganta de Fuego.' WHERE `entry`=4449;
UPDATE `locales_quest` SET `title_loc6`='El libro de contabilidad de Tanaris',`details_loc6`='Ah, ahora que me acuerdo:$B$B¿quieres acabar un trabajito que acepté en Tanaris? Es fácil...$B$BKrinkle Buenacero, de Gadgetzan, anda buscando unos objetos en La Garganta de Fuego y otros sitios. ¿Qué tal si te encargas tú?$B$BSi te interesa, te pasaré su libro de contabilidad por debajo de la puerta. Dáselo a Buenacero cuando vayas a llevarle los objetos.',`objectives_loc6`='Coge el libro de contabilidad de Buenacero y busca los objetos de la lista antes de ir a verlo a Tanaris.' WHERE `entry`=4450;
UPDATE `locales_quest` SET `title_loc6`='La llave de la libertad',`details_loc6`='Esta pequeña llave de latón es muy sencilla. Lo único que destaca es un grabado:$B$B"Letrina Hierro Negro. No hacer copia."$B$BNo tienes ni idea de qué significa o qué abre.',`objectives_loc6`='Busca en La Garganta de Fuego para ver cómo se usa la llave de la letrina de Limugre.' WHERE `entry`=4451;
UPDATE `locales_quest` SET `title_loc6`='Blancoria corrupta' WHERE `entry`=4461;
